// =====================================================
// STOCK ENTRY Client Script (FULL)  STABLE + SUBMIT GUARD + REUSE SPECIAL
//
//  Warehouses set correctly on Type change
//  No warehouse reset on Save / Refresh
//  Default Target Warehouse will NOT go blank after Save
//  Parent + row warehouse dropdown restrictions (MFG Transfer / Rejected from Assembly)
//  PCB-only types (single PA01 row / remove other items)
//  PCB scan table + qty sync
//  Submit Guard checklist (item_code/qty/wh/availability)
//  Enforce Default WH == Row WH (with ONE exception: Reuse + PCB row)
//  Reuse rule:
//    - Non-PCB items: Target = INCOMING-QC - R
//    - PCB item (PA01 0001): Target = PTS Testing - R
// =====================================================

(() => {
  if (window.__SE_FULL_SCRIPT_LOADED__) return;
  window.__SE_FULL_SCRIPT_LOADED__ = true;

  // =====================================================
  // CONFIG
  // =====================================================

  const FIELD_FROM = "from_warehouse";
  const FIELD_TO   = "to_warehouse";
  const FIELD_TYPE = "stock_entry_type";

  const ITEMS_TABLE = "items";
  const ROW_S_WH = "s_warehouse";
  const ROW_T_WH = "t_warehouse";
  const SE_ITEM_QTY_FIELD = "qty";

  // ---- PCB Item ----
  const PCB_ITEM_CODE = "PA01 0001";
  const PCB_ITEM_LABEL = "PA01 0001";

  // ---- Stock Entry Types (must match EXACTLY) ----
  const TYPE_IQC_ACCEPT   = "IQC-Accept";
  const TYPE_IQC_REJECT   = "IQC-Reject";
  const TYPE_PTS_TRANSFER = "Transfereing PCB to PTS testing";
  const TYPE_PCB_ACCEPT   = "PCB-IQC-Accept";
  const TYPE_PCB_REJECT   = "PCB-IQC-Reject";
  const TYPE_MFG_TRANSFER = "Material Transfer for Manufacture";
  const TYPE_REJ_ASM      = "Rejected from Assembly";
  const TYPE_REUSE        = "Reuse";

  // ---- Stock Entry custom PCB fields ----
  const PCB_SECTION_FIELD = "custom_pcb_entry_section";
  const PCB_SCAN_FIELD    = "custom_pcb_scan";
  const PCB_TABLE_FIELD   = "custom_pcb";

  // ---- PCB child table field ----
  const PCB_SERIAL_FIELD  = "pcb_serial_no";

  // ---- Warehouses ----
  const WH_INCOMING = "INCOMING-QC - R";
  const WH_STORE    = "Stores - R";
  const WH_REJECTED = "Rejected items - R";
  const WH_PTS      = "PTS Testing - R";

  // Restrict targets for Manufacture transfer
  const ALLOWED_ASSEMBLY_TARGETS = [
    "Assembly1 - R",
    "Assembly2 - R",
    "Assembly3 - R",
    "Packing - R",
  ];

  // Restrict sources for Rejected from Assembly
  const ALLOWED_ASSEMBLY_SOURCES = [
    "Assembly1 - R",
    "Assembly2 - R",
    "Assembly3 - R",
    "Packing - R",
  ];

  // ---- Warehouse mapping (parent defaults) ----
  // NOTE: Reuse parent target stays Incoming QC. PCB row target is forced to PTS.
  const MAP = {
    [TYPE_IQC_ACCEPT]:   { from: WH_INCOMING, to: WH_STORE },
    [TYPE_IQC_REJECT]:   { from: WH_INCOMING, to: WH_REJECTED },

    [TYPE_PTS_TRANSFER]: { from: WH_INCOMING, to: WH_PTS },

    [TYPE_PCB_ACCEPT]:   { from: WH_PTS,      to: WH_STORE },
    [TYPE_PCB_REJECT]:   { from: WH_PTS,      to: WH_REJECTED },

    // Manufacture transfer: force Source, operator picks Target
    [TYPE_MFG_TRANSFER]: { from: WH_STORE,    to: null },

    // Rejected from Assembly: operator picks source among 4, target fixed
    [TYPE_REJ_ASM]:      { from: null,        to: WH_REJECTED },

    // Reuse
    [TYPE_REUSE]:        { from: WH_REJECTED, to: WH_INCOMING },
  };

  // ---- Type Rules ----
  const TYPES_BLOCK_PCB_ITEM = new Set([TYPE_IQC_ACCEPT, TYPE_IQC_REJECT]);
  const TYPES_ONLY_PCB_ITEM  = new Set([TYPE_PTS_TRANSFER, TYPE_PCB_ACCEPT, TYPE_PCB_REJECT]);

  const TYPES_PCB_SCAN_ALLOWED = new Set([
    TYPE_PTS_TRANSFER,
    TYPE_PCB_ACCEPT,
    TYPE_PCB_REJECT,
    TYPE_MFG_TRANSFER,
    TYPE_REJ_ASM,
    TYPE_REUSE,
  ]);

  // =====================================================
  // HELPERS (no regex, no escapes)
  // =====================================================

  function strip_ws_upper(s) {
    const x = (s || "").toString();
    let out = "";
    for (let i = 0; i < x.length; i++) {
      const c = x[i];
      const code = x.charCodeAt(i);
      const is_ws = (code <= 32); // space/tab/newline etc
      if (!is_ws) out += c;
    }
    return out.toUpperCase();
  }

  function clean_serial(s) {
    const x = (s || "").toString();
    let out = "";
    for (let i = 0; i < x.length; i++) {
      const code = x.charCodeAt(i);
      const is_ws = (code <= 32);
      if (!is_ws) out += x[i];
    }
    return out.trim();
  }

  function is_pcb_item_code(item_code) {
    return strip_ws_upper(item_code) === strip_ws_upper(PCB_ITEM_CODE);
  }

  function get_type(frm) {
    return (frm.doc[FIELD_TYPE] || "").trim();
  }

  function show_msg(title, message, indicator = "orange") {
    frappe.show_alert({ message: title, indicator }, 4);
    frappe.msgprint({ title, indicator, message });
  }

  function has_pcb_item(frm) {
    return (frm.doc[ITEMS_TABLE] || []).some(r => is_pcb_item_code(r.item_code));
  }

  function find_pcb_item_rows(frm) {
    return (frm.doc[ITEMS_TABLE] || []).filter(r => is_pcb_item_code(r.item_code));
  }

  function force_toggle(frm, fieldname, show) {
    const f = frm.get_field(fieldname);
    if (f && f.wrapper) $(f.wrapper).toggle(!!show);
    frm.set_df_property(fieldname, "hidden", show ? 0 : 1);
    frm.refresh_field(fieldname);
  }

  function apply_row_defaults_if_blank(frm, fromWh, toWh) {
    (frm.doc[ITEMS_TABLE] || []).forEach(r => {
      if (!r) return;
      if (fromWh && !r[ROW_S_WH]) frappe.model.set_value(r.doctype, r.name, ROW_S_WH, fromWh);
      if (toWh && !r[ROW_T_WH])   frappe.model.set_value(r.doctype, r.name, ROW_T_WH, toWh);
    });
  }

  // Reuse special: PCB row -> PTS, others -> Incoming
  function apply_reuse_row_targets(frm) {
    if (get_type(frm) !== TYPE_REUSE) return;

    (frm.doc[ITEMS_TABLE] || []).forEach(r => {
      if (!r || !r.item_code) return;

      const desired_to = is_pcb_item_code(r.item_code) ? WH_PTS : WH_INCOMING;
      if (r[ROW_T_WH] !== desired_to) {
        frappe.model.set_value(r.doctype, r.name, ROW_T_WH, desired_to);
      }

      if (frm.doc[FIELD_FROM] && r[ROW_S_WH] !== frm.doc[FIELD_FROM]) {
        frappe.model.set_value(r.doctype, r.name, ROW_S_WH, frm.doc[FIELD_FROM]);
      }
    });
  }

  // =====================================================
  // QUERIES (filters only)
  // =====================================================

  function apply_queries(frm, fromWh, toWh, typeName) {
    frm.set_query(FIELD_FROM, () => {
      if (fromWh) return { filters: { name: fromWh } };
      if (typeName === TYPE_REJ_ASM) return { filters: { name: ["in", ALLOWED_ASSEMBLY_SOURCES] } };
      return {};
    });

    frm.set_query(FIELD_TO, () => {
      if (toWh) return { filters: { name: toWh } };
      if (typeName === TYPE_MFG_TRANSFER) return { filters: { name: ["in", ALLOWED_ASSEMBLY_TARGETS] } };
      return {};
    });

    frm.set_query(ROW_T_WH, ITEMS_TABLE, () => {
      if (typeName === TYPE_MFG_TRANSFER) return { filters: { name: ["in", ALLOWED_ASSEMBLY_TARGETS] } };
      return {};
    });

    frm.set_query(ROW_S_WH, ITEMS_TABLE, () => {
      if (typeName === TYPE_REJ_ASM) return { filters: { name: ["in", ALLOWED_ASSEMBLY_SOURCES] } };
      return {};
    });

    const itemsField = frm.fields_dict && frm.fields_dict[ITEMS_TABLE];
    if (itemsField && itemsField.grid) {
      const grid = itemsField.grid;

      const tw = grid.get_field(ROW_T_WH);
      if (tw) {
        tw.get_query = () => {
          if (typeName === TYPE_MFG_TRANSFER) return { filters: { name: ["in", ALLOWED_ASSEMBLY_TARGETS] } };
          return {};
        };
      }

      const sw = grid.get_field(ROW_S_WH);
      if (sw) {
        sw.get_query = () => {
          if (typeName === TYPE_REJ_ASM) return { filters: { name: ["in", ALLOWED_ASSEMBLY_SOURCES] } };
          return {};
        };
      }
    }
  }

  // =====================================================
  // PCB-only enforcement
  // =====================================================

  function force_single_pa01_row(frm) {
    frm.clear_table(ITEMS_TABLE);

    const r = frm.add_child(ITEMS_TABLE);
    frappe.model.set_value(r.doctype, r.name, "item_code", PCB_ITEM_CODE);
    frappe.model.set_value(r.doctype, r.name, "qty", 0);

    if (frm.doc[FIELD_FROM]) frappe.model.set_value(r.doctype, r.name, ROW_S_WH, frm.doc[FIELD_FROM]);
    if (frm.doc[FIELD_TO])   frappe.model.set_value(r.doctype, r.name, ROW_T_WH, frm.doc[FIELD_TO]);

    frm.refresh_field(ITEMS_TABLE);
  }

  function ensure_pcb_only_item_row(frm) {
    const typeName = get_type(frm);

    if (typeName === TYPE_PCB_ACCEPT || typeName === TYPE_PCB_REJECT) {
      force_single_pa01_row(frm);
      return;
    }

    if (typeName === TYPE_PTS_TRANSFER) {
      const rows = (frm.doc[ITEMS_TABLE] || []);
      const nonPcbRows = rows.filter(r => r.item_code && !is_pcb_item_code(r.item_code));

      if (nonPcbRows.length) {
        nonPcbRows.forEach(r => frappe.model.clear_doc(r.doctype, r.name));
        frm.refresh_field(ITEMS_TABLE);
        show_msg(
          "Only PA01 allowed",
          `For <b>${TYPE_PTS_TRANSFER}</b>, only <b>${PCB_ITEM_LABEL}</b> is allowed. Other items were removed.`,
          "red"
        );
      }

      const pcbRows = (frm.doc[ITEMS_TABLE] || []).filter(r => is_pcb_item_code(r.item_code));
      if (pcbRows.length > 1) {
        pcbRows.slice(1).forEach(r => frappe.model.clear_doc(r.doctype, r.name));
        frm.refresh_field(ITEMS_TABLE);
        show_msg(
          "Only one PA01 row allowed",
          `For <b>${TYPE_PTS_TRANSFER}</b>, keep only one row of <b>${PCB_ITEM_LABEL}</b>. Extra rows were removed.`,
          "red"
        );
      }
    }
  }

  // =====================================================
  // Apply defaults (only on new doc / type change)
  // =====================================================

  function apply_type_defaults(frm, { force = false } = {}) {
    const typeName = get_type(frm);
    const cfg = MAP[typeName];

    apply_queries(frm, cfg?.from || null, cfg?.to || null, typeName);

    if (!cfg) {
      ensure_pcb_only_item_row(frm);
      return;
    }

    if (!force && frm.__se_last_type_applied === typeName) {
      ensure_pcb_only_item_row(frm);
      return;
    }
    frm.__se_last_type_applied = typeName;

    // IMPORTANT FIX: on type change we set correct warehouses
    if (force) {
      if (cfg.from) frm.set_value(FIELD_FROM, cfg.from);
      else frm.set_value(FIELD_FROM, "");

      if (cfg.to) frm.set_value(FIELD_TO, cfg.to);
      else frm.set_value(FIELD_TO, "");
    } else {
      if (cfg.from && !frm.doc[FIELD_FROM]) frm.set_value(FIELD_FROM, cfg.from);
      if (cfg.to && !frm.doc[FIELD_TO])     frm.set_value(FIELD_TO, cfg.to);
    }

    apply_row_defaults_if_blank(frm, cfg.from, cfg.to);
    apply_reuse_row_targets(frm);
    ensure_pcb_only_item_row(frm);
  }

  // =====================================================
  // PCB UI + QTY sync
  // =====================================================

  function clear_pcb_block(frm) {
    if (frm.fields_dict[PCB_SCAN_FIELD]) frm.set_value(PCB_SCAN_FIELD, "");
    if (frm.fields_dict[PCB_TABLE_FIELD]) {
      frm.clear_table(PCB_TABLE_FIELD);
      frm.refresh_field(PCB_TABLE_FIELD);
    }
    sync_pcb_qty_to_item(frm);
  }

  function is_pcb_section_allowed(frm) {
    return TYPES_PCB_SCAN_ALLOWED.has(get_type(frm));
  }

  function toggle_pcb_ui(frm) {
    const show = is_pcb_section_allowed(frm) && has_pcb_item(frm);

    if (PCB_SECTION_FIELD) force_toggle(frm, PCB_SECTION_FIELD, show);
    if (PCB_SCAN_FIELD)    force_toggle(frm, PCB_SCAN_FIELD, show);
    if (PCB_TABLE_FIELD)   force_toggle(frm, PCB_TABLE_FIELD, show);

    if (!show) clear_pcb_block(frm);
  }

  function toggle_pcb_ui_late(frm) {
    setTimeout(() => toggle_pcb_ui(frm), 50);
    setTimeout(() => toggle_pcb_ui(frm), 250);
    setTimeout(() => toggle_pcb_ui(frm), 800);
  }

  function focus_scan(frm) {
    setTimeout(() => {
      const f = frm.get_field(PCB_SCAN_FIELD);
      if (f && f.$input) {
        f.$input.focus();
        f.$input.select();
      }
    }, 80);
  }

  function get_existing_serials(frm) {
    const seen = new Set();
    (frm.doc[PCB_TABLE_FIELD] || []).forEach(r => {
      const v = (r[PCB_SERIAL_FIELD] || "").toString().trim();
      if (v) seen.add(v);
    });
    return seen;
  }

  function count_pcb_rows(frm) {
    let n = 0;
    (frm.doc[PCB_TABLE_FIELD] || []).forEach(r => {
      const v = (r[PCB_SERIAL_FIELD] || "").toString().trim();
      if (v) n += 1;
    });
    return n;
  }

  function sync_pcb_qty_to_item(frm) {
    const pcb_count = count_pcb_rows(frm);
    const rows = find_pcb_item_rows(frm);
    if (!rows.length) return;

    const r0 = rows[0];
    const cur = Number(r0[SE_ITEM_QTY_FIELD] || 0);

    if (cur !== pcb_count) {
      frm.__pcb_qty_syncing = true;
      frappe.model
        .set_value(r0.doctype, r0.name, SE_ITEM_QTY_FIELD, pcb_count)
        .finally(() => (frm.__pcb_qty_syncing = false));
    }
  }

  // =====================================================
  // Item rules
  // =====================================================

  function enforce_item_rules(frm, cdt, cdn) {
    const typeName = get_type(frm);
    const row = locals[cdt][cdn];

    if (TYPES_BLOCK_PCB_ITEM.has(typeName) && is_pcb_item_code(row.item_code)) {
      frappe.model.set_value(cdt, cdn, "item_code", "");
      frappe.model.set_value(cdt, cdn, "qty", 0);
      show_msg(
        "PCB item not allowed in this type",
        `You selected <b>${typeName}</b>.<br><br>For PCB, use <b>${TYPE_PCB_ACCEPT}</b> or <b>${TYPE_PCB_REJECT}</b>.`,
        "red"
      );
      toggle_pcb_ui_late(frm);
      return;
    }

    if (TYPES_ONLY_PCB_ITEM.has(typeName)) {
      if (row.item_code && !is_pcb_item_code(row.item_code)) {
        frappe.model.set_value(cdt, cdn, "item_code", "");
        frappe.model.set_value(cdt, cdn, "qty", 0);
        show_msg(
          "Only PA01 allowed",
          `For <b>${typeName}</b>, only <b>${PCB_ITEM_LABEL}</b> is allowed in Items.`,
          "red"
        );
        toggle_pcb_ui_late(frm);
        return;
      }

      if (is_pcb_item_code(row.item_code)) {
        const all_pcb_rows = find_pcb_item_rows(frm);
        if (all_pcb_rows.length > 1) {
          frappe.model.set_value(cdt, cdn, "item_code", "");
          frappe.model.set_value(cdt, cdn, "qty", 0);
          show_msg(
            "Only one PA01 row allowed",
            `For <b>${typeName}</b>, keep only one row of <b>${PCB_ITEM_LABEL}</b>.`,
            "red"
          );
          toggle_pcb_ui_late(frm);
          return;
        }
      }
    }

    if (typeName === TYPE_REUSE) apply_reuse_row_targets(frm);

    toggle_pcb_ui_late(frm);
  }

  // =====================================================
  // PCB scan -> add row
  // =====================================================

  async function validate_serial_for_type(frm, serial) {
    // TEMP: allow all serials for now (server checks will be added later)
    return { ok: true };
  }

  async function add_pcb_serial(frm, raw) {
    const serial = clean_serial(raw);
    if (!serial) return;

    const typeName = get_type(frm);

    if (!is_pcb_section_allowed(frm)) {
      frm.set_value(PCB_SCAN_FIELD, "");
      show_msg("PCB scanning not allowed", `Current type: <b>${typeName || "(blank)"}</b>.`, "red");
      return;
    }

    if (!has_pcb_item(frm)) {
      frm.set_value(PCB_SCAN_FIELD, "");
      show_msg("PA01 missing", `Add <b>${PCB_ITEM_LABEL}</b> in Items before scanning.`, "red");
      return;
    }

    const seen = get_existing_serials(frm);
    if (seen.has(serial)) {
      frappe.show_alert({ message: `Duplicate: ${serial}`, indicator: "orange" }, 2);
      frm.set_value(PCB_SCAN_FIELD, "");
      focus_scan(frm);
      return;
    }

    if (frm.__pcb_scan_busy) {
      frappe.show_alert({ message: "Please wait", indicator: "blue" }, 1);
      return;
    }

    frm.__pcb_scan_busy = true;

    const v = await validate_serial_for_type(frm, serial);
    if (!v.ok) {
      frm.__pcb_scan_busy = false;
      frm.set_value(PCB_SCAN_FIELD, "");
      show_msg(v.msg_title || "Blocked", v.msg_body || "Not allowed", "red");
      focus_scan(frm);
      return;
    }

    const row = frm.add_child(PCB_TABLE_FIELD);

    // Lookup PCB IQC Line data
    try {
      const iqc_data = await frappe.call({
        method: "erpnext.api.pcb.get_pcb_iqc_data",
        args: { serial_no: serial }
      });
    
      if (iqc_data && iqc_data.message) {
        const data = iqc_data.message;
        // Populate row with fetched data
        if (data.overall_status) row.overall_status = data.overall_status;
        if (data.pts1_status) row.pts1_status = data.pts1_status;
        if (data.pts2_status) row.pts2_status = data.pts2_status;
        if (data.pts3_status) row.pts3_status = data.pts3_status;
        if (data.iqc_date) row.iqc_date = data.iqc_date;
        if (data.dominant_channel) row.dominant_channel = data.dominant_channel;
        if (data.max_value !== undefined) row.max_value = data.max_value;
        if (data.item_code) row.item_code = data.item_code;
      }
    } catch (err) {
      // If lookup fails, use defaults (Pending)
      console.log("PCB IQC Line lookup failed:", err);
    }

    // Set serial number (always set, even if lookup failed)
    row[PCB_SERIAL_FIELD] = serial;

    frm.refresh_field(PCB_TABLE_FIELD);
    frm.set_value(PCB_SCAN_FIELD, "");
    sync_pcb_qty_to_item(frm);
    focus_scan(frm);

    frm.__pcb_scan_busy = false;
  }

  function bind_scan_listener(frm) {
    const f = frm.get_field(PCB_SCAN_FIELD);
    if (!f || !f.$input) return;

    if (f.$input.data("pcb_scan_bound")) return;
    f.$input.data("pcb_scan_bound", true);

    f.$input.on("keyup", (e) => {
      if (e.key === "Enter") {
        e.preventDefault();
        setTimeout(async () => {
          const raw = f.$input.val() || "";
          await add_pcb_serial(frm, raw);
        }, 30);
      }
    });

    f.$input.on("change", () => {
      setTimeout(async () => {
        const raw = f.$input.val() || "";
        await add_pcb_serial(frm, raw);
      }, 30);
    });
  }

  // =====================================================
  // Submit guard
  // =====================================================

  function block_submit_with_checklist(frm, issues) {
    frappe.validated = false;

    const msg = `
      <div style="font-size:13px;">
        <p><b>Cannot Submit.</b> Please fix the below and try again:</p>
        <ol>
          ${issues.map(x => `<li>${x}</li>`).join("")}
        </ol>
        <p style="margin-top:8px;">Tip: Click <b>Update Rate and Availability</b> before submitting.</p>
      </div>
    `;

    frappe.msgprint({
      title: "Fix before Submit",
      indicator: "red",
      message: msg,
    });
  }

  function get_actual_qty(row) {
    const v = row.actual_qty;
    if (v === null || v === undefined || v === "") return null;
    const n = Number(v);
    return Number.isFinite(n) ? n : null;
  }

  function validate_before_submit(frm) {
    const typeName = get_type(frm);
    const issues = [];

    const items = frm.doc.items || [];
    if (!items.length) issues.push("Add at least one item in <b>Items</b> table.");

    if (typeName === TYPE_MFG_TRANSFER) {
      if (!frm.doc.from_warehouse) issues.push("Select <b>Default Source Warehouse</b>.");
      if (!frm.doc.to_warehouse) issues.push("Select <b>Default Target Warehouse</b> (Assembly/Packing).");
      if (frm.doc.to_warehouse && !ALLOWED_ASSEMBLY_TARGETS.includes(frm.doc.to_warehouse)) {
        issues.push(`Target Warehouse must be one of: <b>${ALLOWED_ASSEMBLY_TARGETS.join(", ")}</b>.`);
      }
    }

    if (typeName === TYPE_REJ_ASM) {
      if (!frm.doc.from_warehouse) issues.push("Select <b>Default Source Warehouse</b> (Assembly/Packing).");
      if (frm.doc.from_warehouse && !ALLOWED_ASSEMBLY_SOURCES.includes(frm.doc.from_warehouse)) {
        issues.push(`Source Warehouse must be one of: <b>${ALLOWED_ASSEMBLY_SOURCES.join(", ")}</b>.`);
      }
      if (!frm.doc.to_warehouse) issues.push("Target should be <b>Rejected items - R</b>.");
    }

    items.forEach((r, idx) => {
      const rowNo = idx + 1;

      if (!r.item_code) issues.push(`Row ${rowNo}: <b>Item Code</b> is missing.`);
      const qty = Number(r.qty || 0);
      if (!qty || qty <= 0) issues.push(`Row ${rowNo}: <b>Qty</b> must be greater than 0.`);

      if (!r.s_warehouse) issues.push(`Row ${rowNo}: <b>Source Warehouse</b> is missing.`);
      if (!r.t_warehouse && typeName !== TYPE_MFG_TRANSFER) issues.push(`Row ${rowNo}: <b>Target Warehouse</b> is missing.`);
      if (typeName === TYPE_MFG_TRANSFER && !r.t_warehouse) issues.push(`Row ${rowNo}: <b>Target Warehouse</b> is missing (Assembly/Packing).`);

      if (typeName === TYPE_MFG_TRANSFER && r.t_warehouse && !ALLOWED_ASSEMBLY_TARGETS.includes(r.t_warehouse)) {
        issues.push(`Row ${rowNo}: Target must be one of <b>${ALLOWED_ASSEMBLY_TARGETS.join(", ")}</b>.`);
      }
      if (typeName === TYPE_REJ_ASM && r.s_warehouse && !ALLOWED_ASSEMBLY_SOURCES.includes(r.s_warehouse)) {
        issues.push(`Row ${rowNo}: Source must be one of <b>${ALLOWED_ASSEMBLY_SOURCES.join(", ")}</b>.`);
      }

      const actual = get_actual_qty(r);
      if (r.s_warehouse && qty > 0) {
        if (actual === null) {
          issues.push(`Row ${rowNo}: Click <b>Update Rate and Availability</b> (Actual Qty is blank).`);
        } else if (qty > actual) {
          issues.push(`Row ${rowNo}: Not enough stock in <b>${r.s_warehouse}</b> (Requested ${qty}, Available ${actual}).`);
        }
      }
    });

    if (issues.length) {
      block_submit_with_checklist(frm, issues);
      return false;
    }

    return true;
  }

  // =====================================================
  // EVENTS
  // =====================================================

  frappe.ui.form.on("Stock Entry", {
    validate(frm) {
      const typeName = get_type(frm);

      // safety
      apply_reuse_row_targets(frm);

      const defFrom = frm.doc.from_warehouse;
      const defTo   = frm.doc.to_warehouse;
      if (!defFrom || !defTo) return;

      let errors = [];

      (frm.doc.items || []).forEach((row, i) => {
        const rowNo = i + 1;

        if (row.s_warehouse && row.s_warehouse !== defFrom) {
          errors.push(`Row ${rowNo}: Source <b>${row.s_warehouse}</b> does not match Default Source <b>${defFrom}</b>`);
        }

        if (row.t_warehouse) {
          const allow_mismatch = (
            typeName === TYPE_REUSE &&
            is_pcb_item_code(row.item_code) &&
            defTo === WH_INCOMING &&
            row.t_warehouse === WH_PTS
          );

          if (!allow_mismatch && row.t_warehouse !== defTo) {
            errors.push(`Row ${rowNo}: Target <b>${row.t_warehouse}</b> does not match Default Target <b>${defTo}</b>`);
          }
        }
      });

      if (errors.length) {
        frappe.throw({
          title: "Warehouse Mismatch",
          message: `
            <p>Please fix the following before saving or submitting:</p>
            <ul>
              ${errors.map(e => `<li>${e}</li>`).join("")}
            </ul>
          `,
        });
      }
    },

    setup(frm) {
      if (frm.is_new()) apply_type_defaults(frm, { force: true });
      toggle_pcb_ui_late(frm);
    },

    onload_post_render(frm) {
      if (frm.is_new()) {
        setTimeout(() => apply_type_defaults(frm, { force: true }), 200);
        setTimeout(() => ensure_pcb_only_item_row(frm), 400);
      }
      setTimeout(() => toggle_pcb_ui(frm), 650);
    },

    refresh(frm) {
      toggle_pcb_ui_late(frm);
      bind_scan_listener(frm);
      if (is_pcb_section_allowed(frm) && has_pcb_item(frm)) focus_scan(frm);
      sync_pcb_qty_to_item(frm);
    },

    stock_entry_type(frm) {
      frm.__se_last_type_applied = null;
      apply_type_defaults(frm, { force: true });
      toggle_pcb_ui_late(frm);
    },

    items_add(frm) {
      const typeName = get_type(frm);

      if (typeName === TYPE_PCB_ACCEPT || typeName === TYPE_PCB_REJECT) {
        setTimeout(() => force_single_pa01_row(frm), 50);
        return;
      }
      if (typeName === TYPE_PTS_TRANSFER) {
        setTimeout(() => ensure_pcb_only_item_row(frm), 50);
        return;
      }

      const cfg = MAP[typeName] || { from: null, to: null };
      apply_queries(frm, cfg.from, cfg.to, typeName);
      apply_row_defaults_if_blank(frm, cfg.from, cfg.to);
      apply_reuse_row_targets(frm);
    },

    from_warehouse(frm) {
      const fromWh = frm.doc[FIELD_FROM];
      if (!fromWh) return;
      (frm.doc[ITEMS_TABLE] || []).forEach(r => frappe.model.set_value(r.doctype, r.name, ROW_S_WH, fromWh));
    },

    to_warehouse(frm) {
      const toWh = frm.doc[FIELD_TO];
      if (!toWh) return;

      const typeName = get_type(frm);
      (frm.doc[ITEMS_TABLE] || []).forEach(r => frappe.model.set_value(r.doctype, r.name, ROW_T_WH, toWh));
      if (typeName === TYPE_REUSE) apply_reuse_row_targets(frm);
    },

    before_save(frm) {
      const f = frm.doc[FIELD_FROM];
      const t = frm.doc[FIELD_TO];

      (frm.doc[ITEMS_TABLE] || []).forEach(r => {
        if (f && !r[ROW_S_WH]) frappe.model.set_value(r.doctype, r.name, ROW_S_WH, f);
        if (t && !r[ROW_T_WH]) frappe.model.set_value(r.doctype, r.name, ROW_T_WH, t);
      });

      apply_reuse_row_targets(frm);
    },

    before_submit(frm) {
      return validate_before_submit(frm);
    },
  });

  frappe.ui.form.on("Stock Entry Detail", {
    item_code(frm, cdt, cdn) {
      enforce_item_rules(frm, cdt, cdn);
      sync_pcb_qty_to_item(frm);
    },

    qty(frm, cdt, cdn) {
      if (frm.__pcb_qty_syncing) return;
      const row = locals[cdt][cdn];
      if (is_pcb_item_code(row.item_code)) sync_pcb_qty_to_item(frm);
    },

    t_warehouse(frm, cdt, cdn) {
      const typeName = get_type(frm);
      const row = locals[cdt][cdn];

      if (typeName === TYPE_MFG_TRANSFER && row.t_warehouse) {
        if (!ALLOWED_ASSEMBLY_TARGETS.includes(row.t_warehouse)) {
          const bad = row.t_warehouse;
          frappe.model.set_value(cdt, cdn, "t_warehouse", "");
          show_msg(
            "Invalid Target Warehouse",
            `For <b>${TYPE_MFG_TRANSFER}</b>, Target must be one of:<br><b>${ALLOWED_ASSEMBLY_TARGETS.join(", ")}</b><br><br>Removed: <b>${bad}</b>`,
            "red"
          );
        }
      }

      if (typeName === TYPE_REUSE) {
        // enforce targets
        if (is_pcb_item_code(row.item_code) && row.t_warehouse && row.t_warehouse !== WH_PTS) {
          frappe.model.set_value(cdt, cdn, "t_warehouse", WH_PTS);
          frappe.show_alert({ message: "PCB in Reuse must go to PTS Testing", indicator: "orange" }, 3);
        }
        if (row.item_code && !is_pcb_item_code(row.item_code) && row.t_warehouse && row.t_warehouse !== WH_INCOMING) {
          frappe.model.set_value(cdt, cdn, "t_warehouse", WH_INCOMING);
          frappe.show_alert({ message: "Non-PCB Reuse items must go to Incoming QC", indicator: "orange" }, 3);
        }
      }
    },

    s_warehouse(frm, cdt, cdn) {
      const typeName = get_type(frm);
      const row = locals[cdt][cdn];

      if (typeName === TYPE_REJ_ASM && row.s_warehouse) {
        if (!ALLOWED_ASSEMBLY_SOURCES.includes(row.s_warehouse)) {
          const bad = row.s_warehouse;
          frappe.model.set_value(cdt, cdn, "s_warehouse", "");
          show_msg(
            "Invalid Source Warehouse",
            `For <b>${TYPE_REJ_ASM}</b>, Source must be one of:<br><b>${ALLOWED_ASSEMBLY_SOURCES.join(", ")}</b><br><br>Removed: <b>${bad}</b>`,
            "red"
          );
        }
      }
    },
  });

  frappe.ui.form.on("PCB IQC Line", {
    pcb_serial_no(frm) {
      sync_pcb_qty_to_item(frm);
    },
  });

})();

