    /**
     * Create a device case with transaction (enforces one OPEN case per device)
     */
    suspend fun createDeviceCase(
        deviceId: String,
        caseType: String, // "NEW_BUILD" or "REPAIR_RETURN"
        repairType: String? = null, // "NORMAL_PART" or "PCB_REPAIR" (only for REPAIR_RETURN)
        receivedFromCustomerRef: String? = null,
        returnToCustomerRef: String? = null,
        ownershipTransferred: Boolean = false,
        operatorName: String,
        notes: String = ""
    ): DeviceCase {
        // IMPORTANT: Sync from Supabase first to prevent duplicate cases from multiple devices
        syncBeforeIteration(deviceId)

        return database.withTransaction {
            // Ensure device exists before creating case (FOREIGN KEY constraint)
            var device = getDeviceBySerialNumber(deviceId)
            if (device == null) {
                Log.d(TAG, " Device not found, creating new device: $deviceId")
                // Create device with minimal required fields
                device = Device(
                    serialNumber = deviceId,
                    macId = "", // Will be set when PCB is linked
                    color = "", // Can be updated later
                    createdDate = SerialGenerator.getCurrentTimestamp().split(" ")[0], // Just date part
                    finalStatus = "IN_PROGRESS",
                    totalIterations = 0,
                    totalTimeHours = 0.0,
                    finalOperator = operatorName,
                    firstTestDate = "", // Empty string instead of null
                    finalShipDate = "", // Empty string instead of null
                    needsSync = true,
                    lastSyncTimestamp = 0
                )
                insertDevice(device)
                Log.d(TAG, " Created device: $deviceId")
            }

            // Check for existing OPEN case (after sync, so we have latest data from all devices)
            val existingOpenCase = database.deviceCaseDao().getOpenCaseByDevice(deviceId)
            if (existingOpenCase != null) {
                Log.w(TAG, " Device already has an OPEN case: ${existingOpenCase.caseId} (created by another device or previous session)")
                throw IllegalStateException("Device already has an OPEN case: ${existingOpenCase.caseId}")
            }

            // Create new case
            val newCase = DeviceCase(
                caseId = UUID.randomUUID().toString(),
                deviceId = deviceId,
                caseType = caseType,
                repairType = repairType,
                receivedFromCustomerRef = receivedFromCustomerRef,
                returnToCustomerRef = returnToCustomerRef ?: receivedFromCustomerRef,
                ownershipTransferred = ownershipTransferred,
                status = "OPEN",
                resolution = null,
                openedAt = SerialGenerator.getCurrentTimestamp(),
                closedAt = null,
                operatorName = operatorName,
                notes = notes,
                needsSync = true,
                lastSyncTimestamp = 0
            )
            database.deviceCaseDao().insert(newCase)
            Log.d(TAG, " Created device case: ${newCase.caseId} for device: $deviceId")
            newCase
        }
    }

    /**
     * Replace PCB for a device (deactivate old, create new link)
     */
    suspend fun replacePcb(
        deviceId: String,
        newPcbSerialNumber: String,
        newMacId: String,
        operatorName: String,
        notes: String = ""
    ): DevicePcbLink {
        return database.withTransaction {
            val currentTimestamp = SerialGenerator.getCurrentTimestamp()

            // Deactivate old PCB link
            val oldLink = database.devicePcbLinkDao().getActivePcbLinkByDevice(deviceId)
            oldLink?.let {
                database.devicePcbLinkDao().deactivateActiveLinks(deviceId, currentTimestamp)
                Log.d(TAG, " Deactivated old PCB link: ${it.linkId}")
            }

            // Create/find new PCB
            val newPcb = database.pcbDao().findOrCreatePcb(newPcbSerialNumber, newMacId, notes)

            // Create new PCB link
            val newLink = DevicePcbLink(
                linkId = UUID.randomUUID().toString(),
                deviceId = deviceId,
                pcbId = newPcb.pcbId,
                isActive = true,
                reason = "PCB_REPLACED",
                linkedAt = currentTimestamp,
                unlinkedAt = null,
                operatorName = operatorName,
                notes = notes,
                needsSync = true,
                lastSyncTimestamp = 0
            )
            database.devicePcbLinkDao().insert(newLink)
            Log.d(TAG, " Created new PCB link: ${newLink.linkId} for device: $deviceId")
            newLink
        }
    }

    /**
     * Get or create test configuration
     */
    suspend fun getOrCreateConfiguration(
        deviceId: String,
        caseId: String,
        driverPairingRecordId: Long, // Convert to String
        changedPartsRecordId: Long? = null, // Convert to String
        testStage: String,
        operatorName: String,
        pcbId: String? = null,
        reason: String? = null
    ): TestConfiguration {
        // Convert Long IDs to String
        val driverPairingRecordIdStr = driverPairingRecordId.toString()
        val changedPartsRecordIdStr = changedPartsRecordId?.toString()

        // Get current configuration
        val currentConfig = database.testConfigurationDao().getCurrentConfiguration(deviceId)

        // Check if current config matches (same driver pair, parts, PCB, stage)
        if (currentConfig != null &&
            currentConfig.caseId == caseId &&
            currentConfig.driverPairingRecordId == driverPairingRecordIdStr &&
            currentConfig.changedPartsRecordId == changedPartsRecordIdStr &&
            currentConfig.pcbId == pcbId &&
            currentConfig.testStage == testStage &&
            currentConfig.endedAt == null
        ) {
            Log.d(TAG, " Using existing configuration: ${currentConfig.configurationId}")
            return currentConfig
        }

        // End previous configuration if exists
        currentConfig?.let {
            val timestamp = SerialGenerator.getCurrentTimestamp()
            database.testConfigurationDao().endConfiguration(
                it.configurationId,
                timestamp,
                reason ?: "NEW_CONFIGURATION_CREATED",
                timestamp
            )
            Log.d(TAG, " Ended previous configuration: ${it.configurationId}")
        }

        // Get next iteration number for case + stage
        val nextIteration = database.testConfigurationDao().getNextIterationNumber(caseId, testStage)

        // Create new configuration
        val newConfig = TestConfiguration(
            configurationId = UUID.randomUUID().toString(),
            caseId = caseId,
            deviceId = deviceId,
            pcbId = pcbId,
            driverPairingRecordId = driverPairingRecordIdStr,
            changedPartsRecordId = changedPartsRecordIdStr,
            iterationNumber = nextIteration,
            testStage = testStage,
            latestFrequencyResult = null,
            latestSongResult = null,
            latestFqcResult = null,
            configurationStatus = "IN_PROGRESS",
            createdAt = SerialGenerator.getCurrentTimestamp(),
            lastUpdatedAt = SerialGenerator.getCurrentTimestamp(),
            endedAt = null,
            operatorName = operatorName,
            reason = reason,
            needsSync = true,
            lastSyncTimestamp = 0
        )
        database.testConfigurationDao().insert(newConfig)
        Log.d(TAG, " Created new configuration: ${newConfig.configurationId} for case: $caseId, stage: $testStage, iteration: $nextIteration")
        return newConfig
    }

    /**
     * Update configuration cache (compute from actual tests)
     */
    suspend fun updateConfigurationCache(configurationId: String) {
        val config = database.testConfigurationDao().getById(configurationId)
            ?: return

        // Get latest tests for this configuration
        val latestFreq = database.frequencyTestDao().getLatestByConfiguration(configurationId)
        val latestSong = database.songTestDao().getLatestByConfiguration(configurationId)
        val latestFqc = database.fqcRecordDao().getLatestByConfiguration(configurationId)

        // Update cache fields
        val updatedConfig = config.copy(
            latestFrequencyResult = latestFreq?.result,
            latestSongResult = latestSong?.result,
            latestFqcResult = latestFqc?.overallResult,
            configurationStatus = computeConfigurationStatus(latestFreq, latestSong, latestFqc),
            lastUpdatedAt = SerialGenerator.getCurrentTimestamp()
        )

        database.testConfigurationDao().update(updatedConfig)
        Log.d(TAG, " Updated configuration cache: $configurationId")
    }

    /**
     * Compute configuration status from test results
     */
    private fun computeConfigurationStatus(
        latestFreq: FrequencyTest?,
        latestSong: SongTest?,
        latestFqc: FQCRecord?
    ): String {
        return when {
            latestFqc != null && latestFqc.overallResult == "PASS" -> "CONFIG_PASSED"
            latestFreq != null && latestFreq.result == "FAIL" -> "CONFIG_FAILED"
            latestSong != null && latestSong.result == "FAIL" -> "CONFIG_FAILED"
            latestFreq != null && latestFreq.result == "PASS" &&
                    latestSong != null && latestSong.result == "PASS" -> "ELIGIBLE_FOR_FQC"
            latestFreq != null || latestSong != null -> "IN_PROGRESS"
            else -> "IN_PROGRESS"
        }
    }

    /**
     * End a configuration
     */
    suspend fun endConfiguration(configurationId: String, reason: String) {
        val timestamp = SerialGenerator.getCurrentTimestamp()
        database.testConfigurationDao().endConfiguration(
            configurationId,
            timestamp,
            reason,
            timestamp
        )
        Log.d(TAG, " Ended configuration: $configurationId, reason: $reason")
    }

    /**
     * Get active PCB link for a device (derives MAC ID)
     */
    suspend fun getActivePcbLink(deviceId: String): DevicePcbLink? {
        return database.devicePcbLinkDao().getActivePcbLinkByDevice(deviceId)
    }

    /**
     * Get MAC ID from active PCB link
     */
    suspend fun getMacIdFromActivePcb(deviceId: String): String? {
        val activeLink = getActivePcbLink(deviceId) ?: return null
        val pcb = database.pcbDao().getById(activeLink.pcbId) ?: return null
        return pcb.macId
    }

    /**
     * Helper function to get or create TestConfiguration for a test
     * This implements dual-write logic: creates configuration and links test to it
     */
    private suspend fun getOrCreateConfigurationForTest(
        deviceId: String,
        testStage: String,
        operatorName: String
    ): TestConfiguration {
        // Get or create OPEN case for device (NEW_BUILD if none exists)
        var deviceCase = database.deviceCaseDao().getOpenCaseByDevice(deviceId)
        if (deviceCase == null) {
            // Create NEW_BUILD case if none exists
            deviceCase = createDeviceCase(
                deviceId = deviceId,
                caseType = "NEW_BUILD",
                operatorName = operatorName,
                notes = "Auto-created for test"
            )
            Log.d(TAG, " [DUAL-WRITE] Created NEW_BUILD case: ${deviceCase.caseId}")
        }

        // Get active PCB link
        val activePcbLink = getActivePcbLink(deviceId)
        val pcbId = activePcbLink?.pcbId

        // Get latest driver pairing record
        val driverPairingRecords = database.driverPairingRecordDao().getDriverPairingRecords(deviceId)
        val latestDriverPairing = driverPairingRecords.maxByOrNull { it.recordId }
        val driverPairingRecordId = latestDriverPairing?.recordId ?: 0L

        // Get latest changed parts record (optional)
        val changedPartsRecords = database.changedPartsRecordDao().getChangedPartsBySerial(deviceId)
        val latestChangedParts = changedPartsRecords.maxByOrNull { it.recordId }
        val changedPartsRecordId = latestChangedParts?.recordId

        // Get or create configuration
        return getOrCreateConfiguration(
            deviceId = deviceId,
            caseId = deviceCase.caseId,
            driverPairingRecordId = driverPairingRecordId,
            changedPartsRecordId = changedPartsRecordId,
            testStage = testStage,
            operatorName = operatorName,
            pcbId = pcbId,
            reason = "Test creation"
        )
    }

}

