// Helper function to format MAC ID as XX:XX:XX:XX:XX:XX (for display/confirmation)
fun formatMacId(input: String): String {
    // Remove all non-hex characters (colons, spaces, etc.)
    val cleaned = input.filter { it.isLetterOrDigit() }.uppercase()

    // Limit to 12 hex characters (6 pairs)
    val limited = cleaned.take(12)

    // Format as XX:XX:XX:XX:XX:XX
    return limited.chunked(2).joinToString(":")
}


