    // Animation for waveform
    val infiniteTransition = rememberInfiniteTransition(label = "wave")
    val wavePhase by infiniteTransition.animateFloat(
        initialValue = 0f,
        targetValue = (2 * PI).toFloat(),
        animationSpec = infiniteRepeatable(
            animation = tween(
                durationMillis = (1000f / (currentFreq / 10f)).toInt().coerceIn(100, 2000),
                easing = LinearEasing
            ),
            repeatMode = RepeatMode.Restart
        ),
        label = "phase"
    )

    // Cleanup on dispose
    DisposableEffect(Unit) {
        onDispose {
            audioJob?.cancel()
            audioJob = null
            currentAudioTrack?.let { track ->
                try {
                    if (track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                        track.stop()
                    }
                    track.release()
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", "Error disposing AudioTrack", e)
                }
            }
            currentAudioTrack = null
        }
    }

    // Stop audio function - ROBUST VERSION
    fun stopAllAudio() {
        android.util.Log.d("FrequencyGenerator", "===  STOPPING ALL AUDIO ===")
        android.util.Log.d("FrequencyGenerator", " Current state - playingSingle: $playingSingle, playingSweep: $playingSweep")
        android.util.Log.d("FrequencyGenerator", " AudioJob state: ${audioJob?.isActive ?: "null"}")
        android.util.Log.d("FrequencyGenerator", " CurrentAudioTrack state: ${currentAudioTrack?.playState ?: "null"}")

        // Cancel any running audio job
        audioJob?.cancel()
        audioJob = null
        android.util.Log.d("FrequencyGenerator", " AudioJob cancelled")

        // Stop and release current audio track
        currentAudioTrack?.let { track ->
            try {
                if (track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                    track.stop()
                    android.util.Log.d("FrequencyGenerator", " AudioTrack stopped")
                }
                track.release()
                android.util.Log.d("FrequencyGenerator", " AudioTrack released")
            } catch (e: Exception) {
                android.util.Log.e("FrequencyGenerator", " Error stopping AudioTrack", e)
            }
        }
        currentAudioTrack = null

        // Reset all playing states
        playingSingle = false
        playingSweep = false
        android.util.Log.d("FrequencyGenerator", " All playing states reset to false")

        // Release audio focus properly
        val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager
        try {
            if (hasAudioFocus && currentAudioFocusRequest != null) {
                if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                    audioManager.abandonAudioFocusRequest(currentAudioFocusRequest!!)
                    android.util.Log.d("FrequencyGenerator", " Audio focus request abandoned (API 26+)")
                } else {
                    @Suppress("DEPRECATION")
                    audioManager.abandonAudioFocus(null)
                    android.util.Log.d("FrequencyGenerator", " Audio focus abandoned (legacy)")
                }
            } else {
                // Fallback for cases where we don't have the request reference
                @Suppress("DEPRECATION")
                audioManager.abandonAudioFocus(null)
                android.util.Log.d("FrequencyGenerator", " Audio focus abandoned (fallback)")
            }
            currentAudioFocusRequest = null
            hasAudioFocus = false
            android.util.Log.d("FrequencyGenerator", " Audio focus released")
        } catch (e: Exception) {
            android.util.Log.e("FrequencyGenerator", " Error releasing audio focus", e)
            currentAudioFocusRequest = null
            hasAudioFocus = false
        }

        android.util.Log.d("FrequencyGenerator", " === ALL AUDIO STOPPED ===")
    }

    // Start single frequency audio - ROBUST VERSION
    fun playSingleFrequency() {
        android.util.Log.d("FrequencyGenerator", "===  PLAY SINGLE FREQUENCY REQUESTED ===")
        android.util.Log.d("FrequencyGenerator", " Current state - playingSingle: $playingSingle, playingSweep: $playingSweep")
        android.util.Log.d("FrequencyGenerator", " AudioJob state: ${audioJob?.isActive ?: "null"}")
        android.util.Log.d("FrequencyGenerator", " CurrentAudioTrack state: ${currentAudioTrack?.playState ?: "null"}")

        // If already playing single, stop it
        if (playingSingle) {
            android.util.Log.d("FrequencyGenerator", " Already playing single - stopping")
            stopAllAudio()
            return
        }

        // ALWAYS do a complete stop when switching modes (including abandoning audio focus)
        // This ensures clean state transition - no lingering listeners or state
        val wasSwitchingFromSweep = playingSweep
        if (playingSweep) {
            android.util.Log.d("FrequencyGenerator", " Switching from sweep to single - doing FULL STOP first")
            stopAllAudio()
            // Use coroutine to ensure cleanup completes before starting new mode
            scope.launch {
                kotlinx.coroutines.delay(200) // Give time for audio focus to be fully abandoned
                withContext(Dispatchers.Main) {
                    // Reset flags to ensure clean start
                    playingSingle = false
                    playingSweep = false
                    // Now start single frequency fresh
                    android.util.Log.d("FrequencyGenerator", " Starting single frequency after mode switch cleanup")
                    // Recursively call this function, but now playingSweep will be false, so it will go to the else branch
                    playSingleFrequency()
                }
            }
            return
        } else {
            // No audio playing, do full cleanup
            stopAllAudio()
        }

        // Continue with starting single frequency (no switch needed)
        // Set state immediately to prevent double-clicks
        playingSingle = true
        android.util.Log.d("FrequencyGenerator", " Set playingSingle = true")

        // ALWAYS request fresh audio focus when starting - don't reuse
        val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager

        // Create new audio focus request with unified listener
        android.util.Log.d("FrequencyGenerator", " Requesting FRESH audio focus with unified listener")

        val focusResult = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            val audioFocusRequest = AudioFocusRequest.Builder(AudioManager.AUDIOFOCUS_GAIN)
                .setAudioAttributes(android.media.AudioAttributes.Builder()
                    .setUsage(android.media.AudioAttributes.USAGE_MEDIA)
                    .setContentType(android.media.AudioAttributes.CONTENT_TYPE_MUSIC)
                    .build())
                .setAcceptsDelayedFocusGain(true)
                .setOnAudioFocusChangeListener { focusChange ->
                    android.util.Log.d("FrequencyGenerator", " Audio focus change (unified): $focusChange")
                    when (focusChange) {
                        AudioManager.AUDIOFOCUS_LOSS -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (permanent) - stopping playback")
                            try {
                                currentAudioTrack?.let { track ->
                                    if (track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                                        track.stop()
                                    }
                                    track.release()
                                }
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error stopping on focus loss", e)
                            }
                            currentAudioTrack = null
                            playingSingle = false
                            playingSweep = false
                            hasAudioFocus = false
                            currentAudioFocusRequest = null
                        }
                        AudioManager.AUDIOFOCUS_LOSS_TRANSIENT -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (transient) - keeping playback, not pausing")
                            // Don't pause on transient loss - keep playing
                            // The system will handle it
                        }
                        AudioManager.AUDIOFOCUS_GAIN -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus gained - resuming if needed")
                            hasAudioFocus = true
                            try {
                                currentAudioTrack?.play()
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error resuming on focus gain", e)
                            }
                        }
                    }
                }
                .build()
            currentAudioFocusRequest = audioFocusRequest
            val result = audioManager.requestAudioFocus(audioFocusRequest)
            if (result == AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
                hasAudioFocus = true
            }
            result
        } else {
            @Suppress("DEPRECATION")
            val result = audioManager.requestAudioFocus(
                { focusChange ->
                    android.util.Log.d("FrequencyGenerator", " Audio focus change (unified, legacy): $focusChange")
                    when (focusChange) {
                        AudioManager.AUDIOFOCUS_LOSS -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (permanent) - stopping playback")
                            try {
                                currentAudioTrack?.let { track ->
                                    if (track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                                        track.stop()
                                    }
                                    track.release()
                                }
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error stopping on focus loss", e)
                            }
                            currentAudioTrack = null
                            playingSingle = false
                            playingSweep = false
                            hasAudioFocus = false
                        }
                        AudioManager.AUDIOFOCUS_LOSS_TRANSIENT -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (transient) - keeping playback, not pausing")
                            // Don't pause on transient loss - keep playing
                            // The system will handle it
                        }
                        AudioManager.AUDIOFOCUS_GAIN -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus gained")
                            hasAudioFocus = true
                            try {
                                currentAudioTrack?.play()
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error resuming on focus gain", e)
                            }
                        }
                    }
                },
                AudioManager.STREAM_MUSIC,
                AudioManager.AUDIOFOCUS_GAIN
            )
            if (result == AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
                hasAudioFocus = true
            }
            result
        }

        if (focusResult != AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
            android.util.Log.w("FrequencyGenerator", " Audio focus not granted (result: $focusResult) - cannot play audio")
            playingSingle = false
            currentAudioFocusRequest = null
            hasAudioFocus = false
            return
        }

        android.util.Log.d("FrequencyGenerator", " Audio focus granted - proceeding with audio generation")

        // Create session only when actual play occurs
        if (currentTestSessionId == null) {
            scope.launch {
                try {
                    val sessionId = repository.insertTestSession(
                        com.example.fqcapp.data.entities.TestSession(
                            serialNumber = currentSerialNumber ?: "PLACEHOLDER",
                            operatorPin = "0000", // Default pin
                            operatorName = "Unknown", // Will be updated from session
                            startTimestamp = java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date()),
                            status = "IN_PROGRESS"
                        )
                    )
                    currentTestSessionId = sessionId
                    android.util.Log.d("FrequencyGenerator", "Created frequency test session: $sessionId")
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", "Error creating test session", e)
                }
            }
        }

        // Track start time
        sessionStartTime = System.currentTimeMillis()
        android.util.Log.d("FrequencyGenerator", "=== SINGLE FREQUENCY STARTED ===")
        android.util.Log.d("FrequencyGenerator", "Target Frequency: $currentFreq Hz")

        // Start audio generation
        audioJob = scope.launch(Dispatchers.Default) {
            try {
                android.util.Log.d("FrequencyGenerator", " Starting audio generation coroutine")
                val sampleRate = 48000
                val track = AudioTrack(
                AudioManager.STREAM_MUSIC,
                    sampleRate,
                AudioFormat.CHANNEL_OUT_STEREO,
                AudioFormat.ENCODING_PCM_16BIT,
                    sampleRate * 2,
                AudioTrack.MODE_STREAM
            )

                // Store track reference
                currentAudioTrack = track
                android.util.Log.d("FrequencyGenerator", " AudioTrack created and stored")

                track.play()
                android.util.Log.d("FrequencyGenerator", " AudioTrack started playing")

                val buffer = ShortArray(2048)
                var phase = 0.0
                var lastLogTime = sessionStartTime!!
                val logInterval = 2000L

                // Get system volume for relative volume calculation (once, outside loop)
                val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager
                val maxSystemVolume = audioManager.getStreamMaxVolume(AudioManager.STREAM_MUSIC)

                // Function to calculate volume gains
                fun calculateGains(): Triple<Float, Float, String> {
                    val systemVolume = audioManager.getStreamVolume(AudioManager.STREAM_MUSIC)
                    val systemVolumeRatio = if (maxSystemVolume > 0) systemVolume.toFloat() / maxSystemVolume.toFloat() else 1f

                    // Calculate independent channel gains
                    // At 0%: gain = 0.0 (mute) regardless of system volume
                    // At 100%: gain = systemVolumeRatio (full system volume)
                    // Formula: gain = (sliderValue / 100.0) * systemVolumeRatio
                    val leftGain = if (leftVolume == 0) 0.0f else (leftVolume / 100.0f * systemVolumeRatio).coerceIn(0f, 1f)
                    val rightGain = if (rightVolume == 0) 0.0f else (rightVolume / 100.0f * systemVolumeRatio).coerceIn(0f, 1f)

                    return Triple(leftGain, rightGain, "$systemVolume/$maxSystemVolume")
                }

                // Set initial volume using setStereoVolume (as backup)
                val (initialLeftGain, initialRightGain, _) = calculateGains()
                track.setStereoVolume(initialLeftGain, initialRightGain)

                var lastVolumeUpdateTime = System.currentTimeMillis()
                val volumeUpdateInterval = 50L // Update volume every 50ms for smooth real-time control

                while (isActive && playingSingle) {
                    val currentTime = System.currentTimeMillis()
                    val freq = currentFreq

                    // Calculate current gains and update UI feedback
                    val (currentLeftGain, currentRightGain, volumeInfo) = calculateGains()
                    withContext(Dispatchers.Main) {
                        actualLeftGain = currentLeftGain
                        actualRightGain = currentRightGain
                        systemVolumeInfo = volumeInfo
                    }

                    // Update volume periodically to catch slider and system volume changes
                    if (currentTime - lastVolumeUpdateTime >= volumeUpdateInterval) {
                        // Also try setStereoVolume as backup (though we use manual multiplication)
                        track.setStereoVolume(currentLeftGain, currentRightGain)
                        lastVolumeUpdateTime = currentTime
                    }

                    val angularVelocity = 2.0 * PI * freq / sampleRate

                    // Log every 2 seconds
                    if (currentTime - lastLogTime >= logInterval) {
                        val timeFormatted = java.text.SimpleDateFormat("HH:mm:ss.SSS", java.util.Locale.getDefault()).format(java.util.Date(currentTime))
                        val elapsed = currentTime - sessionStartTime!!
                        val phaseInDegrees = (phase * 180.0 / PI) % 360.0
                        val cyclesPerBuffer = (freq * buffer.size / 2) / sampleRate

                        android.util.Log.d("FrequencyGenerator", "SINGLE_FREQ: TIME=$timeFormatted | ELAPSED=${elapsed}ms | FREQ=${String.format("%.2f", freq)}Hz | PHASE=${String.format("%.1f", phaseInDegrees)} | CYCLES/BUFFER=${String.format("%.2f", cyclesPerBuffer)} | LEFT_VOL=${leftVolume}% | RIGHT_VOL=${rightVolume}% | SYS_VOL=$systemVolumeInfo | LEFT_GAIN=${String.format("%.3f", currentLeftGain)} | RIGHT_GAIN=${String.format("%.3f", currentRightGain)}")
                        lastLogTime = currentTime
                    }

                    // Generate stereo audio with MANUAL volume control for precise independent channels
                    // This ensures 0% = complete mute and precise 1% steps
                    for (i in 0 until buffer.size step 2) {
                        val rawSample = sin(phase) * Short.MAX_VALUE * 0.3

                        // Apply independent channel gains directly to samples
                        // This gives us precise control: 0% = 0.0 gain = mute
                        val leftSample = (rawSample * currentLeftGain).toInt().toShort()
                        val rightSample = (rawSample * currentRightGain).toInt().toShort()

                        buffer[i] = leftSample      // Left channel with independent gain
                        buffer[i + 1] = rightSample  // Right channel with independent gain

                        phase += angularVelocity
                        if (phase >= 2.0 * PI) phase -= 2.0 * PI
                    }

                    track.write(buffer, 0, buffer.size)
                }

                android.util.Log.d("FrequencyGenerator", " === SINGLE FREQUENCY ENDED ===")
                val endTime = System.currentTimeMillis()
                val totalDuration = endTime - sessionStartTime!!
                android.util.Log.d("FrequencyGenerator", " Total Duration: ${totalDuration}ms | Final Freq: ${String.format("%.2f", currentFreq)}Hz")

                // Clean up
                try {
                    if (track.state == AudioTrack.STATE_INITIALIZED && track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                track.stop()
                    }
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", " stop() failed or unnecessary (single)", e)
                }
                try {
                track.release()
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", " release() failed (single)", e)
                }
                currentAudioTrack = null
                android.util.Log.d("FrequencyGenerator", " AudioTrack stopped and released")

                withContext(Dispatchers.Main) {
                    playingSingle = false
                    android.util.Log.d("FrequencyGenerator", " Set playingSingle = false")
                }
            } catch (e: Exception) {
                android.util.Log.e("FrequencyGenerator", " Error in single frequency playback", e)
                withContext(Dispatchers.Main) {
                    playingSingle = false
                    currentAudioTrack = null
                }
            }
        }
    }

    // Channel Test Mode: Plays different frequencies on each channel
    // Left = 440Hz (A4), Right = 880Hz (A5) - makes it easy to hear independence
    fun playChannelTest() = scope.launch(Dispatchers.IO) {
        if (playingSingle || playingSweep) {
            android.util.Log.d("FrequencyGenerator", " CHANNEL_TEST: Already playing, stopping first")
            playingSingle = false
            playingSweep = false
            delay(200) // Wait for cleanup
        }

        withContext(Dispatchers.Main) {
            playingSingle = true
            channelTestMode = true
        }

        try {
            val sampleRate = 44100
            val leftFreq = 440.0  // A4 note
            val rightFreq = 880.0 // A5 note (octave higher)

            val track = AudioTrack(
                AudioManager.STREAM_MUSIC,
                sampleRate,
                AudioFormat.CHANNEL_OUT_STEREO,
                AudioFormat.ENCODING_PCM_16BIT,
                AudioTrack.getMinBufferSize(
                    sampleRate,
                    AudioFormat.CHANNEL_OUT_STEREO,
                    AudioFormat.ENCODING_PCM_16BIT
                ) * 2,
                AudioTrack.MODE_STREAM
            )

            currentAudioTrack = track
            track.play()

            android.util.Log.d("FrequencyGenerator", " CHANNEL_TEST: Playing L=440Hz, R=880Hz")

            val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager
            val maxSystemVolume = audioManager.getStreamMaxVolume(AudioManager.STREAM_MUSIC)

            val buffer = ShortArray(2048)
            var leftPhase = 0.0
            var rightPhase = 0.0
            val leftAngularVelocity = 2.0 * PI * leftFreq / sampleRate
            val rightAngularVelocity = 2.0 * PI * rightFreq / sampleRate

            while (isActive && playingSingle && channelTestMode) {
                // Calculate gains
                val systemVolume = audioManager.getStreamVolume(AudioManager.STREAM_MUSIC)
                val systemVolumeRatio = if (maxSystemVolume > 0) systemVolume.toFloat() / maxSystemVolume.toFloat() else 1f
                val leftGain = if (leftVolume == 0) 0.0f else (leftVolume / 100.0f * systemVolumeRatio).coerceIn(0f, 1f)
                val rightGain = if (rightVolume == 0) 0.0f else (rightVolume / 100.0f * systemVolumeRatio).coerceIn(0f, 1f)

                // Update UI feedback
                withContext(Dispatchers.Main) {
                    actualLeftGain = leftGain
                    actualRightGain = rightGain
                    systemVolumeInfo = "$systemVolume/$maxSystemVolume"
                }

                // Generate independent channel samples
                for (i in 0 until buffer.size step 2) {
                    val leftSample = (sin(leftPhase) * Short.MAX_VALUE * 0.3 * leftGain).toInt().toShort()
                    val rightSample = (sin(rightPhase) * Short.MAX_VALUE * 0.3 * rightGain).toInt().toShort()

                    buffer[i] = leftSample      // Left: 440Hz
                    buffer[i + 1] = rightSample  // Right: 880Hz

                    leftPhase += leftAngularVelocity
                    rightPhase += rightAngularVelocity
                    if (leftPhase >= 2.0 * PI) leftPhase -= 2.0 * PI
                    if (rightPhase >= 2.0 * PI) rightPhase -= 2.0 * PI
                }

                track.write(buffer, 0, buffer.size)
            }

            track.stop()
            track.release()
            currentAudioTrack = null

            withContext(Dispatchers.Main) {
                playingSingle = false
                channelTestMode = false
            }

            android.util.Log.d("FrequencyGenerator", " CHANNEL_TEST: Stopped")
        } catch (e: Exception) {
            android.util.Log.e("FrequencyGenerator", " CHANNEL_TEST: Error", e)
            withContext(Dispatchers.Main) {
                playingSingle = false
                channelTestMode = false
                currentAudioTrack = null
            }
        }
    }

    // Start sweep audio - ROBUST VERSION
    fun playSweep() {
        android.util.Log.d("FrequencyGenerator", "===  PLAY SWEEP REQUESTED ===")
        android.util.Log.d("FrequencyGenerator", " Current state - playingSingle: $playingSingle, playingSweep: $playingSweep")
        android.util.Log.d("FrequencyGenerator", " AudioJob state: ${audioJob?.isActive ?: "null"}")
        android.util.Log.d("FrequencyGenerator", " CurrentAudioTrack state: ${currentAudioTrack?.playState ?: "null"}")

        // If already playing sweep, stop it
        if (playingSweep) {
            android.util.Log.d("FrequencyGenerator", " Already playing sweep - stopping")
            stopAllAudio()
            return
        }

        // ALWAYS do a complete stop when switching modes (including abandoning audio focus)
        // This ensures clean state transition - no lingering listeners or state
        if (playingSingle) {
            android.util.Log.d("FrequencyGenerator", " Switching from single to sweep - doing FULL STOP first")
            stopAllAudio()
            // Use coroutine to ensure cleanup completes before starting new mode
            scope.launch {
                kotlinx.coroutines.delay(200) // Give time for audio focus to be fully abandoned
                withContext(Dispatchers.Main) {
                    // Reset flags to ensure clean start
                    playingSingle = false
                    playingSweep = false
                    // Now start sweep fresh
                    android.util.Log.d("FrequencyGenerator", " Starting sweep after mode switch cleanup")
                    // Recursively call this function, but now playingSingle will be false, so it will go to the else branch
                    playSweep()
                }
            }
            return
        } else {
            // No audio playing, do full cleanup
            stopAllAudio()
        }

        // Continue with starting sweep (no switch needed)
        // Set state immediately to prevent double-clicks
        playingSweep = true
        android.util.Log.d("FrequencyGenerator", " Set playingSweep = true")

        // ALWAYS request fresh audio focus when starting - don't reuse
        val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager

        // Create new audio focus request with unified listener
        android.util.Log.d("FrequencyGenerator", " Requesting FRESH audio focus with unified listener")

        val focusResult = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            val audioFocusRequest = AudioFocusRequest.Builder(AudioManager.AUDIOFOCUS_GAIN)
                .setAudioAttributes(android.media.AudioAttributes.Builder()
                    .setUsage(android.media.AudioAttributes.USAGE_MEDIA)
                    .setContentType(android.media.AudioAttributes.CONTENT_TYPE_MUSIC)
                    .build())
                .setAcceptsDelayedFocusGain(true)
                .setOnAudioFocusChangeListener { focusChange ->
                    android.util.Log.d("FrequencyGenerator", " Audio focus change (unified): $focusChange")
                    when (focusChange) {
                        AudioManager.AUDIOFOCUS_LOSS -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (permanent) - stopping playback")
                            try {
                                currentAudioTrack?.let { track ->
                                    if (track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                                        track.stop()
                                    }
                                    track.release()
                                }
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error stopping on focus loss", e)
                            }
                            currentAudioTrack = null
                            playingSingle = false
                            playingSweep = false
                            hasAudioFocus = false
                            currentAudioFocusRequest = null
                        }
                        AudioManager.AUDIOFOCUS_LOSS_TRANSIENT -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (transient) - keeping playback, not pausing")
                            // Don't pause on transient loss - keep playing
                            // The system will handle it
                        }
                        AudioManager.AUDIOFOCUS_GAIN -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus gained - resuming if needed")
                            hasAudioFocus = true
                            try {
                                currentAudioTrack?.play()
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error resuming on focus gain", e)
                            }
                        }
                    }
                }
                .build()
            currentAudioFocusRequest = audioFocusRequest
            val result = audioManager.requestAudioFocus(audioFocusRequest)
            if (result == AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
                hasAudioFocus = true
            }
            result
        } else {
            @Suppress("DEPRECATION")
            val result = audioManager.requestAudioFocus(
                { focusChange ->
                    android.util.Log.d("FrequencyGenerator", " Audio focus change (unified, legacy): $focusChange")
                    when (focusChange) {
                        AudioManager.AUDIOFOCUS_LOSS -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (permanent) - stopping playback")
                            try {
                                currentAudioTrack?.let { track ->
                                    if (track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                                        track.stop()
                                    }
                                    track.release()
                                }
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error stopping on focus loss", e)
                            }
                            currentAudioTrack = null
                            playingSingle = false
                            playingSweep = false
                            hasAudioFocus = false
                        }
                        AudioManager.AUDIOFOCUS_LOSS_TRANSIENT -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus lost (transient) - keeping playback, not pausing")
                            // Don't pause on transient loss - keep playing
                            // The system will handle it
                        }
                        AudioManager.AUDIOFOCUS_GAIN -> {
                            android.util.Log.d("FrequencyGenerator", " Audio focus gained")
                            hasAudioFocus = true
                            try {
                                currentAudioTrack?.play()
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error resuming on focus gain", e)
                            }
                        }
                    }
                },
                AudioManager.STREAM_MUSIC,
                AudioManager.AUDIOFOCUS_GAIN
            )
            if (result == AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
                hasAudioFocus = true
            }
            result
        }

        if (focusResult != AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
            android.util.Log.w("FrequencyGenerator", " Audio focus not granted (result: $focusResult) - cannot play sweep")
            playingSweep = false
            currentAudioFocusRequest = null
            hasAudioFocus = false
            return
        }

        // Create session only when actual play occurs
        if (currentTestSessionId == null) {
            scope.launch {
                try {
                    val sessionId = repository.insertTestSession(
                        com.example.fqcapp.data.entities.TestSession(
                            serialNumber = currentSerialNumber ?: "PLACEHOLDER",
                            operatorPin = "0000", // Default pin
                            operatorName = "Unknown", // Will be updated from session
                            startTimestamp = java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date()),
                            status = "IN_PROGRESS"
                        )
                    )
                    currentTestSessionId = sessionId
                    android.util.Log.d("FrequencyGenerator", "Created frequency test session: $sessionId")
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", "Error creating test session", e)
                }
            }
        }

        // Start audio generation
        audioJob = scope.launch(Dispatchers.Default) {
            try {
                android.util.Log.d("FrequencyGenerator", " Starting sweep audio generation coroutine")
                val sampleRate = 48000

                // Calculate proper buffer size for stereo audio
                val minBufferSize = AudioTrack.getMinBufferSize(
                    sampleRate,
                    AudioFormat.CHANNEL_OUT_STEREO,
                    AudioFormat.ENCODING_PCM_16BIT
                )
                val bufferSize = (minBufferSize * 2).coerceAtLeast(8192) // Ensure minimum 8KB buffer

                android.util.Log.d("FrequencyGenerator", " Buffer size: $bufferSize bytes (min: $minBufferSize)")

                val track = AudioTrack(
                    AudioManager.STREAM_MUSIC,
                    sampleRate,
                    AudioFormat.CHANNEL_OUT_STEREO,
                    AudioFormat.ENCODING_PCM_16BIT,
                    bufferSize,
                    AudioTrack.MODE_STREAM
                )

                // Verify track is in correct state
                if (track.state != AudioTrack.STATE_INITIALIZED) {
                    android.util.Log.e("FrequencyGenerator", " AudioTrack not initialized! State: ${track.state}")
                    withContext(Dispatchers.Main) {
                        playingSweep = false
                    }
                    return@launch
                }

                android.util.Log.d("FrequencyGenerator", " AudioTrack state: ${track.state} (${if (track.state == AudioTrack.STATE_INITIALIZED) "INITIALIZED" else "UNINITIALIZED"})")

                // Store track reference
                currentAudioTrack = track
                android.util.Log.d("FrequencyGenerator", " AudioTrack created and stored")

                // Start playback BEFORE generating audio
                track.play()

                // Verify playback started
                if (track.playState != AudioTrack.PLAYSTATE_PLAYING) {
                    android.util.Log.e("FrequencyGenerator", " AudioTrack not playing! PlayState: ${track.playState}")
                    withContext(Dispatchers.Main) {
                        playingSweep = false
                    }
                    return@launch
                }

                android.util.Log.d("FrequencyGenerator", " Sweep AudioTrack started playing (PlayState: ${track.playState})")

                // Use smaller buffer for ultra-fast sweeps to reduce latency
                // Note: bufferSize is in samples, not bytes. For stereo, we need 2 samples per frame.
                val audioBufferSize = if (duration <= 1.0f) 512 else 2048
                val buffer = ShortArray(audioBufferSize * 2) // Stereo: 2 channels
                var phase = 0.0
                var sweepStartTime = System.currentTimeMillis()
                var forward = true
                var currentSweepFreq = startFreq
                var loopCount = 0
                var lastLogTime = sweepStartTime
                val logInterval = if (duration <= 1.0f) 50L else 1000L // Log every 50ms for ultra-fast sweeps, 1s for normal

                // Get system volume for relative volume calculation (once, outside loop)
                val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager
                val maxSystemVolume = audioManager.getStreamMaxVolume(AudioManager.STREAM_MUSIC)

                // Function to calculate volume gains
                fun calculateGains(): Triple<Float, Float, String> {
                    val systemVolume = audioManager.getStreamVolume(AudioManager.STREAM_MUSIC)
                    val systemVolumeRatio = if (maxSystemVolume > 0) systemVolume.toFloat() / maxSystemVolume.toFloat() else 1f

                    // Calculate independent channel gains
                    // At 0%: gain = 0.0 (mute) regardless of system volume
                    // At 100%: gain = systemVolumeRatio (full system volume)
                    // Formula: gain = (sliderValue / 100.0) * systemVolumeRatio
                    val leftGain = if (leftVolume == 0) 0.0f else (leftVolume / 100.0f * systemVolumeRatio).coerceIn(0f, 1f)
                    val rightGain = if (rightVolume == 0) 0.0f else (rightVolume / 100.0f * systemVolumeRatio).coerceIn(0f, 1f)

                    return Triple(leftGain, rightGain, "$systemVolume/$maxSystemVolume")
                }

                // Set initial volume using setStereoVolume (as backup)
                val (initialLeftGain, initialRightGain, _) = calculateGains()
                track.setStereoVolume(initialLeftGain, initialRightGain)

                var lastVolumeUpdateTime = System.currentTimeMillis()
                val volumeUpdateInterval = 50L // Update volume every 50ms for smooth real-time control

                // Check if sweep mode
                val isSweep = kotlin.math.abs(startFreq - endFreq) > 0.1f

                android.util.Log.d("FrequencyGenerator", "=== FREQUENCY SWEEP STARTED ===")
                android.util.Log.d("FrequencyGenerator", "Start Freq: $startFreq Hz, End Freq: $endFreq Hz, Duration: $duration sec, Loop: $loop")
                android.util.Log.d("FrequencyGenerator", "Sample Rate: $sampleRate Hz, Buffer Size: ${buffer.size}")

                // Special logging for ultra-fast sweeps
                if (duration <= 1.0f) {
                    val freqRange = kotlin.math.abs(endFreq - startFreq)
                    val frequencyChangeRate = freqRange / duration // Hz per second
                    android.util.Log.d("FrequencyGenerator", " ULTRA_FAST_SWEEP: ${frequencyChangeRate.toInt()} Hz/sec | ${(duration * 1000).toInt()}ms total | Logging every 10ms")
                }

                // FUNDAMENTAL REBUILD: Time-based sweep algorithm
                val sweepDurationMs = (duration * 1000).toLong() // Convert to milliseconds
                val totalSlices = (sweepDurationMs / 10).toInt() // 10ms per slice
                val delta = (endFreq - startFreq) / totalSlices.toFloat() // Hz per slice
                var currentSlice = 0
                var isUpSweep = true
                var timeBasedSweepStartTime = System.currentTimeMillis()
                var lastSliceTime = timeBasedSweepStartTime

                android.util.Log.d("FrequencyGenerator", "FUNDAMENTAL_SWEEP: Duration=${duration}s, TotalSlices=$totalSlices, Delta=${String.format("%.3f", delta)} Hz/slice")

                while (isActive && playingSweep) {
                    val timeBasedCurrentTime = System.currentTimeMillis()
                    val timeBasedElapsedFromStart = timeBasedCurrentTime - timeBasedSweepStartTime

                    // Calculate current slice based on elapsed time
                    val expectedSlice = (timeBasedElapsedFromStart / 10).toInt()

                    // Calculate frequency for current slice
                    val freq = if (isSweep) {
                        if (isUpSweep) {
                            // UP sweep: f(k) = start_freq + k * delta
                            val f = startFreq + expectedSlice * delta
                            f.coerceIn(startFreq, endFreq)
                        } else {
                            // DOWN sweep: f(k) = end_freq - k * delta
                            val f = endFreq - expectedSlice * delta
                            f.coerceIn(startFreq, endFreq)
                        }
                    } else {
                        startFreq
                    }

                    // Update UI immediately for smooth frequency display
                    withContext(Dispatchers.Main) {
                        val oldFreq = currentFreq
                        currentFreq = freq
                        android.util.Log.d("FrequencyGenerator", "UI_UPDATE: OLD_FREQ=${String.format("%.2f", oldFreq)}Hz  NEW_FREQ=${String.format("%.2f", currentFreq)}Hz | SLICE=$expectedSlice/$totalSlices")
                    }

                    // Check if sweep is complete
                    if (expectedSlice >= totalSlices) {
                            if (loop) {
                            // Reset for next iteration
                            currentSlice = 0
                            isUpSweep = !isUpSweep
                                loopCount++
                            timeBasedSweepStartTime = System.currentTimeMillis()
                            lastSliceTime = timeBasedSweepStartTime

                            android.util.Log.d("FrequencyGenerator", "=== LOOP $loopCount STARTED ===")
                            android.util.Log.d("FrequencyGenerator", "Direction: ${if (isUpSweep) "UP" else "DOWN"}")

                            // Continue to next iteration without breaking
                            continue
                            } else {
                                break
                            }
                    }

                    // Log frequency details with time-based algorithm
                    if (timeBasedCurrentTime - lastLogTime >= logInterval) {
                        val timeFormatted = java.text.SimpleDateFormat("HH:mm:ss.SSS", java.util.Locale.getDefault()).format(java.util.Date(timeBasedCurrentTime))
                        val progressPercent = if (isSweep) {
                            (expectedSlice * 100 / totalSlices).coerceIn(0, 100)
                        } else {
                            100
                        }

                        // Enhanced logging for time-based sweep algorithm
                        if (duration <= 1.0f) {
                            val expectedFreq = if (isUpSweep) {
                                startFreq + expectedSlice * delta
                    } else {
                                endFreq - expectedSlice * delta
                            }
                            val freqError = kotlin.math.abs(freq - expectedFreq)

                            android.util.Log.d("FrequencyGenerator", "TIME_BASED_SWEEP: TIME=$timeFormatted | SLICE=$expectedSlice/$totalSlices | PROGRESS=${progressPercent}% | FREQ=${String.format("%.2f", freq)}Hz | EXPECTED=${String.format("%.2f", expectedFreq)}Hz | ERROR=${String.format("%.2f", freqError)}Hz | DIR=${if (isUpSweep) "UP" else "DOWN"} | LOOP=$loopCount | UI_FREQ=${String.format("%.2f", currentFreq)}Hz | UI_MATCH=${freq == currentFreq}")
                        } else {
                            android.util.Log.d("FrequencyGenerator", "TIME_BASED_SWEEP: TIME=$timeFormatted | SLICE=$expectedSlice/$totalSlices | PROGRESS=${progressPercent}% | FREQ=${String.format("%.2f", freq)}Hz | DIR=${if (isUpSweep) "UP" else "DOWN"} | LOOP=$loopCount")
                        }
                        lastLogTime = timeBasedCurrentTime
                    }


                    // Calculate current gains and update UI feedback
                    val (currentLeftGain, currentRightGain, volumeInfo) = calculateGains()
                    withContext(Dispatchers.Main) {
                        actualLeftGain = currentLeftGain
                        actualRightGain = currentRightGain
                        systemVolumeInfo = volumeInfo
                    }

                    // Update volume periodically to catch slider and system volume changes
                    if (timeBasedCurrentTime - lastVolumeUpdateTime >= volumeUpdateInterval) {
                        // Also try setStereoVolume as backup (though we use manual multiplication)
                        track.setStereoVolume(currentLeftGain, currentRightGain)
                        lastVolumeUpdateTime = timeBasedCurrentTime
                    }

                    val angularVelocity = 2.0 * PI * freq / sampleRate

                    // Log detailed audio generation info every 5 seconds
                    if (timeBasedCurrentTime - lastLogTime >= 5000L) {
                        val cyclesPerBuffer = (freq * buffer.size / 2) / sampleRate
                        val phaseInDegrees = (phase * 180.0 / PI) % 360.0

                        android.util.Log.d("FrequencyGenerator", "AUDIO_GEN: Freq=${String.format("%.2f", freq)}Hz | AngularVel=${String.format("%.6f", angularVelocity)} | Phase=${String.format("%.1f", phaseInDegrees)} | Cycles/Buffer=${String.format("%.2f", cyclesPerBuffer)} | LEFT_VOL=${leftVolume}% | RIGHT_VOL=${rightVolume}% | SYS_VOL=$systemVolumeInfo | LeftGain=${String.format("%.3f", currentLeftGain)} | RightGain=${String.format("%.3f", currentRightGain)}")
                    }

                    // Generate stereo audio with MANUAL volume control for precise independent channels
                    // This ensures 0% = complete mute and precise 1% steps
                    // Optimized audio generation for ultra-fast sweeps
                    if (duration <= 1.0f) {
                        // Use faster generation for ultra-fast sweeps - process stereo pairs
                        for (i in 0 until buffer.size step 2) {
                            val rawSample = sin(phase) * Short.MAX_VALUE * 0.3

                            // Apply independent channel gains directly to samples
                            val leftSample = (rawSample * currentLeftGain).toInt().toShort()
                            val rightSample = (rawSample * currentRightGain).toInt().toShort()

                            buffer[i] = leftSample      // Left channel with independent gain
                            buffer[i + 1] = rightSample  // Right channel with independent gain

                            phase += angularVelocity
                            if (phase >= 2.0 * PI) phase -= 2.0 * PI
                        }
                    } else {
                        // Standard generation for normal sweeps
                        for (i in 0 until buffer.size step 2) {
                            val rawSample = sin(phase) * Short.MAX_VALUE * 0.3

                            // Apply independent channel gains directly to samples
                            val leftSample = (rawSample * currentLeftGain).toInt().toShort()
                            val rightSample = (rawSample * currentRightGain).toInt().toShort()

                            buffer[i] = leftSample      // Left channel with independent gain
                            buffer[i + 1] = rightSample  // Right channel with independent gain

                            phase += angularVelocity
                            if (phase >= 2.0 * PI) phase -= 2.0 * PI
                        }
                    }

                    // Write audio data to track
                    val bytesWritten = track.write(buffer, 0, buffer.size)
                    if (bytesWritten < 0) {
                        android.util.Log.e("FrequencyGenerator", " AudioTrack.write() failed! Error code: $bytesWritten")
                    } else if (bytesWritten < buffer.size) {
                        android.util.Log.w("FrequencyGenerator", " Partial write: $bytesWritten/${buffer.size} bytes")
                    }

                    // Time-based control: ensure we don't exceed the target duration
                    val timeControlCurrentTime = System.currentTimeMillis()
                    val timeControlElapsedFromStart = timeControlCurrentTime - timeBasedSweepStartTime
                    val targetSliceTime = expectedSlice * 10L // 10ms per slice

                    // If we're behind schedule, don't wait; if ahead, wait to maintain timing
                    if (timeControlElapsedFromStart < targetSliceTime) {
                        delay(targetSliceTime - timeControlElapsedFromStart)
                    }
                }

                android.util.Log.d("FrequencyGenerator", " === FREQUENCY SWEEP ENDED ===")
                android.util.Log.d("FrequencyGenerator", " Total Loops: $loopCount | Final Freq: ${String.format("%.2f", currentSweepFreq)}Hz")

                // Clean up
                try {
                    if (track.state == AudioTrack.STATE_INITIALIZED && track.playState == AudioTrack.PLAYSTATE_PLAYING) {
                track.stop()
                    }
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", " stop() failed or unnecessary (sweep)", e)
                }
                try {
                track.release()
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", " release() failed (sweep)", e)
                }
                currentAudioTrack = null
                android.util.Log.d("FrequencyGenerator", " AudioTrack stopped and released")

                withContext(Dispatchers.Main) {
                    playingSweep = false
                    android.util.Log.d("FrequencyGenerator", " Set playingSweep = false")
                }
            } catch (e: Exception) {
                android.util.Log.e("FrequencyGenerator", " Error in sweep playback", e)
                withContext(Dispatchers.Main) {
                    playingSweep = false
                    currentAudioTrack = null
                }
            }
        }
    }

    Scaffold(
        topBar = {
            TopAppBar(
                title = { 
                    Column {
                        Text("Single Tone", color = Color.White, fontSize = 18.sp)
                        if (sessionCount > 0) {
                            Text(
                                "Session $sessionCount", 
                                color = accentColor, 
                                fontSize = 12.sp,
                                style = MaterialTheme.typography.bodySmall
                            )
                        }
                    }
                },
                navigationIcon = {
                    if (onBack != null) {
                        IconButton(onClick = {
                            android.util.Log.d("FrequencyGenerator", "Back button pressed - showing review dialog")
                            showReview = true
                        }) {
                            Icon(Icons.Filled.ArrowBack, "Back", tint = Color.White)
                        }
                    }
                },
                colors = TopAppBarDefaults.topAppBarColors(containerColor = bgColor)
            )
        },
        snackbarHost = {
            SnackbarHost(hostState = snackbarHostState)
        }
    ) { padding ->
        val screenInfo = getScreenInfo()
        val responsivePadding = getResponsivePadding()
        val responsiveSpacing = getResponsiveSpacing()

        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(padding)
                .padding(horizontal = responsivePadding, vertical = responsiveSpacing)
                .then(
                    if (screenInfo.isTablet) {
                        Modifier.widthIn(max = getResponsiveMaxWidth())
                    } else {
                        Modifier
                    }
                ),
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.spacedBy(responsiveSpacing)
        ) {
            // Driver Pair Display at the top
            var driverPairsText by remember { mutableStateOf<String?>(null) }
            LaunchedEffect(currentSerialNumber) {
                if (currentSerialNumber != null && currentSerialNumber!!.isNotBlank()) {
                    try {
                        val latestDriverPairing = withContext(Dispatchers.IO) {
                            repository.getLatestDriverPairing(currentSerialNumber!!)
                        }
                        if (latestDriverPairing != null && latestDriverPairing.driverData.isNotBlank()) {
                            val driverDataJson = org.json.JSONArray(latestDriverPairing.driverData)
                            // Get only the latest (last) pair
                            if (driverDataJson.length() > 0) {
                                val lastIndex = driverDataJson.length() - 1
                                val pairObj = driverDataJson.getJSONObject(lastIndex)
                                val driver1 = pairObj.optString("driver1", "")
                                val driver2 = pairObj.optString("driver2", "")
                                if (driver1.isNotBlank() || driver2.isNotBlank()) {
                                    driverPairsText = "Pair ${lastIndex + 1}: R-$driver1 L-$driver2"
                                } else {
                                    driverPairsText = null
                                }
                            } else {
                                driverPairsText = null
                            }
                        } else {
                            driverPairsText = null
                        }
                    } catch (e: Exception) {
                        android.util.Log.e("FrequencyGenerator", "Error fetching driver pairs", e)
                        driverPairsText = null
                    }
                } else {
                    driverPairsText = null
                }
            }

            if (driverPairsText != null) {
                Card(
                    modifier = Modifier.fillMaxWidth(),
                    colors = CardDefaults.cardColors(containerColor = Color(0xFF2196F3).copy(alpha = 0.2f)),
                    shape = RoundedCornerShape(8.dp)
                ) {
                    Row(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(12.dp),
                        verticalAlignment = Alignment.CenterVertically,
                        horizontalArrangement = Arrangement.spacedBy(8.dp)
                    ) {
                        Icon(
                            Icons.Filled.Speaker,
                            contentDescription = "Driver Pairs",
                            tint = Color(0xFF2196F3),
                            modifier = Modifier.size(20.dp)
                        )
                        Text(
                            driverPairsText!!,
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White,
                            fontSize = 13.sp,
                            fontWeight = FontWeight.Medium
                        )
                    }
                }
            }

            // 1. FREQUENCY SWEEP SECTION (TOP)
            Text(
                "Frequency Sweep",
                style = MaterialTheme.typography.titleSmall,
                color = accentColor,
                fontSize = 14.sp
            )

            // Start and End Frequency (Larger)
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.spacedBy(8.dp)
            ) {
                OutlinedTextField(
                    value = startFreqText,
                    onValueChange = { newValue ->
                        android.util.Log.d("FrequencyGenerator", "Start frequency changed: $newValue")
                        startFreqText = newValue
                        // Allow empty string and partial input for better backspace
                        if (newValue.isEmpty() || newValue == "." || newValue == "0." || newValue == "0" || newValue == ".") {
                            // Allow partial input - don't validate yet
                        } else {
                        newValue.toFloatOrNull()?.let { value ->
                            if (value >= 1f && value <= 20000f) {
                                startFreq = value
                                    android.util.Log.d("FrequencyGenerator", "Start frequency set to: $value Hz")
                                }
                            }
                        }
                    },
                    label = { Text("Start (Hz)", fontSize = 13.sp) },
                    modifier = Modifier.weight(1f).height(getResponsiveTextFieldHeight()),
                    colors = OutlinedTextFieldDefaults.colors(
                        focusedBorderColor = accentColor,
                        unfocusedBorderColor = Color(0xFF424242),
                        focusedTextColor = Color.White,
                        unfocusedTextColor = Color.White,
                        focusedLabelColor = accentColor,
                        unfocusedLabelColor = Color.White.copy(0.7f)
                    ),
                    textStyle = MaterialTheme.typography.bodyLarge.copy(fontSize = getResponsiveTextSize(16f).sp),
                    keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number)
                )

                OutlinedTextField(
                    value = endFreqText,
                    onValueChange = { newValue ->
                        android.util.Log.d("FrequencyGenerator", "End frequency changed: $newValue")
                        endFreqText = newValue
                        // Allow empty string and partial input for better backspace
                        if (newValue.isEmpty() || newValue == "." || newValue == "0." || newValue == "0" || newValue == ".") {
                            // Allow partial input - don't validate yet
                        } else {
                        newValue.toFloatOrNull()?.let { value ->
                            if (value >= 1f && value <= 20000f) {
                                endFreq = value
                                    android.util.Log.d("FrequencyGenerator", "End frequency set to: $value Hz")
                                }
                            }
                        }
                    },
                    label = { Text("End (Hz)", fontSize = 13.sp) },
