                        // Allow empty string, decimal input, and partial numbers for better backspace
                        if (newValue.isEmpty() || newValue == "." || newValue == "0." || newValue == "0") {
                            // Allow partial input - don't validate yet
                        } else {
                        newValue.toFloatOrNull()?.let { value ->
                                if (value >= 0.01f && value <= 300f) {
                                duration = value
                                    android.util.Log.d("FrequencyGenerator", "Duration set to: $value seconds")
