            dismissButton = {
                TextButton(
                    onClick = {
                        showSolutionsDialog = false
                        showReview = false
                        selectedIssues = setOf()
                        selectedSolutions = setOf()
                        remark = ""
                        otherSolution = ""
                    },
                    modifier = Modifier.fillMaxWidth()
                ) {
