    // Watch for undo restoration: when previousTestResult is set from undo, show review dialog
    var shouldShowReviewAfterUndo by remember { mutableStateOf(false) }

    // Check for undo payload when screen becomes visible (after navigation back)
    // Check on screen load and when undo state changes
    val undoState by undoManager.undoState.collectAsState()

    // Track if we just restored from undo to prevent clearing flags prematurely
    var isRestoringFromUndo by remember { mutableStateOf(false) }

    // Check for payload whenever screen becomes visible (including after navigation)
    // Use DisposableEffect to check payload when screen is displayed
    DisposableEffect(Unit) {
        android.util.Log.d("FrequencyGenerator", " Screen visible - checking for undo payload")
        val undoPayload = undoManager.getAndClearLastUndo()
        if (undoPayload != null) {
            val (restoredResult, fromScreen) = undoPayload
            android.util.Log.d("FrequencyGenerator", " UNDO PAYLOAD found!")
            android.util.Log.d("FrequencyGenerator", " From screen: $fromScreen, Result: ${restoredResult.outcome}")

            if (fromScreen == "FREQ_GEN") {
                // Set flags to trigger dialog opening
                previousTestResult = restoredResult
                shouldShowReviewAfterUndo = true
                isFromUndo = true
                isRestoringFromUndo = true // Mark that we're restoring
                android.util.Log.d("FrequencyGenerator", " UNDO PAYLOAD: Set flags - dialog will open at step: ${restoredResult.outcome}")
            }
        } else {
            android.util.Log.d("FrequencyGenerator", " No undo payload found")
        }

        onDispose {
            // Cleanup if needed
        }
    }

    // Watch undo state changes - check for payload when undo becomes inactive
    // This catches the case where UNDO was pressed while on Landing Page
    // executeUndo() sets undoState.isActive = false AND stores payload
    // So when isActive changes from true to false, we should check for payload
    var previousUndoActive by remember { mutableStateOf(false) }
    LaunchedEffect(undoState.isActive) {
        val undoBecameInactive = previousUndoActive && !undoState.isActive
        previousUndoActive = undoState.isActive

        if (undoBecameInactive && !isRestoringFromUndo) {
            // Undo was just executed - check for payload
            android.util.Log.d("FrequencyGenerator", " Undo became inactive - checking for payload")
            val undoPayload = undoManager.getAndClearLastUndo()
            if (undoPayload != null) {
                val (restoredResult, fromScreen) = undoPayload
                android.util.Log.d("FrequencyGenerator", " UNDO PAYLOAD found after undo execution!")
                android.util.Log.d("FrequencyGenerator", " From screen: $fromScreen, Result: ${restoredResult.outcome}")

                if (fromScreen == "FREQ_GEN") {
                    // Set flags to trigger dialog opening
                    previousTestResult = restoredResult
                    shouldShowReviewAfterUndo = true
                    isFromUndo = true
                    isRestoringFromUndo = true
                    android.util.Log.d("FrequencyGenerator", " UNDO PAYLOAD: Set flags from undo state change")
                }
            }
        } else if (!undoState.isActive && !isRestoringFromUndo) {
            // Undo is inactive and we're not restoring - clear any stale payload/flags
            val stalePayload = undoManager.getAndClearLastUndo()
            if (stalePayload != null) {
                android.util.Log.d("FrequencyGenerator", " Found stale undo payload (undo not active, not restoring) - cleared")
            }
            // Clear local restoration flags only if we're not restoring
            if (shouldShowReviewAfterUndo) {
                android.util.Log.d("FrequencyGenerator", " Clearing stale restoration flags")
                shouldShowReviewAfterUndo = false
                previousTestResult = null
            }
        }
    }

    // Watch for undo restoration - trigger when both flags are set
    // This opens the review dialog automatically after navigating back from Landing Page
    // Opens at the EXACT step where submission happened (PASSPASS_CHECKS, FAILFAIL_SOLUTIONS, CBPCAN_BE_PASSED, TNDTEST_NOT_DONE)
    LaunchedEffect(previousTestResult, shouldShowReviewAfterUndo) {
        if (shouldShowReviewAfterUndo && previousTestResult != null) {
            val outcome = previousTestResult!!.outcome
            val stepName = when (outcome) {
                com.example.fqcapp.data.TestOutcome.PASS -> "PASS_CHECKS"
                com.example.fqcapp.data.TestOutcome.FAIL -> "FAIL_SOLUTIONS"
                com.example.fqcapp.data.TestOutcome.CAN_BE_PASSED -> "CAN_BE_PASSED"
                com.example.fqcapp.data.TestOutcome.TEST_NOT_DONE -> "TEST_NOT_DONE"
            }

            android.util.Log.d("FrequencyGenerator", " UNDO: Opening review dialog at submission step: $stepName")
            android.util.Log.d("FrequencyGenerator", " Outcome: $outcome")
            android.util.Log.d("FrequencyGenerator", " Dialog will open with fresh selections (not restored)")

            // Minimal delay to ensure navigation to Frequency Generator screen has completed
            kotlinx.coroutines.delay(100) // Reduced delay for faster response
            showReview = true
            shouldShowReviewAfterUndo = false // Reset flag
            isRestoringFromUndo = false // Reset restoration flag after dialog opens
            android.util.Log.d("FrequencyGenerator", " Review dialog opened at step: $stepName")
        }
    }

    // Review Screen - Full Screen Dialog
    if (showReview) {
        AlertDialog(
            onDismissRequest = { showReview = false },
            properties = DialogProperties(usePlatformDefaultWidth = false)
        ) {
            Box(
                modifier = Modifier.fillMaxSize(),
                contentAlignment = Alignment.Center
            ) {
                android.util.Log.d("FrequencyGenerator", " Rendering ReviewScreen with previousTestResult: ${previousTestResult?.outcome}")
                android.util.Log.d("FrequencyGenerator", " PreviousTestResult details: $previousTestResult")

                // Check if device is connected (has serial number - either real like "SLC..." or reference like "TMP-...")
                val hasDeviceConnected = currentSerialNumber?.let { serial ->
                    serial.isNotBlank() && (serial.startsWith("SLC", ignoreCase = true) || serial.startsWith("TMP-", ignoreCase = true))
                } ?: false
                android.util.Log.d("FrequencyGenerator", " Device connection check - Serial: '$currentSerialNumber', Connected: $hasDeviceConnected")

                ReviewScreenContainer(
                    testType = TestType.FREQUENCY,
                    previousTestResult = previousTestResult,
                    hasDeviceConnected = hasDeviceConnected,
                    onResultSubmitted = { result ->
                        // Guard against duplicate submissions
                        if (isSubmitting) {
                            android.util.Log.w("FrequencyGenerator", " Submission already in progress, ignoring duplicate call")
                            return@ReviewScreenContainer
                        }

                        isSubmitting = true
                        scope.launch {
                            try {
                                // Validate device connection for outcomes that require it
                                val hasDeviceConnected = currentSerialNumber?.let { serial ->
                                    serial.isNotBlank() && (serial.startsWith("SLC", ignoreCase = true) || serial.startsWith("TMP-", ignoreCase = true))
                                } ?: false

                                if (!hasDeviceConnected && result.outcome != com.example.fqcapp.data.TestOutcome.TEST_NOT_DONE) {
                                    android.util.Log.e("FrequencyGenerator", " Invalid submission: Device not connected but outcome is ${result.outcome}")
                                    android.util.Log.e("FrequencyGenerator", "   Only TEST_NOT_DONE is allowed when no device is connected")
                                    isSubmitting = false
                                    return@launch
                                }

                                // Capture serial number at the time of submission
                                val capturedSerialNumber = currentSerialNumber ?: ""
                                android.util.Log.d("FrequencyGenerator", " Captured serial number for milestone update: '$capturedSerialNumber'")

                            // Convert lists to JSON strings
                            // IMPORTANT: Filter out PASS checklist items ("No Distortion", "No Noise", "Balance OK")
                            // when result is not PASS - these are implicit in a PASS result
                            val passChecklistItems = setOf("No Distortion", "No Noise", "Balance OK", "no disturbance", "no noise", "balance ok")
                            val filteredIssues = if (result.outcome.name.uppercase() != "PASS") {
                                result.issues.filter { issue -> 
                                    !passChecklistItems.contains(issue.trim()) 
                                }
                            } else {
                                // For PASS, don't save checklist items as issues
                                emptyList()
                            }
                            val issuesJson = JSONArray(filteredIssues).toString()
                            val solutionsJson = JSONArray(result.solutions).toString()

                            // Get operator name from preferences (login name)
                            val prefsManager = com.example.fqcapp.utils.PreferencesManager(context)
                            operatorName = prefsManager.getOperatorName() ?: "Unknown"

                            // Get serial number from device name (if not captured)
                            var finalSerialNumber = capturedSerialNumber
                            if (finalSerialNumber.isBlank() && connectedDeviceName != null) {
                                finalSerialNumber = connectedDeviceName
                                android.util.Log.d("FrequencyGenerator", " Using device name as serial: '$finalSerialNumber'")
                            }

                            android.util.Log.d("FrequencyGenerator", " Creating test - Serial: '$finalSerialNumber', Operator: '$operatorName'")

                            // Get entry time (when screen was opened)
                            val entryTime = sessionStartTime ?: System.currentTimeMillis()
                            val entryTimestamp = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date(entryTime))

                            // Calculate iteration number
                            val iterationNumber = try {
                                if (finalSerialNumber.isNotBlank()) {
                                    repository.getNextIterationNumber(finalSerialNumber)
                                } else {
                                    1
                                }
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", "Error getting iteration number", e)
                                1
                            }

                            android.util.Log.d("FrequencyGenerator", " Iteration number: $iterationNumber")

                            // Placeholder exit timestamp (will be updated at final submit)
                            val placeholderExitTimestamp = entryTimestamp

                            val frequencyTest = FrequencyTest(
                                serialNumber = finalSerialNumber,
                                operatorName = operatorName,
                                entryTimestamp = entryTimestamp,
                                exitTimestamp = placeholderExitTimestamp, // Will be updated at final submit
                                totalTimeSpent = "00:00:00", // Will be calculated at final submit
                                result = result.outcome.name,
                                issues = issuesJson,
                                solutions = solutionsJson,
                                remark = result.remark,
                                iterationNumber = iterationNumber,
                                testStage = result.testStage,
                                timestamp = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date()),
                                needsSync = true
                            )

                            // BUFFER: Defer DB insert until undo window expires
                            // Store result BEFORE closing dialog (needed for undo restoration)
                            previousTestResult = result
                            android.util.Log.d("FrequencyGenerator", " Stored previousTestResult for undo: ${result.outcome}")
                            android.util.Log.d("FrequencyGenerator", " Issues: ${result.issues}, Solutions: ${result.solutions}")

                            android.util.Log.d("FrequencyGenerator", " Calling startUndo with scope: $scope")
                            undoManager.startUndo(
                                testResult = result,
                                onUndo = {
                                    android.util.Log.d("FrequencyGenerator", " UNDO pressed - navigating to Frequency Generator")
                                    android.util.Log.d("FrequencyGenerator", " Result to show at step: ${result.outcome}")

                                    // UndoManager.executeUndo() already stored payload in lastUndoPayload
                                    // Just navigate - DisposableEffect will check payload and open dialog
                                    android.util.Log.d("FrequencyGenerator", " Payload stored in UndoManager - navigating to Frequency Generator")
                                    android.util.Log.d("FrequencyGenerator", " DisposableEffect will detect payload and open review dialog")

                                    // Explicitly navigate to Frequency Generator (don't use onBack since back stack might be cleared)
                                    // Use explicit navigation callback if available, otherwise fall back to onBack
                                    onNavigateToFreqGen?.invoke() ?: onBack?.invoke()
                                },
                                onCommit = suspend {
                                    android.util.Log.d("FrequencyGenerator", " COMMIT after 30s - writing frequency test to DB")

                                    // Get serial number - priority: capturedSerialNumber > currentSerialNumber > connectedDeviceName > session
                                    var serialForMilestone = capturedSerialNumber
                                    if (serialForMilestone.isBlank()) {
                                        serialForMilestone = currentSerialNumber ?: ""
                                    }
                                    if (serialForMilestone.isBlank() && connectedDeviceName != null) {
                                        serialForMilestone = connectedDeviceName
                                        android.util.Log.d("FrequencyGenerator", " Using device name as serial: '$serialForMilestone'")
                                    }
                                    if (serialForMilestone.isBlank() && currentSessionId != null) {
                                        try {
                                            val session = repository.getSessionById(currentSessionId)
                                            serialForMilestone = session?.serialNumber ?: ""
                                        } catch (e: Exception) {
                                            android.util.Log.e("FrequencyGenerator", " Error fetching FQCSession", e)
                                        }
                                    }
                                    val testSessionId = currentTestSessionId
                                    if (serialForMilestone.isBlank() && testSessionId != null) {
                                        try {
                                            val testSession = repository.getTestSessionById(testSessionId)
                                            serialForMilestone = testSession?.serialNumber ?: ""
                                        } catch (e: Exception) {
                                            android.util.Log.e("FrequencyGenerator", " Error fetching TestSession", e)
                                        }
                                    }
                                    if (serialForMilestone.isBlank()) {
                                        serialForMilestone = frequencyTest.serialNumber
                                    }

                                    // Get operator name from preferences (login name)
                                    val prefsManager = com.example.fqcapp.utils.PreferencesManager(context)
                                    val finalOperatorName = prefsManager.getOperatorName() ?: "Unknown"

                                    // Calculate EXIT TIME at final submit (not when review screen closes)
                                    val exitTime = System.currentTimeMillis()

                                    // Parse entry timestamp to get entry time in milliseconds
                                    val dateFormat = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault())
                                    val entryTimeMillis = try {
                                        dateFormat.parse(frequencyTest.entryTimestamp)?.time ?: sessionStartTime ?: exitTime
                                    } catch (e: Exception) {
                                        android.util.Log.e("FrequencyGenerator", "Error parsing entry timestamp: ${frequencyTest.entryTimestamp}", e)
                                        sessionStartTime ?: exitTime
                                    }

                                    // Calculate total time spent
                                    val totalTimeSpentMs = exitTime - entryTimeMillis
                                    val totalTimeSpentSeconds = totalTimeSpentMs / 1000
                                    val hours = totalTimeSpentSeconds / 3600
                                    val minutes = (totalTimeSpentSeconds % 3600) / 60
                                    val seconds = totalTimeSpentSeconds % 60
                                    val totalTimeSpentFormatted = String.format("%02d:%02d:%02d", hours, minutes, seconds)

                                    val exitTimestamp = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date(exitTime))

                                    android.util.Log.d("FrequencyGenerator", " Entry: ${frequencyTest.entryTimestamp} ($entryTimeMillis), Exit: $exitTimestamp ($exitTime), Duration: ${totalTimeSpentMs}ms = $totalTimeSpentFormatted")

                                    // Get iteration number
                                    val iterationNumber = try {
                                        if (serialForMilestone.isNotBlank()) {
                                            repository.getNextIterationNumber(serialForMilestone)
                                        } else {
                                            frequencyTest.iterationNumber
                                        }
                                    } catch (e: Exception) {
                                        android.util.Log.e("FrequencyGenerator", "Error getting iteration number", e)
                                        frequencyTest.iterationNumber
                                    }

                                    android.util.Log.d("FrequencyGenerator", " Final Iteration: $iterationNumber")

                                    try {
                                        // Update frequencyTest with correct values at final submit
                                        val finalFrequencyTest = frequencyTest.copy(
                                            serialNumber = serialForMilestone,
                                            operatorName = finalOperatorName,
                                            exitTimestamp = exitTimestamp, // Exit time calculated at final submit
                                            totalTimeSpent = totalTimeSpentFormatted, // Time calculated at final submit
                                            iterationNumber = iterationNumber
                                        )

                                        android.util.Log.d("FrequencyGenerator", " Saving - Serial: '$serialForMilestone', Operator: '$finalOperatorName', Result: '${finalFrequencyTest.result}', Iteration: $iterationNumber, Time: $totalTimeSpentFormatted")

                                        val testId = repository.insertFrequencyTest(finalFrequencyTest)
                                        lastSubmittedTestId = testId
                                        sessionManager.markSessionHasSubmission()
                                        android.util.Log.d("FrequencyGenerator", " Frequency test committed with ID: $testId")

                                        // Add solution reminders if test failed and has solutions
                                        if (finalFrequencyTest.result.uppercase() == "FAIL" && 
                                            finalFrequencyTest.solutions.isNotBlank() &&
                                            solutionReminderManager != null &&
                                            serialForMilestone.isNotBlank()) {
                                            try {
                                                val solutionsList = try {
                                                    val jsonArray = org.json.JSONArray(finalFrequencyTest.solutions)
                                                    (0 until jsonArray.length()).map { jsonArray.getString(it) }
                                                } catch (e: Exception) {
                                                    emptyList<String>()
                                                }
                                                if (solutionsList.isNotEmpty()) {
                                                    solutionReminderManager.addPendingSolution(
                                                        serialNumber = serialForMilestone,
                                                        testType = com.example.fqcapp.data.TestType.FREQUENCY,
                                                        solutions = solutionsList,
                                                        testId = testId
                                                    )
                                                    android.util.Log.d("FrequencyGenerator", " Added ${solutionsList.size} solution reminders")
                                                }
                                            } catch (e: Exception) {
                                                android.util.Log.e("FrequencyGenerator", " Error adding solution reminders", e)
                                            }
                                        }

                                        // Skip milestone and workflow updates for "TEST_NOT_DONE" - not a test result
                                        if (finalFrequencyTest.result.uppercase() == "TEST_NOT_DONE") {
                                            android.util.Log.d("FrequencyGenerator", " TEST_NOT_DONE selected - skipping milestone and workflow updates (stored locally only)")
                                        } else {
                                            // Log workflow: Frequency Test COMPLETED
                                            if (serialForMilestone.isNotBlank()) {
                                                try {
                                                    val workflowService = com.example.fqcapp.services.DeviceWorkflowService(repository)

                                                    // Map result to workflow result
                                                    val workflowResult = when (finalFrequencyTest.result.uppercase()) {
                                                        "PASS" -> com.example.fqcapp.services.DeviceWorkflowService.RESULT_PASS
                                                        "FAIL" -> com.example.fqcapp.services.DeviceWorkflowService.RESULT_FAIL
                                                        "CAN_PASS" -> com.example.fqcapp.services.DeviceWorkflowService.RESULT_CAN_PASS
                                                        else -> null
                                                    }

                                                    workflowService.logWorkflow(
                                                        serialNumber = serialForMilestone,
                                                        stage = com.example.fqcapp.services.DeviceWorkflowService.STAGE_FREQUENCY_TEST,
                                                        operatorName = finalOperatorName,
                                                        action = com.example.fqcapp.services.DeviceWorkflowService.ACTION_COMPLETED,
                                                        result = workflowResult,
                                                        iterationNumber = iterationNumber,
                                                        recordId = testId
                                                    )
                                                    android.util.Log.d("FrequencyGenerator", " [WORKFLOW] Logged FREQUENCY_TEST COMPLETED")
                                                } catch (e: Exception) {
                                                    android.util.Log.e("FrequencyGenerator", " [WORKFLOW] Error logging workflow COMPLETED", e)
                                                }

                                                // Update milestone for frequency generator - use captured serial number
                                                android.util.Log.d("FrequencyGenerator", " [MILESTONE_CALL_COMMIT] Calling milestone update service (from commit)")
                                                android.util.Log.d("FrequencyGenerator", " [MILESTONE_CALL_COMMIT] Serial number: '$serialForMilestone'")
                                                android.util.Log.d("FrequencyGenerator", " [MILESTONE_CALL_COMMIT] Result value: '${finalFrequencyTest.result}'")
                                                try {
                                                    val milestoneService = com.example.fqcapp.services.MilestoneUpdateService(repository)
                                                    milestoneService.updateFrequencyStatus(serialForMilestone, finalFrequencyTest.result)
                                                    android.util.Log.d("FrequencyGenerator", " [MILESTONE_CALL_COMMIT] Milestone update call completed")
                                                } catch (e: Exception) {
                                                    android.util.Log.e("FrequencyGenerator", " [MILESTONE_CALL_COMMIT] Error updating milestone", e)
                                                    e.printStackTrace()
                                                }
                                            } else {
                                                android.util.Log.e("FrequencyGenerator", " [MILESTONE_CALL_COMMIT] Cannot update milestone - serial number is blank!")
                                            }
                                        }
                                    } catch (e: Exception) {
                                        android.util.Log.e("FrequencyGenerator", " Commit failed", e)
                                    }
                                    Unit
                                },
                                scope = scope,
                                fromScreen = "FREQ_GEN"
                            )
                            android.util.Log.d("FrequencyGenerator", " startUndo called successfully")

                            // Close review dialog
                            showReview = false
                            Log.d("FrequencyGenerator", "Submission buffered for 30s - will commit if not undone")

                            // Navigation logic: 
                            // Always navigate to Landing Page after submission
                            android.util.Log.d("FrequencyGenerator", " Navigating to Landing Page after submit - isFromUndo: $isFromUndo")
                            if (isFromUndo) {
                                // Reset undo flag after navigation
                                isFromUndo = false
                                android.util.Log.d("FrequencyGenerator", " Resetting isFromUndo flag")
                            }
                            // Explicitly navigate to Landing Page
                            onNavigateToLanding?.invoke() ?: onBack?.invoke()
                            android.util.Log.d("FrequencyGenerator", " Navigation to Landing Page triggered")
                            } catch (e: Exception) {
                                android.util.Log.e("FrequencyGenerator", " Error during submission", e)
                            } finally {
                                // Always reset submission flag, even on error or cancellation
                                isSubmitting = false
                            }
                        }
                    },
                    onCancel = {
                        showReview = false
                        // If we cancel from undo-restored dialog, reset the flag
                        if (isFromUndo) {
                            isFromUndo = false
                        }
                    }
                )
            }
        }
    }

    // Undo Snackbar removed - now shows on landing page

    // Session Handover Banner
    sessionHandover?.let { message ->
        Card(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            colors = CardDefaults.cardColors(
                containerColor = Color(0xFFFF9800)
            )
        ) {
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = message,
                    color = Color.White,
                    fontWeight = FontWeight.Bold
                )
                IconButton(
                    onClick = { sessionManager.clearHandoverNotification() }
                ) {
                    Icon(
                        Icons.Filled.Close,
                        contentDescription = "Dismiss",
                        tint = Color.White
                    )
                }
            }
        }
    }
}

@Composable
fun AnimatedWaveform(
    frequency: Float,
    phase: Float,
    color: Color
) {
    Canvas(modifier = Modifier.fillMaxSize()) {
        val width = size.width
        val height = size.height
        val centerY = height / 2f
        val amplitude = height * 0.3f

        val path = Path()
        val points = 80
        val cycles = (frequency / 100f).coerceIn(0.5f, 5f)

        for (i in 0..points) {
            val x = (i.toFloat() / points) * width
            val progress = i.toFloat() / points
            val y = centerY + (sin((progress * cycles * 2 * PI) + phase) * amplitude).toFloat()

            if (i == 0) {
                path.moveTo(x, y)
            } else {
                path.lineTo(x, y)
            }
        }

        drawPath(
            path = path,
            color = color,
            style = Stroke(width = 3.dp.toPx(), cap = StrokeCap.Round)
        )
    }
}

package com.example.fqcapp.data.dao

import androidx.room.*
import com.example.fqcapp.data.entities.FrequencyTest
import kotlinx.coroutines.flow.Flow

@Dao
interface FrequencyTestDao {
    @Insert
    suspend fun insert(test: FrequencyTest): Long


    @Query("SELECT * FROM frequency_tests WHERE serialNumber = :serialNumber ORDER BY timestamp ASC")
    suspend fun getFrequencyTests(serialNumber: String): List<FrequencyTest>

    @Query("SELECT * FROM frequency_tests WHERE testId = :id")
    suspend fun getTestById(id: Long): FrequencyTest?

    @Query("SELECT * FROM frequency_tests WHERE serialNumber = :serialNumber AND needsSync = 1")
    suspend fun getUnsyncedFrequencyTests(serialNumber: String): List<FrequencyTest>

    @Query("SELECT * FROM frequency_tests WHERE needsSync = 1")
    suspend fun getAllUnsyncedFrequencyTests(): List<FrequencyTest>

    @Query("SELECT * FROM frequency_tests ORDER BY timestamp ASC")
    suspend fun getAllFrequencyTests(): List<FrequencyTest>

    @Query("UPDATE frequency_tests SET needsSync = 0, lastSyncTimestamp = :timestamp WHERE testId = :testId")
    suspend fun markAsSynced(testId: Long, timestamp: Long = System.currentTimeMillis())

    @Query("UPDATE frequency_tests SET needsSync = 0, lastSyncTimestamp = :timestamp WHERE testId IN (:testIds)")
    suspend fun markMultipleAsSynced(testIds: List<Long>, timestamp: Long = System.currentTimeMillis())

    @Delete
    suspend fun delete(test: FrequencyTest)

    @Query("DELETE FROM frequency_tests WHERE testId = :testId")
    suspend fun deleteTest(testId: Long)

    @Query("SELECT COALESCE(MAX(iterationNumber), 0) FROM frequency_tests WHERE serialNumber = :serialNumber")
    suspend fun getMaxIterationNumber(serialNumber: String): Int

    @Query("SELECT * FROM frequency_tests WHERE serialNumber = :serialNumber AND testStage = :stage ORDER BY iterationNumber DESC LIMIT 1")
    suspend fun getLatestFrequencyTestByStage(serialNumber: String, stage: String): FrequencyTest?

    @Query("SELECT * FROM frequency_tests WHERE serialNumber = :serialNumber ORDER BY iterationNumber DESC")
    suspend fun getAllFrequencyTestsBySerial(serialNumber: String): List<FrequencyTest>

    // Get latest frequency test by timestamp (excluding TEST_NOT_DONE)
    @Query("SELECT * FROM frequency_tests WHERE serialNumber = :serialNumber AND result != 'TEST_NOT_DONE' ORDER BY timestamp DESC LIMIT 1")
    suspend fun getLatestFrequencyTestByTimestamp(serialNumber: String): FrequencyTest?

    // Get latest frequency test by configuration ID
    @Query("SELECT * FROM frequency_tests WHERE configurationId = :configurationId ORDER BY entryTimestamp DESC LIMIT 1")
    suspend fun getLatestByConfiguration(configurationId: String): FrequencyTest?
}

