@OptIn(ExperimentalMaterial3Api::class, ExperimentalFoundationApi::class)
@Composable
fun TestSongScreen(
    currentSessionId: Long?,
    repository: FQCRepository,
    currentSerialNumber: String = "",
    currentOperatorName: String = "",
    authenticatedOperator: com.example.fqcapp.data.entities.OperatorCredential? = null,
    connectedDeviceName: String? = null, // Device name from Bluetooth scan
    onBack: (() -> Unit)? = null,
    onNavigateToLanding: (() -> Unit)? = null,
    onNavigateToTestSongs: (() -> Unit)? = null,
    undoManager: UndoManager = UndoManager(),
    solutionReminderManager: com.example.fqcapp.core.SolutionReminderManager? = null
) {
    val context = LocalContext.current
    val scope = rememberCoroutineScope()
    val audioFileManager = remember { AudioFileManager(context) }

    // Track entry time when screen is opened
    var sessionStartTime by remember { mutableStateOf<Long?>(null) }
    var actualSerialNumber by remember { mutableStateOf<String>(currentSerialNumber) }
    var actualOperatorName by remember { mutableStateOf<String>(currentOperatorName) }
    var currentIterationNumber by remember { mutableStateOf<Int>(1) }

    // State
    val audioFiles = remember { mutableStateListOf<AudioFile>() }
    var draggedIndex by remember { mutableStateOf<Int?>(null) }
    var vlcPlayer by remember { mutableStateOf<VLCPlayerManager?>(null) }
    var isPlaying by remember { mutableStateOf(false) }
    var currentSong by remember { mutableStateOf<AudioFile?>(null) }
    var currentPosition by remember { mutableStateOf(0) }
    var seekPosition by remember { mutableStateOf(0) }
    var showReviewDialog by remember { mutableStateOf(false) }
    var songToReview by remember { mutableStateOf<AudioFile?>(null) }
    var reviewFromOverallScreen by remember { mutableStateOf(false) } // Track if review was opened from overall review screen
    // Store audio focus request to properly abandon it
    var currentAudioFocusRequest by remember { mutableStateOf<AudioFocusRequest?>(null) }
    var hasAudioFocus by remember { mutableStateOf(false) }
    // Session management
    val sessionManager = remember { SessionManager(context) }
    val reviewValidator = remember { ReviewValidator() }

    // Song playback tracker
    val playbackTracker = remember { com.example.fqcapp.core.SongPlaybackTracker() }

    // Snackbar host state for undo functionality
    val snackbarHostState = remember { SnackbarHostState() }

    // Full-screen overall review state (supports undo buffer)
    var showReview by remember { mutableStateOf(false) }
    var previousTestResult by remember { mutableStateOf<com.example.fqcapp.data.TestResult?>(null) }
    var shouldShowReviewAfterUndo by remember { mutableStateOf(false) }
    var isFromUndo by remember { mutableStateOf(false) }
    var isRestoringFromUndo by remember { mutableStateOf(false) }

    // Session handover notification
    val sessionHandover by sessionManager.sessionHandover.collectAsState()
    val currentSession by sessionManager.currentSession.collectAsState()

    // Session count tracking
    var sessionCount by remember { mutableStateOf(0) }

    // Audio controls
    var volume by remember { mutableStateOf(1.0f) } // 0.0 to 1.0
    var balance by remember { mutableStateOf(0.5f) } // 0.0 = Left, 0.5 = Center, 1.0 = Right
    var isSeeking by remember { mutableStateOf(false) }

    // Storage permission
    val permission = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
        Manifest.permission.READ_MEDIA_AUDIO
    } else {
        Manifest.permission.READ_EXTERNAL_STORAGE
    }

    var hasPermission by remember {
        mutableStateOf(
            ContextCompat.checkSelfPermission(context, permission) == PackageManager.PERMISSION_GRANTED
        )
    }

    val permissionLauncher = rememberLauncherForActivityResult(
        ActivityResultContracts.RequestPermission()
    ) { granted ->
        hasPermission = granted
        if (granted) {
            audioFileManager.copySongsFromResources()
            val files = audioFileManager.getAudioFiles()
            audioFiles.clear()
            audioFiles.addAll(files)
        }
    }

    // Use authenticated operator if provided
    LaunchedEffect(authenticatedOperator) {
        if (authenticatedOperator != null) {
            actualOperatorName = authenticatedOperator.operatorName
            android.util.Log.d("TestSongScreen", " Using authenticated operator: $actualOperatorName (ID: ${authenticatedOperator.operatorId})")
        } else {
            val prefsManager = com.example.fqcapp.utils.PreferencesManager(context)
            actualOperatorName = prefsManager.getOperatorName() ?: currentOperatorName
            android.util.Log.d("TestSongScreen", " No authenticated operator, using preferences: $actualOperatorName")
        }
    }

        // Check for existing individual song reviews and mark as reviewed in tracker
        LaunchedEffect(actualSerialNumber, currentIterationNumber) {
            if (actualSerialNumber.isNotBlank()) {
                try {
                    val existingReviews = repository.getSongTestsBySerial(actualSerialNumber)
                        .filter { 
                            !it.isOverallResult && 
                            it.iterationNumber == currentIterationNumber 
                        }
                    android.util.Log.d("TestSongScreen", " Found ${existingReviews.size} existing individual song reviews")
                    existingReviews.forEach { review ->
                        playbackTracker.markAsReviewed(review.songName, review.result)
                        android.util.Log.d("TestSongScreen", " Marked existing review: ${review.songName} -> ${review.result}")
                    }
                } catch (e: Exception) {
                    android.util.Log.e("TestSongScreen", "Error loading existing reviews", e)
                }
            }
        }

        // Initialize - Capture entry time and get serial/operator
        LaunchedEffect(Unit) {
        // Capture entry time when screen is opened
        sessionStartTime = System.currentTimeMillis()
        android.util.Log.d("TestSongScreen", " Entry time captured: $sessionStartTime")

        // Get serial number - priority: currentSerialNumber > connectedDeviceName > session
        actualSerialNumber = when {
            currentSerialNumber.isNotBlank() -> {
                android.util.Log.d("TestSongScreen", " Serial from parameter: '$currentSerialNumber'")
                currentSerialNumber
            }
            connectedDeviceName != null -> {
                android.util.Log.d("TestSongScreen", " Serial from device name: '$connectedDeviceName'")
                connectedDeviceName
            }
            currentSessionId != null -> {
                try {
                    val session = repository.getSessionById(currentSessionId)
                    val serial = session?.serialNumber ?: ""
                    android.util.Log.d("TestSongScreen", " Serial from session: '$serial'")
                    serial
                } catch (e: Exception) {
                    android.util.Log.e("TestSongScreen", "Error fetching session", e)
                    ""
                }
            }
            else -> ""
        }

        // Calculate iteration number
        if (actualSerialNumber.isNotBlank()) {
            try {
                currentIterationNumber = repository.getNextSongIterationNumber(actualSerialNumber)
                android.util.Log.d("TestSongScreen", " Iteration number: $currentIterationNumber for serial: $actualSerialNumber")
            } catch (e: Exception) {
                android.util.Log.e("TestSongScreen", "Error getting iteration number", e)
                currentIterationNumber = 1
            }
        }
    }

    // Log workflow: Song Test STARTED when screen opens
    // Delayed and non-blocking - runs in background after screen loads
    // Use a Mutex to prevent race conditions and duplicate calls
    val workflowKey = remember(actualSerialNumber, actualOperatorName) { 
        "$actualSerialNumber-$actualOperatorName" 
    }
    val workflowMutex = remember { kotlinx.coroutines.sync.Mutex() }
    var workflowLogged by remember(workflowKey) { mutableStateOf(false) }

    LaunchedEffect(workflowKey) {
        // Only log workflow if we have a valid serial number (not device name) and haven't logged yet
        // Use Mutex to ensure atomic check-and-set operation
        val shouldLog = workflowMutex.withLock {
            if (workflowLogged) {
                false // Already logged
            } else if (actualSerialNumber.isNotBlank() &&
                actualOperatorName != "Unknown" &&
                !actualSerialNumber.matches(Regex("^BT-.*", RegexOption.IGNORE_CASE)) &&
                !actualSerialNumber.matches(Regex("^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})$")) &&
                actualSerialNumber.length >= 10) {
                workflowLogged = true // Mark as logged atomically
                true
            } else {
                false
            }
        }

        if (shouldLog) {
            // Delay workflow logging to avoid blocking screen load
            scope.launch(Dispatchers.IO) {
                try {
                    delay(500) // Small delay to let screen render first
                    val workflowService = com.example.fqcapp.services.DeviceWorkflowService(repository)
                    workflowService.logWorkflow(
                        serialNumber = actualSerialNumber,
                        stage = com.example.fqcapp.services.DeviceWorkflowService.STAGE_SONG_TEST,
                        operatorName = actualOperatorName,
                        action = com.example.fqcapp.services.DeviceWorkflowService.ACTION_STARTED,
                        iterationNumber = currentIterationNumber // Use the same iteration as the test
                    )
                    android.util.Log.d("TestSongScreen", " [WORKFLOW] Logged SONG_TEST STARTED (background, iteration: $currentIterationNumber)")
                } catch (e: kotlinx.coroutines.CancellationException) {
                    android.util.Log.d("TestSongScreen", " [WORKFLOW] Workflow logging cancelled (screen left)")
                } catch (e: Exception) {
                    android.util.Log.e("TestSongScreen", " [WORKFLOW] Error logging workflow STARTED", e)
                }
            }
        } else if (actualSerialNumber.isNotBlank() && !workflowLogged) {
            android.util.Log.w("TestSongScreen", " Skipping workflow log - invalid serial number format: '${actualSerialNumber}'")
        }
    }

    // Initialize audio files
    LaunchedEffect(hasPermission) {
        if (hasPermission) {
            android.util.Log.d("TestSongScreen", "Loading audio files")
            audioFileManager.copySongsFromResources()
            val files = audioFileManager.getAudioFiles()
            audioFiles.clear()
            audioFiles.addAll(files)
            android.util.Log.d("TestSongScreen", "Loaded ${files.size} audio files")
        } else {
            android.util.Log.d("TestSongScreen", "Requesting storage permission")
            permissionLauncher.launch(permission)
        }
    }

    // Handle pending undo from landing page for Test Songs
    // NOTE: This is handled by the DisposableEffect below, so this LaunchedEffect is removed to avoid conflicts

    // Session count tracking
    LaunchedEffect(currentSerialNumber) {
        if (currentSerialNumber.isNotEmpty()) {
            try {
                val existingSessions = repository.getTestSessionsBySerialNumber(currentSerialNumber)
                sessionCount = existingSessions.size + 1 // +1 for current session
                android.util.Log.d("TestSongScreen", "Device: $currentSerialNumber, Session count: $sessionCount")
            } catch (e: Exception) {
                android.util.Log.e("TestSongScreen", "Error getting session count", e)
            }
        }
    }

    // Initialize VLC Player Manager
    val vlcPlayerManager = remember {
        try {
            VLCPlayerManager(context).apply {
                onProgressUpdate = { position ->
                    if (!isSeeking) {
                        currentPosition = position.toInt()
                        seekPosition = position.toInt()
                    }
                }
                onCompletion = {
                    android.util.Log.d("TestSongScreen", "Song completed via VLC")
                    // Stop tracking playback when song completes
                    currentSong?.let { song ->
                        playbackTracker.stopPlayback(song.name)
                    }
                    isPlaying = false
                    currentPosition = 0
                    seekPosition = 0
                }
                onPlaying = {
                    isPlaying = true
                }
                onPaused = {
                    isPlaying = false
                }
                onStopped = {
                    isPlaying = false
                }
                onError = { error ->
                    android.util.Log.e("TestSongScreen", "VLC error: $error")
                    isPlaying = false
                    currentSong = null
                }
            }
        } catch (e: Exception) {
            android.util.Log.e("TestSongScreen", "Failed to initialize VLC Player", e)
            null
        }
    }

    // Update current position while playing
    LaunchedEffect(isPlaying) {
        while (isPlaying) {
            vlcPlayerManager?.updateProgress()
            delay(100) // Update every 100ms
        }
    }

    // Apply volume and balance changes
    LaunchedEffect(volume, balance) {
        vlcPlayerManager?.let { player ->
            val leftVolume = volume * (1f - balance).coerceIn(0f, 1f)
            val rightVolume = volume * balance.coerceIn(0f, 1f)
            player.setStereoVolume(leftVolume, rightVolume)
        }
    }

    // Cleanup
    DisposableEffect(Unit) {
        onDispose {
            try {
                vlcPlayerManager?.let { player ->
                    if (player.isPlaying()) {
                        player.stop()
                    }
                    player.release()
                }
            } catch (e: Exception) {
                                    android.util.Log.e("TestSongScreen", "Error releasing VLC player on dispose", e)
            }
            vlcPlayer = null

            // Release audio focus when component is disposed
            val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager
            try {
                if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                    currentAudioFocusRequest?.let {
                        audioManager.abandonAudioFocusRequest(it)
                    }
                } else {
                    @Suppress("DEPRECATION")
                    audioManager.abandonAudioFocus(null)
                }
                hasAudioFocus = false
                currentAudioFocusRequest = null
                android.util.Log.d("TestSongScreen", "Audio focus released on dispose")
            } catch (e: Exception) {
                android.util.Log.e("TestSongScreen", "Error releasing audio focus on dispose", e)
            }
        }
    }

    val accentColor = Color(0xFFFFC107)
    val bgColor = Color(0xFF212121)
    val surfaceColor = Color(0xFF2B2B2B)

    Box(modifier = Modifier.fillMaxSize()) {
    Scaffold(
        snackbarHost = {
            SnackbarHost(hostState = snackbarHostState)
        },
        topBar = {
            TopAppBar(
                title = { 
                    Column {
                        Text("Test Song", color = Color.White, fontSize = 18.sp)
                        if (sessionCount > 0) {
                            Text(
                                "Session $sessionCount", 
                                color = accentColor, 
                                fontSize = 12.sp,
                                style = MaterialTheme.typography.bodySmall
                            )
                        }
                    }
                },
                navigationIcon = {
                    IconButton(onClick = { 
                        android.util.Log.d("TestSongScreen", "Back button pressed - opening overall review")
                        // Open overall review instead of exiting
                        previousTestResult = null
                        showReview = true
                    }) {
                        Icon(Icons.Filled.ArrowBack, "Back", tint = Color.White)
                    }
                },
                colors = TopAppBarDefaults.topAppBarColors(
                    containerColor = bgColor
                )
            )
        },
        bottomBar = {
            // Full-Featured Now Playing Player
            if (currentSong != null) {
                Surface(
                    modifier = Modifier.fillMaxWidth(),
                    color = surfaceColor,
                    tonalElevation = 8.dp
                ) {
                    Column(
                        modifier = Modifier.padding(16.dp)
                    ) {
                        // Song Info
                        Row(
                            modifier = Modifier.fillMaxWidth(),
                            horizontalArrangement = Arrangement.SpaceBetween,
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            // Album Art Placeholder + Song Info
                            Row(
                                modifier = Modifier.weight(1f),
                                horizontalArrangement = Arrangement.spacedBy(12.dp),
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Box(
                                    modifier = Modifier
                                        .size(56.dp)
                                        .clip(RoundedCornerShape(8.dp))
                                        .background(accentColor),
                                    contentAlignment = Alignment.Center
                                ) {
                                    Icon(
                                        Icons.Filled.MusicNote,
                                        contentDescription = null,
                                        tint = bgColor,
                                        modifier = Modifier.size(32.dp)
                                    )
                                }

                                Column(modifier = Modifier.weight(1f)) {
                                    Text(
                                        currentSong!!.name,
                                        style = MaterialTheme.typography.titleMedium,
                                        color = Color.White,
                                        fontWeight = FontWeight.Bold,
                                        maxLines = 1,
                                        overflow = TextOverflow.Ellipsis
                                    )
                                    Text(
                                        audioFileManager.formatFileSize(currentSong!!.size),
                                        style = MaterialTheme.typography.bodySmall,
                                        color = Color.White.copy(alpha = 0.7f)
                                    )
                                }
                            }

                            // Review Button
                            IconButton(
                                onClick = {
                                    songToReview = currentSong
                                    showReviewDialog = true
                                },
                                enabled = actualSerialNumber.isNotBlank()
                            ) {
                                Icon(
                                    Icons.Filled.RateReview,
                                    "Review",
                                    tint = if (actualSerialNumber.isNotBlank()) accentColor else Color.Gray
                                )
                            }
                        }

                        Spacer(modifier = Modifier.height(12.dp))

                        // Seek Bar
                        Column {
                            Slider(
                                value = seekPosition.toFloat(),
                                onValueChange = { 
                                    android.util.Log.d("TestSongScreen", "Seek position changed: $it")
                                    isSeeking = true
                                    seekPosition = it.toInt()
                                },
                                onValueChangeFinished = {
                                    vlcPlayerManager?.seekTo(seekPosition.toLong())
                                    isSeeking = false
                                },
                                valueRange = 0f..(vlcPlayerManager?.getDuration()?.toFloat() ?: currentSong!!.duration.toFloat()),
                                colors = SliderDefaults.colors(
                                    thumbColor = accentColor,
                                    activeTrackColor = accentColor,
                                    inactiveTrackColor = Color(0xFF424242)
                                )
                            )

                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween
                            ) {
                                Text(
                                    audioFileManager.formatDuration(currentPosition.toLong()),
                                    style = MaterialTheme.typography.bodySmall,
                                    color = Color.White.copy(alpha = 0.7f),
                                    fontSize = 11.sp
                                )
                                Text(
                                    audioFileManager.formatDuration(
                                        vlcPlayerManager?.getDuration()?.toLong() ?: currentSong!!.duration
                                    ),
                                    style = MaterialTheme.typography.bodySmall,
                                    color = Color.White.copy(alpha = 0.7f),
                                    fontSize = 11.sp
                                )
                            }
                        }

                        Spacer(modifier = Modifier.height(8.dp))

                        // Playback Controls
                        Row(
                            modifier = Modifier.fillMaxWidth(),
                            horizontalArrangement = Arrangement.Center,
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            // Skip Backward 10s
                            IconButton(
                                onClick = {
                                    android.util.Log.d("TestSongScreen", "Skip backward 10s button pressed")
                                    vlcPlayerManager?.let { player ->
                                        val currentPos = player.getCurrentPosition()
                                        val newPos = (currentPos - 10000).coerceAtLeast(0L)
                                        player.seekTo(newPos)
                                        currentPosition = newPos.toInt()
                                    }
                                }
                            ) {
                                Icon(
                                    Icons.Filled.Replay10,
                                    "Back 10s",
                                    tint = Color.White,
                                    modifier = Modifier.size(32.dp)
                                )
                            }

                            Spacer(modifier = Modifier.width(8.dp))

                            // Play/Pause Button
                            IconButton(
                                onClick = {
                                    android.util.Log.d("TestSongScreen", "Play/Pause button pressed - isPlaying: $isPlaying")
                                    vlcPlayerManager?.let { player ->
                                        try {
                                            if (isPlaying) {
                                                player.pause()
                                            } else {
                                                player.resume()
                                            }
                                        } catch (e: Exception) {
                                            android.util.Log.e("TestSongScreen", "Error in play/pause", e)
                                            isPlaying = false
                                        }
                                    }
                                },
                                modifier = Modifier
                                    .size(56.dp)
                                    .background(accentColor, CircleShape)
                            ) {
                                Icon(
                                    if (isPlaying) Icons.Filled.Pause else Icons.Filled.PlayArrow,
                                    "Play/Pause",
                                    tint = bgColor,
                                    modifier = Modifier.size(32.dp)
                                )
                            }

                            Spacer(modifier = Modifier.width(8.dp))

                            // Skip Forward 10s
                            IconButton(
                                onClick = {
                                    android.util.Log.d("TestSongScreen", "Skip forward 10s button pressed")
                                    vlcPlayerManager?.let { player ->
                                        val currentPos = player.getCurrentPosition()
                                        val duration = player.getDuration()
                                        val newPos = (currentPos + 10000).coerceAtMost(duration)
                                        player.seekTo(newPos)
                                        currentPosition = newPos.toInt()
                                    }
                                }
                            ) {
                                Icon(
                                    Icons.Filled.Forward10,
                                    "Forward 10s",
                                    tint = Color.White,
                                    modifier = Modifier.size(32.dp)
                                )
                            }

                            Spacer(modifier = Modifier.width(16.dp))

                            // Stop Button
                            IconButton(
                                onClick = {
                                    android.util.Log.d("TestSongScreen", "Stop button pressed")
                                    try {
                                        vlcPlayerManager?.let { player ->
                                            if (player.isPlaying()) {
                                                player.stop()
                                            }
                                            player.release()
                                        }
                                    } catch (e: Exception) {
                                        android.util.Log.e("TestSongScreen", "Error stopping VLC player", e)
                                    }
                                    // Stop tracking playback
                                    currentSong?.let { song ->
                                        playbackTracker.stopPlayback(song.name)
                                    }

                                    isPlaying = false
                                    currentSong = null
                                    currentPosition = 0
                                    seekPosition = 0

                                    // Release audio focus when stopping
                                    val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager
                                    try {
                                        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                                            currentAudioFocusRequest?.let {
                                                audioManager.abandonAudioFocusRequest(it)
                                            }
                                        } else {
                                            @Suppress("DEPRECATION")
                                            audioManager.abandonAudioFocus(null)
                                        }
                                        hasAudioFocus = false
                                        currentAudioFocusRequest = null
                                        android.util.Log.d("TestSongScreen", "Audio focus released on stop")
                                    } catch (e: Exception) {
                                        android.util.Log.e("TestSongScreen", "Error releasing audio focus", e)
                                    }
                                }
                            ) {
                                Icon(
                                    Icons.Filled.Stop,
                                    "Stop",
                                    tint = Color.White,
                                    modifier = Modifier.size(28.dp)
                                )
                            }
                        }

                        Spacer(modifier = Modifier.height(12.dp))

                        // Volume Control
                        Row(
                            modifier = Modifier.fillMaxWidth(),
                            verticalAlignment = Alignment.CenterVertically,
                            horizontalArrangement = Arrangement.spacedBy(8.dp)
                        ) {
                            Icon(
                                Icons.Filled.VolumeDown,
                                "Volume",
                                tint = Color.White.copy(alpha = 0.7f),
                                modifier = Modifier.size(20.dp)
                            )
                            Slider(
                                value = volume,
                                onValueChange = { 
                                    android.util.Log.d("TestSongScreen", "Volume slider changed: $it")
                                    volume = it 
                                },
                                valueRange = 0f..1f,
                                modifier = Modifier.weight(1f),
                                colors = SliderDefaults.colors(
                                    thumbColor = accentColor,
                                    activeTrackColor = accentColor,
                                    inactiveTrackColor = Color(0xFF424242)
                                )
                            )
                            Icon(
                                Icons.Filled.VolumeUp,
                                "Volume",
                                tint = Color.White.copy(alpha = 0.7f),
                                modifier = Modifier.size(20.dp)
                            )
            Text(
                                "${(volume * 100).toInt()}%",
                                color = Color.White.copy(alpha = 0.7f),
                                fontSize = 11.sp,
                                modifier = Modifier.width(40.dp)
                            )
                        }

                        // Balance Control
                        Row(
                            modifier = Modifier.fillMaxWidth(),
                            verticalAlignment = Alignment.CenterVertically,
                            horizontalArrangement = Arrangement.spacedBy(8.dp)
                        ) {
                            Text(
                                "L",
                                color = Color.White.copy(alpha = 0.7f),
                                fontSize = 11.sp,
                                modifier = Modifier.width(20.dp)
                            )
                            Slider(
                                value = balance,
                                onValueChange = { 
                                    android.util.Log.d("TestSongScreen", "Balance slider changed: $it")
                                    balance = it 
                                },
                                valueRange = 0f..1f,
                                modifier = Modifier.weight(1f),
                                colors = SliderDefaults.colors(
                                    thumbColor = accentColor,
                                    activeTrackColor = accentColor,
                                    inactiveTrackColor = Color(0xFF424242)
                                )
                            )
                            Text(
                                "R",
                                color = Color.White.copy(alpha = 0.7f),
                                fontSize = 11.sp,
                                modifier = Modifier.width(20.dp)
                            )
                            TextButton(
                                onClick = { 
                                    android.util.Log.d("TestSongScreen", "Balance reset button pressed")
                                    balance = 0.5f 
                                },
                                modifier = Modifier.height(28.dp),
                                contentPadding = PaddingValues(horizontal = 8.dp)
                            ) {
                                Text("Reset", color = accentColor, fontSize = 10.sp)
                            }
                        }
                    }
                }
            }
        }
    ) { padding ->
        if (!hasPermission) {
            Box(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(padding),
                contentAlignment = Alignment.Center
            ) {
    Column(
                    horizontalAlignment = Alignment.CenterHorizontally,
                    modifier = Modifier.padding(24.dp)
                ) {
                    Icon(
                        Icons.Filled.FolderOff,
                        contentDescription = null,
                        tint = accentColor,
                        modifier = Modifier.size(64.dp)
                    )
                    Spacer(modifier = Modifier.height(16.dp))
                    Text(
                        "Storage permission required",
                        color = Color.White,
                        style = MaterialTheme.typography.titleMedium,
                        fontWeight = FontWeight.Bold
                    )
                    Text(
                        "Allow access to play test songs",
                        color = Color.White.copy(alpha = 0.7f),
                        style = MaterialTheme.typography.bodyMedium
                    )
                }
            }
        } else if (audioFiles.isEmpty()) {
            Box(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(padding),
                contentAlignment = Alignment.Center
            ) {
                Column(horizontalAlignment = Alignment.CenterHorizontally) {
                    CircularProgressIndicator(color = accentColor)
                    Spacer(modifier = Modifier.height(16.dp))
                    Text("Loading songs...", color = Color.White)
                }
            }
        } else {
            // Driver Pair Display state (outside LazyColumn)
            var driverPairsText by remember { mutableStateOf<String?>(null) }
            LaunchedEffect(actualSerialNumber) {
                if (actualSerialNumber.isNotBlank()) {
                    try {
                        val latestDriverPairing = withContext(Dispatchers.IO) {
                            repository.getLatestDriverPairing(actualSerialNumber)
                        }
                        if (latestDriverPairing != null && latestDriverPairing.driverData.isNotBlank()) {
                            val driverDataJson = org.json.JSONArray(latestDriverPairing.driverData)
                            // Get only the latest (last) pair
                            if (driverDataJson.length() > 0) {
                                val lastIndex = driverDataJson.length() - 1
                                val pairObj = driverDataJson.getJSONObject(lastIndex)
                                val driver1 = pairObj.optString("driver1", "")
                                val driver2 = pairObj.optString("driver2", "")
                                if (driver1.isNotBlank() || driver2.isNotBlank()) {
                                    driverPairsText = "Pair ${lastIndex + 1}: R-$driver1 L-$driver2"
                                } else {
                                    driverPairsText = null
                                }
                            } else {
                                driverPairsText = null
                            }
                        } else {
                            driverPairsText = null
                        }
                    } catch (e: Exception) {
                        android.util.Log.e("TestSongScreen", "Error fetching driver pairs", e)
                        driverPairsText = null
                    }
                } else {
                    driverPairsText = null
                }
            }

            LazyColumn(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(padding)
                    .padding(horizontal = 16.dp, vertical = 8.dp),
                verticalArrangement = Arrangement.spacedBy(8.dp)
            ) {
                item {
                    if (driverPairsText != null) {
                        Card(
                            modifier = Modifier.fillMaxWidth(),
                            colors = CardDefaults.cardColors(containerColor = Color(0xFF2196F3).copy(alpha = 0.2f)),
                            shape = RoundedCornerShape(8.dp)
                        ) {
                            Row(
                                modifier = Modifier
                                    .fillMaxWidth()
                                    .padding(12.dp),
                                verticalAlignment = Alignment.CenterVertically,
                                horizontalArrangement = Arrangement.spacedBy(8.dp)
                            ) {
                                Icon(
                                    Icons.Filled.Speaker,
                                    contentDescription = "Driver Pairs",
                                    tint = Color(0xFF2196F3),
                                    modifier = Modifier.size(20.dp)
                                )
                                Text(
                                    driverPairsText!!,
                                    style = MaterialTheme.typography.bodyMedium,
                                    color = Color.White,
                                    fontSize = 13.sp,
                                    fontWeight = FontWeight.Medium
                                )
                            }
                        }
                    }
                }

                item {
                    Column {
                        // Header with overall review button
                        Row(
                            modifier = Modifier.fillMaxWidth(),
                            horizontalArrangement = Arrangement.SpaceBetween,
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            Text(
                                "Available Test Songs",
                                style = MaterialTheme.typography.titleMedium,
                                color = accentColor,
                                fontWeight = FontWeight.Bold
                            )

                            // Overall Headphone Review Button
                            Button(
                                onClick = {
                                    android.util.Log.d("TestSongScreen", " Overall headphone review button pressed")
                                    android.util.Log.d("TestSongScreen", " Setting showReview = true, previousTestResult = null")
                                    previousTestResult = null // Reset for fresh review
                                    showReview = true
                                    android.util.Log.d("TestSongScreen", " showReview set to true")
                                },
                                colors = ButtonDefaults.buttonColors(
                                    containerColor = Color(0xFFFF9800),
                                    contentColor = Color.White
                                ),
                                modifier = Modifier.height(40.dp)
                            ) {
                                Icon(
                                    Icons.Filled.Headphones,
                                    contentDescription = null,
                                    modifier = Modifier.size(18.dp)
                                )
                                Spacer(modifier = Modifier.width(8.dp))
                                Text("Review Headphone", fontSize = 12.sp, fontWeight = FontWeight.Bold)
                            }
                        }

                        Spacer(modifier = Modifier.height(8.dp))
                    }
                }

                itemsIndexed(audioFiles, key = { _, item -> item.file.absolutePath }) { index, audioFile ->
            Card(
                modifier = Modifier
                    .fillMaxWidth()
                    .animateItemPlacement()
                            .clickable {
                                android.util.Log.d("TestSongScreen", "Song selected: ${audioFile.name}")

                                val audioManager = context.getSystemService(android.content.Context.AUDIO_SERVICE) as AudioManager

                                // Abandon previous audio focus if we have it
                                if (hasAudioFocus) {
                                    android.util.Log.d("TestSongScreen", "Abandoning previous audio focus before requesting new one")
                                    try {
                                        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                                            currentAudioFocusRequest?.let {
                                                audioManager.abandonAudioFocusRequest(it)
                                            }
                                        } else {
                                            @Suppress("DEPRECATION")
                                            audioManager.abandonAudioFocus(null)
                                        }
                                        hasAudioFocus = false
                                        currentAudioFocusRequest = null
                                    } catch (e: Exception) {
                                        android.util.Log.e("TestSongScreen", "Error abandoning audio focus", e)
                                    }
                                }

                                // Request audio focus before playing
                                val audioFocusRequest = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                                    AudioFocusRequest.Builder(AudioManager.AUDIOFOCUS_GAIN)
                                        .setAudioAttributes(android.media.AudioAttributes.Builder()
                                            .setUsage(android.media.AudioAttributes.USAGE_MEDIA)
                                            .setContentType(android.media.AudioAttributes.CONTENT_TYPE_MUSIC)
                                            .build())
                                        .setAcceptsDelayedFocusGain(true)
                                        .setOnAudioFocusChangeListener { focusChange ->
                                            android.util.Log.d("TestSongScreen", "Audio focus change: $focusChange")
                                            when (focusChange) {
                                                AudioManager.AUDIOFOCUS_LOSS -> {
                                                    android.util.Log.d("TestSongScreen", "Audio focus lost (permanent) - stopping playback")
                                                    try {
                                                        vlcPlayerManager?.let { player ->
                                                            if (player.isPlaying()) {
                                                                player.stop()
                                                            }
                                                            player.release()
                                                        }
                                                    } catch (e: Exception) {
                                                        android.util.Log.e("TestSongScreen", "Error releasing player on focus loss", e)
                                                    }
                                                    isPlaying = false
                                                    hasAudioFocus = false
                                                    currentAudioFocusRequest = null
                                                }
                                                AudioManager.AUDIOFOCUS_LOSS_TRANSIENT -> {
                                                    android.util.Log.d("TestSongScreen", "Audio focus lost transient - pausing playback")
                                                    try {
                                                        vlcPlayerManager?.pause()
                                                    } catch (e: Exception) {
                                                        android.util.Log.e("TestSongScreen", "Error pausing player", e)
                                                    }
                                                    isPlaying = false
                                                }
                                                AudioManager.AUDIOFOCUS_GAIN -> {
                                                    android.util.Log.d("TestSongScreen", "Audio focus gained - resuming playback if needed")
                                                    hasAudioFocus = true
                                                    // Don't auto-resume, let user control playback
                                                }
                                            }
                                        }
                                        .build()
                                } else {
                                    null
                                }

                                val focusResult = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                                    audioManager.requestAudioFocus(audioFocusRequest!!)
                                } else {
                                    @Suppress("DEPRECATION")
                                    audioManager.requestAudioFocus(
                                        { focusChange ->
                                            android.util.Log.d("TestSongScreen", "Audio focus change (legacy): $focusChange")
                                            when (focusChange) {
                                                AudioManager.AUDIOFOCUS_LOSS -> {
                                                    android.util.Log.d("TestSongScreen", "Audio focus lost (permanent) - stopping playback")
                                                    try {
                                                        vlcPlayerManager?.let { player ->
                                                            if (player.isPlaying()) {
                                                                player.stop()
                                                            }
                                                            player.release()
                                                        }
                                                    } catch (e: Exception) {
                                                        android.util.Log.e("TestSongScreen", "Error releasing player on focus loss", e)
                                                    }
                                                    isPlaying = false
                                                    hasAudioFocus = false
                                                }
                                                AudioManager.AUDIOFOCUS_LOSS_TRANSIENT -> {
                                                    android.util.Log.d("TestSongScreen", "Audio focus lost transient - pausing playback")
                                                    try {
                                                        vlcPlayerManager?.pause()
                                                    } catch (e: Exception) {
                                                        android.util.Log.e("TestSongScreen", "Error pausing player", e)
                                                    }
                                                    isPlaying = false
                                                }
                                                AudioManager.AUDIOFOCUS_GAIN -> {
                                                    android.util.Log.d("TestSongScreen", "Audio focus gained")
                                                    hasAudioFocus = true
                                                }
                                            }
                                        },
                                        AudioManager.STREAM_MUSIC,
                                        AudioManager.AUDIOFOCUS_GAIN
                                    )
                                }

                                if (focusResult != AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
                                    android.util.Log.w("TestSongScreen", "Audio focus not granted (result: $focusResult) - cannot play song")
                                    return@clickable
                                }

                                // Store the audio focus request and mark that we have focus
                                if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                                    currentAudioFocusRequest = audioFocusRequest
                                }
                                hasAudioFocus = true
                                android.util.Log.d("TestSongScreen", "Audio focus granted successfully")

                                // Stop and release current player properly before starting new one
                                try {
                                    vlcPlayerManager?.let { player ->
                                        if (player.isPlaying()) {
                                            player.stop()
                                        }
                                        player.release()
                                    }
                                } catch (e: Exception) {
                                    android.util.Log.e("TestSongScreen", "Error releasing previous VLC player", e)
                                }

                                // Stop tracking for previous song if playing a different song
                                currentSong?.let { previousSong ->
                                    if (previousSong.name != audioFile.name) {
                                        android.util.Log.d("TestSongScreen", " Stopping previous song tracking: ${previousSong.name}")
                                        playbackTracker.stopPlayback(previousSong.name)
                                    }
                                }

                                // Play new song with VLC
                                try {
                                    vlcPlayerManager?.let { player ->
                                        // Apply current volume and balance before playing
                                        val leftVolume = volume * (1f - balance).coerceIn(0f, 1f)
                                        val rightVolume = volume * balance.coerceIn(0f, 1f)
                                        player.setStereoVolume(leftVolume, rightVolume)

                                        // Play the file
                                        player.play(audioFile.file)

                                        android.util.Log.d("TestSongScreen", " VLC: Started playing: ${audioFile.name}")

                                        // Start tracking playback
                                        playbackTracker.startPlayback(audioFile.name)
                                    } ?: run {
                                        android.util.Log.e("TestSongScreen", " VLC Player Manager not initialized")
                                    }

                                    currentSong = audioFile
                                    currentPosition = 0
                                    seekPosition = 0
                                } catch (e: Exception) {
                                    android.util.Log.e("TestSongScreen", " Error playing song with VLC: ${audioFile.name}", e)
                                    currentSong = null
                                    isPlaying = false
                                }
                            },
                        colors = CardDefaults.cardColors(
                            containerColor = if (currentSong == audioFile && isPlaying) 
                                Color(0xFF3B3B3B) else surfaceColor
                        ),
                        shape = RoundedCornerShape(12.dp)
                    ) {
                        Row(
                            modifier = Modifier
                                .fillMaxWidth()
                                .padding(12.dp),
                            horizontalArrangement = Arrangement.SpaceBetween,
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            // Drag Handle (left side - like Spotify)
                            Icon(
                                Icons.Filled.DragHandle,
                                contentDescription = "Reorder",
                                tint = Color.White.copy(alpha = 0.7f),
                                modifier = Modifier
                                    .size(24.dp)
                                    .pointerInput(index) {
                                        detectDragGesturesAfterLongPress(
                                            onDragStart = { offset ->
                                                draggedIndex = index
                                                Log.d("TestSongScreen", "Drag started for index $index")
                                            },
                                            onDrag = { change, dragAmount ->
                                                change.consume()
                                                val delta = dragAmount.y
                                                val threshold = 30f // Reduced threshold for more responsive dragging

                                                if (delta > threshold && index < audioFiles.size - 1) {
                                                    // Move down
                                                    val item = audioFiles[index]
                                                    audioFiles.removeAt(index)
                                                    audioFiles.add(index + 1, item)
                                                    draggedIndex = index + 1
                                                    Log.d("TestSongScreen", "Moved item down from $index to ${index + 1}")
                                                } else if (delta < -threshold && index > 0) {
                                                    // Move up
                                                    val item = audioFiles[index]
                                                    audioFiles.removeAt(index)
                                                    audioFiles.add(index - 1, item)
                                                    draggedIndex = index - 1
                                                    Log.d("TestSongScreen", "Moved item up from $index to ${index - 1}")
                                                }
                                            },
                                            onDragEnd = {
                                                draggedIndex = null
                                                Log.d("TestSongScreen", "Drag ended")
                                            },
                                            onDragCancel = {
                                                draggedIndex = null
                                                Log.d("TestSongScreen", "Drag cancelled")
                                            }
                                        )
                                    }
                            )

                            Spacer(modifier = Modifier.width(8.dp))

                            Row(
                                horizontalArrangement = Arrangement.spacedBy(12.dp),
                                verticalAlignment = Alignment.CenterVertically,
                                modifier = Modifier.weight(1f)
                            ) {
                                // Icon
                                Box(
                                    modifier = Modifier
                                        .size(48.dp)
                                        .clip(RoundedCornerShape(8.dp))
                                        .background(
                                            if (currentSong == audioFile && isPlaying) 
                                                accentColor else Color(0xFF424242)
                                        ),
                                    contentAlignment = Alignment.Center
                                ) {
                                    Icon(
                                        if (currentSong == audioFile && isPlaying) 
                                            Icons.Filled.MusicNote else Icons.Filled.AudioFile,
                                        contentDescription = null,
                                        tint = if (currentSong == audioFile && isPlaying) 
                                            bgColor else Color.White.copy(alpha = 0.7f),
                                        modifier = Modifier.size(24.dp)
                                    )
                                }

                                // Song Info
                                Column(modifier = Modifier.weight(1f)) {
                                    Text(
                                        audioFile.name,
                                        style = MaterialTheme.typography.titleSmall,
                                        color = Color.White,
                                        fontWeight = FontWeight.SemiBold,
                                        maxLines = 1,
                                        overflow = TextOverflow.Ellipsis
                                    )
                                    Spacer(modifier = Modifier.height(2.dp))
                                    Text(
                                        "${audioFileManager.formatDuration(audioFile.duration)}  ${audioFileManager.formatFileSize(audioFile.size)}",
                                        style = MaterialTheme.typography.bodySmall,
                                        color = Color.White.copy(alpha = 0.7f),
                                        fontSize = 11.sp
                                    )
                                }
                            }

                            // Action buttons
                            Row(
                                horizontalArrangement = Arrangement.spacedBy(8.dp),
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                // Review button for this song
                                Button(
                                    onClick = {
                                        android.util.Log.d("TestSongScreen", "Review button pressed for song: ${audioFile.name}")
                                        songToReview = audioFile
                                        reviewFromOverallScreen = false // Review from Test Song Screen, not overall review
                                        showReviewDialog = true
                                    },
                                    enabled = actualSerialNumber.isNotBlank(),
                                    modifier = Modifier.height(36.dp),
                                    colors = ButtonDefaults.buttonColors(
                                        containerColor = if (actualSerialNumber.isNotBlank()) Color(0xFF4CAF50) else Color(0xFF666666),
                                        contentColor = Color.White
                                    ),
                                    contentPadding = PaddingValues(horizontal = 12.dp, vertical = 0.dp)
                                ) {
                                    Text("Review", fontSize = 12.sp, fontWeight = FontWeight.Bold)
                                }

                                if (currentSong == audioFile && isPlaying) {
                                    Icon(
                                        Icons.Filled.PlayArrow,
                                        "Playing",
                                        tint = accentColor,
                                        modifier = Modifier.size(32.dp)
                                    )
                                }
                            }
                        }
                    }
                }

                if (actualSerialNumber.isBlank()) {
                    item {
                        Card(
                            colors = CardDefaults.cardColors(
                                containerColor = Color(0x33FFC107)
                            ),
                            modifier = Modifier.padding(vertical = 8.dp)
                        ) {
                            Row(
                                modifier = Modifier.padding(12.dp),
                                horizontalArrangement = Arrangement.spacedBy(8.dp),
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Icon(
                                    Icons.Filled.Warning,
                                    contentDescription = null,
                                    tint = accentColor,
                                    modifier = Modifier.size(20.dp)
                                )
                                Text(
                                    "Scan a QR code first to enable test result saving",
                                    color = Color.White,
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = 11.sp
                                )
                            }
                            }
                        }
                    }
                }
            }
        }

    // Song Review Dialog - Individual Song (TestSongReviewScreen)
        if (showReviewDialog && songToReview != null) {
            TestSongReviewScreen(
                testType = TestType.SONG,
                previousTestResult = null,
                onResultSubmitted = { result ->
                    android.util.Log.d("TestSongScreen", " Individual song review submitted")
                    android.util.Log.d("TestSongScreen", " Song: ${songToReview?.name}")
                    android.util.Log.d("TestSongScreen", " Outcome: ${result.outcome}")
                    android.util.Log.d("TestSongScreen", " Issues: ${result.issues}")
                    android.util.Log.d("TestSongScreen", " Solutions: ${result.solutions}")
                    android.util.Log.d("TestSongScreen", " Remark: '${result.remark}'")
                    scope.launch {
                        // Individual songs don't require session ID - save directly
                        // Convert issues list to JSON array format for structured storage
                        val issuesJson = if (result.issues.isEmpty()) {
                            "[]"
                        } else {
                            result.issues.joinToString(", ", prefix = "[", postfix = "]") { "\"$it\"" }
                        }

                        // Get entry timestamp
                        val entryTimestamp = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date(sessionStartTime ?: System.currentTimeMillis()))

                        // Get playback duration from tracker for this song
                        val currentSongName = songToReview?.name ?: ""
                        val durationMs = if (currentSongName.isNotEmpty()) {
                            playbackTracker.getSongDuration(currentSongName)
                        } else {
                            0L
                        }

                        // Format duration as HH:MM:SS
                        val totalHours = durationMs / (1000 * 60 * 60)
                        val totalMinutes = (durationMs % (1000 * 60 * 60)) / (1000 * 60)
                        val totalSeconds = (durationMs % (1000 * 60)) / 1000
                        val formattedDuration = String.format("%02d:%02d:%02d", totalHours, totalMinutes, totalSeconds)

                        android.util.Log.d("TestSongScreen", " [DURATION] Song: $currentSongName, Duration: ${durationMs}ms = $formattedDuration")

                        val songTest = SongTest(
                            serialNumber = actualSerialNumber,
                            testStage = result.testStage,
                            operatorName = actualOperatorName,
                            entryTimestamp = entryTimestamp,
                            exitTimestamp = entryTimestamp, // Will be updated at final submit
                            totalTimeSpent = formattedDuration, // Store actual playback duration
                            songName = currentSongName,
                            result = result.outcome.name,
                            issues = issuesJson, // JSON array format: ["Issue 1", "Issue 2"]
                            solutions = "", // Individual songs don't have solutions
                            remark = result.remark,
                            timestamp = java.time.Instant.now().toString(),
                            iterationNumber = currentIterationNumber,
                            isOverallResult = false
                        )

                        android.util.Log.d("TestSongScreen", " Saving individual song test data:")
                        android.util.Log.d("TestSongScreen", "   Song: ${songTest.songName}")
                        android.util.Log.d("TestSongScreen", "   Result: ${songTest.result}")
                        android.util.Log.d("TestSongScreen", "   Issues: ${songTest.issues}")
                        android.util.Log.d("TestSongScreen", "   Serial: ${songTest.serialNumber}")
                        android.util.Log.d("TestSongScreen", "   Operator: ${songTest.operatorName}")
                        android.util.Log.d("TestSongScreen", "   Entry Timestamp: ${songTest.entryTimestamp}")
                        android.util.Log.d("TestSongScreen", "   Iteration Number: ${songTest.iterationNumber}")

                        val operatorId = authenticatedOperator?.operatorId
                        val testId = repository.insertSongTest(songTest, operatorId)
                        android.util.Log.d("TestSongScreen", " Individual song test saved successfully to database with ID: $testId")

                        // Mark song as reviewed in tracker
                        val reviewedSongName = songToReview?.name
                        songToReview?.let { song ->
                            playbackTracker.markAsReviewed(song.name, result.outcome.name)
                            android.util.Log.d("TestSongScreen", " Marked song as reviewed in tracker: ${song.name} -> ${result.outcome.name}")
                        }

                        // Update milestone for test song (individual songs don't update milestone, only overall review does)
                        // Milestone is updated only when overall review is committed

                        showReviewDialog = false
                        val wasFromOverallScreen = reviewFromOverallScreen
                        reviewFromOverallScreen = false
                        songToReview = null

                        if (wasFromOverallScreen) {
                            // Reopen overall review dialog if review was opened from overall review screen
                            android.util.Log.d("TestSongScreen", " Review dialog closed - reopening overall review dialog")
                            kotlinx.coroutines.delay(100) // Small delay to ensure dialog closes properly
                            showReview = true
                            android.util.Log.d("TestSongScreen", " Overall review dialog reopened")
                        } else {
                            // Stay on Test Song Screen if review was opened directly from Test Song Screen
                            android.util.Log.d("TestSongScreen", " Review dialog closed - staying on Test Song page")
                        }
                    }
                },
                onCancel = {
                    showReviewDialog = false
                    val wasFromOverallScreen = reviewFromOverallScreen
                    reviewFromOverallScreen = false
                    songToReview = null

                    if (wasFromOverallScreen) {
                        // Reopen overall review dialog if review was opened from overall review screen
                        android.util.Log.d("TestSongScreen", " Individual song review cancelled - reopening overall review dialog")
                        scope.launch {
                            kotlinx.coroutines.delay(100) // Small delay to ensure dialog closes properly
                            showReview = true
                            android.util.Log.d("TestSongScreen", " Overall review dialog reopened after cancel")
                        }
                    } else {
                        // Stay on Test Song Screen if review was opened directly from Test Song Screen
                        android.util.Log.d("TestSongScreen", " Individual song review cancelled - staying on Test Song page")
                    }
                },
                validator = reviewValidator
            )
        }

        // Handle back button - intercept and open overall review dialog instead of exiting
        BackHandler(enabled = !showReview && !showReviewDialog) {
            android.util.Log.d("TestSongScreen", " Back button pressed - opening overall review dialog instead of exiting")
            previousTestResult = null // Reset for fresh review
            showReview = true
        }

        // Check for undo payload when screen becomes visible (after navigation back)
        val undoState by undoManager.undoState.collectAsState()

        // Track if we just restored from undo to prevent clearing flags prematurely
        var previousUndoActive by remember { mutableStateOf(false) }

        // Check for payload whenever screen becomes visible (including after navigation)
        DisposableEffect(Unit) {
            android.util.Log.d("TestSongScreen", " DisposableEffect: Screen visible - checking for undo payload")
            // Only check for undo payload if we're not already showing review dialog
            if (!showReview) {
                val undoPayload = undoManager.getAndClearLastUndo()
                if (undoPayload != null) {
                    val (restoredResult, fromScreen) = undoPayload
                    android.util.Log.d("TestSongScreen", " UNDO PAYLOAD found!")
                    android.util.Log.d("TestSongScreen", " From screen: $fromScreen, Result: ${restoredResult.outcome}")

                    if (fromScreen == "TEST_SONGS") {
                        // Set flags to trigger dialog opening
                        previousTestResult = restoredResult
                        shouldShowReviewAfterUndo = true
                        isFromUndo = true
                        isRestoringFromUndo = true
                        android.util.Log.d("TestSongScreen", " UNDO PAYLOAD: Set flags - dialog will open at step: ${restoredResult.outcome}")
                    }
                } else {
                    android.util.Log.d("TestSongScreen", " No undo payload found")
                }
            } else {
                android.util.Log.d("TestSongScreen", " Dialog already showing, skipping undo payload check")
            }

            onDispose { }
        }

        // Watch undo state changes - check for payload when undo becomes inactive
        LaunchedEffect(undoState.isActive) {
            val undoBecameInactive = previousUndoActive && !undoState.isActive
            previousUndoActive = undoState.isActive

            if (undoBecameInactive && !isRestoringFromUndo) {
                // Undo was just executed - check for payload
                android.util.Log.d("TestSongScreen", " Undo became inactive - checking for payload")
                val undoPayload = undoManager.getAndClearLastUndo()
                if (undoPayload != null) {
                    val (restoredResult, fromScreen) = undoPayload
                    android.util.Log.d("TestSongScreen", " UNDO PAYLOAD found after undo execution!")

                    if (fromScreen == "TEST_SONGS") {
                        previousTestResult = restoredResult
                        shouldShowReviewAfterUndo = true
                        isFromUndo = true
                        isRestoringFromUndo = true
                        android.util.Log.d("TestSongScreen", " UNDO PAYLOAD: Set flags from undo state change")
                    }
                }
            } else if (!undoState.isActive && !isRestoringFromUndo) {
                // Clear any stale payload/flags
                val stalePayload = undoManager.getAndClearLastUndo()
                if (stalePayload != null) {
                    android.util.Log.d("TestSongScreen", " Found stale undo payload - cleared")
                }
                if (shouldShowReviewAfterUndo) {
                    android.util.Log.d("TestSongScreen", " Clearing stale restoration flags")
                    shouldShowReviewAfterUndo = false
                    previousTestResult = null
                }
            }
        }

        // Watch for undo restoration - trigger when both flags are set
        LaunchedEffect(previousTestResult, shouldShowReviewAfterUndo) {
            if (shouldShowReviewAfterUndo && previousTestResult != null) {
                val outcome = previousTestResult!!.outcome
                val stepName = when (outcome) {
                    com.example.fqcapp.data.TestOutcome.PASS -> "PASS_CHECKS"
                    com.example.fqcapp.data.TestOutcome.FAIL -> "FAIL_SOLUTIONS"
                    com.example.fqcapp.data.TestOutcome.CAN_BE_PASSED -> "CAN_BE_PASSED"
                    com.example.fqcapp.data.TestOutcome.TEST_NOT_DONE -> "TEST_NOT_DONE"
                }

                android.util.Log.d("TestSongScreen", " UNDO: Opening review dialog at submission step: $stepName")
                android.util.Log.d("TestSongScreen", " Outcome: $outcome")

                // Minimal delay to ensure navigation to Test Song screen has completed
                kotlinx.coroutines.delay(100)
                showReview = true
                shouldShowReviewAfterUndo = false
                isRestoringFromUndo = false
                android.util.Log.d("TestSongScreen", " Review dialog opened at step: $stepName")
            }
        }

        // Full-screen review overlay (overall + undo buffer)
        LaunchedEffect(showReview) {
            android.util.Log.d("TestSongScreen", " LaunchedEffect: showReview changed to: $showReview")
        }

        if (showReview) {
            android.util.Log.d("TestSongScreen", " Rendering overall review dialog")
            androidx.compose.material3.BasicAlertDialog(
                onDismissRequest = {
                    // Only dismiss if user explicitly closes (not via back button - that's handled internally)
                    android.util.Log.d("TestSongScreen", " Dialog dismissed")
                    showReview = false
                    if (isFromUndo) {
                        isFromUndo = false
                    }
                },
                properties = androidx.compose.ui.window.DialogProperties(
                    usePlatformDefaultWidth = false,
                    decorFitsSystemWindows = false,
                    dismissOnBackPress = false // Disable default back press handling - we handle it internally
                )
            ) {
                Box(
                    modifier = Modifier
                        .fillMaxSize()
                        .fillMaxWidth(),
                    contentAlignment = Alignment.Center
                ) {
                    TestSongOverallReviewScreenContainer(
                        testType = TestType.SONG,
                        previousTestResult = previousTestResult,
                        onResultSubmitted = { result ->
                            android.util.Log.d("TestSongScreen", " Overall review submitted: ${result.outcome}")
                            // Store result for undo restoration
                            previousTestResult = result
                            android.util.Log.d("TestSongScreen", " Stored previousTestResult for undo: ${result.outcome}")

                            // Close review dialog
                            showReview = false

                            // Start undo timer - buffer the submission
                            android.util.Log.d("TestSongScreen", " Calling startUndo with scope: $scope")
                            undoManager.startUndo(
                                testResult = result,
                                onUndo = {
                                    android.util.Log.d("TestSongScreen", " UNDO pressed - navigating to Test Song Screen")
                                    android.util.Log.d("TestSongScreen", " Result to show at step: ${result.outcome}")

                                    // UndoManager.executeUndo() already stored payload in lastUndoPayload
                                    // Just navigate - DisposableEffect will check payload and open dialog
                                    android.util.Log.d("TestSongScreen", " Payload stored in UndoManager - navigating to Test Song Screen")
                                    android.util.Log.d("TestSongScreen", " DisposableEffect will detect payload and open review dialog")

                                    // Explicitly navigate to Test Song Screen (don't use onBack since back stack might be cleared)
                                    // Use explicit navigation callback if available, otherwise fall back to onBack
                                    onNavigateToTestSongs?.invoke() ?: onBack?.invoke()
                                },
                                onCommit = suspend {
                                    android.util.Log.d("TestSongScreen", " COMMIT after 30s - saving overall song test to DB")

                                    // Save overall song test to database
                                    val issuesJson = if (result.issues.isEmpty()) {
                                        "[]"
                                    } else {
                                        result.issues.joinToString(", ", prefix = "[", postfix = "]") { "\"$it\"" }
                                    }

                                    val solutionsJson = if (result.solutions.isEmpty()) {
                                        "[]"
                                    } else {
                                        result.solutions.joinToString(", ", prefix = "[", postfix = "]") { "\"$it\"" }
                                    }

                                    val entryTimestamp = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date(sessionStartTime ?: System.currentTimeMillis()))
                                    val exitTimestamp = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date())

                                    // Calculate total time spent (from session start to now)
                                    val totalTimeMs = (sessionStartTime ?: System.currentTimeMillis()).let { start ->
                                        System.currentTimeMillis() - start
                                    }
                                    val totalHours = totalTimeMs / (1000 * 60 * 60)
                                    val totalMinutes = (totalTimeMs % (1000 * 60 * 60)) / (1000 * 60)
                                    val totalSeconds = (totalTimeMs % (1000 * 60)) / 1000
                                    val formattedDuration = String.format("%02d:%02d:%02d", totalHours, totalMinutes, totalSeconds)

                                    val songTest = SongTest(
                                        serialNumber = actualSerialNumber,
                                        testStage = result.testStage,
                                        operatorName = actualOperatorName,
                                        entryTimestamp = entryTimestamp,
                                        exitTimestamp = exitTimestamp,
                                        totalTimeSpent = formattedDuration,
                                        songName = "OVERALL", // Overall review doesn't have a specific song
                                        result = result.outcome.name,
                                        issues = issuesJson,
                                        solutions = solutionsJson,
                                        remark = result.remark,
                                        timestamp = java.time.Instant.now().toString(),
                                        iterationNumber = currentIterationNumber,
                                        isOverallResult = true
                                    )

                                    val operatorId = authenticatedOperator?.operatorId
                                    val testId = repository.insertSongTest(songTest, operatorId)
                                    android.util.Log.d("TestSongScreen", " Overall song test committed with ID: $testId")

                                    // Add solution reminders if test failed and has solutions
                                    if (result.outcome == TestOutcome.FAIL && 
                                        result.solutions.isNotEmpty() &&
                                        solutionReminderManager != null &&
                                        actualSerialNumber.isNotBlank()) {
                                        try {
                                            solutionReminderManager.addPendingSolution(
                                                serialNumber = actualSerialNumber,
                                                testType = TestType.SONG,
                                                solutions = result.solutions,
                                                testId = testId
                                            )
                                            android.util.Log.d("TestSongScreen", " Added ${result.solutions.size} solution reminders")
                                        } catch (e: Exception) {
                                            android.util.Log.e("TestSongScreen", " Error adding solution reminders", e)
                                        }
                                    }

                                    // Update milestone
                                    if (actualSerialNumber.isNotBlank() && result.outcome != TestOutcome.TEST_NOT_DONE) {
                                        try {
                                            val milestoneService = com.example.fqcapp.services.MilestoneUpdateService(repository)
                                            milestoneService.updateTestSongStatus(actualSerialNumber, result.outcome.name)
                                            android.util.Log.d("TestSongScreen", " Milestone updated for song test")
                                        } catch (e: Exception) {
                                            android.util.Log.e("TestSongScreen", " Error updating milestone", e)
                                        }
                                    }

                                    // Navigate to landing page
                                    onNavigateToLanding?.invoke() ?: onBack?.invoke() ?: Unit
                                },
                                scope = scope,
                                fromScreen = "TEST_SONGS"
                            )

                            // Navigate to landing page immediately (undo snackbar will show there)
                            onNavigateToLanding?.invoke() ?: onBack?.invoke()
                        },
                        onCancel = {
                            android.util.Log.d("TestSongScreen", " Overall review cancelled")
                            showReview = false
                        },
                        playbackTracker = playbackTracker,
                        onSongReviewRequested = { songName ->
                            // Find the audio file and open review dialog
                            android.util.Log.d("TestSongScreen", " [NAVIGATION] Opening review for song: $songName")
                            val audioFile = audioFiles.find { it.name == songName }
                            if (audioFile != null) {
                                songToReview = audioFile
                                reviewFromOverallScreen = true // Review from overall review screen's song table
                                // Temporarily close overall review dialog to show individual song review
                                showReview = false
                                showReviewDialog = true
                                android.util.Log.d("TestSongScreen", " [NAVIGATION] Individual song review dialog opened (overall review temporarily closed)")
                            } else {
                                android.util.Log.w("TestSongScreen", " Song not found in audio files: $songName")
                            }
                        }
                    )
                }
            }
        }

    // Removed full-screen overlay review (reverted)

    // Restore default back behavior (no forced review on back)

    // Session Handover Banner (inside Box)
    sessionHandover?.let { message ->
        Card(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            colors = CardDefaults.cardColors(
                containerColor = Color(0xFFFF9800)
            )
        ) {
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = message,
                    color = Color.White,
                    fontWeight = FontWeight.Bold
                )
                IconButton(
                    onClick = { sessionManager.clearHandoverNotification() }
                ) {
                    Icon(
                        Icons.Filled.Close,
                        contentDescription = "Dismiss",
                        tint = Color.White
                    )
                }
            }
        }
    }

    // Undo Snackbar (shown when undoState is active)
    if (undoState.isActive && undoState.fromScreen == "TEST_SONGS") {
        Surface(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp)
                .align(Alignment.BottomCenter),
            shape = RoundedCornerShape(12.dp),
            color = Color(0xFF2B2B2B),
            tonalElevation = 8.dp
        ) {
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Column(modifier = Modifier.weight(1f)) {
                    Text(
                        text = "Test saved. Tap undo to reverse.",
                        style = MaterialTheme.typography.bodyMedium,
                        color = Color.White
                    )
                    Text(
                        text = "Auto-saving in ${undoState.timeRemaining}s",
                        style = MaterialTheme.typography.bodySmall,
                        color = Color.White.copy(alpha = 0.7f),
                        fontSize = 12.sp
                    )
                }
                Row(horizontalArrangement = Arrangement.spacedBy(8.dp), verticalAlignment = Alignment.CenterVertically) {
                    TextButton(
                        onClick = { 
                            android.util.Log.d("TestSongScreen", "Cancel (commit now) clicked.")
                            scope.launch { 
                                undoManager.executeCommitNow()
                            }
                        }
                    ) {
                        Text(
                            text = "CANCEL",
                            color = Color(0xFF90CAF9),
                            fontWeight = FontWeight.Bold
                        )
                    }
                    TextButton(
                        onClick = { 
                            android.util.Log.d("TestSongScreen", "Undo button clicked.")
                            undoManager.executeUndo()
                        }
                    ) {
                        Text(
                            text = "UNDO",
                            color = Color(0xFFFFC107),
                            fontWeight = FontWeight.Bold
                        )
                    }
                }
            }
        }
    }
    }  // Closing Box
}

package com.example.fqcapp.data.dao

import androidx.room.*
import com.example.fqcapp.data.entities.SongTest
import kotlinx.coroutines.flow.Flow

@Dao
interface SongTestDao {
    @Insert
    suspend fun insert(test: SongTest): Long


    @Query("SELECT * FROM song_tests WHERE serialNumber = :serialNumber ORDER BY timestamp ASC")
    suspend fun getSongTests(serialNumber: String): List<SongTest>

    @Query("SELECT * FROM song_tests WHERE testId = :id")
    suspend fun getTestById(id: Long): SongTest?

    @Query("SELECT * FROM song_tests WHERE serialNumber = :serialNumber AND needsSync = 1")
    suspend fun getUnsyncedSongTests(serialNumber: String): List<SongTest>

    @Query("SELECT * FROM song_tests WHERE needsSync = 1")
    suspend fun getAllUnsyncedSongTests(): List<SongTest>

    @Query("SELECT * FROM song_tests ORDER BY timestamp DESC")
    suspend fun getAllSongTests(): List<SongTest>

    @Query("UPDATE song_tests SET needsSync = 0, lastSyncTimestamp = :timestamp WHERE testId = :testId")
    suspend fun markAsSynced(testId: Long, timestamp: Long = System.currentTimeMillis())

    @Query("UPDATE song_tests SET needsSync = 0, lastSyncTimestamp = :timestamp WHERE testId IN (:testIds)")
    suspend fun markMultipleAsSynced(testIds: List<Long>, timestamp: Long = System.currentTimeMillis())

    @Query("UPDATE song_tests SET needsSync = :needsSync WHERE testId = :testId")
    suspend fun updateNeedsSync(testId: Long, needsSync: Boolean)

    @Delete
    suspend fun delete(test: SongTest)

    // Get max iteration number for a serial number
    @Query("SELECT COALESCE(MAX(iterationNumber), 0) FROM song_tests WHERE serialNumber = :serialNumber AND isOverallResult = 1")
    suspend fun getMaxIterationNumber(serialNumber: String): Int

    // Get individual song tests (not overall) for a serial number and iteration, ordered by timestamp (latest first)
    @Query("SELECT * FROM song_tests WHERE serialNumber = :serialNumber AND iterationNumber = :iterationNumber AND isOverallResult = 0 ORDER BY timestamp DESC")
    suspend fun getIndividualSongsForIteration(serialNumber: String, iterationNumber: Int): List<SongTest>

    // Delete individual song tests beyond the latest 3 for a given iteration
    @Query("DELETE FROM song_tests WHERE serialNumber = :serialNumber AND iterationNumber = :iterationNumber AND isOverallResult = 0 AND testId NOT IN (SELECT testId FROM song_tests WHERE serialNumber = :serialNumber AND iterationNumber = :iterationNumber AND isOverallResult = 0 ORDER BY timestamp DESC LIMIT 3)")
    suspend fun deleteOldIndividualSongs(serialNumber: String, iterationNumber: Int)

    @Query("SELECT * FROM song_tests WHERE serialNumber = :serialNumber AND testStage = :stage AND isOverallResult = 1 ORDER BY iterationNumber DESC LIMIT 1")
    suspend fun getLatestSongTestByStage(serialNumber: String, stage: String): SongTest?

    @Query("SELECT * FROM song_tests WHERE serialNumber = :serialNumber AND isOverallResult = 1 ORDER BY iterationNumber DESC")
    suspend fun getAllSongTestsBySerial(serialNumber: String): List<SongTest>

    // Get latest song test by timestamp (excluding TEST_NOT_DONE)
    @Query("SELECT * FROM song_tests WHERE serialNumber = :serialNumber AND isOverallResult = 1 AND result != 'TEST_NOT_DONE' ORDER BY timestamp DESC LIMIT 1")
    suspend fun getLatestSongTestByTimestamp(serialNumber: String): SongTest?

    // Get latest song test by configuration ID
    @Query("SELECT * FROM song_tests WHERE configurationId = :configurationId AND isOverallResult = 1 ORDER BY entryTimestamp DESC LIMIT 1")
    suspend fun getLatestByConfiguration(configurationId: String): SongTest?
}

package com.example.fqcapp

import android.util.Log
import androidx.compose.animation.AnimatedContent
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.rememberScrollState
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.foundation.verticalScroll
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.ArrowBack
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.runtime.rememberCoroutineScope
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import com.example.fqcapp.core.CriteriaRepository
import com.example.fqcapp.core.DefaultCriteriaProvider
import kotlinx.coroutines.flow.first
import kotlinx.coroutines.launch

enum class FQCStep {
    CRITERIA_CHECK,
    FINAL_QUALITY
}

enum class FinalQualityType {
    CUSTOMER,
    INTERNATIONAL,
    REVIEWER,
    MARKETING
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun FQCReviewScreen(
    operatorName: String,
    serialNumber: String,
    authenticatedOperator: com.example.fqcapp.data.entities.OperatorCredential? = null,
    solutionReminderManager: com.example.fqcapp.core.SolutionReminderManager? = null,
    onBack: () -> Unit,
    onSuccess: () -> Unit = onBack // Default to onBack if not provided
) {
    val accentColor = Color(0xFFFFC107)
    val bgColor = Color(0xFF212121)
    val surfaceColor = Color(0xFF2B2B2B)
    val context = LocalContext.current
    val scope = rememberCoroutineScope()

    // Load criteria
    val criteria by CriteriaRepository.criteria.collectAsState(DefaultCriteriaProvider.default)
    val fqcCriteria = remember(criteria) {
        if (criteria.fqcCriteria.isNotEmpty()) {
            criteria.fqcCriteria
        } else {
            DefaultCriteriaProvider.default.fqcCriteria
        }
    }

    Log.d("FQCReviewScreen", " FQC Criteria loaded: ${fqcCriteria.size} items - $fqcCriteria")

    // Log initial parameters
    Log.d("FQCReviewScreen", " [INIT] FQCReviewScreen initialized")
    Log.d("FQCReviewScreen", " [INIT] Serial Number (parameter): '$serialNumber'")
    Log.d("FQCReviewScreen", " [INIT] Operator Name (parameter): '$operatorName'")

    // State
    var currentStep by remember { mutableStateOf(FQCStep.CRITERIA_CHECK) }
    val selectedCriteria = remember { mutableStateMapOf<String, Boolean>() }
    var selectedFinalQuality by remember { mutableStateOf<FinalQualityType?>(null) }
    // Editable operator name (defaults to login name or authenticated operator)
    var editableOperatorName by remember { mutableStateOf(operatorName) }
    var operatorId by remember { mutableStateOf("") }

    // Use authenticated operator if provided
    LaunchedEffect(authenticatedOperator) {
        if (authenticatedOperator != null) {
            editableOperatorName = authenticatedOperator.operatorName
            operatorId = authenticatedOperator.operatorId
            Log.d("FQCReviewScreen", " Using authenticated operator: $editableOperatorName (ID: $operatorId)")
        } else {
            editableOperatorName = operatorName
            Log.d("FQCReviewScreen", " No authenticated operator, using parameter: $editableOperatorName")
        }
    }

    // Track entry time for FQC
    var sessionStartTime by remember { mutableStateOf<Long?>(null) }

    // Database and workflow service
    val database = remember { com.example.fqcapp.data.AppDatabase.getDatabase(context) }
    val repo = remember { com.example.fqcapp.data.FQCRepository(database) }
    val workflowService = remember { com.example.fqcapp.services.DeviceWorkflowService(repo) }

    // Capture entry time when screen opens
    // Track if workflow has been logged (only log after user confirms in Device Config)
    var workflowLogged by remember { mutableStateOf(false) }

    LaunchedEffect(Unit) {
        sessionStartTime = System.currentTimeMillis()
        Log.d("FQCReviewScreen", " Entry time captured: $sessionStartTime")
        // Note: Workflow logging is delayed until user actually starts FQC process
        // This ensures iteration is not created until user confirms in Device Config

        // DEBUG: Log all information being displayed in FQC screen
        scope.launch {
            try {
                Log.d("FQCReviewScreen", "")
                Log.d("FQCReviewScreen", " [FQC_SCREEN_DEBUG] Logging all displayed information")
                Log.d("FQCReviewScreen", "")
                Log.d("FQCReviewScreen", " [SCREEN_INFO] Serial Number: '$serialNumber'")
                Log.d("FQCReviewScreen", " [SCREEN_INFO] Operator Name: '$operatorName'")
                Log.d("FQCReviewScreen", " [SCREEN_INFO] Authenticated Operator: ${authenticatedOperator?.operatorName ?: "N/A"} (ID: ${authenticatedOperator?.operatorId ?: "N/A"})")
                Log.d("FQCReviewScreen", " [SCREEN_INFO] FQC Criteria Count: ${fqcCriteria.size}")
                Log.d("FQCReviewScreen", " [SCREEN_INFO] FQC Criteria: $fqcCriteria")

                // Get driver pairing information
                val driverPairingRecords = repo.getDriverPairingRecordsBySerial(serialNumber)
                val latestDriverPairing = repo.getLatestDriverPairing(serialNumber)
                Log.d("FQCReviewScreen", " [DRIVER_PAIRING] Total Records: ${driverPairingRecords.size}")
                if (latestDriverPairing != null) {
                    Log.d("FQCReviewScreen", " [DRIVER_PAIRING] Latest Record:")
                    Log.d("FQCReviewScreen", "   - Record ID: ${latestDriverPairing.recordId}")
                    Log.d("FQCReviewScreen", "   - Serial: ${latestDriverPairing.serialNumber}")
                    Log.d("FQCReviewScreen", "   - Driver Data: ${latestDriverPairing.driverData}")
                    Log.d("FQCReviewScreen", "   - Iteration: ${latestDriverPairing.iterationNumber}")
                    Log.d("FQCReviewScreen", "   - Test Stage: ${latestDriverPairing.testStage}")
                    Log.d("FQCReviewScreen", "   - Entry Timestamp: ${latestDriverPairing.entryTimestamp}")
                    Log.d("FQCReviewScreen", "   - Operator: ${latestDriverPairing.operatorName}")
                } else {
                    Log.w("FQCReviewScreen", " [DRIVER_PAIRING] No driver pairing records found for serial: '$serialNumber'")
                }

                // Get changed parts information
                val changedPartsRecords = repo.getChangedPartsRecordsBySerial(serialNumber)
                val latestChangedParts = changedPartsRecords.maxByOrNull { it.recordId }
                Log.d("FQCReviewScreen", " [CHANGED_PARTS] Total Records: ${changedPartsRecords.size}")
                if (latestChangedParts != null) {
                    Log.d("FQCReviewScreen", " [CHANGED_PARTS] Latest Record:")
                    Log.d("FQCReviewScreen", "   - Record ID: ${latestChangedParts.recordId}")
                    Log.d("FQCReviewScreen", "   - Serial: ${latestChangedParts.serialNumber}")
                    Log.d("FQCReviewScreen", "   - Selected Parts: ${latestChangedParts.selectedParts}")
                    Log.d("FQCReviewScreen", "   - Iteration: ${latestChangedParts.iterationNumber}")
                    Log.d("FQCReviewScreen", "   - Test Stage: ${latestChangedParts.testStage}")
                    Log.d("FQCReviewScreen", "   - Entry Timestamp: ${latestChangedParts.entryTimestamp}")
                    Log.d("FQCReviewScreen", "   - Operator: ${latestChangedParts.operatorName}")
                } else {
                    Log.d("FQCReviewScreen", " [CHANGED_PARTS] No changed parts records found for serial: '$serialNumber'")
                }

                // Get test results
                val freqTests = repo.getFrequencyTestsBySerial(serialNumber)
                val songTests = repo.getSongTestsBySerial(serialNumber)
                val latestFreqTest = freqTests.maxByOrNull { it.testId }
                val latestSongTest = songTests.maxByOrNull { it.testId }
                Log.d("FQCReviewScreen", " [TEST_RESULTS] Frequency Tests: ${freqTests.size}, Song Tests: ${songTests.size}")
                if (latestFreqTest != null) {
                    Log.d("FQCReviewScreen", " [TEST_RESULTS] Latest Frequency Test:")
                    Log.d("FQCReviewScreen", "   - Result: ${latestFreqTest.result}")
                    Log.d("FQCReviewScreen", "   - Test Stage: ${latestFreqTest.testStage}")
                    Log.d("FQCReviewScreen", "   - Iteration: ${latestFreqTest.iterationNumber}")
                    Log.d("FQCReviewScreen", "   - Timestamp: ${latestFreqTest.timestamp}")
                }
                if (latestSongTest != null) {
                    Log.d("FQCReviewScreen", " [TEST_RESULTS] Latest Song Test:")
                    Log.d("FQCReviewScreen", "   - Result: ${latestSongTest.result}")
                    Log.d("FQCReviewScreen", "   - Test Stage: ${latestSongTest.testStage}")
                    Log.d("FQCReviewScreen", "   - Iteration: ${latestSongTest.iterationNumber}")
                    Log.d("FQCReviewScreen", "   - Timestamp: ${latestSongTest.timestamp}")
                }

                Log.d("FQCReviewScreen", "")
            } catch (e: Exception) {
                Log.e("FQCReviewScreen", " [FQC_SCREEN_DEBUG] Error logging debug information", e)
            }
        }
    }

    // Initialize selectedCriteria with all criteria set to false
    LaunchedEffect(fqcCriteria) {
        fqcCriteria.forEach { criterion ->
            if (!selectedCriteria.containsKey(criterion)) {
                selectedCriteria[criterion] = false
            }
        }
    }

    // Track a version counter that updates when selections change
    var validationKey by remember { mutableStateOf(0) }

    // Validation: all criteria must be checked
    // We recalculate this whenever validationKey changes
    val allCriteriaChecked = remember(validationKey, fqcCriteria.size) {
        fqcCriteria.isNotEmpty() && fqcCriteria.all { selectedCriteria[it] == true }
    }

    // Validation: final quality must be selected
    val finalQualitySelected = selectedFinalQuality != null

    Scaffold(
        topBar = {
            TopAppBar(
                title = { 
                    Text(
                        "Final Quality Check (FQC)",
                        color = accentColor,
                        fontSize = 18.sp,
                        fontWeight = FontWeight.Bold
                    )
                },
                navigationIcon = {
                    IconButton(onClick = onBack) {
                        Icon(Icons.Filled.ArrowBack, "Back", tint = Color.White)
                    }
                },
                colors = TopAppBarDefaults.topAppBarColors(
                    containerColor = bgColor
                )
            )
        }
    ) { padding ->
        AnimatedContent(
            targetState = currentStep,
            modifier = Modifier
                .fillMaxSize()
                .padding(padding)
        ) { step ->
            when (step) {
                FQCStep.CRITERIA_CHECK -> {
                        CriteriaCheckScreen(
                        operatorName = editableOperatorName,
                        onOperatorNameChanged = { editableOperatorName = it },
                        serialNumber = serialNumber,
                        fqcCriteria = fqcCriteria,
                        selectedCriteria = selectedCriteria,
                        allCriteriaChecked = allCriteriaChecked,
                        onSelectionChanged = { 
                            validationKey++
                            // Log workflow: FQC STARTED (only once, when first checkbox is checked)
                            if (!workflowLogged && serialNumber.isNotBlank() && selectedCriteria.values.any { it }) {
                                workflowLogged = true
                                scope.launch {
                                    try {
                                        val iterationNumber = workflowService.getNextIterationNumber(
                                            serialNumber,
                                            com.example.fqcapp.services.DeviceWorkflowService.STAGE_FQC
                                        )
                                        workflowService.logWorkflow(
                                            serialNumber = serialNumber,
                                            stage = com.example.fqcapp.services.DeviceWorkflowService.STAGE_FQC,
                                            operatorName = editableOperatorName,
                                            action = com.example.fqcapp.services.DeviceWorkflowService.ACTION_STARTED,
                                            iterationNumber = iterationNumber
                                        )
                                        Log.d("FQCReviewScreen", " [WORKFLOW] Logged FQC STARTED (after user confirmed in Device Config)")
                                    } catch (e: Exception) {
                                        Log.e("FQCReviewScreen", " [WORKFLOW] Error logging workflow STARTED", e)
                                    }
                                }
                            }
                        },
                        onNext = { currentStep = FQCStep.FINAL_QUALITY },
                        onBack = onBack,
                        accentColor = accentColor,
                        bgColor = bgColor,
                        surfaceColor = surfaceColor
                    )
                }
                FQCStep.FINAL_QUALITY -> {
                    FinalQualityScreen(
                        operatorName = editableOperatorName,
                        onOperatorNameChanged = { editableOperatorName = it },
                        serialNumber = serialNumber,
                        selectedFinalQuality = selectedFinalQuality,
                        onSelectionChanged = { selectedFinalQuality = it },
                        finalQualitySelected = finalQualitySelected,
                        onBack = { currentStep = FQCStep.CRITERIA_CHECK },
                        onSave = {
                            scope.launch {
                                try {
                                    // Use the repo already initialized above
                                    // val db = com.example.fqcapp.data.AppDatabase.getDatabase(context)
                                    // val repo = com.example.fqcapp.data.FQCRepository(database)

                                    // Calculate exit time and total time spent
                                    val exitTime = System.currentTimeMillis()
                                    val entryTimeMillis = sessionStartTime ?: exitTime
                                    val totalTimeSpentMs = exitTime - entryTimeMillis
                                    val totalTimeSpentSeconds = totalTimeSpentMs / 1000
                                    val hours = totalTimeSpentSeconds / 3600
                                    val minutes = (totalTimeSpentSeconds % 3600) / 60
                                    val seconds = totalTimeSpentSeconds % 60
                                    val totalTimeSpentFormatted = String.format("%02d:%02d:%02d", hours, minutes, seconds)

                                    val dateFormat = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault())
                                    val entryTimestamp = dateFormat.format(java.util.Date(entryTimeMillis))
                                    val exitTimestamp = dateFormat.format(java.util.Date(exitTime))

                                    // Get all checked criteria as JSON array
                                    val checkedCriteria = selectedCriteria.keys.filter { selectedCriteria[it] == true }
                                    val criteriaJson = if (checkedCriteria.isEmpty()) {
                                        "[]"
                                    } else {
                                        checkedCriteria.joinToString(", ", prefix = "[", postfix = "]") { "\"$it\"" }
                                    }

                                    // Get final quality type as string - ensure it's not empty
                                    val currentFinalQuality = selectedFinalQuality // Store in local variable for smart cast
                                    val finalQualityTypeStr = if (currentFinalQuality != null) {
                                        currentFinalQuality.name
                                    } else {
                                        Log.w("FQCReviewScreen", " WARNING: selectedFinalQuality is null! This should not happen if validation passed.")
                                        ""
                                    }

                                    // Verify serial number is not empty
                                    val finalSerialNumber = if (serialNumber.isBlank()) {
                                        Log.e("FQCReviewScreen", " ERROR: Serial number is blank! Cannot save FQC record.")
                                        return@launch
                                    } else {
                                        serialNumber
                                    }

                                    Log.d("FQCReviewScreen", " [SAVE_START] Preparing to save FQC record:")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Serial Number: '$finalSerialNumber'")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Operator: '$editableOperatorName'")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Selected Final Quality (enum): $selectedFinalQuality")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Final Quality Type (string): '$finalQualityTypeStr'")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Criteria Count: ${checkedCriteria.size}")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Entry Time: $entryTimestamp")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Exit Time: $exitTimestamp")
                                    Log.d("FQCReviewScreen", " [SAVE_START] Total Time: $totalTimeSpentFormatted")

                                    // Get next iteration number
                                    val existingRecords = repo.getAllFQCRecords()
                                    val maxIteration = existingRecords.filter { it.serialNumber == finalSerialNumber }
                                        .maxOfOrNull { it.iterationNumber } ?: 0
                                    val nextIteration = maxIteration + 1

                                    // Determine FQC type based on iteration number
                                    // INITIAL for first iteration (1), REWORK for subsequent iterations
                                    val fqcType = if (nextIteration == 1) "INITIAL" else "REWORK"

                                    Log.d("FQCReviewScreen", " [SAVE_START] Iteration Number: $nextIteration")
                                    Log.d("FQCReviewScreen", " [SAVE_START] FQC Type: $fqcType")

                                    // Create FQCRecord
                                    val fqcRecord = com.example.fqcapp.data.entities.FQCRecord(
                                        serialNumber = finalSerialNumber,
                                        operatorName = editableOperatorName,
                                        timestamp = java.time.Instant.now().toString(), // ISO format for backward compatibility
                                        fqcType = fqcType,
                                        plasticCosmetics = "", // Not used in new flow
                                        modeSelector = "",
                                        button = "",
                                        slider = "",
                                        headbandScratch = "",
                                        headbandShape = "",
                                        screwBush = "",
                                        ledCheck = "",
                                        acBalance = "",
                                        bcBalance = "",
                                        serialVerification = "",
                                        imageCount = 0,
                                        imagesBase64 = "",
                                        imageDescriptions = "",
                                        overallResult = "PASS", // All criteria checked = PASS
                                        majorIssues = "",
                                        remark = "",
                                        latestFreqResult = "",
                                        latestSongResult = "",
                                        fqcDurationSec = totalTimeSpentSeconds.toInt(),
                                        iterationNumber = nextIteration,
                                        testStage = "Final Test", // FQC always uses Final Test stage
                                        entryTimestamp = entryTimestamp,
                                        exitTimestamp = exitTimestamp,
                                        totalTimeSpent = totalTimeSpentFormatted,
                                        criteriaChecked = criteriaJson,
                                        finalQualityType = finalQualityTypeStr,
                                        needsSync = true,
                                        lastSyncTimestamp = 0
                                    )

                                    Log.d("FQCReviewScreen", " [SAVE_CREATE] Created FQCRecord object:")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   serialNumber: '${fqcRecord.serialNumber}'")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   operatorName: '${fqcRecord.operatorName}'")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   finalQualityType: '${fqcRecord.finalQualityType}'")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   criteriaChecked: '${fqcRecord.criteriaChecked}'")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   entryTimestamp: '${fqcRecord.entryTimestamp}'")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   exitTimestamp: '${fqcRecord.exitTimestamp}'")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   totalTimeSpent: '${fqcRecord.totalTimeSpent}'")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   iterationNumber: ${fqcRecord.iterationNumber}")
                                    Log.d("FQCReviewScreen", " [SAVE_CREATE]   fqcType: '${fqcRecord.fqcType}'")

                                    // Save to database
                                    val operatorId = authenticatedOperator?.operatorId
                                    val recordId = repo.insertFQCRecord(fqcRecord, operatorId = operatorId)

                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] FQC record saved successfully!")
                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] Record ID: $recordId")
                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] Serial Number: '$finalSerialNumber'")
                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] Operator: '$editableOperatorName'")
                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] Final Headphone Quality: '$finalQualityTypeStr'")
                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] Criteria Checked (${checkedCriteria.size}): $checkedCriteria")
                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] Entry: $entryTimestamp, Exit: $exitTimestamp, Time: $totalTimeSpentFormatted")
                                    Log.d("FQCReviewScreen", " [SAVE_SUCCESS] Iteration: $nextIteration, Type: $fqcType")

                                    // Verify the saved record by reading it back
                                    try {
                                        val savedRecords = repo.getAllFQCRecords()
                                        val savedRecord = savedRecords.find { it.fqcId == recordId }
                                        if (savedRecord != null) {
                                            Log.d("FQCReviewScreen", " [VERIFY] Verified saved record:")
                                            Log.d("FQCReviewScreen", " [VERIFY]   Serial: '${savedRecord.serialNumber}'")
                                            Log.d("FQCReviewScreen", " [VERIFY]   Operator: '${savedRecord.operatorName}'")
                                            Log.d("FQCReviewScreen", " [VERIFY]   Final Quality: '${savedRecord.finalQualityType}'")
                                            Log.d("FQCReviewScreen", " [VERIFY]   Criteria: '${savedRecord.criteriaChecked}'")
                                            Log.d("FQCReviewScreen", " [VERIFY]   Entry: '${savedRecord.entryTimestamp}'")
                                            Log.d("FQCReviewScreen", " [VERIFY]   Exit: '${savedRecord.exitTimestamp}'")
                                            Log.d("FQCReviewScreen", " [VERIFY]   Time: '${savedRecord.totalTimeSpent}'")

                                            if (savedRecord.finalQualityType.isBlank()) {
                                                Log.e("FQCReviewScreen", " [VERIFY] ERROR: Final Quality Type is empty in saved record!")
                                            } else {
                                                Log.d("FQCReviewScreen", " [VERIFY] Final Quality Type is correctly saved: '${savedRecord.finalQualityType}'")
                                            }

                                            if (savedRecord.serialNumber.isBlank()) {
                                                Log.e("FQCReviewScreen", " [VERIFY] ERROR: Serial Number is empty in saved record!")
                                            } else {
                                                Log.d("FQCReviewScreen", " [VERIFY] Serial Number is correctly saved: '${savedRecord.serialNumber}'")
                                            }
                                        } else {
                                            Log.e("FQCReviewScreen", " [VERIFY] ERROR: Could not find saved record with ID: $recordId")
                                        }
                                    } catch (e: Exception) {
                                        Log.e("FQCReviewScreen", " [VERIFY] Error verifying saved record", e)
                                    }

                                    // Log workflow: FQC COMPLETED
                                    try {
                                        // Map final quality type to workflow result
                                        val workflowResult = when (selectedFinalQuality) {
                                            com.example.fqcapp.FinalQualityType.REVIEWER -> com.example.fqcapp.services.DeviceWorkflowService.RESULT_PASS
                                            com.example.fqcapp.FinalQualityType.CUSTOMER -> com.example.fqcapp.services.DeviceWorkflowService.RESULT_CAN_PASS
                                            com.example.fqcapp.FinalQualityType.INTERNATIONAL -> com.example.fqcapp.services.DeviceWorkflowService.RESULT_PASS
                                            com.example.fqcapp.FinalQualityType.MARKETING -> com.example.fqcapp.services.DeviceWorkflowService.RESULT_PASS
                                            null -> null
                                        }

                                        workflowService.logWorkflow(
                                            serialNumber = finalSerialNumber,
                                            stage = com.example.fqcapp.services.DeviceWorkflowService.STAGE_FQC,
                                            operatorName = editableOperatorName,
                                            action = com.example.fqcapp.services.DeviceWorkflowService.ACTION_COMPLETED,
                                            result = workflowResult,
                                            iterationNumber = nextIteration,
                                            recordId = recordId
                                        )
                                        Log.d("FQCReviewScreen", " [WORKFLOW] Logged FQC COMPLETED")

                                        // Clear all solution reminders when FQC workflow completes
                                        if (solutionReminderManager != null) {
                                            solutionReminderManager.clearAllSolutions(finalSerialNumber)
                                            Log.d("FQCReviewScreen", " [SOLUTION_REMINDER] Cleared all solution reminders for serial: $finalSerialNumber")
                                        }
                                    } catch (e: Exception) {
                                        Log.e("FQCReviewScreen", " [WORKFLOW] Error logging workflow COMPLETED", e)
                                    }

                                    // Update milestone for final QC
                                    val milestoneService = com.example.fqcapp.services.MilestoneUpdateService(repo)
                                    milestoneService.updateFinalQcStatus(serialNumber, selectedFinalQuality)
                                    Log.d("FQCReviewScreen", " [MILESTONE] Final QC milestone updated with quality type: $selectedFinalQuality")

                                    // Save to Supabase
                                    try {
                                        // Get MAC ID if available
                                        var macId = ""
                                        try {
                                            val googleSheetsService = com.example.fqcapp.services.GoogleSheetsService(
                                                context,
                                                repo.getMacIdCacheDao()
                                            )
                                            val currentMacId = googleSheetsService.getMacIdBySerialNumber(finalSerialNumber)
                                            if (currentMacId != null) {
                                                macId = currentMacId
                                            }
                                        } catch (e: Exception) {
                                            Log.w("FQCReviewScreen", " Could not get MAC ID for Supabase save", e)
                                        }

                                        val supabaseService = com.example.fqcapp.supabase.SupabaseFQCService()
                                        val operatorId = authenticatedOperator?.operatorId ?: ""
                                        val supabaseFqcId = supabaseService.saveFQCRecord(
                                            record = fqcRecord,
                                            operatorId = operatorId,
                                            macId = macId
                                        )
                                        if (supabaseFqcId != null) {
                                            Log.d("FQCReviewScreen", " [SUPABASE] FQC record saved to Supabase with ID: $supabaseFqcId")
                                            // Mark as synced in Room DB
                                            repo.markFQCRecordAsSynced(recordId, System.currentTimeMillis())
                                        } else {
                                            Log.w("FQCReviewScreen", " [SUPABASE] Failed to save to Supabase (will retry later)")
                                        }
                                    } catch (e: Exception) {
                                        Log.e("FQCReviewScreen", " [SUPABASE] Error saving to Supabase", e)
                                        // Don't fail the whole operation if Supabase save fails
                                    }

                                    // Navigate to landing page after successful save
                                    onSuccess()

                                    } catch (e: Exception) {
                                        Log.e("FQCReviewScreen", " Error saving FQC record", e)
                                        // On error, just go back (don't navigate to landing)
                                        onBack()
                                    }
                            }
                        },
                        accentColor = accentColor,
                        bgColor = bgColor,
                        surfaceColor = surfaceColor
                    )
                }
            }
        }
    }
}

@Composable
fun CriteriaCheckScreen(
    operatorName: String,
    onOperatorNameChanged: (String) -> Unit,
    serialNumber: String,
    fqcCriteria: List<String>,
    selectedCriteria: MutableMap<String, Boolean>,
    allCriteriaChecked: Boolean,
    onSelectionChanged: () -> Unit,
    onNext: () -> Unit,
    onBack: () -> Unit,
    accentColor: Color,
    bgColor: Color,
    surfaceColor: Color
) {
    Column(
        modifier = Modifier
            .fillMaxSize()
            .verticalScroll(rememberScrollState())
            .padding(16.dp),
        verticalArrangement = Arrangement.spacedBy(12.dp)
    ) {
        // Operator name (editable)
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.spacedBy(12.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                "Operator:",
                color = Color.White.copy(alpha = 0.7f),
                fontSize = 14.sp,
                fontWeight = FontWeight.Medium,
                modifier = Modifier.width(110.dp)
            )
            TextField(
                value = operatorName,
                onValueChange = onOperatorNameChanged,
                modifier = Modifier.weight(1f),
                colors = TextFieldDefaults.colors(
                    focusedTextColor = Color.White,
                    unfocusedTextColor = Color.White,
                    focusedContainerColor = surfaceColor,
                    unfocusedContainerColor = surfaceColor,
                    focusedIndicatorColor = accentColor,
                    unfocusedIndicatorColor = Color(0xFF424242)
                ),
                textStyle = androidx.compose.ui.text.TextStyle(
                fontSize = 14.sp,
                    fontWeight = FontWeight.Normal
                ),
                singleLine = true
            )
        }

        // Serial number (single line, read-only)
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.spacedBy(12.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                "Serial number:",
                color = Color.White.copy(alpha = 0.7f),
                fontSize = 14.sp,
                fontWeight = FontWeight.Medium,
                modifier = Modifier.widthIn(min = 110.dp)
            )
            Text(
                serialNumber,
                color = Color.White,
                fontSize = 14.sp,
                fontWeight = FontWeight.Normal,
                modifier = Modifier.weight(1f)
            )
        }

        Spacer(modifier = Modifier.height(2.dp)) // Reduced by 70%: 8.dp * 0.3 = 2.4.dp, rounded to 2.dp

        // Criteria Checklist
        Card(
            modifier = Modifier.fillMaxWidth(),
            colors = CardDefaults.cardColors(
                containerColor = surfaceColor
            ),
            shape = RoundedCornerShape(12.dp)
        ) {
            Column(modifier = Modifier.padding(4.dp)) { // Reduced by 70%: 12.dp * 0.3 = 3.6.dp, rounded to 4.dp
                Text(
                    "FQC Criteria Checklist",
                    color = accentColor,
                    fontSize = 15.sp,
                    fontWeight = FontWeight.Bold,
                    modifier = Modifier.padding(bottom = 2.dp) // Reduced by 70%: 8.dp * 0.3 = 2.4.dp, rounded to 2.dp
                )

                Divider(
                    color = Color(0xFF424242),
                    modifier = Modifier.padding(vertical = 2.dp) // Reduced by 70%: 8.dp * 0.3 = 2.4.dp, rounded to 2.dp
                )

                // Dynamic criteria checkboxes
                if (fqcCriteria.isEmpty()) {
                    Text(
                        " No criteria loaded. Please sync config from Google Sheets.",
                        color = Color.White.copy(alpha = 0.7f),
                        fontSize = 13.sp,
                        modifier = Modifier.padding(vertical = 5.dp) // Reduced by 70%: 16.dp * 0.3 = 4.8.dp, rounded to 5.dp
                    )
                } else {
                    fqcCriteria.forEach { criterion ->
                        Row(
                            modifier = Modifier
                                .fillMaxWidth()
                                .padding(vertical = 1.dp), // Reduced by 50%: 2.dp * 0.5 = 1.dp
                            verticalAlignment = Alignment.CenterVertically,
                            horizontalArrangement = Arrangement.spacedBy(2.dp) // Reduced by 50%: 4.dp * 0.5 = 2.dp
                        ) {
                            Checkbox(
                                checked = selectedCriteria[criterion] == true,
                                onCheckedChange = { isChecked ->
                                    selectedCriteria[criterion] = isChecked
                                    onSelectionChanged() // Notify parent to update validation
                                },
                                colors = CheckboxDefaults.colors(
                                    checkedColor = accentColor,
                                    uncheckedColor = Color(0xFF424242)
                                )
                            )
                            Text(
                                criterion,
                                color = Color.White,
                                fontSize = 14.sp,
                                modifier = Modifier.weight(1f)
                            )
                        }
                    }

                    Spacer(modifier = Modifier.height(2.dp)) // Reduced by 70%: 8.dp * 0.3 = 2.4.dp, rounded to 2.dp

                    // Progress indicator
                    Text(
                        "Progress: ${selectedCriteria.values.count { it }}/${fqcCriteria.size}",
                        color = if (allCriteriaChecked) accentColor else Color.White.copy(alpha = 0.7f),
                        fontSize = 13.sp,
                        fontWeight = if (allCriteriaChecked) FontWeight.Bold else FontWeight.Normal
                    )
                }
            }
        }

        Spacer(modifier = Modifier.weight(1f))

        // Action Buttons
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            OutlinedButton(
                onClick = onBack,
                modifier = Modifier
                    .weight(1f)
                    .height(50.dp),
                colors = ButtonDefaults.outlinedButtonColors(
                    contentColor = Color.White
                ),
                shape = RoundedCornerShape(12.dp)
            ) {
                Text("Cancel", fontSize = 15.sp, fontWeight = FontWeight.Bold)
            }

            Button(
                onClick = onNext,
                enabled = allCriteriaChecked,
                modifier = Modifier
                    .weight(1f)
                    .height(50.dp),
                colors = ButtonDefaults.buttonColors(
                    containerColor = if (allCriteriaChecked) accentColor else Color(0xFF424242),
                    contentColor = if (allCriteriaChecked) bgColor else Color.White.copy(alpha = 0.5f),
                    disabledContainerColor = Color(0xFF424242),
                    disabledContentColor = Color.White.copy(alpha = 0.5f)
                ),
                shape = RoundedCornerShape(12.dp)
            ) {
                Text("Next", fontSize = 15.sp, fontWeight = FontWeight.Bold)
            }
        }
    }
}

@Composable
fun FinalQualityScreen(
    operatorName: String,
    onOperatorNameChanged: (String) -> Unit,
    serialNumber: String,
    selectedFinalQuality: FinalQualityType?,
    onSelectionChanged: (FinalQualityType) -> Unit,
    finalQualitySelected: Boolean,
    onBack: () -> Unit,
    onSave: () -> Unit,
    accentColor: Color,
    bgColor: Color,
    surfaceColor: Color
) {
