        Spacer(modifier = Modifier.height(8.dp))

        // Final Quality Selection
        Card(
            modifier = Modifier.fillMaxWidth(),
            colors = CardDefaults.cardColors(
                containerColor = surfaceColor
            ),
            shape = RoundedCornerShape(12.dp)
        ) {
            Column(modifier = Modifier.padding(4.dp)) { // Reduced by 70%: 12.dp * 0.3 = 3.6.dp, rounded to 4.dp
                Text(
                    "Final Headphone Quality",
                    color = accentColor,
                    fontSize = 15.sp,
                    fontWeight = FontWeight.Bold,
                    modifier = Modifier.padding(bottom = 2.dp) // Reduced by 70%: 8.dp * 0.3 = 2.4.dp, rounded to 2.dp
                )

                Divider(
                    color = Color(0xFF424242),
                    modifier = Modifier.padding(vertical = 2.dp) // Reduced by 70%: 8.dp * 0.3 = 2.4.dp, rounded to 2.dp
                )

                // Quality options (Radio buttons - only one can be selected)
                FinalQualityType.values().forEach { quality ->
                    Row(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(vertical = 2.dp), // Reduced by 70%: 8.dp * 0.3 = 2.4.dp, rounded to 2.dp
                        verticalAlignment = Alignment.CenterVertically,
                        horizontalArrangement = Arrangement.spacedBy(4.dp) // Reduced by 70%: 12.dp * 0.3 = 3.6.dp, rounded to 4.dp
                    ) {
                        RadioButton(
                            selected = selectedFinalQuality == quality,
                            onClick = { onSelectionChanged(quality) },
                            colors = RadioButtonDefaults.colors(
                                selectedColor = accentColor,
                                unselectedColor = Color(0xFF424242)
                            )
                        )
                        Text(
                            quality.name.replace("_", " ").lowercase().replaceFirstChar { it.uppercase() },
                            color = Color.White,
                            fontSize = 14.sp,
                            modifier = Modifier.weight(1f)
                        )
                    }
                }
            }
        }

        Spacer(modifier = Modifier.weight(1f))

        // Action Buttons
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            OutlinedButton(
                onClick = onBack,
                modifier = Modifier
                    .weight(1f)
                    .height(50.dp),
                colors = ButtonDefaults.outlinedButtonColors(
                    contentColor = Color.White
                ),
                shape = RoundedCornerShape(12.dp)
            ) {
                Text("Back", fontSize = 15.sp, fontWeight = FontWeight.Bold)
            }

            Button(
                onClick = onSave,
                enabled = finalQualitySelected,
                modifier = Modifier
                    .weight(1f)
                    .height(50.dp),
                colors = ButtonDefaults.buttonColors(
                    containerColor = if (finalQualitySelected) accentColor else Color(0xFF424242),
                    contentColor = if (finalQualitySelected) bgColor else Color.White.copy(alpha = 0.5f),
                    disabledContainerColor = Color(0xFF424242),
                    disabledContentColor = Color.White.copy(alpha = 0.5f)
                ),
                shape = RoundedCornerShape(12.dp)
            ) {
