object Routes {
    const val LANDING = "landing"
    const val SCANNER = "scanner"
    const val REVIEW = "review"
    const val DATABASE_VIEWER = "database_viewer"
    const val TEST_SONGS = "test_songs"
    const val FREQ_GEN = "frequency_generator"
