    const val DEVICE_CONFIG = "device_config"
    const val DEVICE_INFORMATION = "device_information"
    const val DRIVER_PAIRING = "driver_pairing"
    const val CHANGED_PART = "changed_part"
    const val OPERATOR_SELECTION = "operator_selection"

    // Helper function to build operator selection route with destination
    fun operatorSelection(destination: String): String {
        return "$OPERATOR_SELECTION/$destination"
    }
}

@Composable
fun AppNav(activity: ComponentActivity, operatorName: String, permissionHandler: PermissionHandler, onLogout: () -> Unit) {
    val nav = rememberNavController()
    val context = LocalContext.current
    val database = remember { AppDatabase.getDatabase(context) }
    val repository = remember { FQCRepository(database) }
    val prefsManager = remember { PreferencesManager(context) }
    val scope = rememberCoroutineScope()
    val eligibilityService = remember { DeviceEligibilityService(repository, database) }
    // MAC ID cache manager for reverse lookup (MAC ID -> Serial Number)
    val macIdCacheManager = remember { MacIdCacheManager(
        context,
        repository.getMacIdCacheDao(),
        GoogleSheetsAPI(context)
    ) }

    // UndoManager for undo functionality
    val undoManager = remember { UndoManager() }

    // SolutionReminderManager for tracking pending solutions
    val solutionReminderManager = remember { SolutionReminderManager() }

    // Eligibility dialog state
    var showEligibilityDialog by remember { mutableStateOf(false) }
    var eligibilityMessage by remember { mutableStateOf("") }
    var eligibilityRequiresDriverPairing by remember { mutableStateOf(false) }
    var eligibilityRequiresFrequencyTest by remember { mutableStateOf(false) }
    var eligibilityRequiresSongTest by remember { mutableStateOf(false) }
    var pendingNavigationRoute by remember { mutableStateOf<String?>(null) }

    // Load criteria from saved JSON file on startup
    LaunchedEffect(Unit) {
        Log.d("AppNav", " App starting - loading criteria from saved JSON file...")
        val loaded = com.example.fqcapp.core.CriteriaJsonStorage.loadFromFile(context)
        if (loaded != null) {
            com.example.fqcapp.core.CriteriaRepository.update(loaded)
            Log.d("AppNav", " Criteria loaded on startup from JSON")
        } else {
            Log.d("AppNav", " No saved criteria JSON file, using defaults")
        }
    }

    // Initialize services with error handling
    val sheetsService = remember {
        try {
            GoogleSheetsService(context, repository.getMacIdCacheDao())
        } catch (e: Exception) {
            e.printStackTrace()
            null
        }
    }
    val bluetoothService = remember {
        try {
            BluetoothService(context)
        } catch (e: Exception) {
            e.printStackTrace()
            null
        }
    }
    val connectionManager = remember {
        try {
            if (sheetsService != null && bluetoothService != null) {
                ConnectionManager(bluetoothService, sheetsService)
            } else null
        } catch (e: Exception) {
            e.printStackTrace()
            null
        }
    }

    // Permission handler is now passed as parameter

    // Current session state
    var currentSessionId by remember { mutableStateOf<Long?>(null) }
    var currentSerialNumber by remember { mutableStateOf<String?>(null) }
    var currentMacAddress by remember { mutableStateOf("") }
    var isReferenceSerial by remember { mutableStateOf(false) } // Track if serial is generated reference
    var dataUploadStatus by remember { mutableStateOf("Unknown") }
    var connectedDeviceName by remember { mutableStateOf<String?>(null) }

    // Device data cache manager
    val deviceDataCache = remember { com.example.fqcapp.core.DeviceDataCacheManager(context) }

    // Fetch device data from Supabase when serial number is set (with caching)
    LaunchedEffect(currentSerialNumber) {
        if (currentSerialNumber != null && currentSerialNumber!!.isNotBlank()) {
            Log.d("Navigation", "")
            Log.d("Navigation", " [NAVIGATION] SERIAL NUMBER SCANNED/SET")
            Log.d("Navigation", "   Serial Number: ${currentSerialNumber}")
            Log.d("Navigation", "   MAC Address: $currentMacAddress")
            Log.d("Navigation", "   Device Name: ${connectedDeviceName ?: "N/A"}")
            Log.d("Navigation", "")

            scope.launch {
                try {
                    // Check cache first
                    val cachedData = deviceDataCache.getCachedData(currentSerialNumber!!)

                    if (cachedData != null) {
                        Log.d("Navigation", " [CACHE] Using cached device data (no API call)")
                        Log.d("Navigation", "   Device Status: ${cachedData.device_status}")
                        Log.d("Navigation", "   Current Stage: ${cachedData.current_stage}")
                        Log.d("Navigation", "   Max Iterations:")
                        Log.d("Navigation", "      Frequency: ${cachedData.max_frequency_iteration}")
                        Log.d("Navigation", "      Song: ${cachedData.max_song_iteration}")
                        Log.d("Navigation", "      FQC: ${cachedData.max_fqc_iteration}")
                        Log.d("Navigation", "      Driver Pairing: ${cachedData.max_driver_pairing_iteration}")
                        Log.d("Navigation", "      Changed Parts: ${cachedData.max_changed_parts_iteration}")
                        Log.d("Navigation", "")
                        return@launch
                    }

                    // Cache miss or expired - fetch from Supabase
                    Log.d("Navigation", " [ACTION] Fetching device data from Supabase (cache miss/expired)...")
                    val deviceDataService = com.example.fqcapp.supabase.SupabaseDeviceDataService()
                    val snapshot = deviceDataService.fetchDeviceData(currentSerialNumber!!)

                    if (snapshot != null) {
                        // Cache the fetched data
                        deviceDataCache.setCachedData(currentSerialNumber!!, snapshot)

                        Log.d("Navigation", "")
                        Log.d("Navigation", " [NAVIGATION] Device data fetched from Supabase")
                        Log.d("Navigation", "   Device Status: ${snapshot.device_status}")
                        Log.d("Navigation", "   Current Stage: ${snapshot.current_stage}")
                        Log.d("Navigation", "   Max Iterations:")
                        Log.d("Navigation", "      Frequency: ${snapshot.max_frequency_iteration}")
                        Log.d("Navigation", "      Song: ${snapshot.max_song_iteration}")
                        Log.d("Navigation", "      FQC: ${snapshot.max_fqc_iteration}")
                        Log.d("Navigation", "      Driver Pairing: ${snapshot.max_driver_pairing_iteration}")
                        Log.d("Navigation", "      Changed Parts: ${snapshot.max_changed_parts_iteration}")
                        Log.d("Navigation", "   Last Update: ${snapshot.last_update_timestamp}")
                        Log.d("Navigation", "")
                    } else {
                        Log.w("Navigation", "")
                        Log.w("Navigation", " [NAVIGATION] No device data found in Supabase (new device)")
                        Log.w("Navigation", "   Serial Number: ${currentSerialNumber}")
                        Log.w("Navigation", "   This is a new device - no previous test data")
                        Log.w("Navigation", "")
                    }
                } catch (e: Exception) {
                    Log.e("Navigation", "")
                    Log.e("Navigation", " [NAVIGATION] Error fetching device data from Supabase")
                    Log.e("Navigation", "   Serial Number: ${currentSerialNumber}")
                    Log.e("Navigation", "   Error: ${e.message}")
                    Log.e("Navigation", "   Stack trace: ${e.stackTraceToString()}")
                    Log.e("Navigation", "")
                }
            }
        }
    }

    // Callback function to update device info (for PCB change)
    val updateDeviceInfoCallback: (String, String) -> Unit = { newSerialNumber, newMacId ->
        currentMacAddress = newMacId
        currentSerialNumber = newSerialNumber
        android.util.Log.d("Navigation", " [PCB_UPDATE] Updated MAC ID to: $newMacId, Serial to: $newSerialNumber")
    }

    // Track current route to only run periodic checks on landing page
    val navBackStackEntry by nav.currentBackStackEntryAsState()
    val currentRoute = navBackStackEntry?.destination?.route ?: Routes.LANDING

    // Periodic Bluetooth device detection - ONLY on landing page
    LaunchedEffect(bluetoothService, currentRoute) {
        // Only start periodic checks if we're on the landing page
        if (bluetoothService != null && currentRoute == Routes.LANDING) {
            android.util.Log.d("Navigation", " Starting periodic Bluetooth check (on landing page: $currentRoute)")
            while (true) {
                try {
                    // Check current route at the start of each iteration
                    val currentNavBackStackEntry = nav.currentBackStackEntry
                    val actualRoute = currentNavBackStackEntry?.destination?.route ?: Routes.LANDING

                    // If we're no longer on landing page, stop immediately
                    if (actualRoute != Routes.LANDING) {
                        android.util.Log.d("Navigation", " Stopping periodic Bluetooth check (navigated away from landing page: $actualRoute)")
                        break
                    }

                    // Check for connected device every 10 seconds
                    val connectedDevice = bluetoothService.probeCurrentlyConnected()
                    if (connectedDevice != null) {
                        android.util.Log.d("Navigation", "Periodic check: Found connected device: ${connectedDevice.name} - ${connectedDevice.macAddress}")

                        // IMPORTANT: If MAC ID changed, clear previous serial number
                        if (currentMacAddress.isNotBlank() && currentMacAddress != connectedDevice.macAddress) {
                            android.util.Log.d("Navigation", " MAC ID changed from $currentMacAddress to ${connectedDevice.macAddress} - clearing previous serial")
                            currentSerialNumber = null
                            isReferenceSerial = false
                        }

                        connectedDeviceName = connectedDevice.name
                        currentMacAddress = connectedDevice.macAddress

                        // If we don't have a serial number, try to look it up from MAC ID (reverse lookup)
                        // This handles the case where device is connected manually without scanning QR code
                        if (currentSerialNumber == null && connectedDevice.macAddress.isNotBlank()) {
                            android.util.Log.d("Navigation", " Performing reverse lookup: MAC ID -> Serial Number")
                            android.util.Log.d("Navigation", "   MAC ID: ${connectedDevice.macAddress}")
                            try {
                                val serialFromMac = macIdCacheManager.getSerialNumberByMacId(connectedDevice.macAddress)
                                if (serialFromMac != null && serialFromMac.isNotBlank()) {
                                    android.util.Log.d("Navigation", " Found serial number from MAC ID: $serialFromMac")
                                    // Directly set the serial number and MAC address
                                    // The device is already connected, so we just need to update our state
                                    currentSerialNumber = serialFromMac
                                    isReferenceSerial = false
                                    android.util.Log.d("Navigation", " Updated device info - Serial: $serialFromMac, MAC: ${connectedDevice.macAddress}")
                                } else {
                                    // MAC ID not found - generate reference serial number
                                    val referenceSerial = SerialGenerator.generateSystemSerial(connectedDevice.macAddress)
                                    android.util.Log.w("Navigation", " Serial number not found for MAC ID: ${connectedDevice.macAddress}")
                                    android.util.Log.w("Navigation", "   Generating reference serial: $referenceSerial")
                                    currentSerialNumber = referenceSerial
                                    isReferenceSerial = true
                                    android.util.Log.d("Navigation", " Using reference serial: $referenceSerial for MAC: ${connectedDevice.macAddress}")
                                }
                            } catch (e: Exception) {
                                android.util.Log.e("Navigation", " Error during MAC ID reverse lookup", e)
                                // On error, still generate reference serial
                                val referenceSerial = SerialGenerator.generateSystemSerial(connectedDevice.macAddress)
                                currentSerialNumber = referenceSerial
                                isReferenceSerial = true
                                android.util.Log.d("Navigation", " Generated reference serial on error: $referenceSerial")
                            }
                        }
                    } else {
                        android.util.Log.d("Navigation", "Periodic check: No connected device found")
                        // Don't clear the current device info immediately - wait for user to scan new QR
                    }
                } catch (e: Exception) {
                    android.util.Log.e("Navigation", "Error in periodic Bluetooth check", e)
                }

                // Wait 10 seconds before next check
                kotlinx.coroutines.delay(10000)
            }
            android.util.Log.d("Navigation", " Periodic Bluetooth check loop ended")
        } else {
            if (bluetoothService != null && currentRoute != Routes.LANDING) {
                android.util.Log.d("Navigation", " Periodic Bluetooth check not started (not on landing page: $currentRoute)")
            } else if (bluetoothService == null) {
                android.util.Log.d("Navigation", " Periodic Bluetooth check not started (bluetoothService is null)")
            }
        }
    }

    // Connection flow state
    var showConnectionProgress by remember { mutableStateOf(false) }
    var connectionStep by remember { mutableStateOf(ConnectionStep.SEARCHING_SHEET) }
