    // Simple direct connection function - bypasses ConnectionManager complexity
    fun simpleConnect(serialNumber: String, macIdOverride: String? = null) {
        android.util.Log.d("Navigation", "Simple connect for serial: $serialNumber${if (macIdOverride != null) " with MAC override: $macIdOverride" else ""}")

        if (sheetsService == null || bluetoothService == null) {
            android.util.Log.w("Navigation", "Services not initialized")
            errorMessage = "Services not initialized"
            showConnectionError = true
            return
        }

        showConnectionProgress = true
        connectionStep = ConnectionStep.SEARCHING_SHEET
        connectionData = null

        scope.launch {
            try {
                val macId: String?

                if (macIdOverride != null && macIdOverride.isNotBlank()) {
                    // Use the provided MAC ID (from PCB change)
                    android.util.Log.d("Navigation", "Step 1: Using provided MAC ID (PCB change): $macIdOverride")
                    macId = macIdOverride
                    connectionStep = ConnectionStep.MAC_FOUND
                    connectionData = macId
                    currentMacAddress = macId
                } else {
                    // Step 1: Search Google Sheets for MAC ID
                    android.util.Log.d("Navigation", "Step 1: Searching Google Sheets for serial: $serialNumber")
                    connectionStep = ConnectionStep.SEARCHING_SHEET
                    connectionData = null

                    macId = sheetsService.getMacIdBySerialNumber(serialNumber)

                    if (macId == null) {
                        android.util.Log.e("Navigation", "MAC ID not found for serial: $serialNumber")
                        errorMessage = "Serial number '$serialNumber' not found in database"
                        showConnectionProgress = false
                        showConnectionError = true
                        return@launch
                    }

                    // Step 2: MAC ID Found - Display it
                    android.util.Log.d("Navigation", "Step 2: MAC ID found: $macId")
                    connectionStep = ConnectionStep.MAC_FOUND
                    connectionData = macId
                    currentMacAddress = macId
                }

                kotlinx.coroutines.delay(1000) // Show MAC ID for 1 second

                // Step 3: Search for Bluetooth device
                android.util.Log.d("Navigation", "Step 3: Searching for Bluetooth device with MAC: $macId")
                connectionStep = ConnectionStep.SEARCHING_DEVICE
                connectionData = macId

                val device = bluetoothService.findDeviceByMacId(macId)

                if (device == null) {
                    android.util.Log.e("Navigation", "Bluetooth device not found for MAC: $macId")
                    errorMessage = "Device not found with MAC address: $macId"
                    showConnectionProgress = false
                    showConnectionError = true
                    return@launch
                }

                // Step 4: Connect to device
                android.util.Log.d("Navigation", "Step 4: Connecting to device: ${device.name}")
                connectionStep = ConnectionStep.CONNECTING
                connectionData = device.name

                val connected = bluetoothService.connectToDevice(device)

                if (!connected) {
                    android.util.Log.e("Navigation", "Failed to connect to device: ${device.name}")
                    errorMessage = "Failed to connect to device: ${device.name}"
                    showConnectionProgress = false
                    showConnectionError = true
                    return@launch
                }

                // Success!
                android.util.Log.d("Navigation", "Connection successful!")
                connectionStep = ConnectionStep.SUCCESS
                kotlinx.coroutines.delay(500) // Show success message briefly
                showConnectionProgress = false

                // Create session and navigate to landing
                try {
                    // Ensure Device and Operator records exist
                    val existingDevice = repository.getDeviceBySerialNumber(serialNumber)
                    if (existingDevice == null) {
                        val today = java.text.SimpleDateFormat("yyyy-MM-dd", java.util.Locale.US)
                            .format(java.util.Date())
                        // NOTE: macId is deprecated - MAC comes from active PCB link
                        // Setting to empty string - MAC will be derived from PCB link when needed
                        repository.insertDevice(Device(
                            serialNumber = serialNumber,
                            macId = "",  // Deprecated - MAC comes from active PCB link
                            color = "",
                            createdDate = today,
                            finalStatus = "IN_PROGRESS",
                            totalIterations = 0,
                            totalTimeHours = 0.0,
                            finalOperator = "",
                            firstTestDate = today,
                            finalShipDate = "",
                            needsSync = true,
                            lastSyncTimestamp = 0
                        ))
                    }

                    val operatorId = prefsManager.getOperatorId()
                    val existingOperator = repository.getOperatorById(operatorId)
                    if (existingOperator == null) {
                        repository.insertOperator(Operator(
                            id = operatorId,
                            name = operatorName
                        ))
                    }

                    val sessionId = repository.insertSession(
                        FQCSession(
                            operatorId = operatorId,
                            serialNumber = serialNumber,
                            status = "active"
                        )
                    )
                    currentSessionId = sessionId
                    currentSerialNumber = serialNumber
                    isReferenceSerial = false // Reset flag when real serial is scanned
                    dataUploadStatus = "Pending"
                    android.util.Log.d("Navigation", "Session created: $sessionId")
                } catch (e: Exception) {
                    android.util.Log.e("Navigation", "Error creating session", e)
                }

            } catch (e: Exception) {
                android.util.Log.e("Navigation", "Exception in simple connect", e)
                errorMessage = "Connection error: ${e.message ?: "Unknown error"}"
                showConnectionProgress = false
                showConnectionError = true
            }
        }
    }

