        Column(modifier = Modifier.padding(12.dp)) {
            Text(
                text = device.serialNumber,
                style = MaterialTheme.typography.titleSmall,
                color = textColor,
                fontWeight = FontWeight.Bold
            )
            Spacer(modifier = Modifier.height(4.dp))
            // Use provided MAC ID (from PCB link) or fallback to deprecated device.macId
            val displayMacId = macId ?: (if (device.macId.isNotBlank()) device.macId else "N/A")
            Text("MAC: $displayMacId", color = textColor.copy(alpha = 0.7f), fontSize = 12.sp)
            Text("Color: ${device.color}", color = textColor.copy(alpha = 0.7f), fontSize = 12.sp)
            Text("Status: ${device.finalStatus}", color = textColor.copy(alpha = 0.7f), fontSize = 12.sp)
            Text("Iterations: ${device.totalIterations}", color = textColor.copy(alpha = 0.7f), fontSize = 12.sp)
        }
    }
}

