            }
            if (overallReview.totalTimeSpent.isNotBlank() && overallReview.totalTimeSpent != "00:00:00") {
                Text("Time Spent: ${overallReview.totalTimeSpent}", color = textColor.copy(alpha = 0.8f), fontSize = 11.sp, fontWeight = FontWeight.Medium)
            }

