            // Result
            Text("Result: ${overallReview.result}", 
                color = when (overallReview.result.uppercase()) {
                    "PASS" -> Color(0xFF4CAF50)
                    "CAN_BE_PASSED", "CAN_PASS" -> Color(0xFFFFC107)
                    else -> Color(0xFFFF5722)
