data class DriverRow(
    val driver1: String = "",
    val driver2: String = "",
    val rowType: String = "" // "interchange", "change", "other"
)

enum class RowActionType {
    INTERCHANGE,
    CHANGE,
    OTHER
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun DriverPairingScreen(
    serialNumber: String,
    macId: String,
    deviceName: String?,
    authenticatedOperator: com.example.fqcapp.data.entities.OperatorCredential? = null,
    onBack: () -> Unit,
    onSave: (List<String>) -> Unit,
    onNavigateToChangedPart: () -> Unit = {},
    solutionReminderManager: com.example.fqcapp.core.SolutionReminderManager? = null
) {
    val context = LocalContext.current
    val scope = rememberCoroutineScope()
    val bgColor = Color(0xFF121212)
    val surfaceColor = Color(0xFF2B2B2B)
    val accentColor = Color(0xFFFFC107) // Yellow - App theme color
    val textColor = Color.White
    val dangerColor = Color(0xFFEF4444) // Red for delete

    // Row 1 - Always visible and editable
    var driver1Row1 by remember { mutableStateOf("") }
    var driver2Row1 by remember { mutableStateOf("") }

    // Dynamic rows (Row 2 onwards)
    var dynamicRows by remember { mutableStateOf<List<DriverRow>>(emptyList()) }

    // Dialog state
    var showAddRowDialog by remember { mutableStateOf(false) }

    // Loading state
    var isLoading by remember { mutableStateOf(true) }
    var isSaving by remember { mutableStateOf(false) }

    // Track entry time for driver pairing
    var sessionStartTime by remember { mutableStateOf<Long?>(null) }

    // Test stage selector state - start as null to force selection
    var selectedTestStage by remember { mutableStateOf<String?>(null) }

    // Show alert dialog when trying to submit without selecting test stage
    var showTestStageRequiredDialog by remember { mutableStateOf(false) }

    // Get operator name - use authenticated operator if available, otherwise fallback to preferences
    val prefsManager = remember { com.example.fqcapp.utils.PreferencesManager(context) }
    var operatorName by remember { mutableStateOf("Unknown") }
    var operatorId by remember { mutableStateOf("") }

    // Use authenticated operator if provided
    LaunchedEffect(authenticatedOperator) {
        if (authenticatedOperator != null) {
            operatorName = authenticatedOperator.operatorName
            operatorId = authenticatedOperator.operatorId
            Log.d("DriverPairing", " Using authenticated operator: $operatorName (ID: $operatorId)")
        } else {
            operatorName = prefsManager.getOperatorName() ?: "Unknown"
            Log.d("DriverPairing", " No authenticated operator, using preferences: $operatorName")
        }
    }

    // Database and Cache Manager
    val database = remember { com.example.fqcapp.data.AppDatabase.getDatabase(context) }
    val repository = remember { com.example.fqcapp.data.FQCRepository(database) }
    val driverPairingSheetsService = remember { DriverPairingSheetsService(context) }
    val cacheManager = remember {
        DriverPairingCacheManager(
            repository.getDriverPairingCacheDao(),
            driverPairingSheetsService
        )
    }

    // Device Workflow Service
    val workflowService = remember { com.example.fqcapp.services.DeviceWorkflowService(repository) }

    // Session timestamp manager
    val sessionTimestampManager = remember { com.example.fqcapp.core.SessionTimestampManager(context) }

    // Capture entry time when screen opens and get session timestamp
    LaunchedEffect(Unit) {
        Log.d("DriverPairing", "")
        Log.d("DriverPairing", " [SCREEN] DRIVER PAIRING SCREEN OPENED")
        Log.d("DriverPairing", "   Serial Number: $serialNumber")
        Log.d("DriverPairing", "   MAC ID: $macId")
        Log.d("DriverPairing", "   Operator: $operatorName")
        Log.d("DriverPairing", "   Operator ID: $operatorId")
        Log.d("DriverPairing", "")

        sessionStartTime = System.currentTimeMillis()
        Log.d("DriverPairing", " [TIMING] Entry time captured: $sessionStartTime")
        // Operator name is set by the authenticatedOperator LaunchedEffect above

        // Get or create session timestamp
        if (serialNumber.isNotBlank()) {
            Log.d("DriverPairing", " [ACTION] Getting/creating session timestamp...")
            val sessionEntryTimestamp = sessionTimestampManager.getSessionEntryTimestamp(serialNumber)
            Log.d("DriverPairing", "    Session entry timestamp: $sessionEntryTimestamp")

            // Log workflow: Driver Pairing STARTED
            Log.d("DriverPairing", " [ACTION] Getting next iteration number from Supabase...")
            try {
                val iterationNumber = workflowService.getNextDriverPairingIteration(serialNumber)
                Log.d("DriverPairing", "    Next iteration: $iterationNumber")

                Log.d("DriverPairing", " [ACTION] Logging workflow event...")
                workflowService.logWorkflow(
                    serialNumber = serialNumber,
                    stage = com.example.fqcapp.services.DeviceWorkflowService.STAGE_DRIVER_PAIRING,
                    operatorName = operatorName,
                    action = com.example.fqcapp.services.DeviceWorkflowService.ACTION_STARTED,
                    iterationNumber = iterationNumber
                )
                Log.d("DriverPairing", "    [WORKFLOW] Logged DRIVER_PAIRING STARTED with iteration: $iterationNumber")
            } catch (e: Exception) {
                Log.e("DriverPairing", "    [WORKFLOW] Error logging workflow STARTED: ${e.message}")
                Log.e("DriverPairing", "   Stack trace: ${e.stackTraceToString()}")
            }
        }
        Log.d("DriverPairing", "")
    }

    // Load data from Google Sheets EVERY TIME screen opens (for multi-user sync)
    // Using serialNumber and macId as keys ensures it reloads when these change or when navigating to screen
    LaunchedEffect(serialNumber, macId) {
        Log.d("DriverPairing", " [SCREEN_OPEN] DriverPairingScreen opened for serial: $serialNumber, MAC: $macId")
        // Load data if we have either serial number OR MAC ID
        if (serialNumber.isNotBlank() || macId.isNotBlank()) {
            isLoading = true
            try {
                Log.d("DriverPairing", " [LOAD_START] Starting to load driver data from Google Sheets (force refresh for multi-user sync)")
                // Always fetch fresh data from Google Sheets (forceRefresh = true by default)
                // Pass MAC ID as fallback in case serial number search fails
                // If serial number is blank, use MAC ID for search
                val searchSerial = if (serialNumber.isNotBlank()) serialNumber else ""
                val data = cacheManager.getDriverData(searchSerial, macId)
                if (data != null) {
                    Log.d("DriverPairing", " [LOAD_SUCCESS] Found existing data with ${data.drivers.size} driver entries")

                    // Set Row 1 drivers (indices 0, 1)
                    if (data.drivers.size >= 2) {
                        driver1Row1 = data.drivers[0]
                        driver2Row1 = data.drivers[1]
                        Log.d("DriverPairing", " [DATA_LOADED] Row 1 - Driver1: '${data.drivers[0]}', Driver2: '${data.drivers[1]}'")
                    } else {
                        // Clear Row 1 if no data
                        driver1Row1 = ""
                        driver2Row1 = ""
                        Log.d("DriverPairing", " [DATA_LOADED] Row 1 - No data, cleared fields")
                    }

                    // Load additional rows (pairs starting from index 2)
                    val additionalRows = mutableListOf<DriverRow>()
                    var i = 2
                    var rowIndex = 2
                    while (i < data.drivers.size - 1) {
                        val row = DriverRow(
                            driver1 = data.drivers[i],
                            driver2 = data.drivers[i + 1],
                            rowType = "" // Could be stored separately if needed
                        )
                        additionalRows.add(row)
                        Log.d("DriverPairing", " [DATA_LOADED] Row $rowIndex - Driver1: '${data.drivers[i]}', Driver2: '${data.drivers[i + 1]}'")
                        i += 2
                        rowIndex++
                    }
                    dynamicRows = additionalRows
                    Log.d("DriverPairing", " [LOAD_COMPLETE] Loaded ${additionalRows.size} additional rows")

                    Toast.makeText(context, "Loaded driver data from Google Sheets", Toast.LENGTH_SHORT).show()
                } else {
                    Log.d("DriverPairing", " [LOAD_EMPTY] No existing data found in Google Sheets, starting fresh")
                    // Clear all fields if no data found
                    driver1Row1 = ""
                    driver2Row1 = ""
                    dynamicRows = emptyList()
                    // If serial not found, we'll insert it when saving
                }
            } catch (e: Exception) {
                Log.e("DriverPairing", " [LOAD_ERROR] Error loading data", e)
                Toast.makeText(context, "Error loading data: ${e.message}", Toast.LENGTH_LONG).show()
            } finally {
                isLoading = false
                Log.d("DriverPairing", " [LOAD_FINISHED] Loading state set to false")
            }
        } else {
            Log.w("DriverPairing", " [LOAD_SKIP] Serial number is blank, skipping load")
            isLoading = false
        }
    }

    Scaffold(
        containerColor = bgColor,
        topBar = {
            TopAppBar(
                title = {
                    Text(
                        "Driver Pairing",
                        color = accentColor,
                        fontSize = 18.sp,
                        fontWeight = FontWeight.Bold
                    )
                },
                navigationIcon = {
                    IconButton(onClick = {
                        Log.d("DriverPairing", " [BACK_BUTTON] Back button tapped")
                        onBack()
                    }) {
                        Icon(
                            Icons.Filled.ArrowBack,
                            contentDescription = "Back",
                            tint = textColor
                        )
                    }
                },
                colors = TopAppBarDefaults.topAppBarColors(
                    containerColor = bgColor
                )
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = {
                    Log.d("DriverPairing", " [FAB_TAP] + button tapped, opening Add Row dialog")
                    Log.d("DriverPairing", " [FAB_STATE] Current rows: ${dynamicRows.size + 1} total (1 static + ${dynamicRows.size} dynamic)")
                    showAddRowDialog = true
                },
                modifier = Modifier.padding(16.dp),
                containerColor = accentColor,
                contentColor = bgColor,
                shape = CircleShape
            ) {
                Icon(
                    Icons.Filled.Add,
                    contentDescription = "Add Row",
                    tint = bgColor
                )
            }
        }
    ) { padding ->
        if (isLoading) {
            Box(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(padding),
                contentAlignment = Alignment.Center
            ) {
                CircularProgressIndicator(color = accentColor)
            }
        } else {
            Box(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(padding)
            ) {
                Column(
                    modifier = Modifier
                        .fillMaxSize()
                        .verticalScroll(rememberScrollState())
                        .padding(16.dp),
                    verticalArrangement = Arrangement.spacedBy(16.dp)
                ) {
                    // Test Stage Selector
                    Column(verticalArrangement = Arrangement.spacedBy(8.dp)) {
                        Text(
                            "Test Result Review",
                            style = MaterialTheme.typography.headlineMedium,
                            fontWeight = FontWeight.Bold,
                            color = Color.White
                        )
                        Text(
                            "Select the test stage and result below",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.7f)
                        )
                        TestStageSelector(
                            selectedStage = selectedTestStage,
                            onStageSelected = { selectedTestStage = it }
                        )
                    }
                    // Device Information Card
                    Card(
                        modifier = Modifier.fillMaxWidth(),
                        colors = CardDefaults.cardColors(containerColor = surfaceColor),
                        shape = RoundedCornerShape(12.dp)
                    ) {
                        Column(
                            modifier = Modifier.padding(16.dp),
                            verticalArrangement = Arrangement.spacedBy(12.dp)
                        ) {
                            Text(
                                text = "Device Information",
                                style = MaterialTheme.typography.titleMedium,
                                color = accentColor,
                                fontWeight = FontWeight.Bold
                            )

                            HorizontalDivider(color = Color(0xFF424242))

                            // Serial Number
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween
                            ) {
                                Text(
                                    "Serial Number:",
                                    color = textColor.copy(alpha = 0.7f),
                                    fontSize = 14.sp
                                )
                                Text(
                                    serialNumber.ifBlank { "Not found" },
                                    color = accentColor,
                                    fontSize = 14.sp,
                                    fontWeight = FontWeight.Medium
                                )
                            }

                            // MAC ID
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween
                            ) {
                                Text(
                                    "MAC ID:",
                                    color = textColor.copy(alpha = 0.7f),
                                    fontSize = 14.sp
                                )
                                Text(
                                    macId.ifBlank { "Unknown" },
                                    color = accentColor,
                                    fontSize = 14.sp,
                                    fontWeight = FontWeight.Medium
                                )
                            }

                            // Device Name (if available)
                            if (deviceName != null) {
                                Row(
                                    modifier = Modifier.fillMaxWidth(),
                                    horizontalArrangement = Arrangement.SpaceBetween
                                ) {
                                    Text(
                                        "Device Name:",
                                        color = textColor.copy(alpha = 0.7f),
                                        fontSize = 14.sp
                                    )
                                    Text(
                                        deviceName,
                                        color = textColor,
                                        fontSize = 14.sp,
                                        fontWeight = FontWeight.Medium
                                    )
                                }
                            }
                        }
                    }

                    // Driver Information Section
                    Card(
                        modifier = Modifier.fillMaxWidth(),
                        colors = CardDefaults.cardColors(containerColor = surfaceColor),
                        shape = RoundedCornerShape(12.dp)
                    ) {
                        Column(
                            modifier = Modifier.padding(16.dp),
                            verticalArrangement = Arrangement.spacedBy(16.dp)
                        ) {
                            Text(
                                text = "Driver Information",
                                style = MaterialTheme.typography.titleMedium,
                                color = accentColor,
                                fontWeight = FontWeight.Bold
                            )

                            HorizontalDivider(color = Color(0xFF424242))

                            // Row 1: Driver 1 and Driver 2 (Always visible)
                            DriverRowInput(
                                label = "Row 1",
                                driver1 = driver1Row1,
                                driver2 = driver2Row1,
                                onDriver1Change = {
                                    Log.d("DriverPairing", " [ROW1_DRIVER1_CHANGE] Changed from '$driver1Row1' to '$it'")
                                    driver1Row1 = it
                                },
                                onDriver2Change = {
                                    Log.d("DriverPairing", " [ROW1_DRIVER2_CHANGE] Changed from '$driver2Row1' to '$it'")
                                    driver2Row1 = it
                                },
                                textColor = textColor,
                                bgColor = bgColor,
                                accentColor = accentColor,
                                showDelete = false
                            )

                            // Dynamic rows (Row 2 onwards)
                            dynamicRows.forEachIndexed { index, row ->
                                DriverRowInput(
                                    label = "Row ${index + 2}",
                                    driver1 = row.driver1,
                                    driver2 = row.driver2,
                                    onDriver1Change = {
                                        val rowNum = index + 2
                                        Log.d("DriverPairing", " [ROW${rowNum}_DRIVER1_CHANGE] Row $rowNum Driver1 changed from '${row.driver1}' to '$it'")
                                        dynamicRows = dynamicRows.mapIndexed { i, r ->
                                            if (i == index) r.copy(driver1 = it) else r
                                        }
                                    },
                                    onDriver2Change = {
                                        val rowNum = index + 2
                                        Log.d("DriverPairing", " [ROW${rowNum}_DRIVER2_CHANGE] Row $rowNum Driver2 changed from '${row.driver2}' to '$it'")
                                        dynamicRows = dynamicRows.mapIndexed { i, r ->
                                            if (i == index) r.copy(driver2 = it) else r
                                        }
                                    },
                                    textColor = textColor,
                                    bgColor = bgColor,
                                    accentColor = accentColor,
                                    rowType = row.rowType,
                                    showDelete = true,
                                    onDelete = {
                                        val rowNum = index + 2
                                        Log.d("DriverPairing", " [ROW_DELETE] Delete button tapped for Row $rowNum (Driver1: '${row.driver1}', Driver2: '${row.driver2}')")
                                        dynamicRows = dynamicRows.filterIndexed { i, _ -> i != index }
                                        Log.d("DriverPairing", " [ROW_DELETE] After deletion: ${dynamicRows.size} dynamic rows remaining")
                                    }
                                )
                            }
                        }
                    }

                    Spacer(modifier = Modifier.height(80.dp)) // Space for FAB

                    // Save Entry Button
                    Button(
                        onClick = {
                            Log.d("DriverPairing", " [SAVE_BUTTON] Save Entry button tapped")

                            // Validate test stage is selected
                            if (selectedTestStage == null) {
                                showTestStageRequiredDialog = true
                                return@Button
                            }
                            Log.d("DriverPairing", " [SAVE_DATA] Serial: $serialNumber, MAC: $macId")
                            Log.d("DriverPairing", " [SAVE_DATA] Row 1 - Driver1: '$driver1Row1', Driver2: '$driver2Row1'")
                            Log.d("DriverPairing", " [SAVE_DATA] Additional rows: ${dynamicRows.size}")
                            dynamicRows.forEachIndexed { index, row ->
                                Log.d("DriverPairing", " [SAVE_DATA] Row ${index + 2} - Driver1: '${row.driver1}', Driver2: '${row.driver2}', Type: '${row.rowType}'")
                            }

                            scope.launch {
                                isSaving = true
                                Log.d("DriverPairing", " [SAVE_START] Starting save process...")
                                try {
                                    val additionalRows = dynamicRows.map { 
                                        Pair(it.driver1, it.driver2) 
                                    }
                                    Log.d("DriverPairing", " [SAVE_PROCESS] Calling cacheManager.saveDriverData...")

                                    // Use cache manager which handles cache + Google Sheets sync
                                    val success = cacheManager.saveDriverData(
                                        serialNumber = serialNumber,
                                        macId = macId,
                                        row1Driver1 = driver1Row1,
                                        row1Driver2 = driver2Row1,
                                        additionalRows = additionalRows
                                    )

                                    Log.d("DriverPairing", " [SAVE_RESULT] Save operation result: $success")

                                    if (success) {
                                        Log.d("DriverPairing", " [SAVE_SUCCESS] Driver data saved to Google Sheets successfully!")

                                        // Now save to structured database record
                                        try {
                                            // Calculate exit time and total time spent
                                            val exitTime = System.currentTimeMillis()
                                            val entryTimeMillis = sessionStartTime ?: exitTime
                                            val totalTimeSpentMs = exitTime - entryTimeMillis
                                            val totalTimeSpentSeconds = totalTimeSpentMs / 1000
                                            val hours = totalTimeSpentSeconds / 3600
                                            val minutes = (totalTimeSpentSeconds % 3600) / 60
                                            val seconds = totalTimeSpentSeconds % 60
                                            val totalTimeSpentFormatted = String.format("%02d:%02d:%02d", hours, minutes, seconds)

                                            // Format timestamps in IST (Indian Standard Time)
                                            val timestampUtils = com.example.fqcapp.utils.TimestampUtils
                                            val entryTimestampIST = timestampUtils.formatTimestampIST(entryTimeMillis)
                                            val exitTimestampIST = timestampUtils.formatTimestampIST(exitTime)

                                            // Build driver data as JSON array (including solution/rowType)
                                            val driverPairs = mutableListOf<Map<String, String>>()
                                            // Add Row 1 (no solution/rowType for first row)
                                            driverPairs.add(mapOf("driver1" to driver1Row1, "driver2" to driver2Row1))
                                            // Add additional rows with solution/rowType
                                            dynamicRows.forEach { row ->
                                                val pairMap = mutableMapOf("driver1" to row.driver1, "driver2" to row.driver2)
                                                // Add solution if rowType is set
                                                if (row.rowType.isNotBlank()) {
                                                    pairMap["solution"] = row.rowType
                                                }
                                                driverPairs.add(pairMap)
                                            }

                                            // Convert to JSON array string (including solution field)
                                            val driverDataJson = driverPairs.joinToString(", ", prefix = "[", postfix = "]") { pair ->
                                                val driver1 = pair["driver1"] ?: ""
                                                val driver2 = pair["driver2"] ?: ""
                                                val solution = pair["solution"]
                                                if (solution != null) {
                                                    """{"driver1":"$driver1","driver2":"$driver2","solution":"$solution"}"""
                                                } else {
                                                    """{"driver1":"$driver1","driver2":"$driver2"}"""
                                                }
                                            }

                                            // Check for duplicates BEFORE saving
                                            Log.d("DriverPairing", " [ACTION] Checking for duplicate driver pairs...")
                                            try {
                                                val existingRecords = repository.getDriverPairingRecordsBySerial(serialNumber)
                                                if (existingRecords.isNotEmpty()) {
                                                    // Normalize new pairs (handle interchange)
                                                    fun normalizePair(d1: String, d2: String): Pair<String, String> {
                                                        val d1Trim = d1.trim()
                                                        val d2Trim = d2.trim()
                                                        return if (d1Trim <= d2Trim) Pair(d1Trim, d2Trim) else Pair(d2Trim, d1Trim)
                                                    }

                                                    val normalizedNewPairs = driverPairs.mapNotNull { pair ->
                                                        val d1 = pair["driver1"]?.trim() ?: ""
                                                        val d2 = pair["driver2"]?.trim() ?: ""
                                                        if (d1.isNotBlank() || d2.isNotBlank()) {
                                                            normalizePair(d1, d2)
                                                        } else null
                                                    }.toSet()

                                                    // Check existing records for duplicates
                                                    val normalizedExistingPairs = mutableSetOf<Pair<String, String>>()
                                                    existingRecords.forEach { record ->
                                                        try {
                                                            val existingJson = JSONArray(record.driverData)
                                                            for (i in 0 until existingJson.length()) {
                                                                val pairObj = existingJson.getJSONObject(i)
                                                                val d1 = pairObj.optString("driver1", "").trim()
                                                                val d2 = pairObj.optString("driver2", "").trim()
                                                                if (d1.isNotBlank() || d2.isNotBlank()) {
                                                                    normalizedExistingPairs.add(normalizePair(d1, d2))
                                                                }
                                                            }
                                                        } catch (e: Exception) {
                                                            Log.w("DriverPairing", "    Error parsing existing record: ${e.message}")
                                                        }
                                                    }

                                                    val duplicates = normalizedNewPairs.intersect(normalizedExistingPairs)
                                                    if (duplicates.isNotEmpty()) {
                                                        Log.w("DriverPairing", "    DUPLICATE DRIVER PAIRS DETECTED:")
                                                        duplicates.forEach { pair ->
                                                            Log.w("DriverPairing", "      - Driver1: ${pair.first}, Driver2: ${pair.second}")
                                                        }
                                                        Toast.makeText(
                                                            context,
                                                            " Duplicate driver pairs detected!\nThe same driver pairs already exist for this device.\nPlease check your input.",
                                                            Toast.LENGTH_LONG
                                                        ).show()
                                                        return@launch // Stop saving
                                                    }
                                                }
                                                Log.d("DriverPairing", "    No duplicates found in local DB")
                                            } catch (e: Exception) {
                                                Log.w("DriverPairing", "    Error checking duplicates in local DB: ${e.message}")
                                                // Continue with save if check fails
                                            }

                                            // Get next iteration number from Supabase
                                            Log.d("DriverPairing", " [ACTION] Getting next iteration number from Supabase...")
                                            val nextIteration = workflowService.getNextDriverPairingIteration(serialNumber)
                                            Log.d("DriverPairing", "    Next iteration: $nextIteration")

                                            // Create DriverPairingRecord
                                            Log.d("DriverPairing", " [ACTION] Creating DriverPairingRecord...")
                                            // Use IST timestamps for both local DB and Supabase
                                            val driverRecord = com.example.fqcapp.data.entities.DriverPairingRecord(
                                                serialNumber = serialNumber,
                                                operatorName = operatorName,
                                                entryTimestamp = entryTimestampIST, // IST format: "yyyy-MM-dd HH:mm:ss"
                                                exitTimestamp = exitTimestampIST, // IST format: "yyyy-MM-dd HH:mm:ss"
                                                totalTimeSpent = totalTimeSpentFormatted,
                                                driverData = driverDataJson,
                                                iterationNumber = nextIteration,
                                                testStage = selectedTestStage!!,
                                                timestamp = entryTimestampIST, // IST format for Supabase
                                                needsSync = true,
                                                lastSyncTimestamp = 0
                                            )

                                            // Save to database
                                            val recordId = repository.insertDriverPairingRecord(driverRecord)

                                            Log.d("DriverPairing", " Saving Driver Pairing Record:")
                                            Log.d("DriverPairing", "  Serial: $serialNumber")
                                            Log.d("DriverPairing", "  Operator: $operatorName")
                                            Log.d("DriverPairing", "  Entry Timestamp (IST): $entryTimestampIST")
                                            Log.d("DriverPairing", "  Exit Timestamp (IST): $exitTimestampIST")
                                            Log.d("DriverPairing", "  Total Time Spent: $totalTimeSpentFormatted")
                                            Log.d("DriverPairing", "  Driver Pairs: ${driverPairs.size}")
                                            Log.d("DriverPairing", "  Driver Data JSON: $driverDataJson")
                                            Log.d("DriverPairing", "  Iteration Number: $nextIteration")
                                            Log.d("DriverPairing", " Driver pairing record saved with ID: $recordId")

                                            // Mark solution reminders as completed if applicable
                                            if (solutionReminderManager != null && serialNumber.isNotBlank()) {
                                                try {
                                                    // Check if any row is an interchange (check dynamicRows which have rowType)
                                                    val hasInterchange = dynamicRows.any { it.rowType == "interchange" }
                                                    if (hasInterchange) {
                                                        solutionReminderManager.markSolutionCompleted(
                                                            serialNumber = serialNumber,
                                                            solution = "INTERCHANGE",
                                                            reminderType = com.example.fqcapp.core.ReminderType.DRIVER_PAIRING
                                                        )
                                                        Log.d("DriverPairing", " Marked INTERCHANGE solution as completed")
                                                    }

                                                    // Check if BC driver was replaced (rowType contains "change" and driver2 is not empty)
                                                    // Also check driverPairs map for solution field
                                                    val hasBCDriverReplacement = dynamicRows.any { 
                                                        it.rowType == "change" && it.driver2.isNotBlank()
                                                    } || driverPairs.any { 
                                                        (it["solution"]?.contains("change", ignoreCase = true) == true) && 
                                                        (it["driver2"]?.isNotBlank() == true)
                                                    }
                                                    if (hasBCDriverReplacement) {
                                                        solutionReminderManager.markSolutionCompleted(
                                                            serialNumber = serialNumber,
                                                            solution = "Replace BC Driver",
                                                            reminderType = com.example.fqcapp.core.ReminderType.DRIVER_PAIRING
                                                        )
                                                        Log.d("DriverPairing", " Marked Replace BC Driver solution as completed")
                                                    }
                                                } catch (e: Exception) {
                                                    Log.e("DriverPairing", " Error marking solution reminders as completed", e)
                                                }
                                            }

                                            // Log workflow: Driver Pairing COMPLETED
                                            try {
                                                workflowService.logWorkflow(
                                                    serialNumber = serialNumber,
                                                    stage = com.example.fqcapp.services.DeviceWorkflowService.STAGE_DRIVER_PAIRING,
                                                    operatorName = operatorName,
                                                    action = com.example.fqcapp.services.DeviceWorkflowService.ACTION_COMPLETED,
                                                    result = com.example.fqcapp.services.DeviceWorkflowService.RESULT_PASS, // Driver pairing always passes if saved
                                                    iterationNumber = nextIteration,
                                                    recordId = recordId
                                                )
                                                Log.d("DriverPairing", " [WORKFLOW] Logged DRIVER_PAIRING COMPLETED")
                                            } catch (e: Exception) {
                                                Log.e("DriverPairing", " [WORKFLOW] Error logging workflow COMPLETED", e)
                                            }

                                            // Save to Supabase
                                            Log.d("DriverPairing", " [ACTION] Uploading to Supabase...")
                                            try {
                                                val supabaseService = com.example.fqcapp.supabase.SupabaseDriverPairingService()
                                                val operatorId = authenticatedOperator?.operatorId ?: ""
                                                if (operatorId.isBlank()) {
                                                    Log.w("DriverPairing", " [WARNING] operatorId is empty for operator: ${driverRecord.operatorName}")
                                                }
                                                val supabaseRecordId = supabaseService.saveDriverPairing(
                                                    record = driverRecord,
                                                    operatorId = operatorId,
                                                    macId = macId
                                                )
                                                if (supabaseRecordId != null) {
                                                    Log.d("DriverPairing", "    [SUPABASE] Upload successful - Record ID: $supabaseRecordId")
                                                    // Mark as synced in Room DB
                                                    repository.markDriverPairingAsSynced(recordId, System.currentTimeMillis())
                                                    Log.d("DriverPairing", "    [LOCAL] Marked as synced in Room DB")
                                                } else {
                                                    Log.w("DriverPairing", "    [SUPABASE] Upload failed (will retry later)")
                                                }
                                            } catch (e: IllegalStateException) {
                                                // Duplicate detection error - show user-friendly message
                                                Log.e("DriverPairing", "    [DUPLICATE] ${e.message}")
                                                Toast.makeText(
                                                    context,
                                                    " Duplicate driver pairs detected!\nThe same driver pairs already exist for this device.\nPlease check your input.",
                                                    Toast.LENGTH_LONG
                                                ).show()
                                                // Don't mark as synced since save was blocked
                                            } catch (e: Exception) {
                                                Log.e("DriverPairing", "    [SUPABASE] Error uploading to Supabase: ${e.message}")
                                                Log.e("DriverPairing", "   Stack trace: ${e.stackTraceToString()}")
                                                // Don't fail the whole operation if Supabase save fails
                                            }

                                        } catch (e: Exception) {
                                            Log.e("DriverPairing", " Error saving driver pairing record", e)
                                        }

                                        Toast.makeText(context, " Driver data saved successfully!", Toast.LENGTH_SHORT).show()

                                        // Call onSave callback with driver list
                                        val drivers = mutableListOf<String>()
                                        drivers.add(driver1Row1)
                                        drivers.add(driver2Row1)
                                        dynamicRows.forEach { row ->
                                            drivers.add(row.driver1)
                                            drivers.add(row.driver2)
                                        }
                                        Log.d("DriverPairing", " [SAVE_CALLBACK] Calling onSave with ${drivers.size} driver entries")
                                        onSave(drivers)

                                        // Navigate back after short delay
                                        kotlinx.coroutines.delay(500)
                                        Log.d("DriverPairing", " [SAVE_NAVIGATE] Navigating back to landing page")
                                        onBack()
                                    } else {
                                        Log.e("DriverPairing", " [SAVE_FAILED] Failed to save driver data")
                                        Toast.makeText(context, " Failed to save driver data", Toast.LENGTH_LONG).show()
                                    }
                                } catch (e: Exception) {
                                    Log.e("DriverPairing", " [SAVE_ERROR] Exception during save", e)
                                    Toast.makeText(context, "Error: ${e.message}", Toast.LENGTH_LONG).show()
                                } finally {
                                    isSaving = false
                                    Log.d("DriverPairing", " [SAVE_FINISHED] Save process completed, isSaving set to false")
                                }
                            }
                        },
                        modifier = Modifier
                            .fillMaxWidth()
                            .height(50.dp),
                        enabled = !isSaving,
                        colors = ButtonDefaults.buttonColors(
                            containerColor = accentColor,
                            contentColor = bgColor
                        ),
                        shape = RoundedCornerShape(12.dp)
                    ) {
                        if (isSaving) {
                            CircularProgressIndicator(
                                modifier = Modifier.size(20.dp),
                                color = bgColor,
                                strokeWidth = 2.dp
                            )
                        } else {
                            Text(
                                "Save Entry",
                                fontSize = 16.sp,
                                fontWeight = FontWeight.Bold
                            )
                        }
                    }
                }
            }
        }

        // Add Row Dialog
        if (showAddRowDialog) {
            AddRowDialog(
                onDismiss = {
                    Log.d("DriverPairing", " [DIALOG_DISMISS] Add Row dialog dismissed without action")
                    showAddRowDialog = false
                },
                onActionSelected = { actionType, otherText ->
                    Log.d("DriverPairing", " [DIALOG_ACTION] Action selected: $actionType, otherText: '$otherText'")
                    val previousRow = if (dynamicRows.isEmpty()) {
                        Log.d("DriverPairing", " [DIALOG_PREV] Using Row 1 as previous row (Driver1: '$driver1Row1', Driver2: '$driver2Row1')")
                        DriverRow(driver1Row1, driver2Row1)
                    } else {
                        val lastRow = dynamicRows.last()
                        Log.d("DriverPairing", " [DIALOG_PREV] Using last dynamic row as previous (Row ${dynamicRows.size + 1}, Driver1: '${lastRow.driver1}', Driver2: '${lastRow.driver2}')")
                        lastRow
                    }

                    val newRow = when (actionType) {
                        RowActionType.INTERCHANGE -> {
                            Log.d("DriverPairing", " [DIALOG_INTERCHANGE] Creating interchange row - swapping drivers")
                            // Swap Driver 1 and Driver 2 from previous row
                            DriverRow(
                                driver1 = previousRow.driver2,
                                driver2 = previousRow.driver1,
                                rowType = "interchange"
                            )
                        }
                        RowActionType.CHANGE -> {
                            Log.d("DriverPairing", " [DIALOG_CHANGE] Creating change row with blank drivers")
                            DriverRow(
                                driver1 = "",
                                driver2 = "",
                                rowType = "change"
                            )
                        }
                        RowActionType.OTHER -> {
                            Log.d("DriverPairing", " [DIALOG_OTHER] Creating other row with notes: '$otherText'")
                            DriverRow(
                                driver1 = "",
                                driver2 = "",
                                rowType = "other: $otherText"
                            )
                        }
                    }

                    Log.d("DriverPairing", " [DIALOG_ADD] Adding new row: Driver1='${newRow.driver1}', Driver2='${newRow.driver2}', Type='${newRow.rowType}'")
                    dynamicRows = dynamicRows + newRow
                    Log.d("DriverPairing", " [DIALOG_ADD] Total rows after add: ${dynamicRows.size + 1} (1 static + ${dynamicRows.size} dynamic)")
                    showAddRowDialog = false
                },
                bgColor = bgColor,
                surfaceColor = surfaceColor,
                accentColor = accentColor,
                textColor = textColor
            )
        }

        // Alert dialog for test stage requirement
        if (showTestStageRequiredDialog) {
            AlertDialog(
                onDismissRequest = { showTestStageRequiredDialog = false },
                title = { Text("Test Stage Required", fontWeight = FontWeight.Bold) },
                text = { Text("Please select a test stage (Primary Test, Secondary Test, or Final Test) before saving.") },
                confirmButton = {
                    Button(onClick = { showTestStageRequiredDialog = false }) {
                        Text("OK")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White.copy(alpha = 0.8f)
            )
        }
    }
}

@Composable
fun DriverRowInput(
    label: String,
    driver1: String,
    driver2: String,
    onDriver1Change: (String) -> Unit,
    onDriver2Change: (String) -> Unit,
    textColor: Color,
    bgColor: Color,
    accentColor: Color,
    rowType: String = "",
    showDelete: Boolean = false,
    onDelete: () -> Unit = {}
) {
    Row(
        modifier = Modifier.fillMaxWidth(),
        horizontalArrangement = Arrangement.spacedBy(12.dp),
        verticalAlignment = Alignment.CenterVertically
    ) {
        // Driver 1
        Column(modifier = Modifier.weight(1f)) {
            Row(verticalAlignment = Alignment.CenterVertically) {
                Text(
                    "Driver 1",
                    color = textColor.copy(alpha = 0.7f),
                    fontSize = 12.sp,
                    modifier = Modifier.padding(bottom = 4.dp)
                )
                if (rowType.isNotEmpty()) {
                    Spacer(modifier = Modifier.width(4.dp))
                    Text(
                        "($rowType)",
                        color = accentColor.copy(alpha = 0.6f),
                        fontSize = 10.sp
                    )
                }
            }
            TextField(
                value = driver1,
                onValueChange = onDriver1Change,
                modifier = Modifier.fillMaxWidth(),
                placeholder = {
                    Text(
                        "Enter Driver 1",
                        color = textColor.copy(alpha = 0.5f),
                        fontSize = 12.sp
                    )
                },
                colors = TextFieldDefaults.colors(
                    focusedTextColor = textColor,
                    unfocusedTextColor = textColor,
                    focusedContainerColor = bgColor,
                    unfocusedContainerColor = bgColor,
                    focusedIndicatorColor = accentColor,
                    unfocusedIndicatorColor = Color(0xFF424242),
                    cursorColor = accentColor
                ),
                textStyle = MaterialTheme.typography.bodyMedium.copy(fontSize = 14.sp),
                singleLine = true,
                keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Text)
            )
        }

        // Driver 2
        Column(modifier = Modifier.weight(1f)) {
            Text(
                "Driver 2",
                color = textColor.copy(alpha = 0.7f),
                fontSize = 12.sp,
                modifier = Modifier.padding(bottom = 4.dp)
            )
            TextField(
                value = driver2,
                onValueChange = onDriver2Change,
                modifier = Modifier.fillMaxWidth(),
                placeholder = {
                    Text(
                        "Enter Driver 2",
                        color = textColor.copy(alpha = 0.5f),
                        fontSize = 12.sp
                    )
                },
                colors = TextFieldDefaults.colors(
                    focusedTextColor = textColor,
                    unfocusedTextColor = textColor,
                    focusedContainerColor = bgColor,
                    unfocusedContainerColor = bgColor,
                    focusedIndicatorColor = accentColor,
                    unfocusedIndicatorColor = Color(0xFF424242),
                    cursorColor = accentColor
                ),
                textStyle = MaterialTheme.typography.bodyMedium.copy(fontSize = 14.sp),
                singleLine = true,
                keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Text)
            )
        }

        // Delete Button (only for dynamic rows)
        if (showDelete) {
            Spacer(modifier = Modifier.width(8.dp))
            IconButton(
                onClick = onDelete,
                modifier = Modifier.size(40.dp)
            ) {
                Icon(
                    Icons.Filled.Delete,
                    contentDescription = "Delete Row",
                    tint = Color(0xFFEF4444),
                    modifier = Modifier.size(24.dp)
                )
            }
        }
    }
}

@Composable
fun AddRowDialog(
    onDismiss: () -> Unit,
    onActionSelected: (RowActionType, String) -> Unit,
    bgColor: Color,
    surfaceColor: Color,
    accentColor: Color,
    textColor: Color
) {
    var selectedAction by remember { mutableStateOf<RowActionType?>(null) }
    var otherText by remember { mutableStateOf("") }

    Dialog(onDismissRequest = onDismiss) {
        Card(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            colors = CardDefaults.cardColors(containerColor = surfaceColor),
            shape = RoundedCornerShape(16.dp)
        ) {
            Column(
                modifier = Modifier.padding(24.dp),
                verticalArrangement = Arrangement.spacedBy(16.dp)
            ) {
                Text(
                    "Select Action",
                    style = MaterialTheme.typography.titleLarge,
                    color = accentColor,
                    fontWeight = FontWeight.Bold
                )

                HorizontalDivider(color = Color(0xFF424242))

                // Driver Interchange Option
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .clickable {
                            Log.d("DriverPairing", " [DIALOG_OPTION] Driver Interchange option tapped")
                            selectedAction = RowActionType.INTERCHANGE
                        }
                        .padding(12.dp),
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    RadioButton(
                        selected = selectedAction == RowActionType.INTERCHANGE,
                        onClick = {
                            Log.d("DriverPairing", " [DIALOG_RADIO] Driver Interchange radio button clicked")
                            selectedAction = RowActionType.INTERCHANGE
                        },
                        colors = RadioButtonDefaults.colors(
                            selectedColor = accentColor,
                            unselectedColor = textColor.copy(alpha = 0.6f)
                        )
                    )
                    Spacer(modifier = Modifier.width(8.dp))
                    Text(
                        "Driver Interchange",
                        color = textColor,
                        fontSize = 16.sp
                    )
                }

                // Driver Change Option
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .clickable {
                            Log.d("DriverPairing", " [DIALOG_OPTION] Driver Change option tapped")
                            selectedAction = RowActionType.CHANGE
                        }
                        .padding(12.dp),
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    RadioButton(
                        selected = selectedAction == RowActionType.CHANGE,
                        onClick = {
                            Log.d("DriverPairing", " [DIALOG_RADIO] Driver Change radio button clicked")
                            selectedAction = RowActionType.CHANGE
                        },
                        colors = RadioButtonDefaults.colors(
                            selectedColor = accentColor,
                            unselectedColor = textColor.copy(alpha = 0.6f)
                        )
                    )
                    Spacer(modifier = Modifier.width(8.dp))
                    Text(
                        "Driver Change",
                        color = textColor,
                        fontSize = 16.sp
                    )
                }

                // Others Option
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .clickable {
                            Log.d("DriverPairing", " [DIALOG_OPTION] Others option tapped")
                            selectedAction = RowActionType.OTHER
                        }
                        .padding(12.dp),
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    RadioButton(
                        selected = selectedAction == RowActionType.OTHER,
                        onClick = {
                            Log.d("DriverPairing", " [DIALOG_RADIO] Others radio button clicked")
                            selectedAction = RowActionType.OTHER
                        },
                        colors = RadioButtonDefaults.colors(
                            selectedColor = accentColor,
                            unselectedColor = textColor.copy(alpha = 0.6f)
                        )
                    )
                    Spacer(modifier = Modifier.width(8.dp))
                    Text(
                        "Others",
                        color = textColor,
                        fontSize = 16.sp
                    )
                }

                // Text box for "Others" option
                if (selectedAction == RowActionType.OTHER) {
                    TextField(
                        value = otherText,
                        onValueChange = {
                            Log.d("DriverPairing", " [DIALOG_OTHER_TEXT] Other text changed from '$otherText' to '$it'")
                            otherText = it
                        },
                        modifier = Modifier.fillMaxWidth(),
                        placeholder = {
                            Text(
                                "Enter details...",
                                color = textColor.copy(alpha = 0.5f)
                            )
                        },
                        colors = TextFieldDefaults.colors(
                            focusedTextColor = textColor,
                            unfocusedTextColor = textColor,
                            focusedContainerColor = bgColor,
                            unfocusedContainerColor = bgColor,
                            focusedIndicatorColor = accentColor,
                            unfocusedIndicatorColor = Color(0xFF424242)
                        ),
                        singleLine = false,
                        maxLines = 3
                    )
                }

                Spacer(modifier = Modifier.height(8.dp))

                // Action Buttons
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    OutlinedButton(
                        onClick = {
                            Log.d("DriverPairing", " [DIALOG_CANCEL] Cancel button tapped")
                            onDismiss()
                        },
                        modifier = Modifier.weight(1f),
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = textColor
                        )
                    ) {
                        Text("Cancel")
                    }

                    Button(
                        onClick = {
                            Log.d("DriverPairing", " [DIALOG_ADD_BUTTON] Add Row button tapped, selectedAction: $selectedAction")
                            if (selectedAction != null) {
                                Log.d("DriverPairing", " [DIALOG_ADD_BUTTON] Calling onActionSelected with action: $selectedAction, otherText: '$otherText'")
                                onActionSelected(selectedAction!!, otherText)
                            } else {
                                Log.w("DriverPairing", " [DIALOG_ADD_BUTTON] Add Row button tapped but no action selected")
                            }
                        },
                        modifier = Modifier.weight(1f),
                        enabled = selectedAction != null,
                        colors = ButtonDefaults.buttonColors(
                            containerColor = accentColor,
                            contentColor = bgColor
                        )
                    ) {
                        Text("Add Row")
                    }
                }
            }
        }
    }
}

package com.example.fqcapp.supabase

import android.util.Log
import com.example.fqcapp.data.entities.SongTest
import io.github.jan.supabase.postgrest.from
import io.github.jan.supabase.postgrest.query.Columns
import io.github.jan.supabase.postgrest.query.Order
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import kotlinx.serialization.Serializable

/**
 * Data transfer object for Supabase song_tests table.
 * This matches the structure of the SongTest entity but uses snake_case for Supabase.
 */
@Serializable
data class SupabaseSongTest(
    val test_id: Long? = null, // Auto-generated, nullable for inserts
    val serial_number: String,
    val operator_name: String,
    val operator_id: Long? = null, // Operator ID (bigint in Supabase) - nullable
    val entry_timestamp: String,
    val exit_timestamp: String,
    val total_time_spent: String,
    val song_name: String,
    val result: String,
    val remark: String? = null, // Nullable because Supabase may return null
    val iteration_number: Int = 1,
    val test_stage: String = "Primary Test", // "Primary Test", "Secondary Test", or "Final Test"
    val is_overall_result: Boolean = false,
    val timestamp: String = ""
)

/**
 * Data transfer object for Supabase song_test_issues table.
 */
@Serializable
data class SupabaseSongTestIssue(
    val test_id: Long,
    val issue_text: String
)

/**
 * Data transfer object for Supabase song_test_solutions table.
 */
@Serializable
data class SupabaseSongTestSolution(
    val test_id: Long,
    val solution_text: String
)

/**
 * Service for saving and retrieving song test data from Supabase.
 */
class SupabaseSongTestService {
    private val TAG = "SupabaseSongTest"
    private val deviceService = SupabaseDeviceService()

    /**
     * Save a song test (overall review or individual song) to Supabase.
     * Returns the inserted test ID if successful, null otherwise.
     * 
     * Also saves issues and solutions to normalized tables.
     * 
     * @param test The song test entity
     * @param operatorId The operator ID (unique identifier) - can be empty/null
     */
    suspend fun saveSongTest(test: SongTest, operatorId: String): Long? = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping save")
            return@withContext null
        }

        try {
            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] UPLOADING SONG TEST")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Operator: ${test.operatorName}")
            Log.d(TAG, "   Song Name: ${test.songName}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Is Overall: ${test.isOverallResult}")
            Log.d(TAG, "   Entry Timestamp: ${test.entryTimestamp}")
            Log.d(TAG, "   Exit Timestamp: ${test.exitTimestamp}")
            Log.d(TAG, "   Total Time: ${test.totalTimeSpent}")
            Log.d(TAG, "")

            // CRITICAL: Ensure device exists in Supabase before inserting test data
            Log.d(TAG, " [STEP 1/3] Ensuring device exists in Supabase...")
            val deviceExists = deviceService.ensureDeviceExists(
                serialNumber = test.serialNumber,
                macAddress = null, // MAC address not available in SongTest entity
                color = null
            )

            if (!deviceExists) {
                Log.e(TAG, "    [ERROR] Failed to ensure device exists, cannot save song test")
                Log.e(TAG, "")
                return@withContext null
            }
            Log.d(TAG, "    [SUCCESS] Device exists or created successfully")

            // Convert to Supabase format
            Log.d(TAG, " [STEP 2/3] Preparing data for upload...")
            // Convert operatorId string to Long? - null if empty or invalid
            val operatorIdLong = try {
                if (operatorId.isBlank()) {
                    null
                } else {
                    operatorId.toLongOrNull()
                }
            } catch (e: Exception) {
                Log.w(TAG, " Could not convert operatorId '$operatorId' to Long, using null", e)
                null
            }

            val insertData = SupabaseSongTest(
                serial_number = test.serialNumber,
                operator_name = test.operatorName,
                operator_id = operatorIdLong, // Now Long? instead of String
                entry_timestamp = test.entryTimestamp,
                exit_timestamp = test.exitTimestamp,
                total_time_spent = test.totalTimeSpent,
                song_name = test.songName,
                result = test.result,
                remark = test.remark,
                iteration_number = test.iterationNumber,
                test_stage = test.testStage,
                is_overall_result = test.isOverallResult,
                timestamp = test.timestamp
            )

            Log.d(TAG, "    [UPLOAD] Data prepared:")
            Log.d(TAG, "      - serial_number: ${insertData.serial_number}")
            Log.d(TAG, "      - operator_name: ${insertData.operator_name}")
            Log.d(TAG, "      - operator_id: ${insertData.operator_id}")
            Log.d(TAG, "      - song_name: ${insertData.song_name}")
            Log.d(TAG, "      - result: ${insertData.result}")
            Log.d(TAG, "      - entry_timestamp: ${insertData.entry_timestamp}")
            Log.d(TAG, "      - exit_timestamp: ${insertData.exit_timestamp}")
            Log.d(TAG, "      - total_time_spent: ${insertData.total_time_spent}")
            Log.d(TAG, "      - iteration_number: ${insertData.iteration_number}")
            Log.d(TAG, "      - is_overall_result: ${insertData.is_overall_result}")

            Log.d(TAG, " [STEP 3/3] Inserting into song_tests table...")
            Log.d(TAG, "    [QUERY] INSERT INTO song_tests (...) VALUES (...)")

            // Insert song test
            val response = client.from("song_tests")
                .insert(insertData) {
                    select(Columns.ALL)
                }

            val insertedTest = response.decodeSingle<SupabaseSongTest>()
            val testId = insertedTest.test_id ?: return@withContext null

            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] SONG TEST UPLOADED SUCCESSFULLY")
            Log.d(TAG, "   Test ID: $testId")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Song: ${test.songName}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "")

            // Save issues to normalized table (if any)
            // IMPORTANT: For PASS results, checklist items (No Distortion, Balance OK, No Noise) 
            // are NOT saved as issues - they are implicit in a PASS result.
            // Only actual problems (for FAIL/CAN_BE_PASSED) should be saved as issues.
            // A PASS means all checklists were checked, so storing them separately is redundant.
            if (test.issues.isNotBlank() && test.result.uppercase() != "PASS") {
                try {
                    saveIssues(testId, test.issues)
                } catch (e: Exception) {
                    Log.e(TAG, " Failed to save issues to normalized table", e)
                    // Don't fail the whole operation if issues save fails
                }
            } else if (test.result.uppercase() == "PASS") {
                Log.d(TAG, " Skipping issues save for PASS result - checklist items are implicit in PASS")
            }

            // Save solutions to normalized table (if any, only for overall review)
            if (test.isOverallResult && test.solutions.isNotBlank()) {
                try {
                    saveSolutions(testId, test.solutions)
                } catch (e: Exception) {
                    Log.e(TAG, " Failed to save solutions to normalized table", e)
                    // Don't fail the whole operation if solutions save fails
                }
            }

            return@withContext testId

        } catch (e: Exception) {
            Log.e(TAG, " Failed to save song test to Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext null
        }
    }

    /**
     * Save multiple individual songs (latest 3) for an iteration.
     * This is called after the overall review is saved.
     */
    suspend fun saveIndividualSongs(
        serialNumber: String,
        iterationNumber: Int,
        individualSongs: List<SongTest>,
        operatorId: String
    ): Boolean = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping save")
            return@withContext false
        }

        try {
            // Ensure device exists
            val deviceExists = deviceService.ensureDeviceExists(
                serialNumber = serialNumber,
                macAddress = null,
                color = null
            )

            if (!deviceExists) {
                Log.e(TAG, " Failed to ensure device exists, cannot save individual songs")
                return@withContext false
            }

            // Only save latest 3 individual songs
            val songsToSave = individualSongs.take(3)

            if (songsToSave.isEmpty()) {
                Log.d(TAG, " No individual songs to save")
                return@withContext true
            }

            Log.d(TAG, " Saving ${songsToSave.size} individual songs to Supabase for serial=$serialNumber, iteration=$iterationNumber")

            // Convert to Supabase format
            // Convert operatorId string to Long? - null if empty or invalid
            val operatorIdLong = try {
                if (operatorId.isBlank()) {
                    null
                } else {
                    operatorId.toLongOrNull()
                }
            } catch (e: Exception) {
                Log.w(TAG, " Could not convert operatorId '$operatorId' to Long, using null", e)
                null
            }

            val insertDataList = songsToSave.map { song ->
                SupabaseSongTest(
                    serial_number = song.serialNumber,
                    operator_name = song.operatorName,
                    operator_id = operatorIdLong, // Now Long? instead of String
                    entry_timestamp = song.entryTimestamp,
                    exit_timestamp = song.exitTimestamp,
                    total_time_spent = song.totalTimeSpent,
                    song_name = song.songName,
                    result = song.result,
                    remark = song.remark,
                    iteration_number = song.iterationNumber,
                    test_stage = song.testStage,
                    is_overall_result = false, // Individual songs are never overall
                    timestamp = song.timestamp
                )
            }

            // Bulk insert individual songs
            val response = client.from("song_tests")
                .insert(insertDataList) {
                    select(Columns.ALL)
                }

            val insertedTests = response.decodeList<SupabaseSongTest>()

            // Save issues for each individual song
            // IMPORTANT: For PASS results, checklist items are NOT saved as issues
            insertedTests.forEachIndexed { index, insertedTest ->
                val originalSong = songsToSave[index]
                val testId = insertedTest.test_id ?: return@forEachIndexed

                // Only save issues for FAIL/CAN_BE_PASSED - PASS results don't need issues saved
                if (originalSong.issues.isNotBlank() && originalSong.result.uppercase() != "PASS") {
                    try {
                        saveIssues(testId, originalSong.issues)
                    } catch (e: Exception) {
                        Log.e(TAG, " Failed to save issues for individual song ${originalSong.songName}", e)
                    }
                } else if (originalSong.result.uppercase() == "PASS") {
                    Log.d(TAG, " Skipping issues save for individual song ${originalSong.songName} - PASS result")
                }
            }

            Log.d(TAG, " Successfully saved ${insertedTests.size} individual songs to Supabase")
            return@withContext true

        } catch (e: Exception) {
            Log.e(TAG, " Failed to save individual songs to Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext false
        }
    }

    /**
     * Save issues to normalized song_test_issues table.
     * Parses issues string (can be JSON array or plain text) and saves each issue separately.
     */
    private suspend fun saveIssues(testId: Long, issuesString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse issues - could be JSON array or plain text
            val issuesList = parseIssuesOrSolutions(issuesString)

            if (issuesList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val issuesData = issuesList.map { issueText ->
                SupabaseSongTestIssue(
                    test_id = testId,
                    issue_text = issueText
                )
            }

            // Bulk insert all issues at once
            if (issuesData.isNotEmpty()) {
                client.from("song_test_issues")
                    .insert(issuesData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${issuesList.size} issues to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving issues to normalized table", e)
            throw e
        }
    }

    /**
     * Save solutions to normalized song_test_solutions table.
     * Parses solutions string (can be JSON array or plain text) and saves each solution separately.
     */
    private suspend fun saveSolutions(testId: Long, solutionsString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse solutions - could be JSON array or plain text
            val solutionsList = parseIssuesOrSolutions(solutionsString)

            if (solutionsList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val solutionsData = solutionsList.map { solutionText ->
                SupabaseSongTestSolution(
                    test_id = testId,
                    solution_text = solutionText
                )
            }

            // Bulk insert all solutions at once
            if (solutionsData.isNotEmpty()) {
                client.from("song_test_solutions")
                    .insert(solutionsData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${solutionsList.size} solutions to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving solutions to normalized table", e)
            throw e
        }
    }

    /**
     * Parse issues or solutions string.
     * Handles both JSON array format (["Issue 1", "Issue 2"]) and plain text.
     */
    private fun parseIssuesOrSolutions(text: String): List<String> {
        if (text.isBlank()) {
            return emptyList()
        }

        return try {
            // Try to parse as JSON array
            if (text.trim().startsWith("[")) {
                val json = kotlinx.serialization.json.Json
                val jsonElement = json.parseToJsonElement(text)
                if (jsonElement is kotlinx.serialization.json.JsonArray) {
                    jsonElement.map { element ->
                        if (element is kotlinx.serialization.json.JsonPrimitive) {
                            element.content
                        } else {
                            element.toString()
                        }
                    }
                } else {
                    listOf(text.trim())
                }
            } else {
                // Plain text - split by comma or newline, or return as single item
                text.split(Regex("[,;\\n]"))
                    .map { it.trim() }
                    .filter { it.isNotBlank() }
                    .ifEmpty { listOf(text.trim()) } // If no delimiters, return as single item
            }
        } catch (e: Exception) {
            // If parsing fails, treat as plain text
            Log.w(TAG, " Failed to parse as JSON, treating as plain text: $text")
            listOf(text.trim())
        }
    }

    /**
     * Get song tests for a specific serial number.
     */
    suspend fun getSongTestsForSerial(serialNumber: String): List<SongTest> = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, cannot get song tests")
            return@withContext emptyList()
        }

        try {
            val response = client.from("song_tests")
                .select {
                    filter {
                        eq("serial_number", serialNumber)
                    }
                    order("entry_timestamp", Order.DESCENDING)
                }

            val supabaseTests = response.decodeList<SupabaseSongTest>()

            return@withContext supabaseTests.map { supabaseTest ->
                SongTest(
                    testId = supabaseTest.test_id ?: 0,
                    serialNumber = supabaseTest.serial_number,
                    operatorName = supabaseTest.operator_name,
                    entryTimestamp = supabaseTest.entry_timestamp,
                    exitTimestamp = supabaseTest.exit_timestamp,
                    totalTimeSpent = supabaseTest.total_time_spent,
                    songName = supabaseTest.song_name,
                    result = supabaseTest.result,
                    issues = "", // Issues are in normalized table, would need separate query
                    solutions = "", // Solutions are in normalized table, would need separate query
                    remark = supabaseTest.remark ?: "", // Handle null from Supabase
                    timestamp = supabaseTest.timestamp,
                    iterationNumber = supabaseTest.iteration_number,
                    isOverallResult = supabaseTest.is_overall_result,
                    needsSync = false, // Already synced
                    lastSyncTimestamp = System.currentTimeMillis()
                )
            }
        } catch (e: Exception) {
            Log.e(TAG, " Failed to get song tests from Supabase", e)
            return@withContext emptyList()
        }
    }
}

package com.example.fqcapp.supabase

import android.util.Log
import com.example.fqcapp.data.entities.FrequencyTest
import io.github.jan.supabase.postgrest.from
import io.github.jan.supabase.postgrest.query.Columns
import io.github.jan.supabase.postgrest.query.Order
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import kotlinx.serialization.Serializable
import org.json.JSONArray

/**
 * Data transfer object for Supabase frequency_tests table.
 * This matches the structure of the FrequencyTest entity but uses snake_case for Supabase.
 */
@Serializable
data class SupabaseFrequencyTest(
    val test_id: Long? = null, // Auto-generated, nullable for inserts
    val serial_number: String,
    val operator_name: String,
    @Serializable(with = IntOrStringSerializer::class)
    val operator_id: String = "", // Operator ID (unique identifier) - handles Int or String from Supabase
    val entry_timestamp: String, // When user entered/started the frequency test
    val exit_timestamp: String, // When user exited/completed the frequency test
    val total_time_spent: String, // Total time in HH:MM:SS format (e.g., "00:05:30")
    val result: String, // "PASS" or "FAIL"
    val issues: String? = null, // JSON array string (nullable for backward compatibility)
    val solutions: String? = null, // JSON array string (nullable for backward compatibility)
    val remark: String,
    val iteration_number: Int = 1,
    val test_stage: String = "Primary Test", // "Primary Test", "Secondary Test", or "Final Test"
    val timestamp: String = "", // ISO format (kept for backward compatibility)
    val needs_sync: Boolean? = null, // Not needed in Supabase as it's always synced
    val last_sync_timestamp: Long? = null // Not needed in Supabase
)

/**
 * Minimal DTO for inserts (excludes issues/solutions if columns don't exist in schema).
 * This is used when the table doesn't have issues/solutions columns yet.
 */
@Serializable
data class SupabaseFrequencyTestInsert(
    val serial_number: String,
    val operator_name: String,
    @Serializable(with = IntOrStringSerializer::class)
    val operator_id: String = "", // Operator ID (unique identifier) - handles Int or String from Supabase
    val entry_timestamp: String,
    val exit_timestamp: String,
    val total_time_spent: String,
    val result: String,
    val remark: String,
    val iteration_number: Int = 1,
    val test_stage: String = "Primary Test", // "Primary Test", "Secondary Test", or "Final Test"
    val timestamp: String = ""
)

/**
 * Data transfer object for Supabase frequency_test_issues table (normalized).
 */
@Serializable
data class SupabaseFrequencyTestIssue(
    val issue_id: Long? = null, // Auto-generated, nullable for inserts
    val test_id: Long,
    val issue_text: String
)

/**
 * Data transfer object for Supabase frequency_test_solutions table (normalized).
 */
@Serializable
data class SupabaseFrequencyTestSolution(
    val solution_id: Long? = null, // Auto-generated, nullable for inserts
    val test_id: Long,
    val solution_text: String
)

/**
 * Service for saving and retrieving frequency test data from Supabase.
 */
class SupabaseFrequencyTestService {
    private val TAG = "SupabaseFreqTest"
    private val deviceService = SupabaseDeviceService()

    /**
     * Convert FrequencyTest entity to Supabase DTO.
     * Note: issues and solutions are stored as empty strings if not in schema,
     * they will be saved to normalized tables separately.
     */
    private fun toSupabaseFormat(test: FrequencyTest, operatorId: String): SupabaseFrequencyTest {
        return SupabaseFrequencyTest(
            test_id = if (test.testId > 0) test.testId else null, // Only include if already has ID
            serial_number = test.serialNumber,
            operator_name = test.operatorName,
            operator_id = operatorId,
            entry_timestamp = test.entryTimestamp,
            exit_timestamp = test.exitTimestamp,
            total_time_spent = test.totalTimeSpent,
            result = test.result,
            issues = test.issues, // Will be empty string if column doesn't exist
            solutions = test.solutions, // Will be empty string if column doesn't exist
            remark = test.remark,
            iteration_number = test.iterationNumber,
            test_stage = test.testStage,
            timestamp = test.timestamp, // For backward compatibility
            needs_sync = false, // Not relevant for Supabase
            last_sync_timestamp = null // Not relevant for Supabase
        )
    }

    /**
     * Save a frequency test to Supabase.
     * Returns the inserted test ID if successful, null otherwise.
     * 
     * Note: Uses minimal insert DTO (without issues/solutions) to avoid schema errors
     * if those columns don't exist. Issues/solutions will be saved to normalized tables separately.
     * 
     * @param test The frequency test entity
     * @param operatorId The operator ID (unique identifier) - must be provided
     */
    suspend fun saveFrequencyTest(test: FrequencyTest, operatorId: String): Long? = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping save")
            return@withContext null
        }

        try {
            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] UPLOADING FREQUENCY TEST")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Operator: ${test.operatorName}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Entry Timestamp: ${test.entryTimestamp}")
            Log.d(TAG, "   Exit Timestamp: ${test.exitTimestamp}")
            Log.d(TAG, "   Total Time: ${test.totalTimeSpent}")
            Log.d(TAG, "")

            // CRITICAL: Ensure device exists in Supabase before inserting test data
            // This is required because of the foreign key constraint
            Log.d(TAG, " [STEP 1/3] Ensuring device exists in Supabase...")
            val deviceExists = deviceService.ensureDeviceExists(
                serialNumber = test.serialNumber,
                macAddress = null, // MAC address not available in FrequencyTest entity
                color = null
            )

            if (!deviceExists) {
                Log.e(TAG, "    [ERROR] Failed to ensure device exists, cannot save frequency test")
                Log.e(TAG, "")
                return@withContext null
            }
            Log.d(TAG, "    [SUCCESS] Device exists or created successfully")

            // Use minimal insert DTO (excludes issues/solutions to avoid schema errors)
            Log.d(TAG, " [STEP 2/3] Preparing data for upload...")

            // Map result values to database-compatible values
            // Database constraint only allows "PASS" or "FAIL"
            // Map "CAN_BE_PASSED" and "CAN_PASS" to "FAIL" (since they're not a pass)
            // Preserve original result in remark if it's different
            val originalResult = test.result.uppercase()
            val dbResult = when (originalResult) {
                "PASS" -> "PASS"
                "FAIL" -> "FAIL"
                "CAN_BE_PASSED", "CAN_PASS", "CAN_PASSED" -> {
                    Log.d(TAG, "    [MAPPING] Mapping '${test.result}' to 'FAIL' for database constraint")
                    "FAIL"
                }
                "TEST_NOT_DONE" -> {
                    Log.d(TAG, "    [MAPPING] Mapping 'TEST_NOT_DONE' to 'FAIL' for database constraint")
                    "FAIL"
                }
                else -> {
                    Log.w(TAG, "    [MAPPING] Unknown result '${test.result}', mapping to 'FAIL'")
                    "FAIL"
                }
            }

            // Preserve original result in remark if it was mapped
            val remarkWithOriginalResult = if (originalResult != dbResult && !test.remark.contains("Original Result:")) {
                val originalResultNote = "Original Result: ${test.result}"
                if (test.remark.isBlank()) {
                    originalResultNote
                } else {
                    "$originalResultNote | ${test.remark}"
                }
            } else {
                test.remark
            }

            val insertData = SupabaseFrequencyTestInsert(
                serial_number = test.serialNumber,
                operator_name = test.operatorName,
                operator_id = operatorId,
                entry_timestamp = test.entryTimestamp,
                exit_timestamp = test.exitTimestamp,
                total_time_spent = test.totalTimeSpent,
                result = dbResult, // Use mapped result
                remark = remarkWithOriginalResult, // Include original result in remark
                iteration_number = test.iterationNumber,
                timestamp = test.timestamp
            )

            Log.d(TAG, "    [UPLOAD] Data prepared:")
            Log.d(TAG, "      - serial_number: ${insertData.serial_number}")
            Log.d(TAG, "      - operator_name: ${insertData.operator_name}")
            Log.d(TAG, "      - operator_id: ${insertData.operator_id}")
            Log.d(TAG, "      - entry_timestamp: ${insertData.entry_timestamp}")
            Log.d(TAG, "      - exit_timestamp: ${insertData.exit_timestamp}")
            Log.d(TAG, "      - total_time_spent: ${insertData.total_time_spent}")
            Log.d(TAG, "      - result: ${insertData.result}")
            Log.d(TAG, "      - iteration_number: ${insertData.iteration_number}")
            Log.d(TAG, "      - remark: ${insertData.remark}")

            Log.d(TAG, " [STEP 3/3] Inserting into frequency_tests table...")
            Log.d(TAG, "    [QUERY] INSERT INTO frequency_tests (...) VALUES (...)")

            val response = client.from("frequency_tests")
                .insert(insertData) {
                    select(Columns.ALL)
                }

            // Parse the response to get the inserted ID
            val inserted = response.decodeSingle<SupabaseFrequencyTest>()
            val insertedId = inserted.test_id ?: return@withContext null

            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] FREQUENCY TEST UPLOADED SUCCESSFULLY")
            Log.d(TAG, "   Test ID: $insertedId")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "")

            // Save issues to normalized table (if any)
            // IMPORTANT: For PASS results, checklist items are NOT saved as issues
            // Only actual problems (for FAIL/CAN_BE_PASSED) should be saved as issues.
            Log.d(TAG, " [NORMALIZED] Checking if issues need to be saved...")
            Log.d(TAG, "   Test result: ${test.result}")
            Log.d(TAG, "   Issues string: '${test.issues}' (length: ${test.issues.length})")
            Log.d(TAG, "   Issues isBlank: ${test.issues.isBlank()}")
            Log.d(TAG, "   Result is PASS: ${test.result.uppercase() == "PASS"}")

            if (test.issues.isNotBlank() && test.result.uppercase() != "PASS") {
                try {
                    Log.d(TAG, " [NORMALIZED] Saving issues to frequency_test_issues table...")
                    Log.d(TAG, "   Test ID: $insertedId")
                    Log.d(TAG, "   Issues JSON: ${test.issues}")
                    saveIssues(insertedId, test.issues)
                    Log.d(TAG, "    Issues saved successfully")
                } catch (e: Exception) {
                    Log.e(TAG, "    [ERROR] Failed to save issues to normalized table")
                    Log.e(TAG, "   Error message: ${e.message}")
                    Log.e(TAG, "   Error type: ${e.javaClass.simpleName}")
                    Log.e(TAG, "   Stack trace: ${e.stackTraceToString()}")
                    // Don't fail the whole operation if issues save fails
                }
            } else if (test.result.uppercase() == "PASS") {
                Log.d(TAG, "    [SKIP] Skipping issues save for PASS result - checklist items are implicit in PASS")
            } else {
                Log.d(TAG, "    [INFO] No issues to save (empty or PASS result)")
            }

            // Save solutions to normalized table (if any)
            Log.d(TAG, " [NORMALIZED] Checking if solutions need to be saved...")
            Log.d(TAG, "   Solutions string: '${test.solutions}' (length: ${test.solutions.length})")
            Log.d(TAG, "   Solutions isBlank: ${test.solutions.isBlank()}")

            if (test.solutions.isNotBlank() && test.result.uppercase() != "PASS") {
                try {
                    Log.d(TAG, " [NORMALIZED] Saving solutions to frequency_test_solutions table...")
                    Log.d(TAG, "   Test ID: $insertedId")
                    Log.d(TAG, "   Solutions JSON: ${test.solutions}")
                    saveSolutions(insertedId, test.solutions)
                    Log.d(TAG, "    Solutions saved successfully")
                } catch (e: Exception) {
                    Log.e(TAG, "    [ERROR] Failed to save solutions to normalized table")
                    Log.e(TAG, "   Error message: ${e.message}")
                    Log.e(TAG, "   Error type: ${e.javaClass.simpleName}")
                    Log.e(TAG, "   Stack trace: ${e.stackTraceToString()}")
                    // Don't fail the whole operation if solutions save fails
                }
            } else if (test.result.uppercase() == "PASS") {
                Log.d(TAG, "    [SKIP] Skipping solutions save for PASS result - checklist items are implicit in PASS")
            } else {
                Log.d(TAG, "    [INFO] No solutions to save (empty or PASS result)")
            }

            return@withContext insertedId

        } catch (e: Exception) {
            Log.e(TAG, " Failed to save frequency test to Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext null
        }
    }

    /**
     * Update an existing frequency test in Supabase.
     */
    suspend fun updateFrequencyTest(test: FrequencyTest): Boolean = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping update")
            return@withContext false
        }

        if (test.testId <= 0) {
            Log.w(TAG, " Test ID is invalid (${test.testId}), cannot update")
            return@withContext false
        }

        try {
            // Note: operatorId is not available in update context, using empty string as fallback
            // This should be fixed by passing operatorId when calling update
            val supabaseTest = toSupabaseFormat(test, "")

            Log.d(TAG, " Updating frequency test in Supabase: ID=${test.testId}, serial=${test.serialNumber}")

            client.from("frequency_tests")
                .update(supabaseTest) {
                    filter {
                        eq("test_id", test.testId)
                    }
                }

            Log.d(TAG, " Successfully updated frequency test in Supabase")
            return@withContext true

        } catch (e: Exception) {
            Log.e(TAG, " Failed to update frequency test in Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext false
        }
    }

    /**
     * Get frequency tests for a specific serial number from Supabase.
     */
    suspend fun getFrequencyTestsForSerial(serialNumber: String): List<FrequencyTest> = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, returning empty list")
            return@withContext emptyList()
        }

        try {
            val response = client.from("frequency_tests")
                .select {
                    filter {
                        eq("serial_number", serialNumber)
                    }
                    order("entry_timestamp", Order.DESCENDING)
                }

            val supabaseTests = response.decodeList<SupabaseFrequencyTest>()

            // Convert back to FrequencyTest entities
            return@withContext supabaseTests.map { supabaseTest ->
                FrequencyTest(
                    testId = supabaseTest.test_id ?: 0,
                    serialNumber = supabaseTest.serial_number,
                    operatorName = supabaseTest.operator_name,
                    entryTimestamp = supabaseTest.entry_timestamp,
                    exitTimestamp = supabaseTest.exit_timestamp,
                    totalTimeSpent = supabaseTest.total_time_spent,
                    result = supabaseTest.result,
                    issues = supabaseTest.issues ?: "", // Handle nullable issues
                    solutions = supabaseTest.solutions ?: "", // Handle nullable solutions
                    remark = supabaseTest.remark,
                    iterationNumber = supabaseTest.iteration_number,
                    timestamp = supabaseTest.timestamp, // For backward compatibility
                    needsSync = false,
                    lastSyncTimestamp = supabaseTest.last_sync_timestamp ?: 0
                )
            }

        } catch (e: Exception) {
            Log.e(TAG, " Failed to fetch frequency tests from Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext emptyList()
        }
    }

    /**
     * Save issues to normalized frequency_test_issues table.
     */
    private suspend fun saveIssues(testId: Long, issuesString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse issues - could be JSON array or plain text
            val issuesList = parseIssuesOrSolutions(issuesString)

            if (issuesList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val issuesData = issuesList.map { issueText ->
                SupabaseFrequencyTestIssue(
                    test_id = testId,
                    issue_text = issueText
                )
            }

            // Bulk insert all issues at once
            if (issuesData.isNotEmpty()) {
                client.from("frequency_test_issues")
                    .insert(issuesData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${issuesList.size} issues to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving issues to normalized table", e)
            throw e
        }
    }

    /**
     * Save solutions to normalized frequency_test_solutions table.
     */
    private suspend fun saveSolutions(testId: Long, solutionsString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse solutions - could be JSON array or plain text
            val solutionsList = parseIssuesOrSolutions(solutionsString)

            if (solutionsList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val solutionsData = solutionsList.map { solutionText ->
                SupabaseFrequencyTestSolution(
                    test_id = testId,
                    solution_text = solutionText
                )
            }

            // Bulk insert all solutions at once
            if (solutionsData.isNotEmpty()) {
                client.from("frequency_test_solutions")
                    .insert(solutionsData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${solutionsList.size} solutions to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving solutions to normalized table", e)
            throw e
        }
    }

    /**
     * Parse issues or solutions string (JSON array or plain text) into a list of strings.
     */
    private fun parseIssuesOrSolutions(text: String): List<String> {
        if (text.isBlank()) {
            return emptyList()
        }

        return try {
            // Try to parse as JSON array
            if (text.trim().startsWith("[")) {
                val jsonArray = JSONArray(text)
                val result = mutableListOf<String>()
                for (i in 0 until jsonArray.length()) {
                    val item = jsonArray.getString(i)
                    if (item.isNotBlank()) {
                        result.add(item.trim())
                    }
                }
                result
            } else {
                // Plain text - split by comma or newline
                text.split(",", "\n")
                    .map { it.trim() }
                    .filter { it.isNotBlank() }
            }
        } catch (e: Exception) {
            Log.w(TAG, " Failed to parse as JSON, treating as plain text: $text", e)
            // Fallback: treat as plain text
            text.split(",", "\n")
                .map { it.trim() }
                .filter { it.isNotBlank() }
        }
    }
}

