@Composable
fun LandingScreen(
    operatorName: String,
    currentSerialNumber: String?,
    currentMacAddress: String,
    currentDeviceName: String? = null, // New parameter for device name
    isReferenceSerial: Boolean = false, // Track if serial is generated reference
    onOpenScanner: () -> Unit,
    onOpenFreqGen: () -> Unit,
    onOpenTestSongs: () -> Unit,
    onOpenReview: () -> Unit,
    onOpenMore: () -> Unit,
    onOpenDeviceInfo: () -> Unit = {}, // New parameter for opening device info
    onLogout: () -> Unit,
    onOpenDrawer: () -> Unit,
    paddingValues: PaddingValues,
    hasBluetoothPermissions: Boolean = true,
    onRequestPermissions: () -> Unit = {},
    dataUploadStatus: String = "Unknown", // New parameter for upload status
    onForgetCurrentDevice: () -> Unit = {}, // New parameter for forgetting current device
    undoManager: UndoManager? = null, // New parameter for undo manager
    solutionReminderManager: com.example.fqcapp.core.SolutionReminderManager? = null, // New parameter for solution reminders
    onNavigateToDriverPairing: () -> Unit = {}, // New parameter for navigating to driver pairing
    onNavigateToChangedParts: () -> Unit = {} // New parameter for navigating to changed parts
) {
    val scope = rememberCoroutineScope()
    val bgColor = Color(0xFF121212)
    val accentColor = Color(0xFF6366F1) // Beautiful purple
    val surfaceColor = Color(0xFF1E1E1E)
    val secondaryColor = Color(0xFF374151)
    val warningColor = Color(0xFFF59E0B) // Beautiful orange
    val successColor = Color(0xFF10B981) // Beautiful green
    val infoColor = Color(0xFF3B82F6) // Beautiful blue
    val dangerColor = Color(0xFFEF4444) // Beautiful red

    val screenInfo = getScreenInfo()
    val responsivePadding = getResponsivePadding()
    val responsiveSpacing = getResponsiveSpacing()
    val context = LocalContext.current

    // Current Status Data - Declare at composable level
    var currentStatusData by remember { mutableStateOf<CurrentStatusData?>(null) }
    var detailedMilestoneData by remember { mutableStateOf<DetailedMilestoneData?>(null) }
    var isDetailedViewExpanded by remember { mutableStateOf(false) }
    var isSyncing by remember { mutableStateOf(false) }

    // Function to get serial number from MAC ID if serial number is null
    val getSerialFromMacId = suspend {
        if (currentSerialNumber != null) {
            currentSerialNumber
        } else if (currentMacAddress.isNotBlank()) {
            try {
                val db = com.example.fqcapp.data.AppDatabase.getDatabase(context)
                val macIdCacheDao = db.macIdCacheDao()
                val macIdCacheManager = MacIdCacheManager(context, macIdCacheDao, GoogleSheetsAPI(context))
                val serialFromMac = macIdCacheManager.getSerialNumberByMacId(currentMacAddress)
                Log.d("LandingScreen", " Looked up serial from MAC $currentMacAddress: $serialFromMac")
                serialFromMac
            } catch (e: Exception) {
                Log.e("LandingScreen", " Error looking up serial from MAC ID", e)
                null
            }
        } else {
            null
        }
    }

    // Function to sync data from Supabase and load from local DB
    val syncAndLoadData: suspend () -> Unit = {
        val serialToUse = getSerialFromMacId()
        if (serialToUse != null && !isSyncing) {
            isSyncing = true
            try {
                val db = com.example.fqcapp.data.AppDatabase.getDatabase(context)
                val repo = com.example.fqcapp.data.FQCRepository(db)

                // Load data from local DB FIRST (show cached data immediately)
                val driverPairingSheetsService = com.example.fqcapp.services.DriverPairingSheetsService(context)
                val driverPairingCacheManager = com.example.fqcapp.services.DriverPairingCacheManager(
                    db.driverPairingCacheDao(),
                    driverPairingSheetsService
                )

                // Load from local DB immediately (fast, non-blocking)
                val loadedCurrentStatus = repo.getCurrentStatusData(serialToUse, currentMacAddress, currentDeviceName, driverPairingCacheManager)
                val loadedDetailedMilestone = repo.getDetailedMilestoneData(serialToUse)
                currentStatusData = loadedCurrentStatus
                detailedMilestoneData = loadedDetailedMilestone

                // Then sync from Supabase in background (non-blocking)
                scope.launch(kotlinx.coroutines.Dispatchers.IO) {
                    try {
                        Log.d("LandingScreen", " Syncing data from Supabase for: $serialToUse (background)")
                        val syncService = DeviceDataSyncService(db)
                        val syncSuccess = syncService.syncDeviceDataFromSupabase(serialToUse)
                        Log.d("LandingScreen", " Sync completed: $syncSuccess")

                        // Reload data after sync completes (only if still on same serial)
                        val currentSerial = getSerialFromMacId()
                        if (serialToUse == currentSerial) {
                            val updatedCurrentStatus = repo.getCurrentStatusData(serialToUse, currentMacAddress, currentDeviceName, driverPairingCacheManager)
                            val updatedDetailedMilestone = repo.getDetailedMilestoneData(serialToUse)
                            currentStatusData = updatedCurrentStatus
                            detailedMilestoneData = updatedDetailedMilestone
                        }
                    } catch (e: kotlinx.coroutines.CancellationException) {
                        Log.d("LandingScreen", " Sync cancelled (expected on navigation)")
                    } catch (e: Exception) {
                        Log.e("LandingScreen", " Error syncing from Supabase", e)
                    }
                }

                // Reduced logging for performance - only log errors
            } catch (e: kotlinx.coroutines.CancellationException) {
                // Expected when navigating away - don't log as error
                Log.d("LandingScreen", " Load cancelled (expected on navigation)")
            } catch (e: Exception) {
                Log.e("LandingScreen", " Error loading data", e)
                // Data loading already happened above, so we're good
            } finally {
                isSyncing = false
            }
        } else {
            // No serial number found, cannot load data
            Log.d("LandingScreen", " No serial number found (serial=$currentSerialNumber, mac=$currentMacAddress), cannot load data")
        }
    }

    // Cache last loaded serial to prevent unnecessary reloads
    var lastLoadedSerial by remember { mutableStateOf<String?>(null) }
    var lastLoadTime by remember { mutableStateOf(0L) }

    // Sync and load when serial number or MAC address changes (with debouncing)
    LaunchedEffect(currentSerialNumber, currentMacAddress) {
        val serialToUse = getSerialFromMacId()

        // Debounce: Only reload if serial changed or it's been more than 2 seconds since last load
        val shouldReload = serialToUse != null && (
            serialToUse != lastLoadedSerial || 
            (System.currentTimeMillis() - lastLoadTime) > 2000
        )

        if (shouldReload) {
            if (serialToUse != null) {
                lastLoadedSerial = serialToUse
                lastLoadTime = System.currentTimeMillis()
                syncAndLoadData()
            } else {
                currentStatusData = null
                detailedMilestoneData = null
                isDetailedViewExpanded = false
                lastLoadedSerial = null
            }
        }
    }

    // Also refresh periodically (every 30 seconds) to catch updates from other screens
    // Only refresh if serial hasn't changed and it's been at least 30 seconds
    LaunchedEffect(Unit) {
        while (true) {
            kotlinx.coroutines.delay(30000) // Check every 30 seconds (reduced from 15s)
            val serialToUse = getSerialFromMacId()

            // Only refresh if:
            // 1. Serial exists
            // 2. Not currently syncing
            // 3. Serial hasn't changed since last load
            // 4. It's been at least 30 seconds since last load
            if (serialToUse != null && 
                !isSyncing && 
                serialToUse == lastLoadedSerial &&
                (System.currentTimeMillis() - lastLoadTime) >= 30000) {
                try {
                    // Only reload from local DB, don't sync again (faster)
                    val db = com.example.fqcapp.data.AppDatabase.getDatabase(context)
                    val repo = com.example.fqcapp.data.FQCRepository(db)
                    // Create DriverPairingCacheManager to check Google Sheets if needed
                    val driverPairingSheetsService = com.example.fqcapp.services.DriverPairingSheetsService(context)
                    val driverPairingCacheManager = com.example.fqcapp.services.DriverPairingCacheManager(
                        db.driverPairingCacheDao(),
                        driverPairingSheetsService
                    )
                    currentStatusData = repo.getCurrentStatusData(serialToUse, currentMacAddress, currentDeviceName, driverPairingCacheManager)
                    detailedMilestoneData = repo.getDetailedMilestoneData(serialToUse)
                    lastLoadTime = System.currentTimeMillis()
                } catch (e: Exception) {
                    Log.e("LandingScreen", " Error refreshing data", e)
                }
            }
        }
    }

    // Solution Reminder Logic - Observe StateFlow for reactive updates
    val allPendingSolutionsMap by solutionReminderManager?.pendingSolutions?.collectAsState() 
        ?: remember { 
            mutableStateOf(emptyMap<String, List<PendingSolution>>()) 
        }

    // Calculate pending solutions for current serial/device
    val pendingSolutions = remember(allPendingSolutionsMap, currentSerialNumber, currentDeviceName) {
        val serialToCheck = currentSerialNumber ?: currentDeviceName
        if (serialToCheck != null) {
            allPendingSolutionsMap[serialToCheck] ?: emptyList()
        } else {
            emptyList<PendingSolution>()
        }
    }

    // Track which solutions have been dismissed (for confirmation dialogs)
    val dismissedSolutions = remember { mutableStateOf(setOf<String>()) }

    Column(
        modifier = Modifier
            .fillMaxSize()
            .verticalScroll(rememberScrollState())
            .padding(paddingValues)
            .padding(responsivePadding)
            .then(
                if (screenInfo.isTablet) {
                    Modifier.widthIn(max = getResponsiveMaxWidth())
                } else {
                    Modifier
                }
            ),
        horizontalAlignment = Alignment.CenterHorizontally,
        verticalArrangement = Arrangement.spacedBy(responsiveSpacing)
    ) {
        // Hamburger Menu Icon (Top Left)
        Row(
            modifier = Modifier.fillMaxWidth(),
            verticalAlignment = Alignment.CenterVertically
        ) {
            IconButton(onClick = onOpenDrawer) {
                Icon(
                    imageVector = Icons.Default.Menu,
                    contentDescription = "Open Menu",
                    tint = accentColor,
                    modifier = Modifier.size(24.dp)
                )
            }
        }

        // Permission Warning Banner (if not granted)
        if (!hasBluetoothPermissions) {
            Card(
                modifier = Modifier.fillMaxWidth(),
                colors = CardDefaults.cardColors(containerColor = warningColor.copy(alpha = 0.15f)),
                shape = RoundedCornerShape(12.dp),
                border = androidx.compose.foundation.BorderStroke(2.dp, warningColor)
            ) {
                Column(
                    modifier = Modifier.padding(16.dp),
                    verticalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    Row(
                        verticalAlignment = Alignment.CenterVertically,
                        horizontalArrangement = Arrangement.spacedBy(8.dp)
                    ) {
                        Text(
                            text = "",
                            style = MaterialTheme.typography.titleLarge
                        )
                        Text(
                            text = "Bluetooth Permissions Required",
                            style = MaterialTheme.typography.titleMedium,
                            color = warningColor,
                            fontWeight = FontWeight.Bold
                        )
                    }
                    Text(
                        text = "Location permission is needed for Bluetooth scanning. Tap below to grant permissions.",
                        style = MaterialTheme.typography.bodyMedium,
                        color = Color.White.copy(alpha = 0.9f)
                    )
            Button(
                        onClick = onRequestPermissions,
                modifier = Modifier.fillMaxWidth(),
                colors = ButtonDefaults.buttonColors(
                            containerColor = warningColor,
                    contentColor = Color.Black
                ),
                        shape = RoundedCornerShape(8.dp)
                    ) {
                        Text(
                            "Grant Permissions Now",
                            fontWeight = FontWeight.Bold
                        )
                    }
                }
            }
        }

        // Current Headphone Status Card - Always show, even if no data (separate card)
        CurrentHeadphoneStatusCard(
            currentStatusData = currentStatusData ?: CurrentStatusData(),
            onViewDetailsClick = { 
                // Always allow expanding, even if no serial number
                // If data is null, it will try to load it
                if (detailedMilestoneData == null && (currentSerialNumber != null || currentMacAddress.isNotBlank())) {
                    // Try to load data when clicking View Details
                    scope.launch {
                        syncAndLoadData()
                        isDetailedViewExpanded = true
                    }
                } else {
                    isDetailedViewExpanded = true
                }
            },
            modifier = Modifier.fillMaxWidth()
        )

        // Detailed Milestone Data Card (shown when expanded)
        if (isDetailedViewExpanded) {
            if (detailedMilestoneData != null) {
                DetailedMilestoneDataCard(
                    detailedData = detailedMilestoneData!!,
                    onHideDetailsClick = { isDetailedViewExpanded = false },
                    modifier = Modifier.fillMaxWidth()
                )
            } else if (isSyncing) {
                // Show loading state when expanding but data is syncing
                Card(
                    modifier = Modifier.fillMaxWidth(),
                    colors = CardDefaults.cardColors(containerColor = surfaceColor),
                    shape = RoundedCornerShape(12.dp)
                ) {
                    Box(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(16.dp),
                        contentAlignment = Alignment.Center
                    ) {
                        Text(
                            text = "Loading detailed data...",
                            color = Color.White.copy(alpha = 0.6f)
                        )
                    }
                }
            } else {
                // Show empty state when no data available
                Card(
                    modifier = Modifier.fillMaxWidth(),
                    colors = CardDefaults.cardColors(containerColor = surfaceColor),
                    shape = RoundedCornerShape(12.dp)
                ) {
                    Box(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(16.dp),
                        contentAlignment = Alignment.Center
                    ) {
                        Text(
                            text = "No detailed data available. Please scan QR code to load device data.",
                            color = Color.White.copy(alpha = 0.6f),
                            textAlign = TextAlign.Center
                        )
                    }
                }
            }
        }

        // Connected Device Section (Always Show)
        Card(
            modifier = Modifier.fillMaxWidth(),
            colors = CardDefaults.cardColors(containerColor = surfaceColor),
            shape = RoundedCornerShape(20.dp),
            elevation = CardDefaults.cardElevation(defaultElevation = 8.dp),
            border = androidx.compose.foundation.BorderStroke(1.dp, accentColor.copy(alpha = 0.2f))
        ) {
            Column(
                modifier = Modifier.padding(12.dp),
                horizontalAlignment = Alignment.CenterHorizontally
            ) {
                // Main Title (smaller font)
                Text(
                    text = if (currentSerialNumber != null) "Device Connected" else if (currentDeviceName != null) "Bluetooth Device Connected" else "No Device Connected",
                    style = MaterialTheme.typography.titleMedium,
                    fontSize = 16.sp,
                    color = if (currentSerialNumber != null) {
                        // Red if device name is not "Sonic Lamb", otherwise blue
                        if (currentDeviceName != "Sonic Lamb") dangerColor else infoColor
                    } else if (currentDeviceName != null) infoColor else warningColor,
                    fontWeight = FontWeight.Bold,
                    textAlign = TextAlign.Center
                )

                // Show device details if connected (either with serial number or just Bluetooth device)
                if (currentSerialNumber != null || currentDeviceName != null) {
                    Spacer(modifier = Modifier.height(12.dp))

                    var showForgetDialog by remember { mutableStateOf(false) }

                    // Device Details Card (Clickable)
                    val deviceCardColor = if (currentDeviceName == "Sonic Lamb") {
                        infoColor.copy(alpha = 0.15f)
                    } else {
                        dangerColor.copy(alpha = 0.15f)
                    }
                    val deviceBorderColor = if (currentDeviceName == "Sonic Lamb") {
                        infoColor.copy(alpha = 0.4f)
                    } else {
                        dangerColor.copy(alpha = 0.4f)
                    }

                    Card(
                        modifier = Modifier
                            .fillMaxWidth()
                            .clickable(
                                enabled = currentSerialNumber != null || currentMacAddress.isNotBlank(),
                                onClick = onOpenDeviceInfo
                            ),
                        colors = CardDefaults.cardColors(containerColor = deviceCardColor),
                        shape = RoundedCornerShape(12.dp),
                        border = androidx.compose.foundation.BorderStroke(1.dp, deviceBorderColor)
                    ) {
                        Column(
                            modifier = Modifier.padding(10.dp),
                            horizontalAlignment = Alignment.CenterHorizontally
                        ) {
                            Text(
                                text = " Device Information",
                                style = MaterialTheme.typography.titleSmall,
                                fontSize = (14 * 1.3).sp, // 30% increase
                                color = if (currentDeviceName == "Sonic Lamb") infoColor else dangerColor,
                                fontWeight = FontWeight.Bold
                            )

                            Spacer(modifier = Modifier.height(8.dp))

                            // Serial Number
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween,
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Text(
                                    text = "Serial Number:",
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (12 * 1.3).sp, // 30% increase
                                    color = Color.White.copy(alpha = 0.8f)
                                )
                                Text(
                                    text = when {
                                        isReferenceSerial -> "Unknown (Reference: ${currentSerialNumber?.take(15)}...)"
                                        currentSerialNumber != null -> currentSerialNumber
                                        else -> "Not found in database"
                                    },
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (12 * 1.3).sp, // 30% increase
                                    color = when {
                                        isReferenceSerial -> warningColor
                                        currentSerialNumber != null -> successColor
                                        else -> warningColor
                                    },
                                    fontWeight = FontWeight.Bold
                                )
                            }

                            Spacer(modifier = Modifier.height(6.dp))

                            // MAC ID
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween,
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Text(
                                    text = "MAC ID:",
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (12 * 1.3).sp, // 30% increase
                                    color = Color.White.copy(alpha = 0.8f)
                                )
                                Text(
                                    text = currentMacAddress.ifBlank { "Unknown" },
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (12 * 1.3).sp, // 30% increase
                                    color = infoColor,
                                    fontWeight = FontWeight.Bold
                                )
                            }

                            Spacer(modifier = Modifier.height(6.dp))

                            // Device Name (if available)
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween,
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Text(
                                    text = "Device Name:",
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (12 * 1.3).sp, // 30% increase
                                    color = Color.White.copy(alpha = 0.8f)
                                )
                                Text(
                                    text = currentDeviceName ?: "Bluetooth Headphone",
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (12 * 1.3).sp, // 30% increase
                                    color = Color.White.copy(alpha = 0.9f),
                                    fontWeight = FontWeight.Medium
                                )
                            }

                            Spacer(modifier = Modifier.height(8.dp))

                            // Data Upload Status
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween,
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Text(
                                    text = "Data Status:",
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (11 * 1.3).sp, // 30% increase
                                    color = Color.White.copy(alpha = 0.6f)
                                )
                                val statusColor = when (dataUploadStatus) {
                                    "Synced" -> successColor
                                    "Pending" -> warningColor
                                    "Failed" -> dangerColor
                                    else -> Color.White.copy(alpha = 0.6f)
                                }
                                Text(
                                    text = dataUploadStatus,
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (11 * 1.3).sp, // 30% increase
                                    color = statusColor,
                                    fontWeight = FontWeight.Medium
                                )
                            }

                            Spacer(modifier = Modifier.height(8.dp))

                            // Forget Device Button
                            Button(
                                onClick = { showForgetDialog = true },
                                modifier = Modifier.fillMaxWidth(),
                                colors = ButtonDefaults.buttonColors(
                                    containerColor = dangerColor.copy(alpha = 0.2f),
                                    contentColor = dangerColor
                                ),
                                shape = RoundedCornerShape(8.dp)
                            ) {
                                Text(
                                    text = " Forget Current Device",
                                    style = MaterialTheme.typography.bodySmall,
                                    fontSize = (12 * 1.3).sp, // 30% increase
                                    fontWeight = FontWeight.Medium
                                )
                            }
                        }
                    }

                    // Solution Reminder Cards - Show all pending solutions inline below Device Information
                    val activeSolutions = pendingSolutions.filter { solution ->
                        val key = "${solution.serialNumber}_${solution.solution}"
                        !dismissedSolutions.value.contains(key)
                    }

                    if (activeSolutions.isNotEmpty()) {
                        Spacer(modifier = Modifier.height(12.dp))

                        activeSolutions.forEachIndexed { index, solution ->
                            if (index > 0) {
                                Spacer(modifier = Modifier.height(8.dp))
                            }

                            SolutionReminderCard(
                                pendingSolution = solution,
                                onNo = {
                                    // Mark solution as dismissed
                                    val key = "${solution.serialNumber}_${solution.solution}"
                                    dismissedSolutions.value = dismissedSolutions.value + key
                                    android.util.Log.d("LandingScreen", " [SOLUTION_REMINDER] User dismissed solution: ${solution.solution}")
                                },
                                onYes = {
                                    android.util.Log.d("LandingScreen", " [SOLUTION_REMINDER] User confirmed solution: ${solution.solution}")
                                    // Mark solution as completed in SolutionReminderManager
                                    solutionReminderManager?.markSolutionCompleted(
                                        serialNumber = solution.serialNumber,
                                        solution = solution.solution,
                                        reminderType = solution.reminderType
                                    )
                                    when (solution.reminderType) {
                                        ReminderType.DRIVER_PAIRING -> {
                                            android.util.Log.d("LandingScreen", " Navigating to Driver Pairing")
                                            onNavigateToDriverPairing()
                                        }
                                        ReminderType.CHANGED_PARTS -> {
                                            android.util.Log.d("LandingScreen", " Navigating to Changed Parts")
                                            onNavigateToChangedParts()
                                        }
                                    }
                                },
                                onSkip = {
                                    // Mark solution as dismissed
                                    val key = "${solution.serialNumber}_${solution.solution}"
                                    dismissedSolutions.value = dismissedSolutions.value + key
                                    android.util.Log.d("LandingScreen", " [SOLUTION_REMINDER] User skipped solution: ${solution.solution}")
                                },
                                modifier = Modifier.fillMaxWidth()
                            )
                        }
                    }

                    // Forget Device Confirmation Dialog
                    if (showForgetDialog) {
                        AlertDialog(
                            onDismissRequest = { showForgetDialog = false },
                            title = {
                                Text(
                                    "Forget Device?",
                                    color = Color.White,
                                    fontWeight = FontWeight.Bold
                                )
                            },
                            text = {
                                Text(
                                    "Are you sure you want to forget the current device?",
                                    color = Color.White.copy(alpha = 0.8f)
                                )
                            },
                            confirmButton = {
                                Button(
                                    onClick = {
                                        showForgetDialog = false
                                        onForgetCurrentDevice()
                                    },
                                    colors = ButtonDefaults.buttonColors(
                                        containerColor = dangerColor,
                                        contentColor = Color.White
                                    )
                                ) {
                                    Text("Forget")
                                }
                            },
                            dismissButton = {
                                TextButton(
                                    onClick = { showForgetDialog = false }
                                ) {
                                    Text("Cancel", color = Color.White)
                                }
                            },
                            containerColor = surfaceColor
                        )
                    }
                } else {
                    // Show scan prompt when no device connected
                    Spacer(modifier = Modifier.height(16.dp))

                    Card(
                        modifier = Modifier.fillMaxWidth(),
                        colors = CardDefaults.cardColors(containerColor = warningColor.copy(alpha = 0.1f)),
                        shape = RoundedCornerShape(12.dp),
                        border = androidx.compose.foundation.BorderStroke(1.dp, warningColor.copy(alpha = 0.3f))
                    ) {
                        Column(
                            modifier = Modifier.padding(16.dp),
                            horizontalAlignment = Alignment.CenterHorizontally
                        ) {
                            Text(
                                text = " No Device Connected",
                                style = MaterialTheme.typography.titleMedium,
                                color = warningColor,
                                fontWeight = FontWeight.Bold
                            )

                            Spacer(modifier = Modifier.height(8.dp))

                            Text(
                                text = "Use the Scan button below to connect a device",
                                style = MaterialTheme.typography.bodyMedium,
                                color = Color.White.copy(alpha = 0.8f),
                                textAlign = TextAlign.Center
                            )
                        }
                    }
                }
            }
        }

        // Responsive Grid of Action Buttons
        if (screenInfo.isTablet) {
            // 2x2 Grid for tablets
            Column(
                modifier = Modifier.fillMaxWidth(),
                verticalArrangement = Arrangement.spacedBy(responsiveSpacing)
            ) {
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.spacedBy(responsiveSpacing)
                ) {
                    // Scan Button - Primary Action
                    Button(
                        onClick = onOpenScanner,
                        modifier = Modifier
                            .weight(1f)
                            .height(getResponsiveButtonSize()),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = accentColor,
                            contentColor = Color.White
                        ),
                        shape = RoundedCornerShape(16.dp),
                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 8.dp)
                    ) {
                            Text(
                                text = "Scan",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold,
                                fontSize = getResponsiveTextSize(16f).sp
                            )
                    }

                    // Frequency Generator Button
                    Button(
                        onClick = onOpenFreqGen,
                        modifier = Modifier
                            .weight(1f)
                            .height(getResponsiveButtonSize()),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = infoColor,
                            contentColor = Color.White
                        ),
                        shape = RoundedCornerShape(16.dp),
                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)
                    ) {
                            Text(
                                text = "Frequency",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold,
                                fontSize = getResponsiveTextSize(16f).sp
                            )
                    }
                }

                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.spacedBy(responsiveSpacing)
                ) {
                    // Test Song Button
                    Button(
                        onClick = onOpenTestSongs,
                        modifier = Modifier
                            .weight(1f)
                            .height(getResponsiveButtonSize()),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = successColor,
                            contentColor = Color.White
                        ),
                        shape = RoundedCornerShape(16.dp),
                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)
                    ) {
                            Text(
                                text = "Test Song",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold,
                                fontSize = getResponsiveTextSize(16f).sp
                            )
                    }

                    // Final QC Button (was More)
                    Button(
                        onClick = onOpenMore,
                        modifier = Modifier
                            .weight(1f)
                            .height(getResponsiveButtonSize()),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = warningColor,
                            contentColor = Color.White
                        ),
                        shape = RoundedCornerShape(16.dp),
                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)
                        ) {
                            Text(
                            text = "Final QC",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold,
                                fontSize = getResponsiveTextSize(16f).sp
                            )
                    }
                }
            }
        } else {
            // Single column for phones
            Column(
                modifier = Modifier.fillMaxWidth(),
                verticalArrangement = Arrangement.spacedBy(responsiveSpacing)
            ) {
                // Scan Button
                Button(
                    onClick = onOpenScanner,
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(getResponsiveButtonSize()),
                    colors = ButtonDefaults.buttonColors(
                        containerColor = accentColor,
                        contentColor = Color.White
                    ),
                    shape = RoundedCornerShape(16.dp),
                    elevation = ButtonDefaults.buttonElevation(defaultElevation = 8.dp)
                ) {
                        Text(
                            text = "Scan",
                            style = MaterialTheme.typography.titleMedium,
                            fontWeight = FontWeight.Bold,
                            fontSize = getResponsiveTextSize(16f).sp
                        )
                }

                // Other buttons in a row
                Row(
            modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.spacedBy(responsiveSpacing)
                ) {
                    // Frequency Generator Button
                    Button(
                        onClick = onOpenFreqGen,
                        modifier = Modifier
                            .weight(1f)
                            .height(getResponsiveButtonSize()),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = infoColor,
                            contentColor = Color.White
                        ),
                        shape = RoundedCornerShape(16.dp),
                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)
                    ) {
                            Text(
                                text = "Frequency",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold,
                                fontSize = getResponsiveTextSize(16f).sp
                            )
                    }

                    // Test Song Button
                    Button(
                        onClick = onOpenTestSongs,
                        modifier = Modifier
                            .weight(1f)
                            .height(getResponsiveButtonSize()),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = successColor,
                            contentColor = Color.White
                        ),
                        shape = RoundedCornerShape(16.dp),
                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)
                    ) {
                            Text(
                                text = "Test Song",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold,
                                fontSize = getResponsiveTextSize(16f).sp
                            )
                    }
                }

                // Final QC Button (was More, full width)
                Button(
                    onClick = onOpenMore,
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(getResponsiveButtonSize()),
                    colors = ButtonDefaults.buttonColors(
                        containerColor = warningColor,
                        contentColor = Color.White
                    ),
                    shape = RoundedCornerShape(16.dp),
                    elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)
                    ) {
                        Text(
                        text = "Final QC",
                            style = MaterialTheme.typography.titleMedium,
                            fontWeight = FontWeight.Bold,
                            fontSize = getResponsiveTextSize(16f).sp
                        )
                }
            }
        }

        // Undo Snackbar - Moved higher up for visibility
        undoManager?.let { manager ->
            Log.d("LandingScreen", "UndoManager exists: true")
            // Use a state variable that gets updated from the StateFlow
            var undoState by remember { 
                val initialState = manager.undoState.value
                Log.d("LandingScreen", "Initial undo state - isActive: ${initialState.isActive}, time: ${initialState.timeRemaining}")
                mutableStateOf(initialState) 
            }

            // Observe StateFlow changes in LaunchedEffect
            androidx.compose.runtime.LaunchedEffect(manager) {
                Log.d("LandingScreen", "LaunchedEffect started - observing undoState")
                manager.undoState.collect { state ->
                    Log.d("LandingScreen", " STATE UPDATE RECEIVED - isActive: ${state.isActive}, timeRemaining: ${state.timeRemaining}, fromScreen: ${state.fromScreen}")
                    undoState = state
                    Log.d("LandingScreen", " Local undoState updated to: isActive=${undoState.isActive}, timeRemaining=${undoState.timeRemaining}")
                }
            }

            if (undoState.isActive) {
                Log.d("LandingScreen", " Snackbar rendering - isActive: ${undoState.isActive}, time: ${undoState.timeRemaining}")
                Surface(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(16.dp),
                    shape = RoundedCornerShape(12.dp),
                    color = Color(0xFF2B2B2B),
                    tonalElevation = 8.dp
                ) {
                    Row(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(16.dp),
                        horizontalArrangement = Arrangement.SpaceBetween,
                        verticalAlignment = Alignment.CenterVertically
                    ) {
                        Column(modifier = Modifier.weight(1f)) {
                            Text(
                                text = "Test saved. Tap undo to reverse.",
                                style = MaterialTheme.typography.bodyMedium,
                                color = Color.White
                            )
                            Text(
                                text = "Auto-saving in ${undoState.timeRemaining}s",
                                style = MaterialTheme.typography.bodySmall,
                                color = Color.White.copy(alpha = 0.7f),
                                fontSize = 12.sp
                            )
                        }
                        Row(horizontalArrangement = Arrangement.spacedBy(8.dp), verticalAlignment = Alignment.CenterVertically) {
                            TextButton(
                                onClick = { 
                                    Log.d("LandingScreen", "Cancel (commit now) clicked.")
                                    // Commit immediately and stop timer
                                    scope.launch { 
                                        manager.executeCommitNow()
                                    }
                                }
                            ) {
                                Text(
                                    text = "CANCEL",
                                    color = Color(0xFF90CAF9),
                                    fontWeight = FontWeight.Bold
                                )
                            }
                            TextButton(
                                onClick = { 
                                    Log.d("LandingScreen", "Undo button clicked. From screen: ${undoState.fromScreen}")
                                    manager.executeUndo()
                                }
                            ) {
                                Text(
                                    text = "UNDO",
                                    color = Color(0xFFFFC107),
                                    fontWeight = FontWeight.Bold
                                )
                            }
                        }
                    }
                }
            } else {
                Log.d("LandingScreen", " Snackbar NOT rendering - undoState.isActive: ${undoState.isActive}")
            }
        } ?: Log.d("LandingScreen", "UndoManager is null - no snackbar")

        Spacer(modifier = Modifier.weight(1f))

        // Logout Button
        TextButton(
            onClick = onLogout,
            modifier = Modifier.fillMaxWidth()
        ) {
            Text(
                text = "Logout",
                color = Color(0xFFCF6679)
            )
        }

    }
}

package com.example.fqcapp.data.entities

import androidx.room.Entity
import androidx.room.PrimaryKey

@Entity(tableName = "devices")
data class Device(
    @PrimaryKey val serialNumber: String,
    // DEPRECATED: macId is deprecated - MAC ID should be derived from active PCB link
    // Use FQCRepository.getMacIdFromActivePcb(deviceId) instead
    // Keeping for backward compatibility during migration, but should not be used
    @Deprecated("Use getMacIdFromActivePcb() instead - MAC comes from active PCB link")
    val macId: String = "",  // Default to empty - MAC comes from active PCB link
    val color: String,
    val createdDate: String, // Format: "2025-10-14"
    val finalStatus: String, // "IN_PROGRESS", "PASSED", "FAILED"
    val totalIterations: Int = 0,
    val totalTimeHours: Double = 0.0,
    val finalOperator: String = "",
    val firstTestDate: String = "",
    val finalShipDate: String = "", // Date when final FQC passed
    val needsSync: Boolean = true, // Flag for Google Sheets sync
    val lastSyncTimestamp: Long = 0
)

package com.example.fqcapp.data

import android.content.Context
import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase
import androidx.room.migration.Migration
import androidx.sqlite.db.SupportSQLiteDatabase
import com.example.fqcapp.data.dao.*
import com.example.fqcapp.data.entities.*
import java.util.UUID
import java.text.SimpleDateFormat
import java.util.Date
import java.util.Locale

@Database(
    entities = [
        Operator::class,
        Device::class,
        Session::class,
        FrequencyTest::class,
        SongTest::class,
        FQCRecord::class,
        FQCSession::class, // Legacy - can be removed later
        MacIdCache::class,
        TestSession::class,
        DriverPairingCache::class,
        MilestoneEntry::class,
        DriverPairingRecord::class,
        DeviceWorkflow::class,
        ChangedPartsRecord::class,
        OperatorCredential::class,
        // New tables for database restructure
        PCB::class,
        DevicePcbLink::class,
        DeviceCase::class,
        TestConfiguration::class
    ],
    version = 18, // Incremented for new tables
    exportSchema = false
)
abstract class AppDatabase : RoomDatabase() {
    abstract fun operatorDao(): OperatorDao
    abstract fun deviceDao(): DeviceDao
    abstract fun sessionDao(): SessionDao
    abstract fun frequencyTestDao(): FrequencyTestDao
    abstract fun songTestDao(): SongTestDao
    abstract fun fqcRecordDao(): FQCRecordDao
    abstract fun fqcSessionDao(): FQCSessionDao // Legacy - can be removed later
    abstract fun macIdCacheDao(): MacIdCacheDao
    abstract fun testSessionDao(): TestSessionDao
    abstract fun driverPairingCacheDao(): DriverPairingCacheDao
    abstract fun milestoneDao(): MilestoneDao
    abstract fun driverPairingRecordDao(): DriverPairingRecordDao
    abstract fun deviceWorkflowDao(): DeviceWorkflowDao
    abstract fun changedPartsRecordDao(): ChangedPartsRecordDao
    abstract fun operatorCredentialDao(): OperatorCredentialDao
    // New DAOs for database restructure
    abstract fun pcbDao(): PcbDao
    abstract fun devicePcbLinkDao(): DevicePcbLinkDao
    abstract fun deviceCaseDao(): DeviceCaseDao
    abstract fun testConfigurationDao(): TestConfigurationDao

    companion object {
        @Volatile
        private var INSTANCE: AppDatabase? = null

        fun getDatabase(context: Context): AppDatabase {
            return INSTANCE ?: synchronized(this) {
                val instance = Room.databaseBuilder(
                    context.applicationContext,
                    AppDatabase::class.java,
                    "fqc_database"
                )
                    .addMigrations(MIGRATION_17_18)
                    .build()
                INSTANCE = instance
                instance
            }
        }

        /**
         * Migration from version 17 to 18:
         * 1. Creates new tables (PCB, DevicePcbLink, DeviceCase, TestConfiguration)
         * 2. Migrates Device.macId to PCB links
         * 3. Creates DeviceCase records for existing devices
         * 4. Creates TestConfiguration records for existing tests
         * 5. Updates tests with configurationId
         */
        private val MIGRATION_17_18 = object : Migration(17, 18) {
            override fun migrate(database: SupportSQLiteDatabase) {
                val dateFormat = SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss", Locale.US)
                val currentTimestamp = dateFormat.format(Date())

                // Step 1: Create new tables (Room will create them, but we ensure they exist)
                database.execSQL("""
                    CREATE TABLE IF NOT EXISTS pcbs (
                        pcbId TEXT NOT NULL PRIMARY KEY,
                        pcbSerialNumber TEXT NOT NULL,
                        macId TEXT NOT NULL,
                        createdAt TEXT NOT NULL,
                        notes TEXT NOT NULL,
                        needsSync INTEGER NOT NULL,
                        lastSyncTimestamp INTEGER NOT NULL
                    )
                """)

                database.execSQL("CREATE UNIQUE INDEX IF NOT EXISTS index_pcbs_pcbSerialNumber ON pcbs(pcbSerialNumber)")
                database.execSQL("CREATE UNIQUE INDEX IF NOT EXISTS index_pcbs_macId ON pcbs(macId)")

                database.execSQL("""
                    CREATE TABLE IF NOT EXISTS device_pcb_links (
                        linkId TEXT NOT NULL PRIMARY KEY,
                        deviceId TEXT NOT NULL,
                        pcbId TEXT NOT NULL,
                        isActive INTEGER NOT NULL,
                        reason TEXT NOT NULL,
                        linkedAt TEXT NOT NULL,
                        unlinkedAt TEXT,
                        operatorName TEXT NOT NULL,
                        notes TEXT NOT NULL,
                        needsSync INTEGER NOT NULL,
                        lastSyncTimestamp INTEGER NOT NULL,
                        FOREIGN KEY(deviceId) REFERENCES devices(serialNumber) ON DELETE CASCADE,
                        FOREIGN KEY(pcbId) REFERENCES pcbs(pcbId) ON DELETE CASCADE
                    )
                """)

                database.execSQL("CREATE INDEX IF NOT EXISTS index_device_pcb_links_deviceId ON device_pcb_links(deviceId)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_device_pcb_links_pcbId ON device_pcb_links(pcbId)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_device_pcb_links_deviceId_isActive ON device_pcb_links(deviceId, isActive)")

                database.execSQL("""
                    CREATE TABLE IF NOT EXISTS device_cases (
                        caseId TEXT NOT NULL PRIMARY KEY,
                        deviceId TEXT NOT NULL,
                        caseType TEXT NOT NULL,
                        repairType TEXT,
                        receivedFromCustomerRef TEXT,
                        returnToCustomerRef TEXT,
                        ownershipTransferred INTEGER NOT NULL,
                        status TEXT NOT NULL,
                        resolution TEXT,
                        openedAt TEXT NOT NULL,
                        closedAt TEXT,
                        operatorName TEXT NOT NULL,
                        notes TEXT NOT NULL,
                        needsSync INTEGER NOT NULL,
                        lastSyncTimestamp INTEGER NOT NULL,
                        FOREIGN KEY(deviceId) REFERENCES devices(serialNumber) ON DELETE CASCADE
                    )
                """)

                database.execSQL("CREATE INDEX IF NOT EXISTS index_device_cases_deviceId ON device_cases(deviceId)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_device_cases_caseType ON device_cases(caseType)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_device_cases_status ON device_cases(status)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_device_cases_deviceId_status ON device_cases(deviceId, status)")

                database.execSQL("""
                    CREATE TABLE IF NOT EXISTS test_configurations (
                        configurationId TEXT NOT NULL PRIMARY KEY,
                        caseId TEXT NOT NULL,
                        deviceId TEXT NOT NULL,
                        pcbId TEXT,
                        driverPairingRecordId TEXT NOT NULL,
                        changedPartsRecordId TEXT,
                        iterationNumber INTEGER NOT NULL,
                        testStage TEXT NOT NULL,
                        latestFrequencyResult TEXT,
                        latestSongResult TEXT,
                        latestFqcResult TEXT,
                        configurationStatus TEXT NOT NULL,
                        createdAt TEXT NOT NULL,
                        lastUpdatedAt TEXT NOT NULL,
                        endedAt TEXT,
                        operatorName TEXT NOT NULL,
                        reason TEXT,
                        needsSync INTEGER NOT NULL,
                        lastSyncTimestamp INTEGER NOT NULL,
                        FOREIGN KEY(caseId) REFERENCES device_cases(caseId) ON DELETE CASCADE,
                        FOREIGN KEY(deviceId) REFERENCES devices(serialNumber) ON DELETE CASCADE,
                        FOREIGN KEY(pcbId) REFERENCES pcbs(pcbId) ON DELETE SET NULL,
                        UNIQUE(caseId, testStage, iterationNumber)
                    )
                """)

                database.execSQL("CREATE INDEX IF NOT EXISTS index_test_configurations_caseId ON test_configurations(caseId)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_test_configurations_deviceId ON test_configurations(deviceId)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_test_configurations_pcbId ON test_configurations(pcbId)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_test_configurations_driverPairingRecordId ON test_configurations(driverPairingRecordId)")
                database.execSQL("CREATE INDEX IF NOT EXISTS index_test_configurations_changedPartsRecordId ON test_configurations(changedPartsRecordId)")
                // Create unique index for (caseId, testStage, iterationNumber) - Room expects this named index
                database.execSQL("CREATE UNIQUE INDEX IF NOT EXISTS index_test_configurations_caseId_testStage_iterationNumber ON test_configurations(caseId, testStage, iterationNumber)")

                // Add configurationId column to test tables if it doesn't exist
                try {
                    database.execSQL("ALTER TABLE frequency_tests ADD COLUMN configurationId TEXT")
                } catch (e: Exception) {
                    // Column might already exist, ignore
                }
                // Always create index (even if column already existed)
                try {
                    database.execSQL("CREATE INDEX IF NOT EXISTS index_frequency_tests_configurationId ON frequency_tests(configurationId)")
                } catch (e: Exception) {
                    // Index might already exist, ignore
                }

                try {
                    database.execSQL("ALTER TABLE song_tests ADD COLUMN configurationId TEXT")
                } catch (e: Exception) {
                    // Column might already exist, ignore
                }
                // Always create index (even if column already existed)
                try {
                    database.execSQL("CREATE INDEX IF NOT EXISTS index_song_tests_configurationId ON song_tests(configurationId)")
                } catch (e: Exception) {
                    // Index might already exist, ignore
                }

                try {
                    database.execSQL("ALTER TABLE fqc_records ADD COLUMN configurationId TEXT")
                } catch (e: Exception) {
                    // Column might already exist, ignore
                }
                // Always create index (even if column already existed)
                try {
                    database.execSQL("CREATE INDEX IF NOT EXISTS index_fqc_records_configurationId ON fqc_records(configurationId)")
                } catch (e: Exception) {
                    // Index might already exist, ignore
                }

                // Migrate changed_parts_records table: Remove old PCB fields, add pcbLinkId
                // SQLite doesn't support DROP COLUMN, so we recreate the table
                try {
                    // Check if old columns exist (by checking if table has isPcbChange column)
                    val checkCursor = database.query("PRAGMA table_info(changed_parts_records)")
                    var hasOldColumns = false
                    while (checkCursor.moveToNext()) {
                        val columnName = checkCursor.getString(1) // Column name is at index 1
                        if (columnName == "isPcbChange") {
                            hasOldColumns = true
                            break
                        }
                    }
                    checkCursor.close()

                    if (hasOldColumns) {
                        // Old structure exists - need to migrate
                        // Step 1: Create new table with correct structure
                        database.execSQL("""
                            CREATE TABLE changed_parts_records_new (
                                recordId INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
                                serialNumber TEXT NOT NULL,
                                macId TEXT NOT NULL,
                                operatorName TEXT NOT NULL,
                                entryTimestamp TEXT NOT NULL,
                                exitTimestamp TEXT NOT NULL,
                                totalTimeSpent TEXT NOT NULL,
                                pcbLinkId TEXT,
                                selectedParts TEXT NOT NULL,
                                iterationNumber INTEGER NOT NULL,
                                testStage TEXT NOT NULL,
                                timestamp TEXT NOT NULL,
                                needsSync INTEGER NOT NULL,
                                lastSyncTimestamp INTEGER NOT NULL
                            )
                        """)

                        // Step 2: Copy data (excluding old PCB columns)
                        database.execSQL("""
                            INSERT INTO changed_parts_records_new 
                            (recordId, serialNumber, macId, operatorName, entryTimestamp, exitTimestamp, 
                             totalTimeSpent, pcbLinkId, selectedParts, iterationNumber, testStage, 
                             timestamp, needsSync, lastSyncTimestamp)
                            SELECT 
                                recordId, serialNumber, macId, operatorName, entryTimestamp, exitTimestamp,
                                totalTimeSpent, NULL as pcbLinkId, selectedParts, iterationNumber, testStage,
                                timestamp, needsSync, lastSyncTimestamp
                            FROM changed_parts_records
                        """)

                        // Step 3: Drop old table
                        database.execSQL("DROP TABLE changed_parts_records")

                        // Step 4: Rename new table
                        database.execSQL("ALTER TABLE changed_parts_records_new RENAME TO changed_parts_records")

                        // Step 5: Recreate indexes
                        database.execSQL("CREATE INDEX IF NOT EXISTS index_changed_parts_records_serialNumber ON changed_parts_records(serialNumber)")
                        database.execSQL("CREATE INDEX IF NOT EXISTS index_changed_parts_records_macId ON changed_parts_records(macId)")
                    } else {
                        // New structure already exists or table doesn't exist - just ensure pcbLinkId column exists
                        try {
                            database.execSQL("ALTER TABLE changed_parts_records ADD COLUMN pcbLinkId TEXT")
                        } catch (e: Exception) {
                            // Column might already exist, ignore
                        }
                        // Ensure indexes exist
                        database.execSQL("CREATE INDEX IF NOT EXISTS index_changed_parts_records_serialNumber ON changed_parts_records(serialNumber)")
                        database.execSQL("CREATE INDEX IF NOT EXISTS index_changed_parts_records_macId ON changed_parts_records(macId)")
                    }
                } catch (e: Exception) {
                    // Table might not exist yet (will be created by Room), ignore
                }

                // Step 2: Migrate Device.macId to PCB links
                val deviceCursor = database.query("SELECT serialNumber, macId, createdDate FROM devices WHERE macId IS NOT NULL AND macId != ''")
                val macIdToPcbIdMap = mutableMapOf<String, String>()

                while (deviceCursor.moveToNext()) {
                    val serialNumber = deviceCursor.getString(0)
                    val macId = deviceCursor.getString(1)
                    val createdDate = if (deviceCursor.columnCount > 2 && !deviceCursor.isNull(2)) {
                        deviceCursor.getString(2)
                    } else {
                        currentTimestamp.substring(0, 10) // Use date part only
                    }

                    // Check if PCB already exists for this MAC ID
                    val existingPcbCursor = database.query(
                        "SELECT pcbId FROM pcbs WHERE macId = ?",
                        arrayOf(macId)
                    )

                    val pcbId = if (existingPcbCursor.moveToFirst()) {
                        existingPcbCursor.getString(0)
                    } else {
                        // Create new PCB record
                        val newPcbId = UUID.randomUUID().toString()
                        database.execSQL("""
                            INSERT INTO pcbs (pcbId, pcbSerialNumber, macId, createdAt, notes, needsSync, lastSyncTimestamp)
                            VALUES (?, ?, ?, ?, ?, ?, ?)
                        """, arrayOf(
                            newPcbId,
                            "MIGRATED_$serialNumber",
                            macId,
                            currentTimestamp,
                            "Migrated from Device.macId on $currentTimestamp",
                            1, // needsSync = true
                            0L  // lastSyncTimestamp = 0
                        ))
                        newPcbId
                    }
                    existingPcbCursor.close()

                    macIdToPcbIdMap[macId] = pcbId

                    // Check if DevicePcbLink already exists
                    val existingLinkCursor = database.query(
                        "SELECT linkId FROM device_pcb_links WHERE deviceId = ? AND pcbId = ?",
                        arrayOf(serialNumber, pcbId)
                    )

                    if (!existingLinkCursor.moveToFirst()) {
                        // Create DevicePcbLink
                        val linkId = UUID.randomUUID().toString()
                        database.execSQL("""
                            INSERT INTO device_pcb_links (
                                linkId, deviceId, pcbId, isActive, reason, linkedAt, 
                                unlinkedAt, operatorName, notes, needsSync, lastSyncTimestamp
                            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
                        """, arrayOf(
                            linkId,
                            serialNumber,
                            pcbId,
                            1, // isActive = true
                            "MIGRATED_FROM_DEVICE_MACID",
                            currentTimestamp,
                            null, // unlinkedAt
                            "SYSTEM",
                            "Migrated from Device.macId on $currentTimestamp",
                            1, // needsSync = true
                            0L  // lastSyncTimestamp = 0
                        ))
                    }
                    existingLinkCursor.close()
                }
                deviceCursor.close()

                // Step 3: Create DeviceCase records for existing devices
                val allDevicesCursor = database.query("SELECT serialNumber, createdDate, finalStatus FROM devices")
                val deviceToCaseIdMap = mutableMapOf<String, String>()

                while (allDevicesCursor.moveToNext()) {
                    val serialNumber = allDevicesCursor.getString(0)
                    val createdDate = if (!allDevicesCursor.isNull(1)) {
                        allDevicesCursor.getString(1)
                    } else {
                        currentTimestamp.substring(0, 10)
                    }
                    val finalStatus = if (!allDevicesCursor.isNull(2)) {
                        allDevicesCursor.getString(2)
                    } else {
                        "IN_PROGRESS"
                    }

                    // Check if device has any tests
                    val hasTestsCursor = database.query(
                        """
                            SELECT COUNT(*) FROM (
                                SELECT 1 FROM frequency_tests WHERE serialNumber = ?
                                UNION ALL
                                SELECT 1 FROM song_tests WHERE serialNumber = ?
                                UNION ALL
                                SELECT 1 FROM fqc_records WHERE serialNumber = ?
                            ) LIMIT 1
                        """,
                        arrayOf(serialNumber, serialNumber, serialNumber)
                    )

                    val hasTests = hasTestsCursor.moveToFirst() && hasTestsCursor.getInt(0) > 0
                    hasTestsCursor.close()

                    // Create DeviceCase
                    val caseId = UUID.randomUUID().toString()
                    val caseStatus = if (hasTests || finalStatus == "PASSED" || finalStatus == "FAILED") {
                        "CLOSED"
                    } else {
                        "OPEN"
                    }

                    database.execSQL("""
                        INSERT INTO device_cases (
                            caseId, deviceId, caseType, repairType, receivedFromCustomerRef,
                            returnToCustomerRef, ownershipTransferred, status, resolution,
                            openedAt, closedAt, operatorName, notes, needsSync, lastSyncTimestamp
                        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
                    """, arrayOf(
                        caseId,
                        serialNumber,
                        "NEW_BUILD",
                        null, // repairType
                        null, // receivedFromCustomerRef
                        null, // returnToCustomerRef
                        0, // ownershipTransferred = false
                        caseStatus,
                        if (caseStatus == "CLOSED") {
                            when (finalStatus) {
                                "PASSED" -> "REPAIRED"
                                "FAILED" -> "SCRAPPED"
                                else -> null
                            }
                        } else null,
                        currentTimestamp,
                        if (caseStatus == "CLOSED") currentTimestamp else null,
                        "SYSTEM",
                        "Migrated from existing device on $currentTimestamp",
                        1, // needsSync = true
                        0L  // lastSyncTimestamp = 0
                    ))

                    deviceToCaseIdMap[serialNumber] = caseId
                }
                allDevicesCursor.close()

                // Step 4: Create TestConfiguration records for existing tests
                // Group tests by (serialNumber, iterationNumber, testStage)
                // For each group, create a TestConfiguration

                // Process FrequencyTests
                val freqTestCursor = database.query(
                    "SELECT DISTINCT serialNumber, iterationNumber, testStage FROM frequency_tests ORDER BY serialNumber, iterationNumber, testStage"
                )

                while (freqTestCursor.moveToNext()) {
                    val serialNumber = freqTestCursor.getString(0)
                    val iterationNumber = freqTestCursor.getInt(1)
                    val testStage = freqTestCursor.getString(2)

                    val caseId = deviceToCaseIdMap[serialNumber] ?: continue

                    // Get active PCB link for this device
                    val pcbLinkCursor = database.query(
                        "SELECT pcbId FROM device_pcb_links WHERE deviceId = ? AND isActive = 1 LIMIT 1",
                        arrayOf(serialNumber)
                    )
                    val pcbId = if (pcbLinkCursor.moveToFirst()) {
                        pcbLinkCursor.getString(0)
                    } else {
                        null
                    }
                    pcbLinkCursor.close()

                    // Find matching DriverPairingRecord (same serialNumber, iterationNumber, testStage)
                    val driverPairingCursor = database.query(
                        "SELECT recordId FROM driver_pairing_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? LIMIT 1",
                        arrayOf(serialNumber, iterationNumber.toString(), testStage)
                    )
                    val driverPairingRecordId = if (driverPairingCursor.moveToFirst()) {
                        driverPairingCursor.getLong(0).toString()
                    } else {
                        // Create a placeholder - use first driver pairing record for this device
                        val fallbackDriverCursor = database.query(
                            "SELECT recordId FROM driver_pairing_records WHERE serialNumber = ? ORDER BY recordId LIMIT 1",
                            arrayOf(serialNumber)
                        )
                        val fallbackId = if (fallbackDriverCursor.moveToFirst()) {
                            fallbackDriverCursor.getLong(0).toString()
                        } else {
                            "0" // Placeholder
                        }
                        fallbackDriverCursor.close()
                        fallbackId
                    }
                    driverPairingCursor.close()

                    // Find matching ChangedPartsRecord (same serialNumber, iterationNumber, testStage)
                    val changedPartsCursor = database.query(
                        "SELECT recordId FROM changed_parts_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? LIMIT 1",
                        arrayOf(serialNumber, iterationNumber.toString(), testStage)
                    )
                    val changedPartsRecordId = if (changedPartsCursor.moveToFirst()) {
                        changedPartsCursor.getLong(0).toString()
                    } else {
                        null
                    }
                    changedPartsCursor.close()

                    // Check if TestConfiguration already exists
                    val existingConfigCursor = database.query(
                        "SELECT configurationId FROM test_configurations WHERE caseId = ? AND testStage = ? AND iterationNumber = ?",
                        arrayOf(caseId, testStage, iterationNumber.toString())
                    )

                    val configurationId = if (existingConfigCursor.moveToFirst()) {
                        existingConfigCursor.getString(0)
                    } else {
                        // Create TestConfiguration
                        val newConfigId = UUID.randomUUID().toString()

                        // Determine configuration status based on test results
                        val freqResultCursor = database.query(
                            "SELECT result FROM frequency_tests WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY testId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestFreqResult = if (freqResultCursor.moveToFirst()) {
                            freqResultCursor.getString(0)
                        } else {
                            null
                        }
                        freqResultCursor.close()

                        val songResultCursor = database.query(
                            "SELECT result FROM song_tests WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY testId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestSongResult = if (songResultCursor.moveToFirst()) {
                            songResultCursor.getString(0)
                        } else {
                            null
                        }
                        songResultCursor.close()

                        val fqcResultCursor = database.query(
                            "SELECT overallResult FROM fqc_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY fqcId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestFqcResult = if (fqcResultCursor.moveToFirst()) {
                            fqcResultCursor.getString(0)
                        } else {
                            null
                        }
                        fqcResultCursor.close()

                        val configStatus = when {
                            latestFqcResult == "PASS" -> "CONFIG_PASSED"
                            latestFreqResult == "FAIL" || latestSongResult == "FAIL" -> "CONFIG_FAILED"
                            latestFreqResult == "PASS" && latestSongResult in listOf("PASS", "CAN_BE_PASSED") -> "ELIGIBLE_FOR_FQC"
                            else -> "IN_PROGRESS"
                        }

                        database.execSQL("""
                            INSERT INTO test_configurations (
                                configurationId, caseId, deviceId, pcbId, driverPairingRecordId,
                                changedPartsRecordId, iterationNumber, testStage,
                                latestFrequencyResult, latestSongResult, latestFqcResult,
                                configurationStatus, createdAt, lastUpdatedAt, endedAt,
                                operatorName, reason, needsSync, lastSyncTimestamp
                            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
                        """, arrayOf(
                            newConfigId,
                            caseId,
                            serialNumber,
                            pcbId,
                            driverPairingRecordId,
                            changedPartsRecordId,
                            iterationNumber,
                            testStage,
                            latestFreqResult,
                            latestSongResult,
                            latestFqcResult,
                            configStatus,
                            currentTimestamp,
                            currentTimestamp,
                            null, // endedAt
                            "SYSTEM",
                            "Migrated from existing test on $currentTimestamp",
                            1, // needsSync = true
                            0L  // lastSyncTimestamp = 0
                        ))

                        newConfigId
                    }
                    existingConfigCursor.close()

                    // Update FrequencyTests with configurationId
                    database.execSQL("""
                        UPDATE frequency_tests 
                        SET configurationId = ? 
                        WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? AND configurationId IS NULL
                    """, arrayOf(configurationId, serialNumber, iterationNumber.toString(), testStage))
                }
                freqTestCursor.close()

                // Process SongTests (similar logic)
                val songTestCursor = database.query(
                    "SELECT DISTINCT serialNumber, iterationNumber, testStage FROM song_tests ORDER BY serialNumber, iterationNumber, testStage"
                )

                while (songTestCursor.moveToNext()) {
                    val serialNumber = songTestCursor.getString(0)
                    val iterationNumber = songTestCursor.getInt(1)
                    val testStage = songTestCursor.getString(2)

                    val caseId = deviceToCaseIdMap[serialNumber] ?: continue

                    // Get or create TestConfiguration (same logic as above)
                    val pcbLinkCursor = database.query(
                        "SELECT pcbId FROM device_pcb_links WHERE deviceId = ? AND isActive = 1 LIMIT 1",
                        arrayOf(serialNumber)
                    )
                    val pcbId = if (pcbLinkCursor.moveToFirst()) {
                        pcbLinkCursor.getString(0)
                    } else {
                        null
                    }
                    pcbLinkCursor.close()

                    val driverPairingCursor = database.query(
                        "SELECT recordId FROM driver_pairing_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? LIMIT 1",
                        arrayOf(serialNumber, iterationNumber.toString(), testStage)
                    )
                    val driverPairingRecordId = if (driverPairingCursor.moveToFirst()) {
                        driverPairingCursor.getLong(0).toString()
                    } else {
                        val fallbackDriverCursor = database.query(
                            "SELECT recordId FROM driver_pairing_records WHERE serialNumber = ? ORDER BY recordId LIMIT 1",
                            arrayOf(serialNumber)
                        )
                        val fallbackId = if (fallbackDriverCursor.moveToFirst()) {
                            fallbackDriverCursor.getLong(0).toString()
                        } else {
                            "0"
                        }
                        fallbackDriverCursor.close()
                        fallbackId
                    }
                    driverPairingCursor.close()

                    val changedPartsCursor = database.query(
                        "SELECT recordId FROM changed_parts_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? LIMIT 1",
                        arrayOf(serialNumber, iterationNumber.toString(), testStage)
                    )
                    val changedPartsRecordId = if (changedPartsCursor.moveToFirst()) {
                        changedPartsCursor.getLong(0).toString()
                    } else {
                        null
                    }
                    changedPartsCursor.close()

                    val existingConfigCursor = database.query(
                        "SELECT configurationId FROM test_configurations WHERE caseId = ? AND testStage = ? AND iterationNumber = ?",
                        arrayOf(caseId, testStage, iterationNumber.toString())
                    )

                    val configurationId = if (existingConfigCursor.moveToFirst()) {
                        existingConfigCursor.getString(0)
                    } else {
                        val newConfigId = UUID.randomUUID().toString()

                        val freqResultCursor = database.query(
                            "SELECT result FROM frequency_tests WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY testId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestFreqResult = if (freqResultCursor.moveToFirst()) {
                            freqResultCursor.getString(0)
                        } else {
                            null
                        }
                        freqResultCursor.close()

                        val songResultCursor = database.query(
                            "SELECT result FROM song_tests WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY testId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestSongResult = if (songResultCursor.moveToFirst()) {
                            songResultCursor.getString(0)
                        } else {
                            null
                        }
                        songResultCursor.close()

                        val fqcResultCursor = database.query(
                            "SELECT overallResult FROM fqc_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY fqcId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestFqcResult = if (fqcResultCursor.moveToFirst()) {
                            fqcResultCursor.getString(0)
                        } else {
                            null
                        }
                        fqcResultCursor.close()

                        val configStatus = when {
                            latestFqcResult == "PASS" -> "CONFIG_PASSED"
                            latestFreqResult == "FAIL" || latestSongResult == "FAIL" -> "CONFIG_FAILED"
                            latestFreqResult == "PASS" && latestSongResult in listOf("PASS", "CAN_BE_PASSED") -> "ELIGIBLE_FOR_FQC"
                            else -> "IN_PROGRESS"
                        }

                        database.execSQL("""
                            INSERT INTO test_configurations (
                                configurationId, caseId, deviceId, pcbId, driverPairingRecordId,
                                changedPartsRecordId, iterationNumber, testStage,
                                latestFrequencyResult, latestSongResult, latestFqcResult,
                                configurationStatus, createdAt, lastUpdatedAt, endedAt,
                                operatorName, reason, needsSync, lastSyncTimestamp
                            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
                        """, arrayOf(
                            newConfigId,
                            caseId,
                            serialNumber,
                            pcbId,
                            driverPairingRecordId,
                            changedPartsRecordId,
                            iterationNumber,
                            testStage,
                            latestFreqResult,
                            latestSongResult,
                            latestFqcResult,
                            configStatus,
                            currentTimestamp,
                            currentTimestamp,
                            null,
                            "SYSTEM",
                            "Migrated from existing test on $currentTimestamp",
                            1,
                            0L
                        ))

                        newConfigId
                    }
                    existingConfigCursor.close()

                    // Update SongTests with configurationId
                    database.execSQL("""
                        UPDATE song_tests 
                        SET configurationId = ? 
                        WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? AND configurationId IS NULL
                    """, arrayOf(configurationId, serialNumber, iterationNumber.toString(), testStage))
                }
                songTestCursor.close()

                // Process FQCRecords (similar logic)
                val fqcRecordCursor = database.query(
                    "SELECT DISTINCT serialNumber, iterationNumber, testStage FROM fqc_records ORDER BY serialNumber, iterationNumber, testStage"
                )

                while (fqcRecordCursor.moveToNext()) {
                    val serialNumber = fqcRecordCursor.getString(0)
                    val iterationNumber = fqcRecordCursor.getInt(1)
                    val testStage = fqcRecordCursor.getString(2)

                    val caseId = deviceToCaseIdMap[serialNumber] ?: continue

                    val pcbLinkCursor = database.query(
                        "SELECT pcbId FROM device_pcb_links WHERE deviceId = ? AND isActive = 1 LIMIT 1",
                        arrayOf(serialNumber)
                    )
                    val pcbId = if (pcbLinkCursor.moveToFirst()) {
                        pcbLinkCursor.getString(0)
                    } else {
                        null
                    }
                    pcbLinkCursor.close()

                    val driverPairingCursor = database.query(
                        "SELECT recordId FROM driver_pairing_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? LIMIT 1",
                        arrayOf(serialNumber, iterationNumber.toString(), testStage)
                    )
                    val driverPairingRecordId = if (driverPairingCursor.moveToFirst()) {
                        driverPairingCursor.getLong(0).toString()
                    } else {
                        val fallbackDriverCursor = database.query(
                            "SELECT recordId FROM driver_pairing_records WHERE serialNumber = ? ORDER BY recordId LIMIT 1",
                            arrayOf(serialNumber)
                        )
                        val fallbackId = if (fallbackDriverCursor.moveToFirst()) {
                            fallbackDriverCursor.getLong(0).toString()
                        } else {
                            "0"
                        }
                        fallbackDriverCursor.close()
                        fallbackId
                    }
                    driverPairingCursor.close()

                    val changedPartsCursor = database.query(
                        "SELECT recordId FROM changed_parts_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? LIMIT 1",
                        arrayOf(serialNumber, iterationNumber.toString(), testStage)
                    )
                    val changedPartsRecordId = if (changedPartsCursor.moveToFirst()) {
                        changedPartsCursor.getLong(0).toString()
                    } else {
                        null
                    }
                    changedPartsCursor.close()

                    val existingConfigCursor = database.query(
                        "SELECT configurationId FROM test_configurations WHERE caseId = ? AND testStage = ? AND iterationNumber = ?",
                        arrayOf(caseId, testStage, iterationNumber.toString())
                    )

                    val configurationId = if (existingConfigCursor.moveToFirst()) {
                        existingConfigCursor.getString(0)
                    } else {
                        val newConfigId = UUID.randomUUID().toString()

                        val freqResultCursor = database.query(
                            "SELECT result FROM frequency_tests WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY testId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestFreqResult = if (freqResultCursor.moveToFirst()) {
                            freqResultCursor.getString(0)
                        } else {
                            null
                        }
                        freqResultCursor.close()

                        val songResultCursor = database.query(
                            "SELECT result FROM song_tests WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY testId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestSongResult = if (songResultCursor.moveToFirst()) {
                            songResultCursor.getString(0)
                        } else {
                            null
                        }
                        songResultCursor.close()

                        val fqcResultCursor = database.query(
                            "SELECT overallResult FROM fqc_records WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? ORDER BY fqcId DESC LIMIT 1",
                            arrayOf(serialNumber, iterationNumber.toString(), testStage)
                        )
                        val latestFqcResult = if (fqcResultCursor.moveToFirst()) {
                            fqcResultCursor.getString(0)
                        } else {
                            null
                        }
                        fqcResultCursor.close()

                        val configStatus = when {
                            latestFqcResult == "PASS" -> "CONFIG_PASSED"
                            latestFreqResult == "FAIL" || latestSongResult == "FAIL" -> "CONFIG_FAILED"
                            latestFreqResult == "PASS" && latestSongResult in listOf("PASS", "CAN_BE_PASSED") -> "ELIGIBLE_FOR_FQC"
                            else -> "IN_PROGRESS"
                        }

                        database.execSQL("""
                            INSERT INTO test_configurations (
                                configurationId, caseId, deviceId, pcbId, driverPairingRecordId,
                                changedPartsRecordId, iterationNumber, testStage,
                                latestFrequencyResult, latestSongResult, latestFqcResult,
                                configurationStatus, createdAt, lastUpdatedAt, endedAt,
                                operatorName, reason, needsSync, lastSyncTimestamp
                            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
                        """, arrayOf(
                            newConfigId,
                            caseId,
                            serialNumber,
                            pcbId,
                            driverPairingRecordId,
                            changedPartsRecordId,
                            iterationNumber,
                            testStage,
                            latestFreqResult,
                            latestSongResult,
                            latestFqcResult,
                            configStatus,
                            currentTimestamp,
                            currentTimestamp,
                            null,
                            "SYSTEM",
                            "Migrated from existing test on $currentTimestamp",
                            1,
                            0L
                        ))

                        newConfigId
                    }
                    existingConfigCursor.close()

                    // Update FQCRecords with configurationId
                    database.execSQL("""
                        UPDATE fqc_records 
                        SET configurationId = ? 
                        WHERE serialNumber = ? AND iterationNumber = ? AND testStage = ? AND configurationId IS NULL
                    """, arrayOf(configurationId, serialNumber, iterationNumber.toString(), testStage))
                }
                fqcRecordCursor.close()
            }
        }
    }
}

