    // Frequency Test operations
    /**
     * Sync data from Supabase before calculating iteration number to prevent duplicates
     * This ensures we have the latest data from all devices before creating new tests
     */
    private suspend fun syncBeforeIteration(serialNumber: String) {
        try {
            Log.d(TAG, " Syncing from Supabase before calculating iteration for: $serialNumber")
            val syncService = com.example.fqcapp.services.DeviceDataSyncService(database)
            val syncSuccess = syncService.syncDeviceDataFromSupabase(serialNumber)
            if (syncSuccess) {
                Log.d(TAG, " Sync completed successfully before iteration calculation")
            } else {
                Log.w(TAG, " Sync completed with errors, but continuing with iteration calculation")
            }
        } catch (e: Exception) {
            Log.e(TAG, " Error syncing before iteration calculation, continuing anyway", e)
            // Continue even if sync fails - better to have a slightly wrong iteration than to block
        }
    }

    /**
     * Check if a test with similar entryTimestamp and testStage already exists (duplicate detection)
     * Returns true if duplicate found, false otherwise
     */
    private suspend fun isDuplicateFrequencyTest(
        serialNumber: String,
        entryTimestamp: String,
        testStage: String,
        result: String,
        toleranceSeconds: Int = 10
    ): Boolean {
        try {
            // Get all tests for this serial and stage
            val existingTests = database.frequencyTestDao().getFrequencyTests(serialNumber)
                .filter { it.testStage == testStage && it.result == result }

            // Parse entryTimestamp and check if any test is within tolerance
            val entryTime = parseTimestamp(entryTimestamp)
            if (entryTime == null) return false

            return existingTests.any { existing ->
                val existingTime = parseTimestamp(existing.entryTimestamp)
                if (existingTime == null) return@any false
                val timeDiff = kotlin.math.abs(entryTime - existingTime) / 1000 // Convert to seconds
                timeDiff <= toleranceSeconds
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error checking for duplicate frequency test", e)
            return false // On error, allow insert
        }
    }

    private suspend fun isDuplicateSongTest(
        serialNumber: String,
        entryTimestamp: String,
        testStage: String,
        result: String,
        toleranceSeconds: Int = 10
    ): Boolean {
        try {
            val existingTests = database.songTestDao().getSongTests(serialNumber)
                .filter { it.testStage == testStage && it.result == result && it.isOverallResult }

            val entryTime = parseTimestamp(entryTimestamp)
            if (entryTime == null) return false

            return existingTests.any { existing ->
                val existingTime = parseTimestamp(existing.entryTimestamp)
                if (existingTime == null) return@any false
                val timeDiff = kotlin.math.abs(entryTime - existingTime) / 1000
                timeDiff <= toleranceSeconds
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error checking for duplicate song test", e)
            return false
        }
    }

    private suspend fun isDuplicateFQCRecord(
        serialNumber: String,
        entryTimestamp: String,
        testStage: String,
        overallResult: String,
        toleranceSeconds: Int = 10
    ): Boolean {
        try {
            val existingRecords = database.fqcRecordDao().getFQCRecords(serialNumber)
                .filter { it.testStage == testStage && it.overallResult == overallResult }

            val entryTime = parseTimestamp(entryTimestamp)
            if (entryTime == null) return false

            return existingRecords.any { existing ->
                val existingTime = parseTimestamp(existing.entryTimestamp)
                if (existingTime == null) return@any false
                val timeDiff = kotlin.math.abs(entryTime - existingTime) / 1000
                timeDiff <= toleranceSeconds
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error checking for duplicate FQC record", e)
            return false
        }
    }

    /**
     * Parse timestamp string to milliseconds
     * Supports formats: "yyyy-MM-dd HH:mm:ss" and ISO format
     */
    private fun parseTimestamp(timestamp: String): Long? {
        if (timestamp.isBlank()) return null
        return try {
            // Try ISO format first
            if (timestamp.contains("T")) {
                java.time.Instant.parse(timestamp).toEpochMilli()
            } else {
                // Try "yyyy-MM-dd HH:mm:ss" format
                val format = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.US)
                format.parse(timestamp)?.time
            }
        } catch (e: Exception) {
            Log.w(TAG, "Failed to parse timestamp: $timestamp", e)
            null
        }
    }

    suspend fun getNextIterationNumber(serialNumber: String): Int {
        // IMPORTANT: Sync from Supabase first to get latest data from all devices
        syncBeforeIteration(serialNumber)

        // Get max iteration number excluding TEST_NOT_DONE results
        val maxIteration = database.frequencyTestDao().getMaxIterationNumber(serialNumber)
        // Also check song tests to get the overall max
        val maxSongIteration = database.songTestDao().getMaxIterationNumber(serialNumber)
        val overallMax = maxOf(maxIteration, maxSongIteration)
        return overallMax + 1
    }

    /**
     * Helper function to get operatorId from operatorName
     */
    private suspend fun getOperatorId(operatorName: String): String {
        return try {
            val operator = database.operatorCredentialDao().getOperatorByName(operatorName)
            operator?.operatorId ?: ""
        } catch (e: Exception) {
            Log.w(TAG, " Could not find operatorId for operator: $operatorName, using empty string")
            ""
        }
    }

    suspend fun insertFrequencyTest(test: FrequencyTest, operatorId: String? = null): Long {
        // Skip saving "TEST_NOT_DONE" to database - this is not a test result, just a reason why test couldn't be done
        // TEST_NOT_DONE should not create an iteration
        if (test.result.uppercase() == "TEST_NOT_DONE") {
            Log.d(TAG, " Skipping database save for TEST_NOT_DONE - not a test result, no iteration created")
            return 0L // Return 0 to indicate no test was saved
        }

        // IMPORTANT: Sync from Supabase first to prevent duplicates from multiple devices
        syncBeforeIteration(test.serialNumber)

        // Check for duplicate test (same serial, entryTimestamp within tolerance, same stage and result)
        if (isDuplicateFrequencyTest(test.serialNumber, test.entryTimestamp, test.testStage, test.result)) {
            Log.w(TAG, " Duplicate frequency test detected - skipping insert")
            Log.w(TAG, "   Serial: ${test.serialNumber}, Stage: ${test.testStage}, EntryTime: ${test.entryTimestamp}, Result: ${test.result}")
            return 0L // Return 0 to indicate test was not saved (duplicate)
        }

        // DUAL-WRITE: Get or create TestConfiguration before saving test
        var configurationId: String? = null
        var configIterationNumber: Int? = null
        var configTestStage: String? = null
        try {
            val config = getOrCreateConfigurationForTest(
                deviceId = test.serialNumber,
                testStage = test.testStage,
                operatorName = test.operatorName
            )
            configurationId = config.configurationId
            configIterationNumber = config.iterationNumber
            configTestStage = config.testStage
            Log.d(TAG, " [DUAL-WRITE] Using configuration: $configurationId")
            Log.d(TAG, "   Configuration iteration: ${config.iterationNumber}, stage: ${config.testStage}")
            Log.d(TAG, "   Test will use EXACT values from configuration (dual-write)")
        } catch (e: Exception) {
            Log.w(TAG, " [DUAL-WRITE] Failed to get/create configuration, continuing without it: ${e.message}")
            // Continue without configurationId - backward compatibility
        }

        // Update test with configurationId AND EXACT iterationNumber/testStage from configuration
        // CRITICAL: iterationNumber and testStage must match configuration EXACTLY (dual-write)
        val testWithConfig = if (configurationId != null && configIterationNumber != null && configTestStage != null) {
            test.copy(
                configurationId = configurationId,
                iterationNumber = configIterationNumber,  // EXACT match from configuration
                testStage = configTestStage  // EXACT match from configuration
            )
        } else {
            test
        }

        // First, save to local Room database (always succeeds)
        val testId = database.frequencyTestDao().insert(testWithConfig)
        Log.d(TAG, " Saved frequency test to Room DB with ID: $testId")

        // Then, try to save to Supabase (non-blocking, with error handling)
        val testWithId = testWithConfig.copy(testId = testId)
        supabaseScope.launch {
            try {
                Log.d(TAG, "")
                Log.d(TAG, " [REPOSITORY] SYNCING FREQUENCY TEST TO SUPABASE")
                Log.d(TAG, "   Test ID (Local): $testId")
                Log.d(TAG, "   Serial Number: ${testWithId.serialNumber}")
                Log.d(TAG, "   Iteration: ${testWithId.iterationNumber}")
                Log.d(TAG, "   Result: ${testWithId.result}")
                Log.d(TAG, "   Operator: ${testWithId.operatorName}")
                Log.d(TAG, "")

                // Get operatorId if not provided
                val finalOperatorId = operatorId ?: getOperatorId(testWithId.operatorName)
                if (finalOperatorId.isBlank()) {
                    Log.w(TAG, " [WARNING] operatorId is empty for operator: ${testWithId.operatorName}")
                }

                val supabaseId = supabaseService.saveFrequencyTest(testWithId, finalOperatorId)
                if (supabaseId != null) {
                    Log.d(TAG, "")
                    Log.d(TAG, " [REPOSITORY] SYNC SUCCESSFUL")
                    Log.d(TAG, "   Local Test ID: $testId")
                    Log.d(TAG, "   Supabase Test ID: $supabaseId")
                    Log.d(TAG, "")
                } else {
                    Log.w(TAG, "")
                    Log.w(TAG, " [REPOSITORY] SYNC FAILED")
                    Log.w(TAG, "   Local Test ID: $testId")
                    Log.w(TAG, "   Will retry later")
                    Log.w(TAG, "")
                }
            } catch (e: Exception) {
                Log.e(TAG, "")
                Log.e(TAG, " [REPOSITORY] ERROR SYNCING TO SUPABASE")
                Log.e(TAG, "   Local Test ID: $testId")
                Log.e(TAG, "   Error: ${e.message}")
                Log.e(TAG, "   Stack trace: ${e.stackTraceToString()}")
                Log.e(TAG, "")
            }
        }

        // Update configuration cache after test creation
        if (configurationId != null) {
            try {
                updateConfigurationCache(configurationId)
            } catch (e: Exception) {
                Log.w(TAG, " Failed to update configuration cache: ${e.message}")
            }
        }

        return testId
    }

    suspend fun getAllFrequencyTests(): List<FrequencyTest> =
        database.frequencyTestDao().getAllFrequencyTests()


    // Song Test operations
    /**
     * Calculate priority score for a song test.
     * Higher score = higher priority for Supabase sync.
     * Priority: FAIL (30) > CAN_BE_PASSED (20) > PASS (10)
     * Bonus: +5 if has issues
     */
    private fun calculateSongPriority(test: SongTest): Int {
        var priority = when (test.result.uppercase()) {
            "FAIL" -> 30
            "CAN_BE_PASSED" -> 20
            "PASS" -> 10
            else -> 5
        }

        // Bonus for songs with issues
        if (test.issues.isNotBlank() && test.issues.trim() != "[]" && test.issues.trim() != "") {
            priority += 5
        }

        return priority
    }

    suspend fun insertSongTest(test: SongTest, operatorId: String? = null): Long {
        // Skip saving "TEST_NOT_DONE" to database - this is not a test result, just a reason why test couldn't be done
        // TEST_NOT_DONE should not create an iteration
        if (test.result.uppercase() == "TEST_NOT_DONE") {
            Log.d(TAG, " Skipping database save for TEST_NOT_DONE - not a test result, no iteration created")
            return 0L // Return 0 to indicate no test was saved
        }

        // IMPORTANT: Sync from Supabase first to prevent duplicates from multiple devices
        syncBeforeIteration(test.serialNumber)

        // Check for duplicate test (only for overall results)
        if (test.isOverallResult && isDuplicateSongTest(test.serialNumber, test.entryTimestamp, test.testStage, test.result)) {
            Log.w(TAG, " Duplicate song test detected - skipping insert")
            Log.w(TAG, "   Serial: ${test.serialNumber}, Stage: ${test.testStage}, EntryTime: ${test.entryTimestamp}, Result: ${test.result}")
            return 0L // Return 0 to indicate test was not saved (duplicate)
        }

        // DUAL-WRITE: Get or create TestConfiguration before saving test
        var configurationId: String? = null
        var configIterationNumber: Int? = null
        var configTestStage: String? = null
        try {
            val config = getOrCreateConfigurationForTest(
                deviceId = test.serialNumber,
                testStage = test.testStage,
                operatorName = test.operatorName
            )
            configurationId = config.configurationId
            configIterationNumber = config.iterationNumber
            configTestStage = config.testStage
            Log.d(TAG, " [DUAL-WRITE] Using configuration: $configurationId")
            Log.d(TAG, "   Configuration iteration: ${config.iterationNumber}, stage: ${config.testStage}")
            Log.d(TAG, "   Test will use EXACT values from configuration (dual-write)")
        } catch (e: Exception) {
            Log.w(TAG, " [DUAL-WRITE] Failed to get/create configuration, continuing without it: ${e.message}")
            // Continue without configurationId - backward compatibility
        }

        // Update test with configurationId AND EXACT iterationNumber/testStage from configuration
        // CRITICAL: iterationNumber and testStage must match configuration EXACTLY (dual-write)
        val testWithConfig = if (configurationId != null && configIterationNumber != null && configTestStage != null) {
            test.copy(
                configurationId = configurationId,
                iterationNumber = configIterationNumber,  // EXACT match from configuration
                testStage = configTestStage  // EXACT match from configuration
            )
        } else {
            test
        }

        // First, save to local Room database (always succeeds)
        val testId = database.songTestDao().insert(testWithConfig)
        Log.d(TAG, " Saved song test to Room DB with ID: $testId")

        // If this is an individual song (not overall), check and delete old songs beyond 3
        if (!testWithConfig.isOverallResult && testWithConfig.serialNumber.isNotBlank()) {
            try {
                database.songTestDao().deleteOldIndividualSongs(testWithConfig.serialNumber, testWithConfig.iterationNumber)
            } catch (e: Exception) {
                Log.e(TAG, "Error deleting old individual songs", e)
            }
        }

        // For individual songs: Only sync top 3 songs by TIME SPENT to Supabase (matching table display)
        if (!test.isOverallResult && test.serialNumber.isNotBlank()) {
            try {
                // Get all individual songs for this iteration
                val allSongs = database.songTestDao()
                    .getIndividualSongsForIteration(testWithConfig.serialNumber, testWithConfig.iterationNumber)

                // Parse time spent (HH:MM:SS) to milliseconds for sorting
                fun parseTimeToMs(timeStr: String): Long {
                    return try {
                        val parts = timeStr.split(":")
                        if (parts.size == 3) {
                            val hours = parts[0].toLongOrNull() ?: 0L
                            val minutes = parts[1].toLongOrNull() ?: 0L
                            val seconds = parts[2].toLongOrNull() ?: 0L
                            (hours * 3600 + minutes * 60 + seconds) * 1000
                        } else {
                            0L
                        }
                    } catch (e: Exception) {
                        Log.w(TAG, " Failed to parse time: $timeStr", e)
                        0L
                    }
                }

                // Calculate time spent (in ms) for each song
                val songsWithTime = allSongs.map { song ->
                    Pair(song, parseTimeToMs(song.totalTimeSpent))
                }

                // Sort by time spent (descending) and take top 3
                val top3Songs = songsWithTime
                    .sortedByDescending { it.second }
                    .take(3)
                    .map { it.first }

                Log.d(TAG, " [TIME] Total songs: ${allSongs.size}, Top 3 songs by time spent selected")
                top3Songs.forEachIndexed { index, song ->
                    val timeMs = parseTimeToMs(song.totalTimeSpent)
                    Log.d(TAG, "   ${index + 1}. ${song.songName} (Time: ${song.totalTimeSpent} = ${timeMs}ms, Result: ${song.result})")
                }

                // Update needsSync flags: only top 3 should sync
                allSongs.forEach { song ->
                    val shouldSync = top3Songs.any { it.testId == song.testId }
                    if (song.needsSync != shouldSync) {
                        // Update needsSync flag in database
                        database.songTestDao().updateNeedsSync(song.testId, shouldSync)
                        Log.d(TAG, "   ${if (shouldSync) "" else ""} ${song.songName}: needsSync = $shouldSync")
                    }
                }

                // Only sync the current song if it's in top 3
                val currentSongShouldSync = top3Songs.any { it.testId == testId }
                if (!currentSongShouldSync) {
                    Log.d(TAG, " [TIME] Song '${testWithConfig.songName}' not in top 3 by time spent - skipping Supabase sync")
                    return testId
                }
            } catch (e: Exception) {
                Log.e(TAG, " Error calculating top 3 songs by time", e)
                // Continue with sync if calculation fails
            }
        }

        // Then, try to save to Supabase (non-blocking, with error handling)
        val testWithId = testWithConfig.copy(testId = testId)
        supabaseScope.launch {
            try {
                Log.d(TAG, "")
                Log.d(TAG, " [REPOSITORY] SYNCING SONG TEST TO SUPABASE")
                Log.d(TAG, "   Test ID (Local): $testId")
                Log.d(TAG, "   Serial Number: ${testWithId.serialNumber}")
                Log.d(TAG, "   Song Name: ${testWithId.songName}")
                Log.d(TAG, "   Iteration: ${testWithId.iterationNumber}")
                Log.d(TAG, "   Result: ${testWithId.result}")
                Log.d(TAG, "   Is Overall: ${testWithId.isOverallResult}")
                Log.d(TAG, "   Operator: ${testWithId.operatorName}")
                Log.d(TAG, "   Priority: ${calculateSongPriority(testWithId)}")
                Log.d(TAG, "")

                // Get operatorId if not provided
                val finalOperatorId = operatorId ?: getOperatorId(testWithId.operatorName)
                if (finalOperatorId.isBlank()) {
                    Log.w(TAG, " [WARNING] operatorId is empty for operator: ${testWithId.operatorName}")
                }

                val supabaseId = supabaseSongTestService.saveSongTest(testWithId, finalOperatorId)
                if (supabaseId != null) {
                    Log.d(TAG, "")
                    Log.d(TAG, " [REPOSITORY] SYNC SUCCESSFUL")
                    Log.d(TAG, "   Local Test ID: $testId")
                    Log.d(TAG, "   Supabase Test ID: $supabaseId")
                    Log.d(TAG, "")
                } else {
                    Log.w(TAG, " Failed to sync song test to Supabase (will retry later)")
                }
            } catch (e: Exception) {
                Log.e(TAG, " Error syncing song test to Supabase", e)
            }
        }

        // Update configuration cache after test creation
        if (configurationId != null) {
            try {
                updateConfigurationCache(configurationId)
            } catch (e: Exception) {
                Log.w(TAG, " Failed to update configuration cache: ${e.message}")
            }
        }

        return testId
    }

