    // FQC Record operations
    suspend fun insertFQCRecord(fqcRecord: FQCRecord, macId: String = "", operatorId: String? = null): Long {
        // IMPORTANT: Sync from Supabase first to prevent duplicates from multiple devices
        syncBeforeIteration(fqcRecord.serialNumber)

        // Check for duplicate FQC record
        if (isDuplicateFQCRecord(fqcRecord.serialNumber, fqcRecord.entryTimestamp, fqcRecord.testStage, fqcRecord.overallResult)) {
            Log.w(TAG, " Duplicate FQC record detected - skipping insert")
            Log.w(TAG, "   Serial: ${fqcRecord.serialNumber}, Stage: ${fqcRecord.testStage}, EntryTime: ${fqcRecord.entryTimestamp}, Result: ${fqcRecord.overallResult}")
            return 0L // Return 0 to indicate record was not saved (duplicate)
        }

        // DUAL-WRITE: Get or create TestConfiguration before saving test
        var configurationId: String? = null
        var configIterationNumber: Int? = null
        var configTestStage: String? = null
        try {
            val config = getOrCreateConfigurationForTest(
                deviceId = fqcRecord.serialNumber,
                testStage = fqcRecord.testStage,
                operatorName = fqcRecord.operatorName
            )
            configurationId = config.configurationId
            configIterationNumber = config.iterationNumber
            configTestStage = config.testStage
            Log.d(TAG, " [DUAL-WRITE] Using configuration: $configurationId")
            Log.d(TAG, "   Configuration iteration: ${config.iterationNumber}, stage: ${config.testStage}")
            Log.d(TAG, "   FQC record will use EXACT values from configuration (dual-write)")
        } catch (e: Exception) {
            Log.w(TAG, " [DUAL-WRITE] Failed to get/create configuration, continuing without it: ${e.message}")
            // Continue without configurationId - backward compatibility
        }

        // Update FQC record with configurationId AND EXACT iterationNumber/testStage from configuration
        // CRITICAL: iterationNumber and testStage must match configuration EXACTLY (dual-write)
        val fqcRecordWithConfig = if (configurationId != null && configIterationNumber != null && configTestStage != null) {
            fqcRecord.copy(
                configurationId = configurationId,
                iterationNumber = configIterationNumber,  // EXACT match from configuration
                testStage = configTestStage  // EXACT match from configuration
            )
        } else {
            fqcRecord
        }

        // First, save to local Room database (always succeeds)
        val fqcId = database.fqcRecordDao().insertFQCRecord(fqcRecordWithConfig)
        Log.d(TAG, " Saved FQC record to Room DB with ID: $fqcId")

        // Then, try to save to Supabase (non-blocking, with error handling)
        val fqcWithId = fqcRecordWithConfig.copy(fqcId = fqcId)
        supabaseScope.launch {
            try {
                Log.d(TAG, "")
                Log.d(TAG, " [REPOSITORY] SYNCING FQC RECORD TO SUPABASE")
                Log.d(TAG, "   FQC ID (Local): $fqcId")
                Log.d(TAG, "   Serial Number: ${fqcWithId.serialNumber}")
                Log.d(TAG, "   Iteration: ${fqcWithId.iterationNumber}")
                Log.d(TAG, "   Result: ${fqcWithId.overallResult}")
                Log.d(TAG, "   FQC Type: ${fqcWithId.fqcType}")
                Log.d(TAG, "   Operator: ${fqcWithId.operatorName}")
                Log.d(TAG, "")

                // Get operatorId if not provided
                val finalOperatorId = operatorId ?: getOperatorId(fqcWithId.operatorName)
                if (finalOperatorId.isBlank()) {
                    Log.w(TAG, " [WARNING] operatorId is empty for operator: ${fqcWithId.operatorName}")
                }

                val supabaseService = com.example.fqcapp.supabase.SupabaseFQCService()
                val supabaseId = supabaseService.saveFQCRecord(fqcWithId, finalOperatorId, macId)
                if (supabaseId != null) {
                    Log.d(TAG, "")
                    Log.d(TAG, " [REPOSITORY] SYNC SUCCESSFUL")
                    Log.d(TAG, "   Local FQC ID: $fqcId")
                    Log.d(TAG, "   Supabase FQC ID: $supabaseId")
                    // Mark as synced in Room DB (if method exists)
                    try {
                        database.fqcRecordDao().markAsSynced(fqcId, System.currentTimeMillis())
                        Log.d(TAG, "    Marked as synced in Room DB")
                    } catch (e: Exception) {
                        // Method might not exist, that's okay
                        Log.d(TAG, "    Note: markAsSynced method not available for FQC records")
                    }
                    Log.d(TAG, "")
                } else {
                    Log.w(TAG, "")
                    Log.w(TAG, " [REPOSITORY] SYNC FAILED")
                    Log.w(TAG, "   Local FQC ID: $fqcId")
                    Log.w(TAG, "   Will retry later")
                    Log.w(TAG, "")
                }
            } catch (e: Exception) {
                Log.e(TAG, "")
                Log.e(TAG, " [REPOSITORY] ERROR SYNCING TO SUPABASE")
                Log.e(TAG, "   Local FQC ID: $fqcId")
                Log.e(TAG, "   Error: ${e.message}")
                Log.e(TAG, "   Stack trace: ${e.stackTraceToString()}")
                Log.e(TAG, "")
            }
        }

        // If FQC passed and a quality type is selected, update device status to PASSED/DONE
        if (fqcRecord.overallResult.uppercase() == "PASS" && 
            fqcRecord.finalQualityType.isNotBlank() &&
            fqcRecord.finalQualityType.uppercase() in listOf("REVIEWER", "CUSTOMER", "INTERNATIONAL", "MARKETING")) {
            try {
                val device = getDeviceBySerialNumber(fqcRecord.serialNumber)
                if (device != null) {
                    val updatedDevice = device.copy(
                        finalStatus = "PASSED", // Change from "IN_PROGRESS" to "PASSED"
                        finalOperator = fqcRecord.operatorName,
                        finalShipDate = java.time.LocalDate.now().toString()
                    )
                    updateDevice(updatedDevice)
                    Log.d(TAG, " Updated device status to PASSED for serial: ${fqcRecord.serialNumber}")

                    // Also sync device status to Supabase
                    supabaseScope.launch {
                        try {
                            val deviceService = com.example.fqcapp.supabase.SupabaseDeviceService()
                            deviceService.updateDeviceStatus(
                                serialNumber = fqcRecord.serialNumber,
                                status = "PASSED",
                                finalOperator = fqcRecord.operatorName,
                                finalShipDate = java.time.LocalDate.now().toString()
                            )
                            Log.d(TAG, " Successfully synced device status to Supabase")
                        } catch (e: Exception) {
                            Log.e(TAG, " Error syncing device status to Supabase", e)
                        }
                    }
                }
            } catch (e: Exception) {
                Log.e(TAG, " Error updating device status after FQC pass", e)
            }
        }

        // Update configuration cache after FQC record creation
        if (configurationId != null) {
            try {
                updateConfigurationCache(configurationId)
            } catch (e: Exception) {
                Log.w(TAG, " Failed to update configuration cache: ${e.message}")
            }
        }

        return fqcId
    }

