    private suspend fun isDuplicateDriverPairing(
        serialNumber: String,
        entryTimestamp: String,
        driverData: String,
        toleranceSeconds: Int = 10
    ): Boolean {
        try {
            val existingRecords = database.driverPairingRecordDao().getDriverPairingRecords(serialNumber)

            val entryTime = parseTimestamp(entryTimestamp)
            if (entryTime == null) return false

            return existingRecords.any { existing ->
                val existingTime = parseTimestamp(existing.entryTimestamp)
                if (existingTime == null) return@any false
                val timeDiff = kotlin.math.abs(entryTime - existingTime) / 1000
                // Check if timestamp is close AND driver data matches
                timeDiff <= toleranceSeconds && existing.driverData == driverData
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error checking for duplicate driver pairing", e)
            return false
        }
    }

    private suspend fun isDuplicateChangedParts(
        serialNumber: String,
        entryTimestamp: String,
        selectedParts: String,
        toleranceSeconds: Int = 10
    ): Boolean {
        try {
            val existingRecords = database.changedPartsRecordDao().getChangedPartsBySerial(serialNumber)

            val entryTime = parseTimestamp(entryTimestamp)
            if (entryTime == null) return false

            return existingRecords.any { existing ->
                val existingTime = parseTimestamp(existing.entryTimestamp)
                if (existingTime == null) return@any false
                val timeDiff = kotlin.math.abs(entryTime - existingTime) / 1000
                // Check if timestamp is close AND selected parts match
                timeDiff <= toleranceSeconds && existing.selectedParts == selectedParts
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error checking for duplicate changed parts", e)
            return false
        }
    }

    // Driver Pairing Record operations
    suspend fun insertDriverPairingRecord(record: DriverPairingRecord): Long {
        // IMPORTANT: Sync from Supabase first to prevent duplicates from multiple devices
        syncBeforeIteration(record.serialNumber)

        // Check for duplicate driver pairing record
        if (isDuplicateDriverPairing(record.serialNumber, record.entryTimestamp, record.driverData)) {
            Log.w(TAG, " Duplicate driver pairing record detected - skipping insert")
            Log.w(TAG, "   Serial: ${record.serialNumber}, EntryTime: ${record.entryTimestamp}, DriverData: ${record.driverData.take(50)}...")
            return 0L // Return 0 to indicate record was not saved (duplicate)
        }

        // DUAL-WRITE: Get or create TestConfiguration after saving driver pairing
        // Note: Driver pairing is saved first, then configuration is created/updated
        val recordId = database.driverPairingRecordDao().insertDriverPairingRecord(record)

        // Update configuration after driver pairing is saved
        try {
            val config = getOrCreateConfigurationForTest(
                deviceId = record.serialNumber,
                testStage = record.testStage,
                operatorName = record.operatorName
            )
            Log.d(TAG, " [DUAL-WRITE] Configuration updated after driver pairing: ${config.configurationId}")
        } catch (e: Exception) {
            Log.w(TAG, " [DUAL-WRITE] Failed to update configuration after driver pairing: ${e.message}")
            // Continue - configuration update is not critical for driver pairing
        }

        return recordId
    }

