    /**
     * Find device by MAC ID through active PCB link
     * This is used when device connects after PCB replacement - MAC changed but device is same
     * Returns device if found, null otherwise
     * 
     * Flow:
     * 1. Find PCB by MAC ID
     * 2. Find active PCB link for that PCB
     * 3. Get device ID from link
     * 4. Return device
     * 
     * This ensures that when PCB is replaced, we can still find the device using the new MAC ID
     * and the device keeps its original serial number (reference or printed).
     */
    suspend fun getDeviceByMacIdThroughPcbLink(macId: String): Device? {
        // Find PCB by MAC ID
        val pcb = database.pcbDao().getByMacId(macId) ?: return null

        // Find active PCB link for this PCB
        val activeLink = database.devicePcbLinkDao().getActiveLinkByPcbId(pcb.pcbId) ?: return null

        // Get device from link
        return database.deviceDao().getDevice(activeLink.deviceId)
    }

