            latestDriverPairing = macRecords.maxByOrNull { it.iterationNumber }
        }
        if (latestDriverPairing == null && deviceName != null && deviceName.isNotBlank()) {
            latestDriverPairing = database.driverPairingRecordDao().getLatestDriverPairing(deviceName)
            // #region agent log
            try {
                val logFile = java.io.File("c:\\Users\\pc\\Desktop\\.cursor\\debug.log")
                val logEntry = org.json.JSONObject().apply {
                    put("sessionId", "debug-session")
                    put("runId", "run1")
                    put("hypothesisId", "C")
                    put("location", "FQCRepository.kt:1079-result")
                    put("message", "Local DB query by device name result")
                    put("data", org.json.JSONObject().apply {
                        put("deviceName", deviceName)
                        put("found", latestDriverPairing != null)
                    })
                    put("timestamp", System.currentTimeMillis())
                }
                logFile.appendText(logEntry.toString() + "\n")
            } catch (e: Exception) {}
            // #endregion
        }

        // If still not found in database, check Google Sheets via DriverPairingCacheManager
        var driverPairingStatus: DriverPairingStatus? = null
        // #region agent log
        try {
            val logFile = java.io.File("c:\\Users\\pc\\Desktop\\.cursor\\debug.log")
            val logEntry = org.json.JSONObject().apply {
                put("sessionId", "debug-session")
                put("runId", "run1")
                put("hypothesisId", "D")
                put("location", "FQCRepository.kt:1084")
                put("message", "Decision: calling getDriverData")
                put("data", org.json.JSONObject().apply {
                    put("latestDriverPairingIsNull", latestDriverPairing == null)
                    put("driverPairingCacheManagerIsNull", driverPairingCacheManager == null)
                    put("willCallGetDriverData", latestDriverPairing == null && driverPairingCacheManager != null)
                })
                put("timestamp", System.currentTimeMillis())
            }
            logFile.appendText(logEntry.toString() + "\n")
        } catch (e: Exception) {}
        // #endregion
        if (latestDriverPairing == null && driverPairingCacheManager != null) {
            try {
                val googleSheetsData = driverPairingCacheManager.getDriverData(serialNumber, macId)
                if (googleSheetsData != null && googleSheetsData.drivers.isNotEmpty()) {
                    // Parse the first pair from Google Sheets data
                    // Google Sheets format: [Driver1_Row1, Driver2_Row1, Driver1_Row2, Driver2_Row2, ...]
                    if (googleSheetsData.drivers.size >= 2) {
                        val leftDriver = googleSheetsData.drivers[0]
                        val rightDriver = googleSheetsData.drivers[1]
                        driverPairingStatus = DriverPairingStatus(
                            iterationNumber = 1, // Google Sheets doesn't track iterations, use 1 as default
                            leftDriver = leftDriver,
                            rightDriver = rightDriver
                        )
                    }
                }
            } catch (e: Exception) {
                android.util.Log.e("FQCRepository", " Error checking Google Sheets for driver pairing", e)
            }
        }

        // If found in database, use that instead
        if (latestDriverPairing != null) {
            val driverPair = ResultFormatter.parseDriverData(latestDriverPairing.driverData)
            driverPairingStatus = DriverPairingStatus(
                iterationNumber = latestDriverPairing.iterationNumber,
                leftDriver = driverPair?.first ?: "",
                rightDriver = driverPair?.second ?: ""
            )
        }

        // Helper function to get stage priority (lower number = lower stage)
        fun getStagePriority(stage: String): Int {
            return when (stage) {
                "Primary Test" -> 1
                "Secondary Test" -> 2
                "Final Test" -> 3
                else -> 0
            }
        }

        // Get latest frequency test for each stage independently (optimized - parallel queries)
        val frequencyStages = stages.associateWith { stage ->
            val test = database.frequencyTestDao().getLatestFrequencyTestByStage(serialNumber, stage)
            val result = test?.result ?: ""
            val color = when (result.uppercase()) {
                "PASS" -> Color(0xFF10B981) // Green
                "CAN_BE_PASSED", "CAN BE PASSED", "CBP" -> Color(0xFFF59E0B) // Yellow
                "FAIL" -> Color(0xFFEF4444) // Red
                else -> Color(0xFF9CA3AF) // Gray
            }
            StageStatus(
                stageName = ResultFormatter.getStageDisplayName(stage),
                result = result,
                color = color
            )
        }

        // Get latest song test for each stage independently
        val songStages = stages.associateWith { stage ->
            val test = database.songTestDao().getLatestSongTestByStage(serialNumber, stage)
            val result = test?.result ?: ""
            val color = when (result.uppercase()) {
                "PASS" -> Color(0xFF10B981) // Green
                "CAN_BE_PASSED", "CAN BE PASSED", "CBP" -> Color(0xFFF59E0B) // Yellow
                "FAIL" -> Color(0xFFEF4444) // Red
                else -> Color(0xFF9CA3AF) // Gray
            }
            StageStatus(
                stageName = ResultFormatter.getStageDisplayName(stage),
                result = result,
                color = color
            )
        }

        // Get latest FQC for each stage independently
        val fqcStages = stages.associateWith { stage ->
            val record = database.fqcRecordDao().getLatestFQCByStage(serialNumber, stage)
            val result = record?.overallResult ?: ""
            val color = when (result.uppercase()) {
                "PASS" -> Color(0xFF10B981) // Green
                "CAN_BE_PASSED", "CAN BE PASSED", "CBP" -> Color(0xFFF59E0B) // Yellow
                "FAIL" -> Color(0xFFEF4444) // Red
                else -> Color(0xFF9CA3AF) // Gray
            }
            StageStatus(
                stageName = ResultFormatter.getStageDisplayName(stage),
                result = result,
                color = color
            )
        }

        return CurrentStatusData(
            driverPairing = driverPairingStatus,
            frequencyStages = frequencyStages,
            songStages = songStages,
            fqcStages = fqcStages
        )
    }

    /**
     * Get detailed milestone data for all iterations.
     * Returns all iterations ordered by iteration number DESC (latest first).
     */
    suspend fun getDetailedMilestoneData(serialNumber: String): DetailedMilestoneData {
        // Reduced logging for performance - only log errors

        // Get all frequency tests
        val allFrequencyTests = database.frequencyTestDao().getAllFrequencyTestsBySerial(serialNumber)

        val frequencyIterations = allFrequencyTests.map { test ->
            FrequencyIteration(
                iterationNumber = test.iterationNumber,
                testStage = test.testStage,
                result = test.result,
                solutions = ResultFormatter.parseSolutions(test.solutions),
                resultText = ResultFormatter.formatFrequencyResult(test)
            )
        }

        // Get all song tests (overall results only)
        val allSongTests = database.songTestDao().getAllSongTestsBySerial(serialNumber)

        val songIterations = allSongTests.map { test ->
            SongIteration(
                iterationNumber = test.iterationNumber,
                testStage = test.testStage,
                result = test.result,
                solutions = ResultFormatter.parseSolutions(test.solutions),
                resultText = ResultFormatter.formatSongResult(test)
            )
        }

        // Get all FQC records
        val allFQCRecords = database.fqcRecordDao().getAllFQCRecordsBySerial(serialNumber)

        val fqcIterations = allFQCRecords.map { record ->
            FQCIteration(
                iterationNumber = record.iterationNumber,
                testStage = record.testStage,
                result = record.overallResult,
                resultText = ResultFormatter.formatFQCResult(record)
            )
        }

        // Get all driver pairing records
        val allDriverPairingRecords = database.driverPairingRecordDao().getAllDriverPairingBySerial(serialNumber)

        val driverPairingIterations = allDriverPairingRecords.map { record ->
            val driverPair = ResultFormatter.parseDriverData(record.driverData)
            DriverPairingIteration(
                iterationNumber = record.iterationNumber,
                leftDriver = driverPair?.first,
                rightDriver = driverPair?.second,
                resultText = ResultFormatter.formatDriverPairingResult(driverPair?.first, driverPair?.second)
            )
        }

        // Calculate max iteration across all test types
        val maxIteration = maxOf(
            frequencyIterations.maxOfOrNull { it.iterationNumber } ?: 0,
            songIterations.maxOfOrNull { it.iterationNumber } ?: 0,
            fqcIterations.maxOfOrNull { it.iterationNumber } ?: 0,
            driverPairingIterations.maxOfOrNull { it.iterationNumber } ?: 0
        )

        return DetailedMilestoneData(
            driverPairingIterations = driverPairingIterations,
            frequencyIterations = frequencyIterations,
            songIterations = songIterations,
            fqcIterations = fqcIterations,
            maxIteration = maxIteration
        )
    }

