                        .padding(horizontal = 16.dp, vertical = 8.dp),
                    horizontalArrangement = Arrangement.spacedBy(8.dp)
                ) {
                    Button(
                        onClick = {
                            if (deviceType != "New") {
                                pendingDeviceType = "New"
                                showDeviceTypeChangeDialog = true
                            }
                        },
                        modifier = Modifier.weight(1f),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = if (deviceType == "New") Color(0xFF4CAF50) else Color(0xFF424242),
                            contentColor = Color.White
                        )
                    ) {
                        Text("New Build", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold)
                    }
                    Button(
                        onClick = {
                            if (deviceType != "Repair") {
                                pendingDeviceType = "Repair"
                                showDeviceTypeChangeDialog = true
                            }
                        },
                        modifier = Modifier.weight(1f),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = if (deviceType == "Repair") Color(0xFFFF9800) else Color(0xFF424242),
                            contentColor = Color.White
                        )
                    ) {
                        Text("Repair", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold)
                    }
                    Button(
                        onClick = {
                            if (deviceType != "Rnd") {
                                pendingDeviceType = "Rnd"
                                showDeviceTypeChangeDialog = true
                            }
                        },
                        modifier = Modifier.weight(1f),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = if (deviceType == "Rnd") Color(0xFF9C27B0) else Color(0xFF424242),
                            contentColor = Color.White
                        )
                    ) {
                        Text("R&D", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold)
                    }
                }
            }
        }
    ) { padding ->
        if (!permitted) {
            Box(Modifier.fillMaxSize().padding(padding), contentAlignment = Alignment.Center) {
                Text(
                    "Camera permission is required to scan.",
                    textAlign = TextAlign.Center,
                    color = Color.White
                )
            }
            return@Scaffold
        }

        // Start CameraX
        LaunchedEffect(permitted) {
            if (!permitted) return@LaunchedEffect
            val provider = ProcessCameraProvider.getInstance(ctx).get()

            val preview = Preview.Builder()
                .apply {
                    // Helpful defaults via Camera2 interop
                    Camera2Interop.Extender(this)
                        .setCaptureRequestOption(
                            android.hardware.camera2.CaptureRequest.CONTROL_AF_MODE,
                            android.hardware.camera2.CaptureRequest.CONTROL_AF_MODE_CONTINUOUS_PICTURE
                        )
                        .setCaptureRequestOption(
                            android.hardware.camera2.CaptureRequest.CONTROL_AE_MODE,
                            android.hardware.camera2.CaptureRequest.CONTROL_AE_MODE_ON
                        )
                }
                .build()
                .also { it.setSurfaceProvider(previewView.surfaceProvider) }

            val analysis = ImageAnalysis.Builder()
                .setBackpressureStrategy(ImageAnalysis.STRATEGY_KEEP_ONLY_LATEST)
                .setOutputImageFormat(ImageAnalysis.OUTPUT_IMAGE_FORMAT_YUV_420_888)
                .build()
                .apply {
                    setAnalyzer(
                        ContextCompat.getMainExecutor(ctx),
                        BarcodeAnalyzer { hit: ScanHit ->
                            val value = hit.text ?: hit.raw
                            if (!value.isNullOrEmpty() && !showDialog) {
                                // Auto-zoom if the code is tiny in view (< ~6% of area)
                                hit.box?.let { b ->
                                    val w = max(1, previewView.width)
                                    val h = max(1, previewView.height)
                                    val areaPct = (b.width().toFloat() * b.height().toFloat()) / (w.toFloat() * h.toFloat())
                                    if (areaPct < 0.06f) {
                                        val wantPct = 0.20f
                                        val factor = (wantPct / areaPct.coerceAtLeast(0.01f)).coerceIn(1f, 8f)
                                        val cur = camera?.cameraInfo?.zoomState?.value?.linearZoom ?: 0f
                                        val newZoom = (cur * factor).coerceIn(0f, 1f)
                                        scope.launch { camera?.cameraControl?.smoothLinearZoom(newZoom) }
                                    }
                                }
                                // Show dialog instead of directly calling onScanned
                                previewView.post {
                                    scannedValue = value
                                    showDialog = true
                                }
                            }
                        }
                    )
                }

            provider.unbindAll()
            camera = provider.bindToLifecycle(lifecycle, CameraSelector.DEFAULT_BACK_CAMERA, preview, analysis)
            flashAvailable = camera?.cameraInfo?.hasFlashUnit() == true
        }

        // Preview and reticle overlay
        Box(Modifier.fillMaxSize().padding(padding)) {
            AndroidView(factory = { previewView }, modifier = Modifier.fillMaxSize())
            ReticleOverlay()

            // Zoom Controls at Bottom
            Row(
                modifier = Modifier
                    .align(Alignment.BottomCenter)
                    .fillMaxWidth()
                    .padding(16.dp),
                horizontalArrangement = Arrangement.spacedBy(8.dp, Alignment.CenterHorizontally)
            ) {
                // Zoom In Button
                Button(
                    onClick = {
                        scope.launch {
                            val newZoom = (currentZoom + 0.2f).coerceIn(0f, 1f)
                            camera?.cameraControl?.smoothLinearZoom(newZoom)
                            currentZoom = newZoom
                        }
                    },
                    colors = ButtonDefaults.buttonColors(
                        containerColor = Color(0xFFFFC107),
                        contentColor = Color.Black
                    ),
                    modifier = Modifier.height(40.dp)
                ) {
                    Text("Zoom In", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold, fontSize = 12.sp)
                }

                // Zoom Out Button
                Button(
                    onClick = {
                        scope.launch {
                            val newZoom = (currentZoom - 0.2f).coerceIn(0f, 1f)
                            camera?.cameraControl?.smoothLinearZoom(newZoom)
                            currentZoom = newZoom
                        }
                    },
                    colors = ButtonDefaults.buttonColors(
                        containerColor = Color(0xFFFF9800),
                        contentColor = Color.Black
                    ),
                    modifier = Modifier.height(40.dp)
                ) {
                    Text("Zoom Out", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold, fontSize = 12.sp)
                }

                // Reset Button
                Button(
                    onClick = {
                        scope.launch {
                            camera?.cameraControl?.smoothLinearZoom(0f)
                            currentZoom = 0f
                        }
                    },
                    colors = ButtonDefaults.buttonColors(
                        containerColor = Color(0xFF424242),
                        contentColor = Color.White
                    ),
                    modifier = Modifier.height(40.dp)
                ) {
                    Text("Reset", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold, fontSize = 12.sp)
                }

                // Manual Entry Button
                Button(
                    onClick = {
                        last6Digits = ""
                        selectedColor = null
                        showColorSelection = true
                    },
                    colors = ButtonDefaults.buttonColors(
                        containerColor = Color(0xFF2196F3),
                        contentColor = Color.White
                    ),
                    modifier = Modifier.height(40.dp)
                ) {
                    Text("Manual", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold, fontSize = 12.sp)
                }
            }
        }

        // Scan Result Dialog
        if (showDialog && scannedValue != null) {
            AlertDialog(
                onDismissRequest = { },
                title = {
                    Text(
                        "Scanned Result",
                        color = Color(0xFFFFC107),
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    )
                },
                text = {
                    Column {
                        Text(
                            "Serial Number:",
                            style = MaterialTheme.typography.bodySmall,
                            color = Color.White.copy(alpha = 0.7f)
                        )
                        Spacer(modifier = Modifier.height(8.dp))
                        Text(
                            scannedValue!!,
                            style = MaterialTheme.typography.titleMedium,
                            color = Color.White,
                            fontWeight = androidx.compose.ui.text.font.FontWeight.SemiBold
                        )
                    }
                },
                confirmButton = {
                    Button(
                        onClick = {
                            showDialog = false
                            if (deviceType == "Repair") {
                                // Show repair intake form instead of directly scanning
                                showRepairIntakeForm = true
                            } else {
                                // Normal flow: proceed with scan (New Build or R&D)
                                onScanned(scannedValue!!, null)
                            }
                        },
                        colors = ButtonDefaults.buttonColors(
                            containerColor = Color(0xFFFFC107),
                            contentColor = Color.Black
                        )
                    ) {
                        Text("OK")
                    }
                },
                dismissButton = {
                    OutlinedButton(
                        onClick = {
                            showDialog = false
                            scannedValue = null
                        },
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = Color.White
                        )
                    ) {
                        Text("Rescan")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White
            )
        }

        // Step 1: Color Selection Dialog
        if (showColorSelection) {
            AlertDialog(
                onDismissRequest = { showColorSelection = false },
                title = {
                    Text(
                        "Select Headphone Color",
                        color = Color(0xFFFFC107),
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    )
                },
                text = {
                    Column(verticalArrangement = Arrangement.spacedBy(12.dp)) {
                        Text(
                            "Choose the headphone color:",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.7f)
                        )
                        Spacer(modifier = Modifier.height(8.dp))

                        // Obsidian Black
                        OutlinedButton(
                            onClick = {
                                selectedColor = "OB"
                                showColorSelection = false
                                showManualEntry = true
                            },
                            modifier = Modifier.fillMaxWidth(),
                            colors = ButtonDefaults.outlinedButtonColors(
                                contentColor = Color.White
                            ),
                            border = androidx.compose.foundation.BorderStroke(2.dp, Color(0xFF424242))
                        ) {
                            Text("Obsidian Black (OB)", modifier = Modifier.padding(vertical = 8.dp))
                        }

                        // Ember Gray
                        OutlinedButton(
                            onClick = {
                                selectedColor = "EG"
                                showColorSelection = false
                                showManualEntry = true
                            },
                            modifier = Modifier.fillMaxWidth(),
                            colors = ButtonDefaults.outlinedButtonColors(
                                contentColor = Color.White
                            ),
                            border = androidx.compose.foundation.BorderStroke(2.dp, Color(0xFF424242))
                        ) {
                            Text("Ember Gray (EG)", modifier = Modifier.padding(vertical = 8.dp))
                        }

                        // Moonstone White
                        OutlinedButton(
                            onClick = {
                                selectedColor = "MW"
                                showColorSelection = false
                                showManualEntry = true
                            },
                            modifier = Modifier.fillMaxWidth(),
                            colors = ButtonDefaults.outlinedButtonColors(
                                contentColor = Color.White
                            ),
                            border = androidx.compose.foundation.BorderStroke(2.dp, Color(0xFF424242))
                        ) {
                            Text("Moonstone White (MW)", modifier = Modifier.padding(vertical = 8.dp))
                        }
                    }
                },
                confirmButton = {},
                dismissButton = {
                    OutlinedButton(
                        onClick = { showColorSelection = false },
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = Color.White
                        )
                    ) {
                        Text("Cancel")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White
            )
        }

        // Step 2: Enter Last 6 Digits Dialog
        if (showManualEntry && selectedColor != null) {
            val partialSerial = "$serialNumberPrefix$selectedColor"

            AlertDialog(
                onDismissRequest = { showManualEntry = false },
                title = {
                    Text(
                        "Enter Last 6 Digits",
                        color = Color(0xFFFFC107),
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    )
                },
                text = {
                    Column {
                        Text(
                            "60% complete:",
                            style = MaterialTheme.typography.bodySmall,
                            color = Color.White.copy(alpha = 0.5f)
                        )
                        Text(
                            partialSerial,
                            style = MaterialTheme.typography.titleLarge,
                            color = Color(0xFFFFC107),
                            fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                        )
                        Spacer(modifier = Modifier.height(16.dp))
                        Text(
                            "Enter the remaining 6 digits:",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.7f)
                        )
                        Spacer(modifier = Modifier.height(12.dp))
                        OutlinedTextField(
                            value = last6Digits,
                            onValueChange = { 
                                if (it.length <= 6 && it.all { ch -> ch.isDigit() }) {
                                    last6Digits = it
                                }
                            },
                            label = { Text("Last 6 Digits") },
                            placeholder = { Text("000000") },
                            singleLine = true,
                            keyboardOptions = androidx.compose.foundation.text.KeyboardOptions(
                                keyboardType = androidx.compose.ui.text.input.KeyboardType.Number
                            ),
                            colors = androidx.compose.material3.OutlinedTextFieldDefaults.colors(
                                focusedBorderColor = Color(0xFFFFC107),
                                unfocusedBorderColor = Color(0xFF424242),
                                focusedLabelColor = Color(0xFFFFC107),
                                unfocusedLabelColor = Color.White.copy(alpha = 0.6f),
                                cursorColor = Color(0xFFFFC107),
                                focusedTextColor = Color.White,
                                unfocusedTextColor = Color.White
                            ),
                            modifier = Modifier.fillMaxWidth()
                        )
                        Spacer(modifier = Modifier.height(8.dp))
                        Text(
                            "Complete serial: $partialSerial${last6Digits.padEnd(6, '_')}",
                            style = MaterialTheme.typography.bodySmall,
                            color = if (last6Digits.length == 6) Color(0xFF4CAF50) else Color.White.copy(alpha = 0.5f),
                            fontWeight = if (last6Digits.length == 6) androidx.compose.ui.text.font.FontWeight.Bold else null
                        )
                    }
                },
                confirmButton = {
                    Button(
                        onClick = {
                            if (last6Digits.length == 6) {
                                val fullSerial = "$partialSerial$last6Digits"
                                showManualEntry = false
                                scannedValue = fullSerial
                                // Show dialog or repair form based on device type
                                if (deviceType == "Repair") {
                                    showRepairIntakeForm = true
                                } else {
                                    showDialog = true
                                }
                            }
                        },
                        enabled = last6Digits.length == 6,
                        colors = ButtonDefaults.buttonColors(
                            containerColor = Color(0xFFFFC107),
                            contentColor = Color.Black
                        )
                    ) {
                        Text("Submit")
                    }
                },
                dismissButton = {
                    OutlinedButton(
                        onClick = {
                            showManualEntry = false
                            // Go back to color selection
                            showColorSelection = true
                        },
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = Color.White
                        )
                    ) {
                        Text("Back")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White
            )
        }

        // Repair Intake Form Dialog
        if (showRepairIntakeForm && scannedValue != null) {
            AlertDialog(
                onDismissRequest = { showRepairIntakeForm = false },
                title = {
                    Text(
                        "Repair Intake Form",
                        color = Color(0xFFFFC107),
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    )
                },
                text = {
                    Column(
                        verticalArrangement = Arrangement.spacedBy(16.dp),
                        modifier = Modifier.fillMaxWidth()
                    ) {
                        // Serial Number Display
                        Text(
                            "Serial Number: $scannedValue",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White,
                            fontWeight = androidx.compose.ui.text.font.FontWeight.SemiBold
                        )
                        Divider(color = Color(0xFF424242))

                        // Repair Type Selection
                        Text(
                            "Repair Type:",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.7f)
                        )
                        Row(
                            horizontalArrangement = Arrangement.spacedBy(12.dp)
                        ) {
                            Row(
                                verticalAlignment = Alignment.CenterVertically,
                                modifier = Modifier.weight(1f)
                            ) {
                                RadioButton(
                                    selected = repairType == "NORMAL_PART",
                                    onClick = { repairType = "NORMAL_PART" },
                                    colors = RadioButtonDefaults.colors(
                                        selectedColor = Color(0xFFFFC107)
                                    )
                                )
                                Text(
                                    "Normal Part",
                                    color = Color.White,
                                    modifier = Modifier.padding(start = 4.dp)
                                )
                            }
                            Row(
                                verticalAlignment = Alignment.CenterVertically,
                                modifier = Modifier.weight(1f)
                            ) {
                                RadioButton(
                                    selected = repairType == "PCB_REPAIR",
                                    onClick = { repairType = "PCB_REPAIR" },
                                    colors = RadioButtonDefaults.colors(
                                        selectedColor = Color(0xFFFFC107)
                                    )
                                )
                                Text(
                                    "PCB Repair",
                                    color = Color.White,
                                    modifier = Modifier.padding(start = 4.dp)
                                )
                            }
                        }

                        // Received From
                        OutlinedTextField(
                            value = receivedFromCustomer,
                            onValueChange = { receivedFromCustomer = it },
                            label = { Text("Received From (Customer/CRM ID)") },
                            modifier = Modifier.fillMaxWidth(),
                            singleLine = true,
                            colors = androidx.compose.material3.OutlinedTextFieldDefaults.colors(
                                focusedBorderColor = Color(0xFFFFC107),
                                unfocusedBorderColor = Color(0xFF424242),
                                focusedLabelColor = Color(0xFFFFC107),
                                unfocusedLabelColor = Color.White.copy(alpha = 0.6f),
                                cursorColor = Color(0xFFFFC107),
                                focusedTextColor = Color.White,
                                unfocusedTextColor = Color.White
                            )
                        )

                        // Notes
                        OutlinedTextField(
                            value = repairNotes,
                            onValueChange = { repairNotes = it },
                            label = { Text("Notes") },
                            modifier = Modifier.fillMaxWidth(),
                            maxLines = 3,
                            colors = androidx.compose.material3.OutlinedTextFieldDefaults.colors(
                                focusedBorderColor = Color(0xFFFFC107),
                                unfocusedBorderColor = Color(0xFF424242),
                                focusedLabelColor = Color(0xFFFFC107),
                                unfocusedLabelColor = Color.White.copy(alpha = 0.6f),
                                cursorColor = Color(0xFFFFC107),
                                focusedTextColor = Color.White,
                                unfocusedTextColor = Color.White
                            )
                        )
                    }
                },
                confirmButton = {
                    Button(
                        onClick = {
                            if (repairType != null && receivedFromCustomer.isNotBlank()) {
                                if (repairType == "PCB_REPAIR") {
                                    // Create repair case first, then show PCB scanning screen
                                    scope.launch {
                                        try {
                                            val deviceCase = repository.createDeviceCase(
                                                deviceId = scannedValue!!,
                                                caseType = "REPAIR_RETURN",
                                                repairType = repairType,
                                                receivedFromCustomerRef = receivedFromCustomer,
                                                returnToCustomerRef = receivedFromCustomer, // Always same as received from
                                                ownershipTransferred = false, // Always false since returnTo = receivedFrom
                                                operatorName = operatorName,
                                                notes = repairNotes
                                            )
                                            Log.d("ScannerScreen", " Created repair case for PCB repair: ${deviceCase.caseId}")
                                            showRepairIntakeForm = false
                                            showPcbScanScreen = true
                                        } catch (e: Exception) {
                                            Log.e("ScannerScreen", " Failed to create repair case", e)
                                            android.widget.Toast.makeText(ctx, "Failed to create repair case: ${e.message}", android.widget.Toast.LENGTH_LONG).show()
                                        }
                                    }
                                } else {
                                    // Normal part repair - create repair case and proceed
                                    scope.launch {
                                        try {
                                            val deviceCase = repository.createDeviceCase(
                                                deviceId = scannedValue!!,
                                                caseType = "REPAIR_RETURN",
                                                repairType = repairType,
                                                receivedFromCustomerRef = receivedFromCustomer,
                                                returnToCustomerRef = receivedFromCustomer, // Always same as received from
                                                ownershipTransferred = false, // Always false since returnTo = receivedFrom
                                                operatorName = operatorName,
                                                notes = repairNotes
                                            )
                                            Log.d("ScannerScreen", " Created repair case: ${deviceCase.caseId}")
                                            showRepairIntakeForm = false
                                            onScanned(scannedValue!!, null)
                                        } catch (e: Exception) {
                                            Log.e("ScannerScreen", " Failed to create repair case", e)
                                            android.widget.Toast.makeText(ctx, "Failed to create repair case: ${e.message}", android.widget.Toast.LENGTH_LONG).show()
                                        }
                                    }
                                }
                            }
                        },
                        enabled = repairType != null && receivedFromCustomer.isNotBlank(),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = Color(0xFFFFC107),
                            contentColor = Color.Black
                        )
                    ) {
                        Text("Continue")
                    }
                },
                dismissButton = {
                    OutlinedButton(
                        onClick = {
                            showRepairIntakeForm = false
                            // Reset form
                            repairType = null
                            receivedFromCustomer = ""
                            repairNotes = ""
                        },
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = Color.White
                        )
                    ) {
                        Text("Cancel")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White
            )
        }

        // Device Type Change Confirmation Dialog
        if (showDeviceTypeChangeDialog && pendingDeviceType != null) {
            val typeDisplayName = when (pendingDeviceType) {
                "New" -> "New Build"
                "Repair" -> "Repair"
                "Rnd" -> "R&D"
                else -> pendingDeviceType
            }

            AlertDialog(
                onDismissRequest = {
                    showDeviceTypeChangeDialog = false
                    pendingDeviceType = null
                },
                title = {
                    Text(
                        "Change Device Type",
                        color = Color(0xFFFFC107),
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    )
                },
                text = {
                    Text(
                        "Do you want to change the device type to $typeDisplayName?",
                        color = Color.White
                    )
                },
                confirmButton = {
                    Button(
                        onClick = {
                            previousDeviceType = deviceType
                            deviceType = pendingDeviceType!!
                            showDeviceTypeChangeDialog = false
                            pendingDeviceType = null
                        },
                        colors = ButtonDefaults.buttonColors(
                            containerColor = Color(0xFFFFC107),
                            contentColor = Color.Black
                        )
                    ) {
                        Text("Yes")
                    }
                },
                dismissButton = {
                    OutlinedButton(
                        onClick = {
                            showDeviceTypeChangeDialog = false
                            pendingDeviceType = null
                        },
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = Color.White
                        )
                    ) {
                        Text("No")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White
            )
        }

        // PCB Scanning Screen Dialog
        if (showPcbScanScreen) {
            // Fetch MAC ID when PCB serial is entered
            LaunchedEffect(newPcbSerial) {
                if (newPcbSerial.isNotBlank() && newPcbMacId.isBlank()) {
                    isFetchingMacId = true
                    macIdFetchError = null
                    try {
                        val fetchedMacId = googleSheetsService.getMacIdBySerialNumber(newPcbSerial)
                        if (fetchedMacId != null) {
                            // Store raw MAC ID (without colons) for consistency
                            // Extract raw MAC ID (remove colons) from fetched MAC ID
                            newPcbMacId = fetchedMacId.filter { it.isLetterOrDigit() }.uppercase()
                            Log.d("ScannerScreen", " Found MAC ID for PCB Serial $newPcbSerial: ${formatMacId(newPcbMacId)}")
                        } else {
                            macIdFetchError = "MAC ID not found in Google Sheets"
                            Log.w("ScannerScreen", " MAC ID not found for PCB Serial: $newPcbSerial")
                        }
                    } catch (e: Exception) {
                        macIdFetchError = "Error fetching MAC ID: ${e.message}"
                        Log.e("ScannerScreen", " Error fetching MAC ID", e)
                    } finally {
                        isFetchingMacId = false
                    }
                } else if (newPcbSerial.isBlank()) {
                    // Clear MAC ID if PCB serial is cleared
                    newPcbMacId = ""
                    macIdFetchError = null
                }
            }

            AlertDialog(
                onDismissRequest = { showPcbScanScreen = false },
                title = {
                    Text(
                        "Scan New PCB",
                        color = Color(0xFFFFC107),
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    )
                },
                text = {
                    Column(
                        verticalArrangement = Arrangement.spacedBy(16.dp),
                        modifier = Modifier.fillMaxWidth()
                    ) {
                        Text(
                            "Device Serial: $scannedValue",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White,
                            fontWeight = androidx.compose.ui.text.font.FontWeight.SemiBold
                        )
                        Divider(color = Color(0xFF424242))

                        Text(
                            "Enter PCB Serial Number OR MAC ID:",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.7f)
                        )

                        // PCB Serial Number Field
                        OutlinedTextField(
                            value = newPcbSerial,
                            onValueChange = { 
                                newPcbSerial = it
                                // Clear MAC ID when serial changes (will be refetched)
                                if (it.isBlank()) {
                                    newPcbMacId = ""
                                }
                            },
                            label = { Text("PCB Serial Number") },
                            modifier = Modifier.fillMaxWidth(),
                            singleLine = true,
                            colors = androidx.compose.material3.OutlinedTextFieldDefaults.colors(
                                focusedBorderColor = Color(0xFFFFC107),
                                unfocusedBorderColor = Color(0xFF424242),
                                focusedLabelColor = Color(0xFFFFC107),
                                unfocusedLabelColor = Color.White.copy(alpha = 0.6f),
                                cursorColor = Color(0xFFFFC107),
                                focusedTextColor = Color.White,
                                unfocusedTextColor = Color.White
                            )
                        )

                        // MAC ID fetching status
                        if (isFetchingMacId) {
                            Text(
                                "Fetching MAC ID from Google Sheets...",
                                style = MaterialTheme.typography.bodySmall,
                                color = Color(0xFFFFC107)
                            )
                        } else if (macIdFetchError != null && newPcbSerial.isNotBlank()) {
                            Text(
                                macIdFetchError!!,
                                style = MaterialTheme.typography.bodySmall,
                                color = Color(0xFFFF5252)
                            )
                        } else if (newPcbMacId.isNotBlank()) {
                            Text(
                                "MAC ID (from Google Sheets): ${formatMacId(newPcbMacId)}",
                                style = MaterialTheme.typography.bodySmall,
                                color = Color(0xFF4CAF50)
                            )
                        }

                        Text(
                            "OR",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.5f),
                            modifier = Modifier.padding(vertical = 8.dp)
                        )

                        // MAC ID Field (direct entry) - Plain text input, format only for confirmation
                        OutlinedTextField(
                            value = newPcbMacId,
                            onValueChange = { input ->
                                // Filter to only hex characters, uppercase, limit to 12
                                val cleaned = input.filter { it.isLetterOrDigit() }.uppercase().take(12)
                                newPcbMacId = cleaned
                                Log.d("ScannerScreen", " [MAC_ID_INPUT] User typed: '$input' -> Cleaned: '$cleaned'")
                            },
                            label = { Text("MAC ID (Direct Entry)") },
                            modifier = Modifier.fillMaxWidth(),
                            singleLine = true,
                            placeholder = { Text("Type hex digits (e.g., DC0D300DBE75)") },
                            colors = androidx.compose.material3.OutlinedTextFieldDefaults.colors(
                                focusedBorderColor = Color(0xFFFFC107),
                                unfocusedBorderColor = Color(0xFF424242),
                                focusedLabelColor = Color(0xFFFFC107),
                                unfocusedLabelColor = Color.White.copy(alpha = 0.6f),
                                cursorColor = Color(0xFFFFC107),
                                focusedTextColor = Color.White,
                                unfocusedTextColor = Color.White
                            )
                        )

                    }
                },
                confirmButton = {
                    Button(
                        onClick = {
                            // Either PCB Serial or MAC ID should be provided
                            if (newPcbSerial.isNotBlank() || newPcbMacId.isNotBlank()) {
                                // If MAC ID was entered, show confirmation dialog with formatted MAC ID
                                if (newPcbMacId.isNotBlank()) {
                                    showMacIdConfirmation = true
                                } else {
                                    // Only PCB serial provided (MAC ID fetched from Google Sheets), proceed directly
                                    val finalMacId = if (newPcbMacId.isNotBlank()) {
                                        formatMacId(newPcbMacId)
                                    } else {
                                        ""
                                    }

                                    // Update MAC ID if callback is provided (for PCB changes)
                                    if (finalMacId.isNotBlank() && onMacIdUpdate != null) {
                                        Log.d("ScannerScreen", " [PCB_CHANGE] Updating MAC ID to: $finalMacId")
                                        onMacIdUpdate(finalMacId)
                                    }

                                    // TODO: Replace PCB logic here (create PCB link, deactivate old one, etc.)
                                    // Proceed with scan
                                    showPcbScanScreen = false
                                    onScanned(scannedValue!!, null)
                                }
                            }
                        },
                        enabled = newPcbSerial.isNotBlank() || newPcbMacId.isNotBlank(),
                        colors = ButtonDefaults.buttonColors(
                            containerColor = Color(0xFFFFC107),
                            contentColor = Color.Black
                        )
                    ) {
                        Text("Confirm Replace PCB")
                    }
                },
                dismissButton = {
                    OutlinedButton(
                        onClick = {
                            showPcbScanScreen = false
                            newPcbSerial = ""
                            newPcbMacId = ""
                            isFetchingMacId = false
                            macIdFetchError = null
                        },
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = Color.White
                        )
                    ) {
                        Text("Cancel")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White
            )
        }

        // MAC ID Confirmation Dialog (shows formatted MAC ID before submitting)
        if (showMacIdConfirmation && newPcbMacId.isNotBlank()) {
            val formattedMacId = formatMacId(newPcbMacId)

            AlertDialog(
                onDismissRequest = { showMacIdConfirmation = false },
                title = {
                    Text(
                        "Confirm MAC ID",
                        color = Color(0xFFFFC107),
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    )
                },
                text = {
                    Column(verticalArrangement = Arrangement.spacedBy(12.dp)) {
                        Text(
                            "Please verify the MAC ID:",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.7f)
                        )
                        Text(
                            formattedMacId,
                            style = MaterialTheme.typography.titleLarge,
                            color = Color(0xFFFFC107),
                            fontWeight = androidx.compose.ui.text.font.FontWeight.Bold,
                            modifier = Modifier.padding(vertical = 8.dp)
                        )
                        Text(
                            "Raw input: $newPcbMacId",
                            style = MaterialTheme.typography.bodySmall,
                            color = Color.White.copy(alpha = 0.5f)
                        )
                    }
                },
                confirmButton = {
                    Button(
                        onClick = {
                            // Replace PCB using backend service
                            scope.launch {
                                try {
                                    val finalMacId = formattedMacId
                                    val pcbSerial = if (newPcbSerial.isNotBlank()) newPcbSerial else "UNKNOWN"

                                    val pcbLink = repository.replacePcb(
                                        deviceId = scannedValue!!,
                                        newPcbSerialNumber = pcbSerial,
                                        newMacId = finalMacId,
                                        operatorName = operatorName,
                                        notes = "PCB replaced via Scanner Screen (Repair flow)"
                                    )

                                    Log.d("ScannerScreen", " PCB replaced successfully, linkId: ${pcbLink.linkId}")

                                    // Update MAC ID if callback is provided (for Navigation)
                                    if (onMacIdUpdate != null) {
                                        Log.d("ScannerScreen", " [PCB_CHANGE] Updating MAC ID to: $finalMacId")
                                        onMacIdUpdate(finalMacId)
                                    }

                                    // Proceed with scan, passing MAC ID override
                                    showMacIdConfirmation = false
                                    showPcbScanScreen = false
                                    onScanned(scannedValue!!, finalMacId)
                                } catch (e: Exception) {
                                    Log.e("ScannerScreen", " Failed to replace PCB", e)
                                    android.widget.Toast.makeText(ctx, "Failed to replace PCB: ${e.message}", android.widget.Toast.LENGTH_LONG).show()
                                }
                            }
                        },
                        colors = ButtonDefaults.buttonColors(
                            containerColor = Color(0xFFFFC107),
                            contentColor = Color.Black
                        )
                    ) {
                        Text("Confirm")
                    }
                },
                dismissButton = {
                    OutlinedButton(
                        onClick = { showMacIdConfirmation = false },
                        colors = ButtonDefaults.outlinedButtonColors(
                            contentColor = Color.White
                        )
                    ) {
                        Text("Edit")
                    }
                },
                containerColor = Color(0xFF2B2B2B),
                titleContentColor = Color.White,
                textContentColor = Color.White
            )
        }
    }
}

@Composable
private fun ReticleOverlay(modifier: Modifier = Modifier) {
    Canvas(
        modifier = modifier
            .fillMaxSize()
            .padding(24.dp)
    ) {
        val boxW = size.minDimension * 0.6f
        val left = (size.width - boxW) / 2f
        val top = (size.height - boxW) / 2f
        val right = left + boxW
        val bottom = top + boxW
        val l = boxW * 0.15f
        val stroke = 6.dp.toPx()
        // corners
        drawLine(Color.White, Offset(left, top), Offset(left + l, top), stroke)
        drawLine(Color.White, Offset(left, top), Offset(left, top + l), stroke)
        drawLine(Color.White, Offset(right, top), Offset(right - l, top), stroke)
        drawLine(Color.White, Offset(right, top), Offset(right, top + l), stroke)
        drawLine(Color.White, Offset(left, bottom), Offset(left + l, bottom), stroke)
        drawLine(Color.White, Offset(left, bottom), Offset(left, bottom - l), stroke)
        drawLine(Color.White, Offset(right, bottom), Offset(right - l, bottom), stroke)
        drawLine(Color.White, Offset(right, bottom), Offset(right, bottom - l), stroke)
    }
}

