@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun FrequencyGeneratorScreen(
    currentSessionId: Long?,
    repository: FQCRepository,
    initialSerialNumber: String? = null, // Add serial number parameter (renamed to avoid conflict)
    authenticatedOperator: com.example.fqcapp.data.entities.OperatorCredential? = null,
    connectedDeviceName: String? = null, // Device name from Bluetooth connection
    longPressSpeed: Int = 10,
    onBack: (() -> Unit)? = null,
    onNavigateToLanding: (() -> Unit)? = null,
    onNavigateToFreqGen: (() -> Unit)? = null,
    undoManager: UndoManager = UndoManager(),
    solutionReminderManager: com.example.fqcapp.core.SolutionReminderManager? = null
) {
    val context = androidx.compose.ui.platform.LocalContext.current
    val prefs = remember { 
        context.getSharedPreferences("frequency_generator_prefs", android.content.Context.MODE_PRIVATE) 
    }
    val scope = rememberCoroutineScope()
    val snackbarHostState = remember { SnackbarHostState() }
    val onBackPressedDispatcher = androidx.activity.compose.LocalOnBackPressedDispatcherOwner.current?.onBackPressedDispatcher

    // Session tracking
    var currentTestSessionId by remember { mutableStateOf<Long?>(null) }
    var sessionStartTime by remember { mutableStateOf<Long?>(null) }

    // Session management
    val sessionManager = remember { SessionManager(context) }
    val reviewValidator = remember { ReviewValidator() }
    val sessionTimestampManager = remember { com.example.fqcapp.core.SessionTimestampManager(context) }

    // Review screen state
    var showReview by remember { mutableStateOf(false) }
    var previousTestResult by remember { mutableStateOf<com.example.fqcapp.data.TestResult?>(null) }
    // Navigation state machine: track where we are and where we should go
    var isFromUndo by remember { mutableStateOf(false) } // True if we're restoring from undo
    var shouldNavigateToLanding by remember { mutableStateOf(false) } // True if after submit we should go to landing
    var isSubmitting by remember { mutableStateOf(false) } // Guard to prevent duplicate submissions
    // Note: shouldShowReviewAfterUndo is declared in LaunchedEffect section below
    var lastSubmittedTestId by remember { mutableStateOf<Long?>(null) }
    // Use parameter as initial value, fallback to device name, then null
    var currentSerialNumber by remember { mutableStateOf<String?>(initialSerialNumber ?: connectedDeviceName) }
    var operatorName by remember { mutableStateOf("Unknown") }
    var operatorId by remember { mutableStateOf("") }

    // Use authenticated operator if provided
    LaunchedEffect(authenticatedOperator) {
        if (authenticatedOperator != null) {
            operatorName = authenticatedOperator.operatorName
            operatorId = authenticatedOperator.operatorId
            android.util.Log.d("FrequencyGenerator", " Using authenticated operator: $operatorName (ID: $operatorId)")
        } else {
            val prefsManager = com.example.fqcapp.utils.PreferencesManager(context)
            operatorName = prefsManager.getOperatorName() ?: "Unknown"
            android.util.Log.d("FrequencyGenerator", " No authenticated operator, using preferences: $operatorName")
        }
    }

    // Debug log state
    var debugLogs by remember { mutableStateOf<List<String>>(emptyList()) }

    // Session handover notification
    val sessionHandover by sessionManager.sessionHandover.collectAsState()

    // Device Workflow Service
    val workflowService = remember { com.example.fqcapp.services.DeviceWorkflowService(repository) }

    // UI Colors
    val accentColor = Color(0xFFFFC107)
    val bgColor = Color(0xFF212121)
    val surfaceColor = Color(0xFF2B2B2B)

    // Log workflow: Frequency Test STARTED when screen opens
    // Delayed and non-blocking - runs in background after screen loads
    // Use a Mutex to prevent race conditions and duplicate calls
    val workflowKey = remember(currentSerialNumber, operatorName) { 
        "$currentSerialNumber-$operatorName" 
    }
    val workflowMutex = remember { kotlinx.coroutines.sync.Mutex() }
    var workflowLogged by remember(workflowKey) { mutableStateOf(false) }

    LaunchedEffect(workflowKey) {
        // Only log workflow if we have a valid serial number (not device name) and haven't logged yet
        // Use Mutex to ensure atomic check-and-set operation
        val shouldLog = workflowMutex.withLock {
            if (workflowLogged) {
                false // Already logged
            } else if (currentSerialNumber != null && 
                currentSerialNumber!!.isNotBlank() && 
                operatorName != "Unknown" &&
                !currentSerialNumber!!.matches(Regex("^BT-.*", RegexOption.IGNORE_CASE)) && // Not a device name
                !currentSerialNumber!!.matches(Regex("^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})$")) && // Not a MAC address
                currentSerialNumber!!.length >= 10) { // Serial numbers are typically 10+ chars
                workflowLogged = true // Mark as logged atomically
                true
            } else {
                false
            }
        }

        if (shouldLog) {
            // Delay workflow logging to avoid blocking screen load
            scope.launch(Dispatchers.IO) {
                try {
                    delay(500) // Small delay to let screen render first
                    val iterationNumber = workflowService.getNextIterationNumber(
                        currentSerialNumber!!,
                        com.example.fqcapp.services.DeviceWorkflowService.STAGE_FREQUENCY_TEST
                    )
                    workflowService.logWorkflow(
                        serialNumber = currentSerialNumber!!,
                        stage = com.example.fqcapp.services.DeviceWorkflowService.STAGE_FREQUENCY_TEST,
                        operatorName = operatorName,
                        action = com.example.fqcapp.services.DeviceWorkflowService.ACTION_STARTED,
                        iterationNumber = iterationNumber
                    )
                    android.util.Log.d("FrequencyGenerator", " [WORKFLOW] Logged FREQUENCY_TEST STARTED (background)")
                } catch (e: kotlinx.coroutines.CancellationException) {
                    // Ignore cancellation exceptions (screen left composition)
                    android.util.Log.d("FrequencyGenerator", " [WORKFLOW] Workflow logging cancelled (screen left)")
                } catch (e: Exception) {
                    android.util.Log.e("FrequencyGenerator", " [WORKFLOW] Error logging workflow STARTED", e)
                }
            }
        } else if (currentSerialNumber != null && currentSerialNumber!!.isNotBlank() && !workflowLogged) {
            android.util.Log.w("FrequencyGenerator", " Skipping workflow log - invalid serial number format: '${currentSerialNumber}'")
        }
    }

    // State - Load from SharedPreferences
    var currentFreq by rememberSaveable { mutableStateOf(prefs.getFloat("current_freq", 100f)) }
    var startFreq by rememberSaveable { mutableStateOf(prefs.getFloat("start_freq", 20f)) }
    var endFreq by rememberSaveable { mutableStateOf(prefs.getFloat("end_freq", 120f)) }
    var duration by rememberSaveable { mutableStateOf(prefs.getFloat("duration", 0.1f)) }
    var loop by rememberSaveable { mutableStateOf(prefs.getBoolean("loop", false)) }
    // Separate left and right channel volumes (0-100, representing 0-100%)
    var leftVolume by rememberSaveable { mutableStateOf(prefs.getInt("left_volume", 100)) }
    var rightVolume by rememberSaveable { mutableStateOf(prefs.getInt("right_volume", 100)) }
    var playingSingle by remember { mutableStateOf(false) } // Single frequency mode
    var playingSweep by remember { mutableStateOf(false) }  // Sweep mode
    var showFreqDialog by remember { mutableStateOf(false) }

    // Real-time gain values for visual feedback
    var actualLeftGain by remember { mutableStateOf(0.0f) }
    var actualRightGain by remember { mutableStateOf(0.0f) }
    var systemVolumeInfo by remember { mutableStateOf("") }

    // Channel test mode
    var channelTestMode by remember { mutableStateOf(false) }

    // Audio focus management
    var currentAudioFocusRequest by remember { mutableStateOf<AudioFocusRequest?>(null) }
    var hasAudioFocus by remember { mutableStateOf(false) }

    // Text input state variables for better text field handling
    var startFreqText by remember { mutableStateOf(startFreq.toInt().toString()) }
    var endFreqText by remember { mutableStateOf(endFreq.toInt().toString()) }
    var durationText by remember { mutableStateOf(duration.toString()) }
    var loopSweep by remember { mutableStateOf(loop) }

    // Sync text variables with numeric variables
    LaunchedEffect(startFreq) {
        startFreqText = startFreq.toInt().toString()
    }
    LaunchedEffect(endFreq) {
        endFreqText = endFreq.toInt().toString()
    }
    LaunchedEffect(duration) {
        durationText = duration.toString()
    }
    LaunchedEffect(loop) {
        loopSweep = loop
    }

    // Save settings whenever they change
    LaunchedEffect(currentFreq) {
        prefs.edit().putFloat("current_freq", currentFreq).apply()
    }
    LaunchedEffect(startFreq) {
        prefs.edit().putFloat("start_freq", startFreq).apply()
    }
    LaunchedEffect(endFreq) {
        prefs.edit().putFloat("end_freq", endFreq).apply()
    }
    LaunchedEffect(duration) {
        prefs.edit().putFloat("duration", duration).apply()
    }
    LaunchedEffect(loop) {
        prefs.edit().putBoolean("loop", loop).apply()
    }
    LaunchedEffect(leftVolume) {
        prefs.edit().putInt("left_volume", leftVolume).apply()
    }
    LaunchedEffect(rightVolume) {
        prefs.edit().putInt("right_volume", rightVolume).apply()
    }

    // Long press state for fast increment/decrement
    var isHoldingPlus by remember { mutableStateOf(false) }
    var isHoldingMinus by remember { mutableStateOf(false) }

    // Fast increment/decrement on hold
    LaunchedEffect(isHoldingPlus) {
        if (isHoldingPlus) {
            delay(300) // Initial delay before fast increment starts
            while (isActive && isHoldingPlus) {
                currentFreq = (currentFreq + 5f).coerceAtMost(20000f)
                delay(50) // Fast increment every 50ms
            }
        }
    }

    LaunchedEffect(isHoldingMinus) {
        if (isHoldingMinus) {
            delay(300) // Initial delay before fast decrement starts
            while (isActive && isHoldingMinus) {
                currentFreq = (currentFreq - 5f).coerceAtLeast(1f)
                delay(50) // Fast decrement every 50ms
            }
        }
    }

    // Review state
    var showFailDialog by remember { mutableStateOf(false) }    // Dialog B
    var showSolutionsDialog by remember { mutableStateOf(false) } // Dialog C
    var selectedIssues by remember { mutableStateOf(setOf<String>()) }

    // Handle system back navigation (gesture and button)
    androidx.compose.runtime.DisposableEffect(Unit) {
        val callback = object : androidx.activity.OnBackPressedCallback(true) {
            override fun handleOnBackPressed() {
                android.util.Log.d("FrequencyGenerator", "System back navigation - showing review dialog")
                showReview = true
            }
        }
        onBackPressedDispatcher?.addCallback(callback)
        onDispose { callback.remove() }
    }
    var selectedSolutions by remember { mutableStateOf(setOf<String>()) }
    var remark by remember { mutableStateOf("") }
    var otherSolution by remember { mutableStateOf("") }
    var reviewLogs by rememberSaveable { mutableStateOf(listOf<ReviewLog>()) }

    // Session count tracking
    var sessionCount by remember { mutableStateOf(0) }

    // Serial number from session (would be passed from navigation in real app)
    val serialNumber = "TEST-SERIAL-001" // TODO: Get from navigation or session

    // Audio track reference
    var audioJob by remember { mutableStateOf<kotlinx.coroutines.Job?>(null) }
    var currentAudioTrack by remember { mutableStateOf<AudioTrack?>(null) }


    // Hardcoded issues and solutions
    val issueOptions = listOf(
        "Right to left shift",
        "Left to right shift", 
        "Right Dominant",
        "Left Dominant",
        "Right chir",
        "Left chir",
        "Wire noise",
        "Battery noise",
        "U clamp issue",
        "Sorbathane issue",
        "High pressure"
    )

    val availableSolutions = listOf(
        "INTERCHANGE",
        "DRIVER CHANGE",
        "Replace BC Driver",
        "Replace AC Driver",
        "2dB programm",
        "Headband Replace"
    )

    // Function to log review results and complete session
    fun logReview(reviewLog: ReviewLog) {
        android.util.Log.d("FrequencyGenerator", " logReview called with result: ${reviewLog.result}")

        // Skip recording if "Test not done" - this is not a valid test session
        if (reviewLog.result == "test_not_done") {
            android.util.Log.d("FrequencyGenerator", "Test not done - skipping session recording")
            return
        }

        scope.launch {
            try {
                // Get current session info
                val session = currentTestSessionId?.let { repository.getTestSessionById(it) }
                val serialNumber = reviewLog.serialNumber ?: "PLACEHOLDER"
                val operatorName = session?.operatorName ?: "Unknown"

                android.util.Log.d("FrequencyGenerator", "Session info - ID: $currentTestSessionId, Serial: $serialNumber, Operator: $operatorName")

                // Calculate timing
                val endTime = System.currentTimeMillis()
                val startTime = sessionStartTime ?: endTime
                val durationMs = endTime - startTime
                val durationHours = durationMs / (1000 * 60 * 60)
                val durationMinutes = (durationMs % (1000 * 60 * 60)) / (1000 * 60)
                val durationSeconds = (durationMs % (1000 * 60)) / 1000
                val durationFormatted = String.format("%d:%02d:%02d", durationHours, durationMinutes, durationSeconds)

                android.util.Log.d("FrequencyGenerator", "Duration calculation: ${durationMs}ms = $durationFormatted")

                val startTimeFormatted = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date(startTime))
                val endTimeFormatted = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault()).format(java.util.Date(endTime))

                android.util.Log.d("FrequencyGenerator", "Frequency test ended at: $endTimeFormatted")
                android.util.Log.d("FrequencyGenerator", "Duration: $durationFormatted")

                // Get iteration number from Supabase
                android.util.Log.d("FrequencyGenerator", "")
                android.util.Log.d("FrequencyGenerator", " [FREQUENCY TEST] PREPARING TO SAVE TEST")
