                android.util.Log.d("FrequencyGenerator", "      - entryTimestamp: $sessionEntryTimestamp")
                android.util.Log.d("FrequencyGenerator", "      - exitTimestamp: $endTimeFormatted")
                android.util.Log.d("FrequencyGenerator", "      - totalTimeSpent: $totalTimeSpentFormatted")
                android.util.Log.d("FrequencyGenerator", "      - result: ${frequencyTest.result}")
