                android.util.Log.d("FrequencyGenerator", "      - issues count: ${reviewLog.issues.size}")
                android.util.Log.d("FrequencyGenerator", "      - solutions count: ${reviewLog.solutions.size + if (reviewLog.otherSolution.isNotEmpty()) 1 else 0}")

                android.util.Log.d("FrequencyGenerator", "")
                android.util.Log.d("FrequencyGenerator", " [LOCAL] Saving to Room Database...")

                // Save to database
                try {
                    val operatorId = authenticatedOperator?.operatorId
                    val testId = repository.insertFrequencyTest(frequencyTest, operatorId)
                    android.util.Log.d("FrequencyGenerator", "    [LOCAL] Frequency test saved to Room DB with ID: $testId")
