package com.example.fqcapp.data.dao

import androidx.room.*
import com.example.fqcapp.data.entities.FQCRecord
import kotlinx.coroutines.flow.Flow

@Dao
interface FQCRecordDao {

    @Query("SELECT * FROM fqc_records WHERE serialNumber = :serialNumber ORDER BY timestamp ASC")
    suspend fun getFQCRecords(serialNumber: String): List<FQCRecord>

    @Query("SELECT * FROM fqc_records WHERE serialNumber = :serialNumber ORDER BY timestamp ASC")
    fun getFQCRecordsFlow(serialNumber: String): Flow<List<FQCRecord>>

    @Query("SELECT * FROM fqc_records WHERE serialNumber = :serialNumber AND overallResult = 'PASS' ORDER BY timestamp DESC LIMIT 1")
    suspend fun getLastPassedFQC(serialNumber: String): FQCRecord?

    @Insert
    suspend fun insertFQCRecord(fqcRecord: FQCRecord): Long

    @Update
    suspend fun updateFQCRecord(fqcRecord: FQCRecord)

    @Query("SELECT * FROM fqc_records WHERE serialNumber = :serialNumber AND needsSync = 1")
    suspend fun getUnsyncedFQCRecords(serialNumber: String): List<FQCRecord>

    @Query("SELECT * FROM fqc_records WHERE needsSync = 1")
    suspend fun getAllUnsyncedFQCRecords(): List<FQCRecord>

    @Query("SELECT * FROM fqc_records ORDER BY timestamp DESC")
    suspend fun getAllFQCRecords(): List<FQCRecord>

    @Query("UPDATE fqc_records SET needsSync = 0, lastSyncTimestamp = :timestamp WHERE fqcId = :fqcId")
    suspend fun markAsSynced(fqcId: Long, timestamp: Long = System.currentTimeMillis())

    @Query("UPDATE fqc_records SET needsSync = 0, lastSyncTimestamp = :timestamp WHERE fqcId IN (:fqcIds)")
    suspend fun markMultipleAsSynced(fqcIds: List<Long>, timestamp: Long = System.currentTimeMillis())

    @Delete
    suspend fun deleteFQCRecord(fqcRecord: FQCRecord)

    @Query("SELECT * FROM fqc_records WHERE serialNumber = :serialNumber AND testStage = :stage ORDER BY iterationNumber DESC LIMIT 1")
    suspend fun getLatestFQCByStage(serialNumber: String, stage: String): FQCRecord?

    @Query("SELECT * FROM fqc_records WHERE serialNumber = :serialNumber ORDER BY iterationNumber DESC")
    suspend fun getAllFQCRecordsBySerial(serialNumber: String): List<FQCRecord>

    // Get latest FQC record by timestamp
    @Query("SELECT * FROM fqc_records WHERE serialNumber = :serialNumber ORDER BY timestamp DESC LIMIT 1")
    suspend fun getLatestFQCByTimestamp(serialNumber: String): FQCRecord?

    // Get latest FQC record by configuration ID
    @Query("SELECT * FROM fqc_records WHERE configurationId = :configurationId ORDER BY entryTimestamp DESC LIMIT 1")
    suspend fun getLatestByConfiguration(configurationId: String): FQCRecord?
}

