                                // Ensure Operator record exists
                                val operator = repository.getOperatorById(operatorId)
                                if (operator == null) {
                                    repository.insertOperator(
                                        Operator(
                                            id = operatorId,
                                            name = operatorName
                                        )
                                    )
                                }

                                val sessionId = repository.insertSession(
                                    FQCSession(
                                        operatorId = operatorId,
                                        serialNumber = serial,
                                        status = "active"
                                    )
                                )
                                currentSessionId = sessionId
                                currentSerialNumber = serial
                                android.util.Log.d("Navigation", "Session created successfully: $sessionId")
                            } catch (e: Exception) {
                                android.util.Log.e("Navigation", "Error creating session", e)
                            }
                        }
                    }
                },
                onDismiss = { 
                    showConnectionError = false
                    // Also show device selection option if error was device-related
                    if (errorMessage.contains("not found nearby", ignoreCase = true) && bluetoothService != null) {
                        scope.launch {
                            showDeviceSelection = true
                            nearbyDevices = bluetoothService.getNearbyDevices(15)
                        }
                    }
                }
            )
        }

        // Device Selection Dialog
        if (showDeviceSelection) {
            DeviceSelectionDialog(
                devices = nearbyDevices,
                onDeviceSelected = { macAddress ->
                    showDeviceSelection = false
                    if (connectionManager == null) {
                        // Services failed, skip connection
                        return@DeviceSelectionDialog
                    }
                    showConnectionProgress = true
                    connectionStep = ConnectionStep.SEARCHING_DEVICE
                    scope.launch {
                        connectionManager.connectToDeviceByMac(
                            macAddress = macAddress,
                            onStepChange = { step, data ->
                                connectionStep = step
                                connectionData = data
                            },
                            onError = { error ->
                                errorMessage = error
                                showConnectionProgress = false
                                showConnectionError = true
                            },
                            onSuccess = {
                                showConnectionProgress = false
                                pendingSerialNumber?.let { serial ->
                                    scope.launch {
                                        try {
                                            val operatorId = prefsManager.getOperatorId()

                                            // Ensure Device record exists
                                            repository.insertDevice(
                                                Device(
                                                    serialNumber = serial,
                                                    macId = currentMacAddress.ifBlank { "Unknown" },
                                                    color = "Unknown",
                                                    createdDate = java.text.SimpleDateFormat("yyyy-MM-dd", java.util.Locale.getDefault()).format(java.util.Date()),
                                                    finalStatus = "IN_PROGRESS"
                                                )
                                            )

                                            // Ensure Operator record exists
                                            val operator = repository.getOperatorById(operatorId)
                                            if (operator == null) {
                                                repository.insertOperator(
                                                    Operator(
                                                        id = operatorId,
                                                        name = operatorName
                                                    )
                                                )
                                            }

                                            val sessionId = repository.insertSession(
                                                FQCSession(
                                                    operatorId = operatorId,
                                                    serialNumber = serial,
                                                    status = "active"
                                                )
                                            )
                                            currentSessionId = sessionId
                                            currentSerialNumber = serial
                                            android.util.Log.d("Navigation", "Session created successfully: $sessionId")
                                        } catch (e: Exception) {
                                            android.util.Log.e("Navigation", "Error creating session", e)
                                        }
                                    }
                                }
                            }
                        )
                    }
                },
                onDismiss = { showDeviceSelection = false }
            )
        }

        // Permission Request Dialog
        if (showPermissionRequest && permissionHandler != null) {
            PermissionRequestDialog(
                missingPermissions = missingPermissions,
                permissionExplanation = permissionHandler.getPermissionExplanation(missingPermissions),
                onRequestPermissions = {
                    permissionHandler.requestBluetoothPermissions { granted ->
                        if (granted) {
                            showPermissionRequest = false
                            pendingSerialNumber?.let { serial ->
                                simpleConnect(serial)
                            }
                        } else {
                            // Permissions denied - show error
                            showPermissionRequest = false
                            errorMessage = "Bluetooth permissions are required for automatic device connection.\n\nPlease grant Location permission from App Settings to enable this feature.\n\nYou can still use the app by selecting \"Continue Anyway\" when scanning."
                            showConnectionError = true
                        }
                    }
                },
                onOpenSettings = {
                    showPermissionRequest = false
                    permissionHandler.openAppSettings()
                },
                onDismiss = {
                    showPermissionRequest = false
                    // Show error suggesting to continue without Bluetooth
                    errorMessage = "Bluetooth permissions not granted.\n\nYou can use \"Continue Anyway\" to skip automatic Bluetooth connection."
                    showConnectionError = true
                }
            )
        }

        // Serial Prefix Setup Dialog
        if (showPrefixDialog) {
            SerialPrefixSetupDialog(
                currentPrefix = serialNumberPrefix,
                onDismiss = { showPrefixDialog = false },
                onSave = { prefix ->
                    prefsManager.saveSerialNumberPrefix(prefix)
                    serialNumberPrefix = prefix
                }
            )
        }

        // Unpair Confirmation Dialog
        if (showUnpairConfirmDialog) {
            androidx.compose.material3.AlertDialog(
                onDismissRequest = { showUnpairConfirmDialog = false },
                icon = {
                    androidx.compose.material3.Text("", style = androidx.compose.material3.MaterialTheme.typography.headlineLarge)
                },
                title = { 
                    androidx.compose.material3.Text(
                        "Forget All Devices?",
                        fontWeight = androidx.compose.ui.text.font.FontWeight.Bold
                    ) 
                },
                text = { 
                    Column {
                        androidx.compose.material3.Text(
                            "This will remove ALL paired Bluetooth devices from your phone's memory.",
                            style = androidx.compose.material3.MaterialTheme.typography.bodyMedium
                        )
                        Spacer(modifier = Modifier.height(12.dp))
                        androidx.compose.material3.Text(
                            "Benefits:",
                            fontWeight = androidx.compose.ui.text.font.FontWeight.Bold,
                            style = androidx.compose.material3.MaterialTheme.typography.bodyMedium
                        )
                        androidx.compose.material3.Text(
                            " Clears device storage limit\n Fixes connection issues\n Fresh start for new pairings",
                            style = androidx.compose.material3.MaterialTheme.typography.bodySmall
                        )
                        Spacer(modifier = Modifier.height(8.dp))
                        androidx.compose.material3.Text(
                            "Are you sure?",
                            fontWeight = androidx.compose.ui.text.font.FontWeight.Bold,
                            color = androidx.compose.ui.graphics.Color(0xFFFF5722)
                        )
                    }
                },
                confirmButton = {
                    androidx.compose.material3.Button(
                        onClick = {
                            showUnpairConfirmDialog = false
                            bluetoothService?.let { service ->
                                val count = service.unpairAllDevices()
                                unpairResult = if (count > 0) {
                                    " Successfully removed $count device(s) from memory!\n\nYour device storage is now clear and ready for new connections."
                                } else {
                                    " No paired devices found.\n\nYour device memory is already clear!"
                                }
                                showUnpairResultDialog = true
                            } ?: run {
                                unpairResult = " Bluetooth service not available.\n\nPlease check Bluetooth permissions."
                                showUnpairResultDialog = true
                            }
                        },
                        colors = androidx.compose.material3.ButtonDefaults.buttonColors(
                            containerColor = androidx.compose.ui.graphics.Color(0xFFFF5722)
                        )
                    ) {
                        androidx.compose.material3.Text("Yes, Forget All", fontWeight = androidx.compose.ui.text.font.FontWeight.Bold)
                    }
                },
                dismissButton = {
                    androidx.compose.material3.OutlinedButton(
                        onClick = { showUnpairConfirmDialog = false }
                    ) {
                        androidx.compose.material3.Text("Cancel")
                    }
                }
            )
        }

        // Unpair Result Dialog
        if (showUnpairResultDialog) {
            androidx.compose.material3.AlertDialog(
                onDismissRequest = { showUnpairResultDialog = false },
                title = { androidx.compose.material3.Text("Devices Removed") },
                text = { androidx.compose.material3.Text(unpairResult) },
                confirmButton = {
                    androidx.compose.material3.TextButton(
                        onClick = { showUnpairResultDialog = false }
                    ) {
                        androidx.compose.material3.Text("OK")
                    }
                }
            )
        }

        // Long Press Speed Dialog
        if (showLongPressSpeedDialog) {
            LongPressSpeedDialog(
                currentSpeed = longPressSpeed,
                onSpeedSelected = { newSpeed ->
                    longPressSpeed = newSpeed
                    prefsManager.saveLongPressSpeed(newSpeed)
                },
                onDismiss = { showLongPressSpeedDialog = false }
            )
        }
    }
}

@OptIn(androidx.compose.material3.ExperimentalMaterial3Api::class)
@Composable
fun DatabaseViewerScreen(
    repository: FQCRepository,
    onBack: () -> Unit
) {
    val scope = rememberCoroutineScope()
    val bgColor = Color(0xFF212121)
    val surfaceColor = Color(0xFF2B2B2B)
    val accentColor = Color(0xFFFFC107)
    val textColor = Color.White
    val dividerColor = Color(0xFF424242)

    var isLoading by remember { mutableStateOf(true) }
    var expandedSections by remember { mutableStateOf<Set<String>>(setOf()) }

    // Data state
    var devices by remember { mutableStateOf<List<Device>>(emptyList()) }
    var operators by remember { mutableStateOf<List<Operator>>(emptyList()) }
    var testSessions by remember { mutableStateOf<List<com.example.fqcapp.data.entities.TestSession>>(emptyList()) }
    var frequencyTests by remember { mutableStateOf<List<com.example.fqcapp.data.entities.FrequencyTest>>(emptyList()) }
    var songTests by remember { mutableStateOf<List<com.example.fqcapp.data.entities.SongTest>>(emptyList()) }
    var fqcRecords by remember { mutableStateOf<List<com.example.fqcapp.data.entities.FQCRecord>>(emptyList()) }
    var driverPairingRecords by remember { mutableStateOf<List<com.example.fqcapp.data.entities.DriverPairingRecord>>(emptyList()) }
    var macIdCache by remember { mutableStateOf<List<com.example.fqcapp.data.entities.MacIdCache>>(emptyList()) }
    var workflowEvents by remember { mutableStateOf<List<com.example.fqcapp.data.entities.DeviceWorkflow>>(emptyList()) }

    LaunchedEffect(Unit) {
        scope.launch {
            try {
                withContext(Dispatchers.IO) {
                    // Load all data
                    devices = repository.getAllDevices().first()
                    operators = repository.getAllOperators().first()
                    testSessions = repository.getAllTestSessions()
                    frequencyTests = repository.getAllFrequencyTests()
                    songTests = repository.getAllSongTests()
                    fqcRecords = repository.getAllFQCRecords()
                    driverPairingRecords = repository.getAllDriverPairingRecords()
                    macIdCache = repository.getAllCachedMacIds()
                    // Load workflow events (get all, then sort by timestamp)
                    workflowEvents = repository.getAllWorkflowEvents().sortedByDescending { it.timestamp }

                    // Debug: Log loaded FQC records
                    android.util.Log.d("DatabaseViewer", " Loaded ${fqcRecords.size} FQC records")
                    fqcRecords.forEachIndexed { index, record ->
                        android.util.Log.d("DatabaseViewer", " FQC Record #${index + 1} (ID: ${record.fqcId}):")
                        android.util.Log.d("DatabaseViewer", "   Serial: '${record.serialNumber}'")
                        android.util.Log.d("DatabaseViewer", "   Operator: '${record.operatorName}'")
                        android.util.Log.d("DatabaseViewer", "   Final Quality Type: '${record.finalQualityType}'")
                        android.util.Log.d("DatabaseViewer", "   Criteria: '${record.criteriaChecked}'")
                        android.util.Log.d("DatabaseViewer", "   Entry: '${record.entryTimestamp}'")
                        android.util.Log.d("DatabaseViewer", "   Exit: '${record.exitTimestamp}'")
                        android.util.Log.d("DatabaseViewer", "   Time: '${record.totalTimeSpent}'")
                        android.util.Log.d("DatabaseViewer", "   Iteration: ${record.iterationNumber}")
                        android.util.Log.d("DatabaseViewer", "   FQC Type: '${record.fqcType}'")
                    }
                }
                isLoading = false
            } catch (e: Exception) {
                android.util.Log.e("DatabaseViewer", "Error loading database", e)
                isLoading = false
            }
        }
    }

    Scaffold(
        containerColor = bgColor,
        topBar = {
            TopAppBar(
                title = { Text("Database Viewer", color = textColor) },
                navigationIcon = {
                    IconButton(onClick = onBack) {
                        Icon(
                            imageVector = Icons.Filled.ArrowBack,
                            contentDescription = "Back",
                            tint = textColor
                        )
                    }
                },
                colors = TopAppBarDefaults.topAppBarColors(
                    containerColor = surfaceColor,
                    titleContentColor = textColor,
                    navigationIconContentColor = textColor
                )
            )
        }
    ) { padding ->
        if (isLoading) {
            Box(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(padding),
                contentAlignment = Alignment.Center
            ) {
                CircularProgressIndicator(color = accentColor)
            }
        } else {
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(padding)
                    .verticalScroll(rememberScrollState())
                    .padding(16.dp),
                verticalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                // Summary Card
                DatabaseSummaryCard(
                    devices = devices,
                    operators = operators,
                    testSessions = testSessions,
                    frequencyTests = frequencyTests,
                    songTests = songTests,
                    fqcRecords = fqcRecords,
                    driverPairingRecords = driverPairingRecords,
                    macIdCache = macIdCache,
                    workflowEvents = workflowEvents,
                    surfaceColor = surfaceColor,
                    textColor = textColor
                )

                // Devices Section
                DatabaseSection(
                    title = "Devices",
                    count = devices.size,
                    isExpanded = expandedSections.contains("devices"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("devices")) {
                            expandedSections - "devices"
                        } else {
                            expandedSections + "devices"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (devices.isEmpty()) {
                        Text("No devices found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        devices.forEach { device ->
                            DeviceCard(device, surfaceColor, textColor)
                        }
                    }
                }

                // Test Sessions Section
                DatabaseSection(
                    title = "Test Sessions",
                    count = testSessions.size,
                    isExpanded = expandedSections.contains("sessions"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("sessions")) {
                            expandedSections - "sessions"
                        } else {
                            expandedSections + "sessions"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (testSessions.isEmpty()) {
                        Text("No sessions found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        testSessions.forEach { session ->
                            TestSessionCard(session, surfaceColor, textColor)
                        }
                    }
                }

                // Frequency Tests Section
                DatabaseSection(
                    title = "Frequency Tests",
                    count = frequencyTests.size,
                    isExpanded = expandedSections.contains("frequency"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("frequency")) {
                            expandedSections - "frequency"
                        } else {
                            expandedSections + "frequency"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (frequencyTests.isEmpty()) {
                        Text("No frequency tests found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        frequencyTests.forEach { test ->
                            FrequencyTestCard(test, surfaceColor, textColor)
                        }
                    }
                }

                // Song Tests Section
                DatabaseSection(
                    title = "Song Tests",
                    count = songTests.size,
                    isExpanded = expandedSections.contains("songs"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("songs")) {
                            expandedSections - "songs"
                        } else {
                            expandedSections + "songs"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (songTests.isEmpty()) {
                        Text("No song tests found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        // Group song tests by serial number and iteration number
                        val groupedTests = songTests.groupBy { "${it.serialNumber}_${it.iterationNumber}" }
                        groupedTests.values.forEach { tests ->
                            // Find overall review (isOverallResult = true)
                            val overallReview = tests.find { it.isOverallResult }
                            val individualSongs = tests.filter { !it.isOverallResult }.sortedByDescending { it.timestamp }

                            if (overallReview != null) {
                                // Display overall review with all details and individual songs
                                SongTestCard(
                                    overallReview = overallReview,
                                    individualSongs = individualSongs,
                                    surfaceColor = surfaceColor,
                                    textColor = textColor
                                )
                                Spacer(modifier = Modifier.height(8.dp))
                            } else {
                                // If no overall review, just show individual songs
                                individualSongs.forEach { song ->
                                    IndividualSongCard(song, surfaceColor, textColor)
                                    Spacer(modifier = Modifier.height(4.dp))
                                }
                            }
                        }
                    }
                }

                // FQC Records Section
                DatabaseSection(
                    title = "FQC Records",
                    count = fqcRecords.size,
                    isExpanded = expandedSections.contains("fqc"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("fqc")) {
                            expandedSections - "fqc"
                        } else {
                            expandedSections + "fqc"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (fqcRecords.isEmpty()) {
                        Text("No FQC records found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        fqcRecords.forEach { record ->
                            FQCRecordCard(record, surfaceColor, textColor)
                        }
                    }
                }

                // Driver Pairing Records Section
                DatabaseSection(
                    title = "Driver Pairing Records",
                    count = driverPairingRecords.size,
                    isExpanded = expandedSections.contains("driverPairing"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("driverPairing")) {
                            expandedSections - "driverPairing"
                        } else {
                            expandedSections + "driverPairing"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (driverPairingRecords.isEmpty()) {
                        Text("No driver pairing records found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        driverPairingRecords.forEach { record ->
                            DriverPairingRecordCard(record, surfaceColor, textColor)
                        }
                    }
                }

                // Workflow Events Section (NEW - Shows audit trail)
                DatabaseSection(
                    title = "Workflow Events (Audit Trail)",
                    count = workflowEvents.size,
                    isExpanded = expandedSections.contains("workflow"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("workflow")) {
                            expandedSections - "workflow"
                        } else {
                            expandedSections + "workflow"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (workflowEvents.isEmpty()) {
                        Text("No workflow events found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        // Group by device serial for easier viewing
                        val groupedByDevice = workflowEvents.groupBy { it.serialNumber }
                        groupedByDevice.forEach { (serial, events) ->
                            Text(
                                "Device: $serial (${events.size} events)",
                                color = accentColor,
                                fontWeight = FontWeight.Bold,
                                fontSize = 13.sp,
                                modifier = Modifier.padding(vertical = 8.dp)
                            )
                            events.forEach { event ->
                                WorkflowEventCard(event, surfaceColor, textColor)
                            }
                            Spacer(modifier = Modifier.height(8.dp))
                        }
                    }
                }

                // Operators Section
                DatabaseSection(
                    title = "Operators",
                    count = operators.size,
                    isExpanded = expandedSections.contains("operators"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("operators")) {
                            expandedSections - "operators"
                        } else {
                            expandedSections + "operators"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (operators.isEmpty()) {
                        Text("No operators found", color = textColor.copy(alpha = 0.6f))
                    } else {
                        operators.forEach { operator ->
                            OperatorCard(operator, surfaceColor, textColor)
                        }
                    }
                }

                // MAC ID Cache Section
                DatabaseSection(
                    title = "MAC ID Cache",
                    count = macIdCache.size,
                    isExpanded = expandedSections.contains("mac"),
                    onExpandToggle = {
                        expandedSections = if (expandedSections.contains("mac")) {
                            expandedSections - "mac"
                        } else {
                            expandedSections + "mac"
                        }
                    },
                    surfaceColor = surfaceColor,
                    textColor = textColor,
                    accentColor = accentColor
                ) {
                    if (macIdCache.isEmpty()) {
                        Text("No MAC ID cache entries", color = textColor.copy(alpha = 0.6f))
                    } else {
                        macIdCache.forEach { cache ->
                            MacIdCacheCard(cache, surfaceColor, textColor)
                        }
                    }
                }
            }
        }
    }
}

@Composable
fun DatabaseSummaryCard(
    devices: List<Device>,
    operators: List<Operator>,
    testSessions: List<com.example.fqcapp.data.entities.TestSession>,
    frequencyTests: List<com.example.fqcapp.data.entities.FrequencyTest>,
    songTests: List<com.example.fqcapp.data.entities.SongTest>,
    fqcRecords: List<com.example.fqcapp.data.entities.FQCRecord>,
    driverPairingRecords: List<com.example.fqcapp.data.entities.DriverPairingRecord>,
    macIdCache: List<com.example.fqcapp.data.entities.MacIdCache>,
