    ) {
        Column(modifier = Modifier.padding(16.dp)) {
            Text(
                text = "Database Summary",
                style = MaterialTheme.typography.titleLarge,
                color = textColor,
                fontWeight = FontWeight.Bold
            )
            Spacer(modifier = Modifier.height(12.dp))
            DatabaseSummaryRow("Devices", devices.size.toString(), textColor)
            DatabaseSummaryRow("Test Sessions", testSessions.size.toString(), textColor)
            DatabaseSummaryRow("Frequency Tests", frequencyTests.size.toString(), textColor)
            DatabaseSummaryRow("Song Tests", songTests.size.toString(), textColor)
            DatabaseSummaryRow("FQC Records", fqcRecords.size.toString(), textColor)
            DatabaseSummaryRow("Driver Pairing Records", driverPairingRecords.size.toString(), textColor)
            DatabaseSummaryRow("Workflow Events", workflowEvents.size.toString(), textColor)
