    }
}

@Composable
fun DatabaseSummaryRow(label: String, value: String, textColor: Color) {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .padding(vertical = 4.dp),
        horizontalArrangement = Arrangement.SpaceBetween
    ) {
        Text(label, color = textColor.copy(alpha = 0.7f))
        Text(value, color = textColor, fontWeight = FontWeight.Bold)
    }
}

@Composable
fun DatabaseSection(
    title: String,
    count: Int,
    isExpanded: Boolean,
    onExpandToggle: () -> Unit,
    surfaceColor: Color,
    textColor: Color,
    accentColor: Color,
    content: @Composable () -> Unit
) {
    val dividerColor = Color(0xFF424242)
    Surface(
        modifier = Modifier.fillMaxWidth(),
        color = surfaceColor,
        shape = RoundedCornerShape(12.dp)
    ) {
        Column {
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .clickable(onClick = onExpandToggle)
                    .padding(16.dp),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = "$title ($count)",
                    style = MaterialTheme.typography.titleMedium,
                    color = textColor,
                    fontWeight = FontWeight.Bold
                )
                Icon(
                    imageVector = if (isExpanded) Icons.Filled.ExpandLess else Icons.Filled.ExpandMore,
                    contentDescription = if (isExpanded) "Collapse" else "Expand",
                    tint = accentColor
                )
            }

            if (isExpanded) {
                Divider(color = dividerColor)
                Column(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(12.dp),
                    verticalArrangement = Arrangement.spacedBy(8.dp)
                ) {
                    content()
                }
            }
        }
    }
}

@Composable
fun DeviceCard(
    device: Device, 
    surfaceColor: Color, 
    textColor: Color,
    macId: String? = null  // Optional MAC ID (derived from active PCB link if provided)
) {
    Surface(
        modifier = Modifier.fillMaxWidth(),
        color = surfaceColor.copy(alpha = 0.5f),
        shape = RoundedCornerShape(8.dp)
    ) {
        Column(modifier = Modifier.padding(12.dp)) {
            Text(
                text = device.serialNumber,
                style = MaterialTheme.typography.titleSmall,
                color = textColor,
                fontWeight = FontWeight.Bold
            )
            Spacer(modifier = Modifier.height(4.dp))
            // Use provided MAC ID (from PCB link) or fallback to deprecated device.macId
            val displayMacId = macId ?: (if (device.macId.isNotBlank()) device.macId else "N/A")
