@Composable
fun SongTestCard(
    overallReview: com.example.fqcapp.data.entities.SongTest,
    individualSongs: List<com.example.fqcapp.data.entities.SongTest>,
    surfaceColor: Color,
    textColor: Color
) {
    Surface(
        modifier = Modifier.fillMaxWidth(),
        color = surfaceColor.copy(alpha = 0.5f),
        shape = RoundedCornerShape(8.dp)
    ) {
        Column(modifier = Modifier.padding(12.dp)) {
            // Overall Review Header
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = "OVERALL HEADPHONE REVIEW",
                    style = MaterialTheme.typography.titleSmall,
                    color = textColor,
                    fontWeight = FontWeight.Bold
                )
                Text(
                    text = overallReview.result,
                    color = when (overallReview.result.uppercase()) {
                        "PASS" -> Color(0xFF4CAF50)
                        "CAN_BE_PASSED", "CAN_PASS" -> Color(0xFFFFC107)
                        else -> Color(0xFFFF5722)
                    },
                    fontWeight = FontWeight.Bold,
                    fontSize = 12.sp
                )
            }

            Divider(modifier = Modifier.padding(vertical = 8.dp), color = textColor.copy(alpha = 0.3f))

            // Basic Info
            Text("Serial: ${overallReview.serialNumber}", color = textColor.copy(alpha = 0.9f), fontSize = 12.sp, fontWeight = FontWeight.Medium)
            Text("Operator: ${overallReview.operatorName}", color = textColor.copy(alpha = 0.9f), fontSize = 12.sp, fontWeight = FontWeight.Medium)
            Text("Iteration: ${overallReview.iterationNumber}", color = textColor.copy(alpha = 0.9f), fontSize = 12.sp, fontWeight = FontWeight.Medium)

            Spacer(modifier = Modifier.height(4.dp))

            // Entry/Exit Timestamps and Total Time
            if (overallReview.entryTimestamp.isNotBlank()) {
                Text("Entry: ${overallReview.entryTimestamp}", color = textColor.copy(alpha = 0.8f), fontSize = 11.sp)
            }
            if (overallReview.exitTimestamp.isNotBlank()) {
                Text("Exit: ${overallReview.exitTimestamp}", color = textColor.copy(alpha = 0.8f), fontSize = 11.sp)
            }
            if (overallReview.totalTimeSpent.isNotBlank() && overallReview.totalTimeSpent != "00:00:00") {
                Text("Time Spent: ${overallReview.totalTimeSpent}", color = textColor.copy(alpha = 0.8f), fontSize = 11.sp, fontWeight = FontWeight.Medium)
            }

            Spacer(modifier = Modifier.height(4.dp))

            // Result
            Text("Result: ${overallReview.result}", 
                color = when (overallReview.result.uppercase()) {
                    "PASS" -> Color(0xFF4CAF50)
                    "CAN_BE_PASSED", "CAN_PASS" -> Color(0xFFFFC107)
                    else -> Color(0xFFFF5722)
                },
                fontSize = 12.sp,
                fontWeight = FontWeight.Bold
            )

            // Issues
            if (overallReview.issues.isNotBlank() && overallReview.issues != "[]") {
                Spacer(modifier = Modifier.height(4.dp))
                Text("Issues:", color = textColor.copy(alpha = 0.9f), fontSize = 11.sp, fontWeight = FontWeight.Medium)
                Text(overallReview.issues, color = textColor.copy(alpha = 0.8f), fontSize = 11.sp)
            }

            // Solutions
            if (overallReview.solutions.isNotBlank() && overallReview.solutions != "[]") {
                Spacer(modifier = Modifier.height(4.dp))
                Text("Solutions:", color = textColor.copy(alpha = 0.9f), fontSize = 11.sp, fontWeight = FontWeight.Medium)
                Text(overallReview.solutions, color = textColor.copy(alpha = 0.8f), fontSize = 11.sp)
            }

            // Remark
            if (overallReview.remark.isNotBlank()) {
                Spacer(modifier = Modifier.height(4.dp))
                Text("Remark:", color = textColor.copy(alpha = 0.9f), fontSize = 11.sp, fontWeight = FontWeight.Medium)
                Text(overallReview.remark, color = textColor.copy(alpha = 0.8f), fontSize = 11.sp)
            }

            // Individual Songs Tested
            if (individualSongs.isNotEmpty()) {
                Spacer(modifier = Modifier.height(8.dp))
                Divider(modifier = Modifier.padding(vertical = 4.dp), color = textColor.copy(alpha = 0.3f))
                Text(
                    "Individual Songs Tested (${individualSongs.size}):",
                    color = textColor.copy(alpha = 0.9f),
                    fontSize = 12.sp,
                    fontWeight = FontWeight.Bold
                )
                Spacer(modifier = Modifier.height(4.dp))
                individualSongs.forEachIndexed { index, song ->
                    IndividualSongCard(song, surfaceColor.copy(alpha = 0.3f), textColor)
                    if (index < individualSongs.size - 1) {
                        Spacer(modifier = Modifier.height(4.dp))
                    }
                }
            } else {
                Spacer(modifier = Modifier.height(4.dp))
                Text("No individual songs tested", color = textColor.copy(alpha = 0.5f), fontSize = 11.sp, fontStyle = FontStyle.Italic)
            }
        }
    }
}

