@Composable
fun WorkflowEventCard(event: com.example.fqcapp.data.entities.DeviceWorkflow, surfaceColor: Color, textColor: Color) {
    Surface(
        modifier = Modifier.fillMaxWidth(),
        color = surfaceColor.copy(alpha = 0.5f),
        shape = RoundedCornerShape(8.dp)
    ) {
        Column(modifier = Modifier.padding(12.dp)) {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Column {
                    Text(
                        text = event.stage,
                        style = MaterialTheme.typography.titleSmall,
                        color = textColor,
                        fontWeight = FontWeight.Bold
                    )
                    Text(
                        text = event.action,
                        color = when (event.action) {
                            "STARTED" -> Color(0xFF2196F3) // Blue
                            "COMPLETED" -> Color(0xFF4CAF50) // Green
                            "FAILED" -> Color(0xFFFF5722) // Red
                            else -> textColor.copy(alpha = 0.7f)
                        },
                        fontSize = 11.sp,
                        fontWeight = FontWeight.Medium
                    )
                }
                if (event.result != null) {
                    Text(
                        text = event.result,
                        color = when (event.result.uppercase()) {
                            "PASS" -> Color(0xFF4CAF50) // Green
                            "FAIL" -> Color(0xFFFF5722) // Red
                            "CAN_PASS", "CAN_BE_PASSED" -> Color(0xFFFFC107) // Yellow
                            else -> textColor
                        },
                        fontSize = 12.sp,
                        fontWeight = FontWeight.Bold
                    )
                }
            }

            Spacer(modifier = Modifier.height(4.dp))
            Divider(color = textColor.copy(alpha = 0.3f))
            Spacer(modifier = Modifier.height(4.dp))

            // Operator and Serial
            Text("Operator: ${event.operatorName}", color = textColor.copy(alpha = 0.9f), fontSize = 12.sp, fontWeight = FontWeight.Medium)
            Text("Serial: ${event.serialNumber}", color = textColor.copy(alpha = 0.9f), fontSize = 12.sp, fontWeight = FontWeight.Medium)

            Spacer(modifier = Modifier.height(4.dp))

            // Iteration and Timestamp
            if (event.iterationNumber > 0) {
                Text("Iteration: ${event.iterationNumber}", color = textColor.copy(alpha = 0.8f), fontSize = 11.sp)
            }

            // Format timestamp
            val timestampFormat = java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.getDefault())
            val timestampStr = timestampFormat.format(java.util.Date(event.timestamp))
            Text("Time: $timestampStr", color = textColor.copy(alpha = 0.7f), fontSize = 11.sp)

            // Milestone Status (if available)
            if (event.milestoneStatus != null) {
                Spacer(modifier = Modifier.height(4.dp))
                Text(
                    text = "Milestone: ${event.milestoneStatus}",
                    color = when (event.milestoneStatus) {
                        "pass" -> Color(0xFF4CAF50)
                        "fail" -> Color(0xFFFF5722)
                        "can_pass" -> Color(0xFFFFC107)
                        else -> textColor.copy(alpha = 0.6f)
                    },
                    fontSize = 11.sp,
                    fontWeight = FontWeight.Medium
                )
            }

            // Record ID (if available)
            if (event.recordId != null) {
                Text("Record ID: ${event.recordId}", color = textColor.copy(alpha = 0.6f), fontSize = 10.sp)
            }

            // Sync status
            if (event.syncedToSupabase) {
                Text(" Synced to Supabase", color = Color(0xFF4CAF50), fontSize = 10.sp)
            } else {
                Text(" Not synced", color = Color(0xFFFFC107), fontSize = 10.sp)
            }
        }
    }
}

@Composable
private fun AppNavGraph(
    nav: NavHostController,
    operatorName: String,
    currentSerialNumber: String?,
    currentMacAddress: String,
    connectedDeviceName: String?,
    serialNumberPrefix: String,
    longPressSpeed: Int,
    onLogout: () -> Unit,
    onOpenDrawer: () -> Unit,
    onSerialNumberScanned: (String, String?) -> Unit,  // (serialNumber, macIdOverride)
    currentSessionId: Long?,
    repository: FQCRepository,
    paddingValues: androidx.compose.foundation.layout.PaddingValues,
    activity: ComponentActivity,
    permissionHandler: PermissionHandler?,
    bluetoothService: BluetoothService?,
    onMacDetected: (String) -> Unit,
    dataUploadStatus: String,
    macIdCacheManager: MacIdCacheManager,
    onForgetCurrentDevice: () -> Unit,
    undoManager: com.example.fqcapp.core.UndoManager,
    solutionReminderManager: SolutionReminderManager
) {
    val context = LocalContext.current
    val database = remember { AppDatabase.getDatabase(context) }
    val prefsManager = remember { PreferencesManager(context) }
    val scope = rememberCoroutineScope()
    val eligibilityService = remember { DeviceEligibilityService(repository, database) }
    // Create DriverPairingCacheManager for centralized Google Sheets access
    val driverPairingSheetsService = remember { DriverPairingSheetsService(context) }
    val driverPairingCacheManager = remember { DriverPairingCacheManager(
        database.driverPairingCacheDao(),
        driverPairingSheetsService
    ) }

    // Capture callback for use in LaunchedEffect
    val onSerialScanned = onSerialNumberScanned

    // Track current route for loading state management
    val navBackStackEntry by nav.currentBackStackEntryAsState()
    val currentRoute = navBackStackEntry?.destination?.route ?: Routes.LANDING

    // Eligibility dialog state
    var showEligibilityDialog by remember { mutableStateOf(false) }
    var eligibilityMessage by remember { mutableStateOf("") }
    var eligibilityRequiresDriverPairing by remember { mutableStateOf(false) }
    var eligibilityRequiresFrequencyTest by remember { mutableStateOf(false) }
    var eligibilityRequiresSongTest by remember { mutableStateOf(false) }
    var pendingNavigationRoute by remember { mutableStateOf<String?>(null) }

    // Loading state for skeleton screen (only for FQC eligibility check)
    var isLoadingFQC by remember { mutableStateOf(false) }

    // Shared state for authenticated operators
    var authenticatedOperatorForDriverPairing by remember { mutableStateOf<com.example.fqcapp.data.entities.OperatorCredential?>(null) }
    var authenticatedOperatorForChangedPart by remember { mutableStateOf<com.example.fqcapp.data.entities.OperatorCredential?>(null) }
    var authenticatedOperatorForFreqGen by remember { mutableStateOf<com.example.fqcapp.data.entities.OperatorCredential?>(null) }
    var authenticatedOperatorForTestSongs by remember { mutableStateOf<com.example.fqcapp.data.entities.OperatorCredential?>(null) }
    var authenticatedOperatorForFQC by remember { mutableStateOf<com.example.fqcapp.data.entities.OperatorCredential?>(null) }

    // Authentication dialog states
    var showAuthDialogForFreqGen by remember { mutableStateOf(false) }
    var showAuthDialogForTestSongs by remember { mutableStateOf(false) }
    var showAuthDialogForFQC by remember { mutableStateOf(false) }

    // Helper function to create operator credential from preferences (when password lock is disabled)
    fun createOperatorFromPreferences(): com.example.fqcapp.data.entities.OperatorCredential? {
        val operatorName = prefsManager.getOperatorName()
        val operatorId = prefsManager.getOperatorId()
        return if (operatorName != null && operatorName.isNotBlank() && operatorId != -1L) {
            com.example.fqcapp.data.entities.OperatorCredential(
                operatorId = operatorId.toString(),
                operatorName = operatorName,
                password = "" // Not needed when password lock is disabled
            )
        } else {
            null
        }
    }

    // Authentication dialogs (only when password lock is ON - shown as overlay)
    // When password lock is OFF, we navigate to OPERATOR_SELECTION route instead
    val passwordLockEnabled = prefsManager.isPasswordLockEnabled()
    android.util.Log.d("Navigation", " Checking password lock: enabled=$passwordLockEnabled, showFreqGen=$showAuthDialogForFreqGen, showTestSongs=$showAuthDialogForTestSongs, showFQC=$showAuthDialogForFQC")

    // Only show dialogs when password lock is enabled
    if (showAuthDialogForFreqGen && passwordLockEnabled) {
        android.util.Log.d("Navigation", " Showing auth dialog for FreqGen (password lock ON)")
        com.example.fqcapp.ui.OperatorAuthenticationDialog(
            onAuthenticated = { operator ->
                authenticatedOperatorForFreqGen = operator
                showAuthDialogForFreqGen = false
                // Navigate directly - no eligibility check needed for Frequency
                nav.navigate(Routes.FREQ_GEN)
            },
            onCancel = {
                showAuthDialogForFreqGen = false
            },
            requirePassword = true
        )
    }

    if (showAuthDialogForTestSongs && passwordLockEnabled) {
        android.util.Log.d("Navigation", " Showing auth dialog for TestSongs (password lock ON)")
        com.example.fqcapp.ui.OperatorAuthenticationDialog(
            onAuthenticated = { operator ->
                authenticatedOperatorForTestSongs = operator
                showAuthDialogForTestSongs = false
                // Navigate directly - no eligibility check needed for Test Songs
                nav.navigate(Routes.TEST_SONGS)
            },
            onCancel = {
                showAuthDialogForTestSongs = false
            },
            requirePassword = true
        )
    }

    if (showAuthDialogForFQC && passwordLockEnabled) {
        android.util.Log.d("Navigation", " Showing auth dialog for FQC (password lock ON)")
        com.example.fqcapp.ui.OperatorAuthenticationDialog(
            onAuthenticated = { operator ->
                authenticatedOperatorForFQC = operator
                showAuthDialogForFQC = false
                // Check eligibility before navigating
                val serialNumber = currentSerialNumber ?: connectedDeviceName ?: ""
                if (serialNumber.isNotBlank()) {
                    isLoadingFQC = true
                    scope.launch {
                        try {
                            val eligibility = eligibilityService.canEnterTestPage(serialNumber, "fqc", currentMacAddress, connectedDeviceName, driverPairingCacheManager)
                            isLoadingFQC = false
                            if (eligibility.canProceed) {
                                nav.navigate(Routes.DEVICE_CONFIG)
                            } else {
                                eligibilityMessage = eligibility.reason ?: "Test not available."
                                eligibilityRequiresDriverPairing = eligibility.requiresDriverPairing
                                eligibilityRequiresFrequencyTest = eligibility.requiresFrequencyTest
                                eligibilityRequiresSongTest = eligibility.requiresSongTest
                                pendingNavigationRoute = Routes.DEVICE_CONFIG
                                showEligibilityDialog = true
                            }
                        } catch (e: Exception) {
                            isLoadingFQC = false
                            android.util.Log.e("Navigation", "Error checking eligibility", e)
                        }
                    }
                } else {
                    nav.navigate(Routes.DEVICE_CONFIG)
                }
            },
            onCancel = {
                showAuthDialogForFQC = false
                isLoadingFQC = false
            },
            requirePassword = true
        )
    }

    // Eligibility dialog
    if (showEligibilityDialog) {
        TestEligibilityDialog(
            message = eligibilityMessage,
            requiresDriverPairing = eligibilityRequiresDriverPairing,
            requiresFrequencyTest = eligibilityRequiresFrequencyTest,
            requiresSongTest = eligibilityRequiresSongTest,
            onEnterDriverPairing = {
                nav.navigate(Routes.DRIVER_PAIRING)
            },
            onEnterFrequencyTest = if (eligibilityRequiresFrequencyTest) {
                {
                    if (prefsManager.isPasswordLockEnabled()) {
                        showAuthDialogForFreqGen = true
                    } else {
                        nav.navigate("${Routes.OPERATOR_SELECTION}/${Routes.FREQ_GEN}")
                    }
                }
            } else null,
            onEnterSongTest = if (eligibilityRequiresSongTest) {
                {
                    if (prefsManager.isPasswordLockEnabled()) {
                        showAuthDialogForTestSongs = true
                    } else {
                        nav.navigate("${Routes.OPERATOR_SELECTION}/${Routes.TEST_SONGS}")
                    }
                }
            } else null,
            onDismiss = {
                showEligibilityDialog = false
                pendingNavigationRoute = null
                // Clear loading state when dialog is dismissed
                isLoadingFQC = false
            }
        )
    }

    // Clear loading state when navigation happens
    LaunchedEffect(currentRoute) {
        if (currentRoute == Routes.DEVICE_CONFIG || currentRoute == Routes.FQC_REVIEW) {
            isLoadingFQC = false
        }
    }

    // Wrap NavHost and skeleton screen in Box for proper overlay
    Box(modifier = Modifier.fillMaxSize()) {
        NavHost(navController = nav, startDestination = Routes.LANDING) {
        // Database Viewer
        composable(Routes.DATABASE_VIEWER) {
            DatabaseViewerScreen(
                repository = repository,
                onBack = { nav.popBackStack() }
            )
        }

        // Landing
        composable(Routes.LANDING) {
            var hasPermissions by remember {
                mutableStateOf(permissionHandler?.hasAllBluetoothPermissions() ?: true)
            }

            // Workaround: Use a local state that tracks isReferenceSerial
            // Since isReferenceSerial is not accessible directly, we'll derive it from currentSerialNumber
            val isRefSerial = remember(currentSerialNumber) {
                currentSerialNumber?.startsWith("TMP-") ?: false
            }

            // Probe existing BT connection on first composition
            LaunchedEffect(Unit) {
                if (bluetoothService != null) {
                    android.util.Log.d("Navigation", "Probing for currently connected device on startup...")
                    val connected = bluetoothService.probeCurrentlyConnected()
                    if (connected != null) {
                        android.util.Log.d("Navigation", "Found connected device on startup: ${connected.name} - ${connected.macAddress}")
                        onMacDetected(connected.macAddress)
                        // Perform reverse lookup: MAC ID -> Serial Number
                        val currentSerial = currentSerialNumber
                        if (currentSerial == null && connected.macAddress.isNotBlank()) {
                            android.util.Log.d("Navigation", " Performing reverse lookup on startup: MAC ID -> Serial Number")
                            android.util.Log.d("Navigation", "   MAC ID: ${connected.macAddress}")
                            try {
                                // Step 1: Check MAC ID cache (for printed serials)
                                val serialFromMac = macIdCacheManager.getSerialNumberByMacId(connected.macAddress)
                                if (serialFromMac != null && serialFromMac.isNotBlank()) {
                                    android.util.Log.d("Navigation", " Found serial number from MAC ID cache: $serialFromMac")
                                    onSerialNumberScanned(serialFromMac, null)
                                } else {
                                    // Step 2: Check PCB links (for devices after PCB replacement)
                                    // This handles case where PCB was replaced - MAC changed but device is same
                                    val deviceFromPcbLink = repository.getDeviceByMacIdThroughPcbLink(connected.macAddress)
                                    if (deviceFromPcbLink != null) {
                                        android.util.Log.d("Navigation", " Found existing device through PCB link: ${deviceFromPcbLink.serialNumber}")
                                        android.util.Log.d("Navigation", "   Device was found via PCB link (PCB may have been replaced)")
                                        // Use existing device serial (could be printed or reference serial)
                                        onSerialNumberScanned(deviceFromPcbLink.serialNumber, null)
                                    } else {
                                        // Step 3: Device doesn't exist - generate new reference serial
                                        val referenceSerial = SerialGenerator.generateSystemSerial(connected.macAddress)
                                        android.util.Log.w("Navigation", " Serial number not found for MAC ID: ${connected.macAddress}")
                                        android.util.Log.w("Navigation", "   Generating new reference serial: $referenceSerial")
                                        // Use onSerialNumberScanned - it will detect TMP- prefix and set isReferenceSerial = true
                                        onSerialNumberScanned(referenceSerial, connected.macAddress)
                                        android.util.Log.d("Navigation", " Using new reference serial on startup: $referenceSerial")
                                    }
                                }
                            } catch (e: Exception) {
                                android.util.Log.e("Navigation", " Error during MAC ID reverse lookup on startup", e)
                                // On error, try PCB link lookup as fallback
                                try {
                                    val deviceFromPcbLink = repository.getDeviceByMacIdThroughPcbLink(connected.macAddress)
                                    if (deviceFromPcbLink != null) {
                                        android.util.Log.d("Navigation", " Found device through PCB link (fallback): ${deviceFromPcbLink.serialNumber}")
                                        onSerialNumberScanned(deviceFromPcbLink.serialNumber, null)
                                    } else {
                                        // Last resort: generate reference serial
                                        val referenceSerial = SerialGenerator.generateSystemSerial(connected.macAddress)
                                        onSerialNumberScanned(referenceSerial, connected.macAddress)
                                        android.util.Log.d("Navigation", " Generated reference serial on error: $referenceSerial")
                                    }
                                } catch (e2: Exception) {
                                    android.util.Log.e("Navigation", " Error in PCB link lookup fallback", e2)
                                    val referenceSerial = SerialGenerator.generateSystemSerial(connected.macAddress)
                                    onSerialNumberScanned(referenceSerial, connected.macAddress)
                                    android.util.Log.d("Navigation", " Generated reference serial on error: $referenceSerial")
                                }
                            }
                        }
                    } else {
                        android.util.Log.d("Navigation", "No connected device found on startup")
                    }
                }
            }

            LandingScreen(
                operatorName = operatorName,
                currentSerialNumber = currentSerialNumber,
                currentMacAddress = currentMacAddress,
                currentDeviceName = connectedDeviceName,
                isReferenceSerial = isRefSerial,
                onOpenScanner = { nav.navigate(Routes.SCANNER) },
                onOpenFreqGen = { 
                    // Navigate directly - no eligibility check needed for Frequency
                    if (prefsManager.isPasswordLockEnabled()) {
                        showAuthDialogForFreqGen = true
                    } else {
                        nav.navigate("${Routes.OPERATOR_SELECTION}/${Routes.FREQ_GEN}")
                    }
                },
                onOpenTestSongs = { 
                    // Navigate directly - no eligibility check needed for Test Songs
                    if (prefsManager.isPasswordLockEnabled()) {
                        showAuthDialogForTestSongs = true
                    } else {
                        nav.navigate("${Routes.OPERATOR_SELECTION}/${Routes.TEST_SONGS}")
                    }
                },
                onOpenReview = { nav.navigate(Routes.REVIEW) },
                onOpenMore = { 
                    // Only use actual serial number, not device name
                    val serialNumber = currentSerialNumber ?: ""
                    if (serialNumber.isBlank()) {
                        // No serial number - allow navigation (will be handled by screen)
                        if (prefsManager.isPasswordLockEnabled()) {
                            showAuthDialogForFQC = true
                        } else {
                            nav.navigate("${Routes.OPERATOR_SELECTION}/${Routes.DEVICE_CONFIG}")
                        }
                    } else {
                        // Show skeleton loading screen
                        isLoadingFQC = true
                        // Check eligibility before navigation
                        scope.launch {
                            try {
                                val eligibility = eligibilityService.canEnterTestPage(serialNumber, "fqc", currentMacAddress, connectedDeviceName, driverPairingCacheManager)
                                isLoadingFQC = false // Hide loading once check completes
                                if (eligibility.canProceed) {
                                    if (prefsManager.isPasswordLockEnabled()) {
                                        showAuthDialogForFQC = true
                                    } else {
                                        nav.navigate("${Routes.OPERATOR_SELECTION}/${Routes.DEVICE_CONFIG}")
                                    }
                                } else {
                                    eligibilityMessage = eligibility.reason ?: "Test not available."
                                    eligibilityRequiresDriverPairing = eligibility.requiresDriverPairing
                                    eligibilityRequiresFrequencyTest = eligibility.requiresFrequencyTest
                                    eligibilityRequiresSongTest = eligibility.requiresSongTest
                                    pendingNavigationRoute = Routes.DEVICE_CONFIG
                                    showEligibilityDialog = true
                                }
                            } catch (e: Exception) {
                                isLoadingFQC = false // Hide loading on error
                                android.util.Log.e("Navigation", "Error checking eligibility for FQC", e)
                                eligibilityMessage = "Error checking eligibility: ${e.message}"
                                showEligibilityDialog = true
                            }
                        }
                    }
                },
                onOpenDeviceInfo = {
                    if (currentSerialNumber != null || currentMacAddress.isNotBlank()) {
                        nav.navigate(Routes.DEVICE_INFORMATION)
                    }
                },
                onLogout = onLogout,
                onOpenDrawer = onOpenDrawer,
                paddingValues = paddingValues,
                hasBluetoothPermissions = hasPermissions,
                onRequestPermissions = {
                    permissionHandler?.requestBluetoothPermissions { granted ->
                        hasPermissions = granted
                    }
                },
                dataUploadStatus = dataUploadStatus,
                onForgetCurrentDevice = onForgetCurrentDevice,
                undoManager = undoManager,
                solutionReminderManager = solutionReminderManager,
                onNavigateToDriverPairing = {
                    // Navigate to Driver Pairing screen
                    nav.navigate(Routes.DRIVER_PAIRING) {
                        popUpTo(Routes.LANDING) { inclusive = false }
                    }
                },
                onNavigateToChangedParts = {
                    // Navigate to Changed Parts screen
                    nav.navigate(Routes.CHANGED_PART) {
                        popUpTo(Routes.LANDING) { inclusive = false }
                    }
                }
            )
        }

        // Operator Selection Screen (used when password lock is OFF)
        composable("${Routes.OPERATOR_SELECTION}/{destination}") { backStackEntry ->
            val destination = backStackEntry.arguments?.getString("destination") ?: Routes.LANDING
            android.util.Log.d("Navigation", " OperatorSelection route: destination=$destination")

            com.example.fqcapp.ui.OperatorSelectionScreen(
                onOperatorSelected = { operator ->
                    android.util.Log.d("Navigation", " Operator selected: ${operator.operatorName}, navigating to: $destination")
                    // Set the authenticated operator based on destination
                    when (destination) {
                        Routes.FREQ_GEN -> authenticatedOperatorForFreqGen = operator
                        Routes.TEST_SONGS -> authenticatedOperatorForTestSongs = operator
                        Routes.FQC_REVIEW, Routes.DEVICE_CONFIG -> authenticatedOperatorForFQC = operator
                        Routes.DRIVER_PAIRING, "driver_pairing" -> authenticatedOperatorForDriverPairing = operator
                        Routes.CHANGED_PART, "changed_part" -> authenticatedOperatorForChangedPart = operator
                    }
                    // Navigate to the destination, replacing operator selection in back stack
                    nav.navigate(destination) {
                        popUpTo(Routes.OPERATOR_SELECTION) { inclusive = true }
                    }
                },
                onCancel = {
                    android.util.Log.d("Navigation", " Cancelled operator selection, going back")
                    nav.popBackStack()
                }
            )
        }

        // Scanner with dialog
        composable(Routes.SCANNER) {
            // Track MAC ID override for PCB changes
            var pcbChangeMacId by remember { mutableStateOf<String?>(null) }

            ScannerScreen(
                serialNumberPrefix = serialNumberPrefix,
                onBack = { nav.popBackStack() },
                onScanned = { serialNumber, macIdOverride ->
                    // Use MAC ID override if provided (from PCB change), otherwise null (will lookup from Sheets)
                    val macIdToUse = macIdOverride ?: pcbChangeMacId
                    if (macIdToUse != null) {
                        android.util.Log.d("Navigation", " [PCB_CHANGE] Using new MAC ID: $macIdToUse for serial: $serialNumber")
                        pcbChangeMacId = null // Reset after use
                    }
                    onSerialNumberScanned(serialNumber, macIdToUse)
                    nav.popBackStack() // Return to landing after scanning
                },
                onMacIdUpdate = { newMacId ->
                    // Store MAC ID for PCB change - will be used when onScanned is called
                    pcbChangeMacId = newMacId
                    android.util.Log.d("Navigation", " [PCB_CHANGE] MAC ID stored for use: $newMacId")
                }
            )
        }

        // Review screen
        composable(Routes.REVIEW) {
            ReviewScreen(
                scannedData = currentSerialNumber,
                onBack = { nav.popBackStack() }
            )
        }

        // Test Songs
        composable(Routes.TEST_SONGS) {
            TestSongScreen(
                currentSessionId = currentSessionId,
                repository = repository,
                currentSerialNumber = currentSerialNumber ?: "",
                currentOperatorName = operatorName,
                authenticatedOperator = authenticatedOperatorForTestSongs,
                connectedDeviceName = connectedDeviceName,
                onBack = { 
                    authenticatedOperatorForTestSongs = null
                    nav.popBackStack() 
                },
                onNavigateToLanding = {
                    // Explicitly navigate to Landing Page, clearing back stack
                    nav.navigate(Routes.LANDING) {
                        popUpTo(Routes.LANDING) { inclusive = true }
                    }
                },
                onNavigateToTestSongs = {
                    // Explicitly navigate to Test Songs screen (used for undo navigation)
                    nav.navigate(Routes.TEST_SONGS) {
                        // Clear back stack to Landing Page to ensure clean navigation
                        popUpTo(Routes.LANDING) { inclusive = false }
                    }
                },
                undoManager = undoManager,
                solutionReminderManager = solutionReminderManager
            )
        }

        // Frequency Generator
        composable(Routes.FREQ_GEN) {
            FrequencyGeneratorScreen(
                currentSessionId = currentSessionId,
                repository = repository,
                initialSerialNumber = currentSerialNumber, // Pass serial number from Navigation
                authenticatedOperator = authenticatedOperatorForFreqGen,
                connectedDeviceName = connectedDeviceName, // Pass device name from Bluetooth
                onBack = { 
                    authenticatedOperatorForFreqGen = null
                    nav.popBackStack() 
                },
                onNavigateToLanding = {
                    // Explicitly navigate to Landing Page, clearing back stack
                    nav.navigate(Routes.LANDING) {
                        popUpTo(Routes.LANDING) { inclusive = true }
                    }
                },
                onNavigateToFreqGen = {
                    // Explicitly navigate to Frequency Generator (used for undo navigation)
                    nav.navigate(Routes.FREQ_GEN) {
                        // Clear back stack to Landing Page to ensure clean navigation
                        popUpTo(Routes.LANDING) { inclusive = false }
                    }
                },
                undoManager = undoManager,
                solutionReminderManager = solutionReminderManager
            )
        }

        // Device Config (before FQC)
        composable(Routes.DEVICE_CONFIG) {
            val initialSerialNumber = currentSerialNumber ?: connectedDeviceName ?: ""
            val initialMacId = currentMacAddress

            android.util.Log.d("Navigation", " [DEVICE_CONFIG] Navigating to Device Config with serial: '$initialSerialNumber'")

            DeviceConfigScreen(
                serialNumber = initialSerialNumber,
                macId = initialMacId,
                operatorName = operatorName,
                authenticatedOperator = authenticatedOperatorForFQC,
                onBack = {
                    authenticatedOperatorForFQC = null
                    nav.popBackStack()
                },
                onNext = { deviceType, remark ->
                    // Store device type and remark temporarily (can be passed to FQC or stored)
                    android.util.Log.d("Navigation", " [DEVICE_CONFIG] Device Type: $deviceType, Remark: $remark")
                    // Navigate to FQC Review
                    nav.navigate(Routes.FQC_REVIEW) {
                        // Pass device type and remark as arguments or store in state
                        // For now, we'll just navigate - FQC can access these if needed
                    }
                }
            )
        }

        // FQC Review (from More button)
        composable(Routes.FQC_REVIEW) {
            // Use currentSerialNumber, with fallback to connectedDeviceName
            // The FQCReviewScreen will handle validation and logging if serial is empty
            val initialSerialNumber = currentSerialNumber ?: connectedDeviceName ?: ""

            android.util.Log.d("Navigation", " [FQC] Navigating to FQC Review with serial: '$initialSerialNumber'")

            FQCReviewScreen(
                operatorName = operatorName,
                serialNumber = initialSerialNumber,
                authenticatedOperator = authenticatedOperatorForFQC,
                solutionReminderManager = solutionReminderManager,
                onBack = { 
                    authenticatedOperatorForFQC = null
                    nav.popBackStack() 
                },
                onSuccess = {
                    authenticatedOperatorForFQC = null
                    nav.navigate(Routes.LANDING) {
                        popUpTo(Routes.LANDING) { inclusive = true }
                    }
                }
            )
        }

        // Device Information Screen
        composable(Routes.DEVICE_INFORMATION) {
            DeviceInformationScreen(
                serialNumber = currentSerialNumber ?: "",
                macId = currentMacAddress,
                deviceName = connectedDeviceName,
                onBack = { nav.popBackStack() },
                onNavigateToDriverPairing = { operator ->
                    authenticatedOperatorForDriverPairing = operator
                    nav.navigate(Routes.DRIVER_PAIRING)
                },
                onNavigateToChangedPart = { operator ->
                    authenticatedOperatorForChangedPart = operator
                    nav.navigate(Routes.CHANGED_PART)
                },
                passwordLockEnabled = prefsManager.isPasswordLockEnabled(),
                onNavigateToOperatorSelection = { destination ->
                    // Navigate to operator selection route when password lock is OFF
                    nav.navigate("${Routes.OPERATOR_SELECTION}/$destination")
                }
            )
        }

        // Driver Pairing Information
        composable(Routes.DRIVER_PAIRING) {
            DriverPairingScreen(
                serialNumber = currentSerialNumber ?: "",
                macId = currentMacAddress,
                deviceName = connectedDeviceName,
                authenticatedOperator = authenticatedOperatorForDriverPairing,
                onBack = { 
                    authenticatedOperatorForDriverPairing = null // Clear on back
                    nav.popBackStack() 
                },
                onSave = { drivers ->
                    // Data is already saved to Google Sheets in DriverPairingScreen
                    // This callback is called after successful save
                    android.util.Log.d("Navigation", " Driver pairing data saved successfully")
                },
                onNavigateToChangedPart = {},
                solutionReminderManager = solutionReminderManager
            )
        }

        // Changed Part Screen
        composable(Routes.CHANGED_PART) {
            val localContext = LocalContext.current
            val localScope = rememberCoroutineScope()

            ChangedPartScreen(
                serialNumber = currentSerialNumber ?: "",
                authenticatedOperator = authenticatedOperatorForChangedPart,
                onBack = { 
                    authenticatedOperatorForChangedPart = null // Clear on back
                    nav.popBackStack() 
                },
                onPcbSerialNumberChanged = { newSerialNumber ->
                    // Update MAC ID when PCB serial number changes
                    localScope.launch {
                        try {
                            android.util.Log.d("Navigation", " [PCB_CHANGE] Updating MAC ID for new serial: $newSerialNumber")
                            val sheetsService = GoogleSheetsService(localContext, repository.getMacIdCacheDao())
                            val newMacId = sheetsService.getMacIdBySerialNumber(newSerialNumber)

                            if (newMacId != null) {
                                android.util.Log.d("Navigation", " [PCB_CHANGE] New MAC ID: $newMacId")
                                // Note: State update will require screen refresh
                                // The MAC ID lookup is successful, user should navigate back and reconnect
                                android.widget.Toast.makeText(
                                    localContext,
                                    "PCB changed! New MAC ID: $newMacId. Please navigate back and reconnect to see updated data.",
                                    android.widget.Toast.LENGTH_LONG
                                ).show()
                            } else {
                                android.util.Log.w("Navigation", " [PCB_CHANGE] MAC ID not found for serial: $newSerialNumber")
                                android.widget.Toast.makeText(
                                    localContext,
                                    "Warning: MAC ID not found for new serial number",
                                    android.widget.Toast.LENGTH_LONG
                                ).show()
                            }
                        } catch (e: Exception) {
                            android.util.Log.e("Navigation", " [PCB_CHANGE] Error updating MAC ID", e)
                        }
                    }
                },
                solutionReminderManager = solutionReminderManager
            )
        }
        } // Close NavHost

        // Skeleton loading screen - show while checking FQC eligibility (overlay on top)
        if (isLoadingFQC) {
            SkeletonLoadingScreen()
        }
    }
}

package com.example.fqcapp.services

import android.util.Log
import com.example.fqcapp.data.FQCRepository
import com.example.fqcapp.data.entities.DeviceWorkflow
import com.example.fqcapp.data.entities.MilestoneEntry
import kotlinx.coroutines.CancellationException
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.SupervisorJob
import kotlinx.coroutines.launch
import kotlinx.coroutines.withContext

/**
 * Service to log device workflow events and integrate with milestones.
 * 
 * This service:
 * 1. Logs workflow events (who did what, when)
 * 2. Updates milestones automatically
 * 3. Syncs to Supabase (when implemented)
 */
class DeviceWorkflowService(
    private val repository: FQCRepository
) {
    companion object {
        private const val TAG = "DeviceWorkflow"

        // Independent scope for Supabase sync that survives composition cancellation
        private val supabaseSyncScope = CoroutineScope(SupervisorJob() + Dispatchers.IO)

        // Stage constants
        const val STAGE_DRIVER_PAIRING = "DRIVER_PAIRING"
        const val STAGE_CHANGED_PARTS = "CHANGED_PARTS"
        const val STAGE_FREQUENCY_TEST = "FREQUENCY_TEST"
        const val STAGE_SONG_TEST = "SONG_TEST"
        const val STAGE_FQC = "FQC"

        // Action constants
        const val ACTION_STARTED = "STARTED"
        const val ACTION_COMPLETED = "COMPLETED"
        const val ACTION_FAILED = "FAILED"
        const val ACTION_RETRY = "RETRY"

        // Result constants
        const val RESULT_PASS = "PASS"
        const val RESULT_FAIL = "FAIL"
        const val RESULT_CAN_PASS = "CAN_PASS"
        const val RESULT_CAN_BE_PASSED = "CAN_BE_PASSED"
    }

    /**
     * Log a workflow event and update milestone if needed
     */
    suspend fun logWorkflow(
        serialNumber: String,
        stage: String,
        operatorName: String,
        action: String,
        result: String? = null,
        iterationNumber: Int = 1,
        recordId: Long? = null,
        details: String = "{}"
    ) = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, " [LOG_WORKFLOW] Logging workflow event:")
            Log.d(TAG, "   Serial: $serialNumber")
            Log.d(TAG, "   Stage: $stage")
            Log.d(TAG, "   Operator: $operatorName")
            Log.d(TAG, "   Action: $action")
            Log.d(TAG, "   Result: $result")
            Log.d(TAG, "   Iteration: $iterationNumber")

            // Map result to milestone status
            val milestoneStatus = when {
                action == ACTION_COMPLETED && result != null -> {
                    mapResultToMilestoneStatus(result)
                }
                else -> null
            }

            Log.d(TAG, "   Milestone Status: $milestoneStatus")

            // Create workflow event
            val workflow = DeviceWorkflow(
                serialNumber = serialNumber,
                stage = stage,
                operatorName = operatorName,
                action = action,
                result = result,
                iterationNumber = iterationNumber,
                recordId = recordId,
                milestoneStatus = milestoneStatus,
                details = details,
                timestamp = System.currentTimeMillis(),
                syncedToSupabase = false,
                supabaseSyncTimestamp = 0
            )

            // Save to local database
            val workflowId = repository.insertDeviceWorkflow(workflow)
            Log.d(TAG, " [LOG_WORKFLOW] Workflow event saved with ID: $workflowId")

            // Update milestone if action is COMPLETED
            if (action == ACTION_COMPLETED && milestoneStatus != null) {
                updateMilestoneFromWorkflow(serialNumber, stage, milestoneStatus, iterationNumber)
            }

            // Sync to Supabase in independent scope (fire-and-forget)
            // This ensures sync continues even if the calling composable is disposed
            supabaseSyncScope.launch {
                try {
                    val supabaseService = com.example.fqcapp.supabase.SupabaseDeviceWorkflowService()
                    val supabaseId = supabaseService.saveDeviceWorkflow(workflow.copy(workflowId = workflowId))
                    if (supabaseId != null) {
                        Log.d(TAG, " [SUPABASE] Device workflow saved to Supabase with ID: $supabaseId")
                        // Mark as synced in Room DB
                        repository.markWorkflowAsSynced(workflowId, System.currentTimeMillis())
                    } else {
                        Log.w(TAG, " [SUPABASE] Failed to save to Supabase (will retry later)")
                    }
                } catch (e: CancellationException) {
                    // Re-throw cancellation to respect coroutine cancellation
                    throw e
                } catch (e: Exception) {
                    Log.e(TAG, " [SUPABASE] Error saving device workflow to Supabase", e)
                    // Don't fail the whole operation if Supabase save fails
                }
            }

            workflowId
        } catch (e: Exception) {
            Log.e(TAG, " [LOG_WORKFLOW] Error logging workflow event", e)
            throw e
        }
    }

    /**
     * Map result to milestone status
     */
    private fun mapResultToMilestoneStatus(result: String): String {
        return when (result.uppercase()) {
            RESULT_PASS -> "pass"
            RESULT_FAIL -> "fail"
            RESULT_CAN_PASS, RESULT_CAN_BE_PASSED -> "can_pass"
            else -> "default"
        }
    }

    /**
     * Update milestone based on workflow event
     * Uses MilestoneUpdateService to ensure proper milestone creation logic
     */
    private suspend fun updateMilestoneFromWorkflow(
        serialNumber: String,
        stage: String,
        milestoneStatus: String,
        iterationNumber: Int
    ) {
        try {
            Log.d(TAG, " [UPDATE_MILESTONE] Updating milestone from workflow:")
            Log.d(TAG, "   Serial: $serialNumber")
            Log.d(TAG, "   Stage: $stage")
            Log.d(TAG, "   Status: $milestoneStatus")
            Log.d(TAG, "   Iteration: $iterationNumber")

            // Get all milestones for this device
            val allMilestones = repository.getMilestonesForSerial(serialNumber)

            // Find milestone for this iteration, or create if doesn't exist
            val milestone = allMilestones.find { it.iterationIndex == iterationNumber }
                ?: run {
                    // Create new milestone for this specific iteration
                    Log.d(TAG, " [UPDATE_MILESTONE] Creating new milestone iteration: $iterationNumber")
                    val newMilestone = MilestoneEntry(
                        serialNumber = serialNumber,
                        iterationIndex = iterationNumber
                    )
                    repository.upsertMilestone(newMilestone)
                    // Fetch it back to get the ID
                    repository.getMilestonesForSerial(serialNumber)
                        .find { it.iterationIndex == iterationNumber }
                        ?: newMilestone
                }

            // Update the appropriate stage
            val updatedMilestone = updateMilestoneStage(milestone, stage, milestoneStatus)

            // Save updated milestone
            repository.upsertMilestone(updatedMilestone)
            Log.d(TAG, " [UPDATE_MILESTONE] Milestone updated successfully")

        } catch (e: Exception) {
            Log.e(TAG, " [UPDATE_MILESTONE] Error updating milestone", e)
        }
    }

    /**
     * Update specific stage in milestone
     */
    private fun updateMilestoneStage(
        milestone: MilestoneEntry,
        stage: String,
        status: String
    ): MilestoneEntry {
        return when (stage) {
            STAGE_DRIVER_PAIRING -> milestone.copy(driverInfoStatus = status)
            STAGE_FREQUENCY_TEST -> milestone.copy(frequencyStatus = status)
            STAGE_SONG_TEST -> milestone.copy(testSongStatus = status)
            STAGE_FQC -> milestone.copy(finalQcStatus = status)
            else -> milestone
        }
    }

    /**
     * Get workflow history for a device
     */
    suspend fun getDeviceWorkflowHistory(serialNumber: String): List<DeviceWorkflow> {
        return withContext(Dispatchers.IO) {
            try {
                repository.getDeviceWorkflowHistory(serialNumber)
            } catch (e: Exception) {
                Log.e(TAG, " Error getting device workflow history", e)
                emptyList()
            }
        }
    }

    /**
     * Get workflow history for a device at a specific stage
     */
    suspend fun getDeviceWorkflowForStage(
        serialNumber: String,
        stage: String
    ): List<DeviceWorkflow> {
        return withContext(Dispatchers.IO) {
            try {
                repository.getDeviceWorkflowForStage(serialNumber, stage)
            } catch (e: Exception) {
                Log.e(TAG, " Error getting device workflow for stage", e)
                emptyList()
            }
        }
    }

    /**
     * Get latest workflow event for a device at a stage
     */
    suspend fun getLatestWorkflowForStage(
        serialNumber: String,
        stage: String
    ): DeviceWorkflow? {
        return withContext(Dispatchers.IO) {
            try {
                repository.getLatestWorkflowForStage(serialNumber, stage)
            } catch (e: Exception) {
                Log.e(TAG, " Error getting latest workflow for stage", e)
                null
            }
        }
    }

    /**
     * Get next iteration number for a stage (DEPRECATED - use test-specific methods)
     * This method is kept for backward compatibility but now queries Supabase.
     */
    suspend fun getNextIterationNumber(
        serialNumber: String,
        stage: String
    ): Int {
        return withContext(Dispatchers.IO) {
            try {
                // Map stage to test type
                val testType = when (stage) {
                    STAGE_FREQUENCY_TEST -> "frequency"
                    STAGE_SONG_TEST -> "song"
                    STAGE_FQC -> "fqc"
                    STAGE_DRIVER_PAIRING -> "driver_pairing"
                    STAGE_CHANGED_PARTS -> "changed_parts"
                    else -> {
                        // Fallback to local DB for unknown stages
                        Log.w(TAG, " Unknown stage: $stage, falling back to local DB")
                        return@withContext repository.getMaxWorkflowIterationForStage(serialNumber, stage) + 1
                    }
                }

                // Use Supabase-based method
                getNextIterationFromSupabase(serialNumber, testType)
            } catch (e: Exception) {
                Log.e(TAG, " Error getting next iteration number", e)
                // Fallback to local DB
                try {
                    repository.getMaxWorkflowIterationForStage(serialNumber, stage) + 1
                } catch (e2: Exception) {
                    1
                }
            }
        }
    }

    /**
     * Get next iteration number from Supabase for a specific test type.
     * This is the new standard method that queries Supabase directly.
     * 
     * @param serialNumber The device serial number
     * @param testType One of: "frequency", "song", "fqc", "driver_pairing", "changed_parts"
     * @return Next iteration number (maxIteration + 1, or 1 if no records exist)
     */
    suspend fun getNextIterationFromSupabase(
        serialNumber: String,
        testType: String
    ): Int {
        return withContext(Dispatchers.IO) {
            try {
                // Validate serial number - don't query Supabase with device names or MAC addresses
                val normalizedSerial = serialNumber.trim()
                val isLikelyDeviceName = normalizedSerial.matches(Regex("^BT-.*", RegexOption.IGNORE_CASE))
                val isLikelyMacAddress = normalizedSerial.matches(Regex("^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})$"))
                val isTooShort = normalizedSerial.length < 10

                if (isLikelyDeviceName || isLikelyMacAddress || isTooShort) {
                    Log.w(TAG, "")
                    Log.w(TAG, " [WORKFLOW] Invalid serial number format - skipping Supabase query")
                    Log.w(TAG, "   Serial Number: '$serialNumber'")
                    Log.w(TAG, "   Reason: ${when {
                        isLikelyDeviceName -> "Looks like device name"
                        isLikelyMacAddress -> "Looks like MAC address"
                        isTooShort -> "Too short (< 10 chars)"
                        else -> "Unknown"
                    }}")
                    Log.w(TAG, "   Returning iteration 1 (fallback - no Supabase query)")
                    Log.w(TAG, "")
                    return@withContext 1 // Return iteration 1 immediately - don't query Supabase
                }

                Log.d(TAG, "")
                Log.d(TAG, " [WORKFLOW] GETTING NEXT ITERATION FROM SUPABASE")
                Log.d(TAG, "   Serial Number: $serialNumber")
                Log.d(TAG, "   Test Type: $testType")
                Log.d(TAG, "")

                val deviceDataService = com.example.fqcapp.supabase.SupabaseDeviceDataService()
                val nextIteration = deviceDataService.getNextIterationNumber(serialNumber, testType)

                Log.d(TAG, "")
                Log.d(TAG, " [WORKFLOW] ITERATION NUMBER RETRIEVED")
                Log.d(TAG, "   Serial Number: $serialNumber")
                Log.d(TAG, "   Test Type: $testType")
                Log.d(TAG, "   Next Iteration: $nextIteration")
                Log.d(TAG, "")

                return@withContext nextIteration
            } catch (e: Exception) {
                Log.e(TAG, "")
                Log.e(TAG, " [WORKFLOW] ERROR GETTING ITERATION FROM SUPABASE")
                Log.e(TAG, "   Serial Number: $serialNumber")
                Log.e(TAG, "   Test Type: $testType")
                Log.e(TAG, "   Error: ${e.message}")
                Log.e(TAG, "   Stack trace: ${e.stackTraceToString()}")
                Log.e(TAG, "    Falling back to iteration 1")
                Log.e(TAG, "")
                // Fallback: return 1 if Supabase query fails
                return@withContext 1
            }
        }
    }

    /**
     * Get next iteration number for frequency test.
     * Queries Supabase frequency_tests table.
     */
    suspend fun getNextFrequencyTestIteration(serialNumber: String): Int {
        return getNextIterationFromSupabase(serialNumber, "frequency")
    }

    /**
     * Get next iteration number for song test.
     * Queries Supabase song_tests table (only overall results).
     */
    suspend fun getNextSongTestIteration(serialNumber: String): Int {
        return getNextIterationFromSupabase(serialNumber, "song")
    }

    /**
     * Get next iteration number for FQC record.
     * Queries Supabase fqc_records table.
     */
    suspend fun getNextFQCIteration(serialNumber: String): Int {
        return getNextIterationFromSupabase(serialNumber, "fqc")
    }

    /**
     * Get next iteration number for driver pairing.
     * Queries Supabase driver_pairing_records table.
     */
    suspend fun getNextDriverPairingIteration(serialNumber: String): Int {
        return getNextIterationFromSupabase(serialNumber, "driver_pairing")
    }

    /**
     * Get next iteration number for changed parts.
     * Queries Supabase changed_part_records table.
     */
    suspend fun getNextChangedPartsIteration(serialNumber: String): Int {
        return getNextIterationFromSupabase(serialNumber, "changed_parts")
    }
}

package com.example.fqcapp.services

import android.content.Context
import android.util.Log
import com.example.fqcapp.data.dao.MacIdCacheDao
import com.example.fqcapp.data.entities.MacIdCache
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

class MacIdCacheManager(
    private val context: Context,
    private val macIdCacheDao: MacIdCacheDao,
    private val googleSheetsAPI: GoogleSheetsAPI
) {
    companion object {
        private const val TAG = "MacIdCacheManager"
        private const val CACHE_EXPIRY_MS = 60 * 60 * 1000L // 1 hour
    }
    
    /**
     * Check if cache needs refresh (older than 1 hour)
     */
    suspend fun shouldRefreshCache(): Boolean = withContext(Dispatchers.IO) {
        try {
            val lastUpdateTime = macIdCacheDao.getLastUpdateTime()
            if (lastUpdateTime == null) {
                Log.d(TAG, "Cache is empty, needs refresh")
                return@withContext true
            }
            
            val currentTime = System.currentTimeMillis()
            val timeDiff = currentTime - lastUpdateTime
            val needsRefresh = timeDiff > CACHE_EXPIRY_MS
            
            Log.d(TAG, "Cache last updated: ${lastUpdateTime}, current: ${currentTime}, diff: ${timeDiff}ms, needs refresh: ${needsRefresh}")
            return@withContext needsRefresh
        } catch (e: Exception) {
            Log.e(TAG, "Error checking cache expiry", e)
            return@withContext true // Refresh on error
        }
    }
    
    /**
     * Download Sheet2 and merge with existing cache
     */
    suspend fun refreshCache(): Boolean = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, "Starting cache refresh...")
            
            // Fetch all rows from Sheet2 (columns A:C)
            val sheetData = googleSheetsAPI.fetchMacLookupData()
            
            if (sheetData.isEmpty()) {
                Log.e(TAG, "No data received from Google Sheets")
                return@withContext false
            }
            
            Log.d(TAG, "Downloaded ${sheetData.size} entries from Google Sheets")
            
            // Parse and filter valid entries (non-blank, no #REF!)
            val validData = sheetData.filter { row ->
                row.size >= 3 && 
                row[1].isNotBlank() && // Serial Number (Column B) must not be empty
                row[2].isNotBlank() && // MAC ID (Column C) must not be empty
                !row[1].contains("#REF!") && // Filter out #REF! errors in serial number
                !row[2].contains("#REF!") // Filter out #REF! errors in MAC ID
            }
            
            Log.d(TAG, "Filtered to ${validData.size} valid entries")
            
            if (validData.isEmpty()) {
                Log.w(TAG, "No valid entries found after filtering")
                return@withContext false
            }
            
            // Get existing cache entries
            val existingEntries = macIdCacheDao.getAllCachedEntries()
            val existingSerialNumbers = existingEntries.map { it.serialNumber }.toSet()
            
            Log.d(TAG, "Found ${existingEntries.size} existing cache entries")
            
            // Create new cache entries
            val currentTime = System.currentTimeMillis()
            val newCacheEntries = validData.map { row ->
                MacIdCache(
                    serialNumber = row[1], // Column B - Serial Number
                    macAddress = row[2],   // Column C - MAC ID
                    lastUpdated = currentTime
                )
            }
            
            // Merge: keep existing + add new (no duplicates)
            val entriesToInsert = mutableListOf<MacIdCache>()
            
            // Add existing entries that are not in the new data
            existingEntries.forEach { existing ->
                if (!newCacheEntries.any { it.serialNumber == existing.serialNumber }) {
                    entriesToInsert.add(existing)
                }
            }
            
            // Add all new entries (this will replace existing ones with same serial)
            entriesToInsert.addAll(newCacheEntries)
            
            Log.d(TAG, "Merged cache: ${entriesToInsert.size} total entries (${existingEntries.size} existing + ${newCacheEntries.size} new)")
            
            // Insert all entries (REPLACE strategy handles duplicates)
            macIdCacheDao.insertAll(entriesToInsert)
            
            Log.d(TAG, " Cache refresh completed successfully")
            return@withContext true
            
        } catch (e: Exception) {
            Log.e(TAG, "Error refreshing cache", e)
            e.printStackTrace()
            return@withContext false
        }
    }
    
    /**
     * Get MAC ID from cache, refresh if not found
     */
    suspend fun getMacId(serialNumber: String): String? = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, "Looking up MAC for serial: $serialNumber")
            
            // Query local cache first
            val cachedEntry = macIdCacheDao.getMacBySerial(serialNumber)
            
            if (cachedEntry != null) {
                Log.d(TAG, " Found MAC in cache: ${cachedEntry.macAddress} for serial: $serialNumber")
                return@withContext cachedEntry.macAddress
            }
            
            Log.d(TAG, "Serial not found in cache, refreshing cache...")
            
            // If not found, refresh cache and try again
            val refreshSuccess = refreshCache()
            
            if (refreshSuccess) {
                // Try again after refresh
                val refreshedEntry = macIdCacheDao.getMacBySerial(serialNumber)
                if (refreshedEntry != null) {
                    Log.d(TAG, " Found MAC after cache refresh: ${refreshedEntry.macAddress} for serial: $serialNumber")
                    return@withContext refreshedEntry.macAddress
                }
            }
            
            Log.w(TAG, " Serial number $serialNumber not found even after cache refresh")
            return@withContext null
            
        } catch (e: Exception) {
            Log.e(TAG, "Error getting MAC ID", e)
            e.printStackTrace()
            return@withContext null
        }
    }
    
    /**
     * Get serial number by MAC ID (reverse lookup)
     */
    suspend fun getSerialNumberByMacId(macId: String): String? = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, "Looking up serial for MAC: $macId")
            
            // Query local cache first
            val cachedEntry = macIdCacheDao.getSerialByMac(macId)
            
            if (cachedEntry != null) {
                Log.d(TAG, " Found serial in cache: ${cachedEntry.serialNumber} for MAC: $macId")
                return@withContext cachedEntry.serialNumber
            }
            
            Log.d(TAG, "MAC not found in cache, refreshing cache...")
            
            // If not found, refresh cache and try again
            val refreshSuccess = refreshCache()
            
            if (refreshSuccess) {
                // Try again after refresh
                val refreshedEntry = macIdCacheDao.getSerialByMac(macId)
                if (refreshedEntry != null) {
                    Log.d(TAG, " Found serial after cache refresh: ${refreshedEntry.serialNumber} for MAC: $macId")
                    return@withContext refreshedEntry.serialNumber
                }
            }
            
            Log.w(TAG, " MAC ID $macId not found even after cache refresh")
            return@withContext null
            
        } catch (e: Exception) {
            Log.e(TAG, "Error getting serial number", e)
            e.printStackTrace()
            return@withContext null
        }
    }
    
    /**
     * Get cache statistics for debugging
     */
    suspend fun getCacheStats(): Map<String, Any?> = withContext(Dispatchers.IO) {
        try {
            val cacheSize = macIdCacheDao.getCacheSize()
            val lastUpdateTime = macIdCacheDao.getLastUpdateTime()
            val needsRefresh = shouldRefreshCache()
            
            mapOf(
                "cacheSize" to cacheSize,
                "lastUpdateTime" to (lastUpdateTime ?: 0L),
                "needsRefresh" to needsRefresh,
                "lastUpdateFormatted" to if (lastUpdateTime != null) {
                    java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss", java.util.Locale.US)
                        .format(java.util.Date(lastUpdateTime))
                } else "Never"
            )
        } catch (e: Exception) {
            Log.e(TAG, "Error getting cache stats", e)
            mapOf("error" to (e.message ?: "Unknown error"))
        }
    }
}

package com.example.fqcapp.services

import android.content.Context
import android.util.Log
import com.example.fqcapp.data.dao.DriverPairingCacheDao
import com.example.fqcapp.data.entities.DriverPairingCache
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import kotlinx.coroutines.CompletableDeferred
import kotlinx.coroutines.sync.Mutex
import kotlinx.coroutines.sync.withLock
import org.json.JSONArray

class DriverPairingCacheManager(
    private val driverPairingCacheDao: DriverPairingCacheDao,
    private val driverPairingSheetsService: DriverPairingSheetsService
) {
    companion object {
        private const val TAG = "DriverPairingCacheManager"
        private const val CACHE_EXPIRY_MS = 15 * 60 * 1000L // 15 minutes
        private const val WORK_START_HOUR = 9 // 9 AM
        private const val WORK_END_HOUR = 20 // 8 PM
    }

    // Request deduplication: track ongoing requests to prevent duplicate API calls
    private val ongoingRequests = mutableMapOf<String, CompletableDeferred<DriverPairingData?>>()
    private val requestsMutex = Mutex()

    /**
     * Check if current time is within work hours (9am - 8pm)
     */
    private fun isWithinWorkHours(): Boolean {
        val calendar = java.util.Calendar.getInstance()
        val currentHour = calendar.get(java.util.Calendar.HOUR_OF_DAY)
        val result = currentHour >= WORK_START_HOUR && currentHour < WORK_END_HOUR
        Log.d(TAG, " [WORK_HOURS_CHECK] Current hour: $currentHour, within work hours (9am-8pm): $result")
        return result
    }

    /**
     * Check if cache needs refresh based on 15-minute rule during work hours
     */
    private suspend fun shouldRefreshCache(cached: DriverPairingCache?): Boolean = withContext(Dispatchers.IO) {
        if (cached == null) {
            Log.d(TAG, " [CACHE_CHECK] No cache exists, needs refresh")
            return@withContext true
        }

        // Outside work hours: use longer expiry (1 hour)
        if (!isWithinWorkHours()) {
            val timeDiff = System.currentTimeMillis() - cached.lastUpdated
            val needsRefresh = timeDiff > (60 * 60 * 1000L) // 1 hour outside work hours
            Log.d(TAG, " [CACHE_CHECK] Outside work hours - timeDiff: ${timeDiff}ms, needs refresh: $needsRefresh")
            return@withContext needsRefresh
        }

        // Within work hours: 15-minute expiry
        val timeDiff = System.currentTimeMillis() - cached.lastUpdated
        val needsRefresh = timeDiff > CACHE_EXPIRY_MS
        Log.d(TAG, " [CACHE_CHECK] Within work hours (9am-8pm) - timeDiff: ${timeDiff}ms (${timeDiff / 1000 / 60} mins), needs refresh (15min): $needsRefresh")
        return@withContext needsRefresh
    }

    /**
     * Get driver data - Uses cache-first approach with 15-minute refresh interval
     * Only fetches from Google Sheets if cache is missing or stale (> 15 minutes)
     * Implements request deduplication to prevent concurrent duplicate API calls
     * This reduces API calls while still maintaining reasonable data freshness
     * 
     * @param serialNumber Serial number to search for
     * @param macId MAC ID to use as fallback if serial number search fails
     */
    suspend fun getDriverData(serialNumber: String, macId: String = ""): DriverPairingData? = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, " [GET_DATA] Getting driver data for serial: $serialNumber, MAC: $macId")

            // Step 1: Check cache first (cache-first approach)
            // #region agent log
            try {
                val logFile = java.io.File("c:\\Users\\pc\\Desktop\\.cursor\\debug.log")
                val logEntry = org.json.JSONObject().apply {
                    put("sessionId", "debug-session")
                    put("runId", "run1")
                    put("hypothesisId", "E")
                    put("location", "DriverPairingCacheManager.kt:78")
                    put("message", "Checking cache for driver data")
                    put("data", org.json.JSONObject().apply {
                        put("serialNumber", serialNumber)
                        put("macId", macId)
                        put("serialNumberIsBlank", serialNumber.isBlank())
                    })
                    put("timestamp", System.currentTimeMillis())
                }
                logFile.appendText(logEntry.toString() + "\n")
            } catch (e: Exception) {}
            // #endregion
            val cached = if (serialNumber.isNotBlank()) {
                driverPairingCacheDao.getBySerialNumber(serialNumber)
            } else {
                null
            }
            // #region agent log
            try {
                val logFile = java.io.File("c:\\Users\\pc\\Desktop\\.cursor\\debug.log")
                val logEntry = org.json.JSONObject().apply {
                    put("sessionId", "debug-session")
                    put("runId", "run1")
                    put("hypothesisId", "E")
                    put("location", "DriverPairingCacheManager.kt:82-result")
                    put("message", "Cache lookup result")
                    put("data", org.json.JSONObject().apply {
                        put("serialNumber", serialNumber)
                        put("cachedFound", cached != null)
                        put("cachedSerial", cached?.serialNumber ?: "null")
                        put("cachedMacId", cached?.macId ?: "null")
                        put("cachedLastUpdated", cached?.lastUpdated?.toString() ?: "null")
                    })
                    put("timestamp", System.currentTimeMillis())
                }
                logFile.appendText(logEntry.toString() + "\n")
            } catch (e: Exception) {}
            // #endregion

            // Step 2: If cache exists and is fresh (< 15 minutes), return cached data immediately
            val shouldRefresh = if (cached != null) shouldRefreshCache(cached) else true
            // #region agent log
            try {
                val logFile = java.io.File("c:\\Users\\pc\\Desktop\\.cursor\\debug.log")
                val logEntry = org.json.JSONObject().apply {
                    put("sessionId", "debug-session")
                    put("runId", "run1")
                    put("hypothesisId", "E")
                    put("location", "DriverPairingCacheManager.kt:85")
                    put("message", "Cache freshness check")
                    put("data", org.json.JSONObject().apply {
                        put("cachedFound", cached != null)
                        put("shouldRefresh", shouldRefresh)
                        put("willUseCache", cached != null && !shouldRefresh)
                    })
                    put("timestamp", System.currentTimeMillis())
                }
                logFile.appendText(logEntry.toString() + "\n")
            } catch (e: Exception) {}
            // #endregion
            if (cached != null && !shouldRefresh) {
                Log.d(TAG, " [GET_DATA] Using fresh cached data (age: ${(System.currentTimeMillis() - cached.lastUpdated) / 1000 / 60} minutes)")
                val drivers = try {
                    JSONArray(cached.driverData).let { jsonArray ->
                        (0 until jsonArray.length()).map { jsonArray.getString(it) }
                    }
                } catch (e: Exception) {
                    Log.w(TAG, " [GET_DATA] Error parsing cached driver data", e)
                    emptyList<String>()
                }
                if (drivers.isNotEmpty()) {
                    return@withContext DriverPairingData(
                        serialNumber = cached.serialNumber,
                        macId = cached.macId,
                        drivers = drivers
                    )
                }
            }

            // Step 3: Check for ongoing request (request deduplication)
            val requestKey = serialNumber.ifBlank { macId }
            if (requestKey.isNotBlank()) {
                val existingRequest = requestsMutex.withLock {
                    ongoingRequests[requestKey]
                }

                if (existingRequest != null) {
                    Log.d(TAG, " [GET_DATA] Waiting for ongoing request for key: $requestKey")
                    val result = existingRequest.await()
                    Log.d(TAG, " [GET_DATA] Received result from ongoing request")
                    return@withContext result
                }
            }

            // Step 4: Create new request and track it
            val deferred = if (requestKey.isNotBlank()) {
                CompletableDeferred<DriverPairingData?>().also { deferred ->
                    requestsMutex.withLock {
                        ongoingRequests[requestKey] = deferred
                    }
                }
            } else {
                null
            }

            try {
                // Step 5: Cache is missing or stale - fetch from Google Sheets
                // #region agent log
                try {
                    val logFile = java.io.File("c:\\Users\\pc\\Desktop\\.cursor\\debug.log")
                    val logEntry = org.json.JSONObject().apply {
                        put("sessionId", "debug-session")
                        put("runId", "run1")
                        put("hypothesisId", "F")
                        put("location", "DriverPairingCacheManager.kt:132")
                        put("message", "Calling Google Sheets API")
                        put("data", org.json.JSONObject().apply {
                            put("serialNumber", serialNumber)
                            put("macId", macId)
                            put("cachedExists", cached != null)
                            put("reason", if (cached != null) "cache_stale" else "cache_missing")
                        })
                        put("timestamp", System.currentTimeMillis())
                    }
                    logFile.appendText(logEntry.toString() + "\n")
                } catch (e: Exception) {}
                // #endregion
                if (cached != null) {
                    Log.d(TAG, " [GET_DATA] Cache is stale (age: ${(System.currentTimeMillis() - cached.lastUpdated) / 1000 / 60} minutes), fetching from Google Sheets")
                } else {
                    Log.d(TAG, " [GET_DATA] No cache found, fetching from Google Sheets")
                }

                var sheetsData: DriverPairingData? = null

                // Try serial number search first if available
                if (serialNumber.isNotBlank()) {
                    sheetsData = driverPairingSheetsService.getDriverDataBySerial(serialNumber)
                }

                // Step 6: If serial number search fails (or serial is blank) and MAC ID is available, try MAC ID search
                if (sheetsData == null && macId.isNotBlank()) {
                    Log.d(TAG, " [GET_DATA] Serial number search ${if (serialNumber.isBlank()) "not attempted (blank)" else "failed"}, trying MAC ID search: $macId")
                    sheetsData = driverPairingSheetsService.getDriverDataByMacId(macId)
                    if (sheetsData != null) {
                        Log.d(TAG, " [GET_DATA] Found data by MAC ID: ${sheetsData.drivers.size} driver entries")
                    }
                }

                if (sheetsData != null) {
                    Log.d(TAG, " [GET_DATA] Fetched fresh data from Google Sheets: ${sheetsData.drivers.size} driver entries")
                    Log.d(TAG, " [GET_DATA] Serial: ${sheetsData.serialNumber}, MAC: ${sheetsData.macId}")

                    // Update cache with fresh data
                    val driverDataJson = JSONArray(sheetsData.drivers).toString()
                    val cacheEntry = DriverPairingCache(
                        serialNumber = sheetsData.serialNumber,
                        macId = sheetsData.macId,
                        driverData = driverDataJson,
                        lastUpdated = System.currentTimeMillis(),
                        lastSyncedToSheets = System.currentTimeMillis() // Just fetched, so in sync
                    )
                    driverPairingCacheDao.insert(cacheEntry)
                    Log.d(TAG, " [GET_DATA] Updated cache with Google Sheets data")

                    // Complete deferred request
                    deferred?.complete(sheetsData)
                    return@withContext sheetsData
                }

                // Step 7: If Google Sheets fetch fails, try to return cached data as fallback (even if stale)
                if (cached != null) {
                    Log.w(TAG, " [GET_DATA] Google Sheets fetch failed, using cached data as fallback (may be stale)")
                    val drivers = try {
                        JSONArray(cached.driverData).let { jsonArray ->
                            (0 until jsonArray.length()).map { jsonArray.getString(it) }
                        }
                    } catch (e: Exception) {
                        emptyList<String>()
                    }
                    if (drivers.isNotEmpty()) {
                        val result = DriverPairingData(
                            serialNumber = cached.serialNumber,
                            macId = cached.macId,
                            drivers = drivers
                        )
                        deferred?.complete(result)
                        return@withContext result
                    }
                }

                Log.w(TAG, " [GET_DATA] No data found in Google Sheets or cache")
                deferred?.complete(null)
                return@withContext null

            } catch (e: Exception) {
                Log.e(TAG, " [GET_DATA] Error getting driver data", e)
                // Try cache as fallback on error
                try {
                    val cached = driverPairingCacheDao.getBySerialNumber(serialNumber)
                    if (cached != null) {
                        Log.d(TAG, " [GET_DATA] Using cached data as fallback after error")
                        val drivers = try {
                            JSONArray(cached.driverData).let { jsonArray ->
                                (0 until jsonArray.length()).map { jsonArray.getString(it) }
                            }
                        } catch (ex: Exception) {
                            emptyList<String>()
                        }
                        val result = DriverPairingData(
                            serialNumber = cached.serialNumber,
                            macId = cached.macId,
                            drivers = drivers
                        )
                        deferred?.complete(result)
                        return@withContext result
                    }
                } catch (ex: Exception) {
                    Log.e(TAG, " [GET_DATA] Error getting cached data", ex)
                }
                deferred?.complete(null)
                return@withContext null
            } finally {
                // Remove from ongoing requests
                if (requestKey.isNotBlank()) {
                    requestsMutex.withLock {
                        ongoingRequests.remove(requestKey)
                    }
                }
            }
        } catch (e: Exception) {
            Log.e(TAG, " [GET_DATA] Unexpected error", e)
            return@withContext null
        }
    }

    /**
     * Save driver data - updates cache immediately, syncs to Google Sheets
     */
    suspend fun saveDriverData(
        serialNumber: String,
        macId: String,
        row1Driver1: String,
        row1Driver2: String,
        additionalRows: List<Pair<String, String>>
    ): Boolean = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, " Saving driver data for serial: $serialNumber")

            // Build driver data list
            val drivers = mutableListOf<String>()
            drivers.add(row1Driver1)
            drivers.add(row1Driver2)
            additionalRows.forEach { (driver1, driver2) ->
                drivers.add(driver1)
                drivers.add(driver2)
            }

            // 1. Update local cache immediately (optimistic update)
            val driverDataJson = JSONArray(drivers).toString()
            val currentTime = System.currentTimeMillis()
            val cacheEntry = DriverPairingCache(
                serialNumber = serialNumber,
                macId = macId,
                driverData = driverDataJson,
                lastUpdated = currentTime,
                lastSyncedToSheets = 0L // Mark as unsynced until successful
            )
            driverPairingCacheDao.insert(cacheEntry)
            Log.d(TAG, " Updated local cache")

            // 2. Sync to Google Sheets in background
            try {
                val syncSuccess = driverPairingSheetsService.saveDriverData(
                    serialNumber = serialNumber,
                    macId = macId,
                    row1Driver1 = row1Driver1,
                    row1Driver2 = row1Driver2,
                    additionalRows = additionalRows
                )

                if (syncSuccess) {
                    // Update sync timestamp
                    val updatedCache = cacheEntry.copy(
                        lastSyncedToSheets = System.currentTimeMillis()
                    )
                    driverPairingCacheDao.update(updatedCache)
                    Log.d(TAG, " Synced to Google Sheets successfully")
                } else {
                    Log.w(TAG, " Failed to sync to Google Sheets, but cache is updated")
                }

                return@withContext syncSuccess
            } catch (e: Exception) {
                Log.e(TAG, "Error syncing to Google Sheets", e)
                // Cache is still updated, so return true (local save succeeded)
                return@withContext true
            }

        } catch (e: Exception) {
            Log.e(TAG, "Error saving driver data", e)
            return@withContext false
        }
    }

    /**
     * Delete driver data (local cache and Google Sheets)
     */
    suspend fun deleteDriverData(serialNumber: String): Boolean = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, " [DELETE] Deleting driver data for serial: $serialNumber")

            // 1. Delete from local cache
            driverPairingCacheDao.deleteBySerial(serialNumber)
            Log.d(TAG, " [DELETE] Deleted from local cache")

            // 2. Delete from Google Sheets
            val deleteSuccess = driverPairingSheetsService.deleteDriverData(serialNumber)
            if (deleteSuccess) {
                Log.d(TAG, " [DELETE] Deleted from Google Sheets")
            } else {
                Log.w(TAG, " [DELETE] Failed to delete from Google Sheets (but local cache deleted)")
            }

            return@withContext deleteSuccess
        } catch (e: Exception) {
            Log.e(TAG, " [DELETE] Error deleting driver data", e)
            return@withContext false
        }
    }

    /**
     * Check if there are unsynced entries that need to be synced to Google Sheets
     */
    suspend fun hasUnsyncedEntries(): Boolean = withContext(Dispatchers.IO) {
        try {
            val unsynced = driverPairingCacheDao.getUnsyncedEntries(System.currentTimeMillis())
            return@withContext unsynced.isNotEmpty()
        } catch (e: Exception) {
            Log.e(TAG, "Error checking unsynced entries", e)
            return@withContext false
        }
    }

    /**
     * Sync all unsynced entries to Google Sheets
     */
    suspend fun syncUnsyncedEntries(): Int = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, " [SYNC_START] Starting manual sync of unsynced driver pairing entries...")
            val unsynced = driverPairingCacheDao.getUnsyncedEntries(System.currentTimeMillis())
            Log.d(TAG, " [SYNC_CHECK] Found ${unsynced.size} unsynced entries")

            if (unsynced.isEmpty()) {
                Log.d(TAG, " [SYNC_EMPTY] No unsynced entries found, sync complete")
                return@withContext 0
            }

            var successCount = 0
            var failureCount = 0
            unsynced.forEachIndexed { index, cache ->
                try {
                    Log.d(TAG, " [SYNC_ENTRY] Syncing entry ${index + 1}/${unsynced.size}: Serial=${cache.serialNumber}, MAC=${cache.macId}")

                    // Parse driver data
                    val drivers = JSONArray(cache.driverData).let { jsonArray ->
                        (0 until jsonArray.length()).map { jsonArray.getString(it) }
                    }

                    Log.d(TAG, " [SYNC_PARSE] Parsed ${drivers.size} driver entries from cache")

                    if (drivers.size >= 2) {
                        val row1Driver1 = drivers[0]
                        val row1Driver2 = drivers[1]
                        val additionalRows = mutableListOf<Pair<String, String>>()

                        var i = 2
                        while (i < drivers.size - 1) {
                            additionalRows.add(Pair(drivers[i], drivers[i + 1]))
                            i += 2
                        }

                        Log.d(TAG, " [SYNC_SAVE] Saving to Google Sheets - Row1: Driver1='$row1Driver1', Driver2='$row1Driver2', Additional rows: ${additionalRows.size}")

                        val success = driverPairingSheetsService.saveDriverData(
                            serialNumber = cache.serialNumber,
                            macId = cache.macId,
                            row1Driver1 = row1Driver1,
                            row1Driver2 = row1Driver2,
                            additionalRows = additionalRows
                        )

                        if (success) {
                            Log.d(TAG, " [SYNC_SUCCESS] Successfully synced entry: ${cache.serialNumber}")
                            val updatedCache = cache.copy(
                                lastSyncedToSheets = System.currentTimeMillis()
                            )
                            driverPairingCacheDao.update(updatedCache)
                            successCount++
                        } else {
                            Log.w(TAG, " [SYNC_FAIL] Failed to sync entry: ${cache.serialNumber}")
                            failureCount++
                        }
                    } else {
                        Log.w(TAG, " [SYNC_INVALID] Entry ${cache.serialNumber} has invalid driver data (${drivers.size} entries, need at least 2)")
                        failureCount++
                    }
                } catch (e: Exception) {
                    Log.e(TAG, " [SYNC_ERROR] Error syncing entry ${cache.serialNumber}", e)
                    failureCount++
                }
            }

            Log.d(TAG, " [SYNC_COMPLETE] Sync finished: $successCount successful, $failureCount failed out of ${unsynced.size} total")
            return@withContext successCount
        } catch (e: Exception) {
            Log.e(TAG, "Error syncing unsynced entries", e)
            return@withContext 0
        }
    }
}

