package com.example.fqcapp.supabase

import android.util.Log
import com.example.fqcapp.data.entities.SongTest
import io.github.jan.supabase.postgrest.from
import io.github.jan.supabase.postgrest.query.Columns
import io.github.jan.supabase.postgrest.query.Order
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import kotlinx.serialization.Serializable

/**
 * Data transfer object for Supabase song_tests table.
 * This matches the structure of the SongTest entity but uses snake_case for Supabase.
 */
@Serializable
data class SupabaseSongTest(
    val test_id: Long? = null, // Auto-generated, nullable for inserts
    val serial_number: String,
    val operator_name: String,
    val operator_id: Long? = null, // Operator ID (bigint in Supabase) - nullable
    val entry_timestamp: String,
    val exit_timestamp: String,
    val total_time_spent: String,
    val song_name: String,
    val result: String,
    val remark: String? = null, // Nullable because Supabase may return null
    val iteration_number: Int = 1,
    val test_stage: String = "Primary Test", // "Primary Test", "Secondary Test", or "Final Test"
    val is_overall_result: Boolean = false,
    val timestamp: String = ""
)

/**
 * Data transfer object for Supabase song_test_issues table.
 */
@Serializable
data class SupabaseSongTestIssue(
    val test_id: Long,
    val issue_text: String
)

/**
 * Data transfer object for Supabase song_test_solutions table.
 */
@Serializable
data class SupabaseSongTestSolution(
    val test_id: Long,
    val solution_text: String
)

/**
 * Service for saving and retrieving song test data from Supabase.
 */
class SupabaseSongTestService {
    private val TAG = "SupabaseSongTest"
    private val deviceService = SupabaseDeviceService()

    /**
     * Save a song test (overall review or individual song) to Supabase.
     * Returns the inserted test ID if successful, null otherwise.
     * 
     * Also saves issues and solutions to normalized tables.
     * 
     * @param test The song test entity
     * @param operatorId The operator ID (unique identifier) - can be empty/null
     */
    suspend fun saveSongTest(test: SongTest, operatorId: String): Long? = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping save")
            return@withContext null
        }

        try {
            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] UPLOADING SONG TEST")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Operator: ${test.operatorName}")
            Log.d(TAG, "   Song Name: ${test.songName}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Is Overall: ${test.isOverallResult}")
            Log.d(TAG, "   Entry Timestamp: ${test.entryTimestamp}")
            Log.d(TAG, "   Exit Timestamp: ${test.exitTimestamp}")
            Log.d(TAG, "   Total Time: ${test.totalTimeSpent}")
            Log.d(TAG, "")

            // CRITICAL: Ensure device exists in Supabase before inserting test data
            Log.d(TAG, " [STEP 1/3] Ensuring device exists in Supabase...")
            val deviceExists = deviceService.ensureDeviceExists(
                serialNumber = test.serialNumber,
                macAddress = null, // MAC address not available in SongTest entity
                color = null
            )

            if (!deviceExists) {
                Log.e(TAG, "    [ERROR] Failed to ensure device exists, cannot save song test")
                Log.e(TAG, "")
                return@withContext null
            }
            Log.d(TAG, "    [SUCCESS] Device exists or created successfully")

            // Convert to Supabase format
            Log.d(TAG, " [STEP 2/3] Preparing data for upload...")
            // Convert operatorId string to Long? - null if empty or invalid
            val operatorIdLong = try {
                if (operatorId.isBlank()) {
                    null
                } else {
                    operatorId.toLongOrNull()
                }
            } catch (e: Exception) {
                Log.w(TAG, " Could not convert operatorId '$operatorId' to Long, using null", e)
                null
            }

            val insertData = SupabaseSongTest(
                serial_number = test.serialNumber,
                operator_name = test.operatorName,
                operator_id = operatorIdLong, // Now Long? instead of String
                entry_timestamp = test.entryTimestamp,
                exit_timestamp = test.exitTimestamp,
                total_time_spent = test.totalTimeSpent,
                song_name = test.songName,
                result = test.result,
                remark = test.remark,
                iteration_number = test.iterationNumber,
                test_stage = test.testStage,
                is_overall_result = test.isOverallResult,
                timestamp = test.timestamp
            )

            Log.d(TAG, "    [UPLOAD] Data prepared:")
            Log.d(TAG, "      - serial_number: ${insertData.serial_number}")
            Log.d(TAG, "      - operator_name: ${insertData.operator_name}")
            Log.d(TAG, "      - operator_id: ${insertData.operator_id}")
            Log.d(TAG, "      - song_name: ${insertData.song_name}")
            Log.d(TAG, "      - result: ${insertData.result}")
            Log.d(TAG, "      - entry_timestamp: ${insertData.entry_timestamp}")
            Log.d(TAG, "      - exit_timestamp: ${insertData.exit_timestamp}")
            Log.d(TAG, "      - total_time_spent: ${insertData.total_time_spent}")
            Log.d(TAG, "      - iteration_number: ${insertData.iteration_number}")
            Log.d(TAG, "      - is_overall_result: ${insertData.is_overall_result}")

            Log.d(TAG, " [STEP 3/3] Inserting into song_tests table...")
            Log.d(TAG, "    [QUERY] INSERT INTO song_tests (...) VALUES (...)")

            // Insert song test
            val response = client.from("song_tests")
                .insert(insertData) {
                    select(Columns.ALL)
                }

            val insertedTest = response.decodeSingle<SupabaseSongTest>()
            val testId = insertedTest.test_id ?: return@withContext null

            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] SONG TEST UPLOADED SUCCESSFULLY")
            Log.d(TAG, "   Test ID: $testId")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Song: ${test.songName}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "")

            // Save issues to normalized table (if any)
            // IMPORTANT: For PASS results, checklist items (No Distortion, Balance OK, No Noise) 
            // are NOT saved as issues - they are implicit in a PASS result.
            // Only actual problems (for FAIL/CAN_BE_PASSED) should be saved as issues.
            // A PASS means all checklists were checked, so storing them separately is redundant.
            if (test.issues.isNotBlank() && test.result.uppercase() != "PASS") {
                try {
                    saveIssues(testId, test.issues)
                } catch (e: Exception) {
                    Log.e(TAG, " Failed to save issues to normalized table", e)
                    // Don't fail the whole operation if issues save fails
                }
            } else if (test.result.uppercase() == "PASS") {
                Log.d(TAG, " Skipping issues save for PASS result - checklist items are implicit in PASS")
            }

            // Save solutions to normalized table (if any, only for overall review)
            if (test.isOverallResult && test.solutions.isNotBlank()) {
                try {
                    saveSolutions(testId, test.solutions)
                } catch (e: Exception) {
                    Log.e(TAG, " Failed to save solutions to normalized table", e)
                    // Don't fail the whole operation if solutions save fails
                }
            }

            return@withContext testId

        } catch (e: Exception) {
            Log.e(TAG, " Failed to save song test to Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext null
        }
    }

    /**
     * Save multiple individual songs (latest 3) for an iteration.
     * This is called after the overall review is saved.
     */
    suspend fun saveIndividualSongs(
        serialNumber: String,
        iterationNumber: Int,
        individualSongs: List<SongTest>,
        operatorId: String
    ): Boolean = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping save")
            return@withContext false
        }

        try {
            // Ensure device exists
            val deviceExists = deviceService.ensureDeviceExists(
                serialNumber = serialNumber,
                macAddress = null,
                color = null
            )

            if (!deviceExists) {
                Log.e(TAG, " Failed to ensure device exists, cannot save individual songs")
                return@withContext false
            }

            // Only save latest 3 individual songs
            val songsToSave = individualSongs.take(3)

            if (songsToSave.isEmpty()) {
                Log.d(TAG, " No individual songs to save")
                return@withContext true
            }

            Log.d(TAG, " Saving ${songsToSave.size} individual songs to Supabase for serial=$serialNumber, iteration=$iterationNumber")

            // Convert to Supabase format
            // Convert operatorId string to Long? - null if empty or invalid
            val operatorIdLong = try {
                if (operatorId.isBlank()) {
                    null
                } else {
                    operatorId.toLongOrNull()
                }
            } catch (e: Exception) {
                Log.w(TAG, " Could not convert operatorId '$operatorId' to Long, using null", e)
                null
            }

            val insertDataList = songsToSave.map { song ->
                SupabaseSongTest(
                    serial_number = song.serialNumber,
                    operator_name = song.operatorName,
                    operator_id = operatorIdLong, // Now Long? instead of String
                    entry_timestamp = song.entryTimestamp,
                    exit_timestamp = song.exitTimestamp,
                    total_time_spent = song.totalTimeSpent,
                    song_name = song.songName,
                    result = song.result,
                    remark = song.remark,
                    iteration_number = song.iterationNumber,
                    test_stage = song.testStage,
                    is_overall_result = false, // Individual songs are never overall
                    timestamp = song.timestamp
                )
            }

            // Bulk insert individual songs
            val response = client.from("song_tests")
                .insert(insertDataList) {
                    select(Columns.ALL)
                }

            val insertedTests = response.decodeList<SupabaseSongTest>()

            // Save issues for each individual song
            // IMPORTANT: For PASS results, checklist items are NOT saved as issues
            insertedTests.forEachIndexed { index, insertedTest ->
                val originalSong = songsToSave[index]
                val testId = insertedTest.test_id ?: return@forEachIndexed

                // Only save issues for FAIL/CAN_BE_PASSED - PASS results don't need issues saved
                if (originalSong.issues.isNotBlank() && originalSong.result.uppercase() != "PASS") {
                    try {
                        saveIssues(testId, originalSong.issues)
                    } catch (e: Exception) {
                        Log.e(TAG, " Failed to save issues for individual song ${originalSong.songName}", e)
                    }
                } else if (originalSong.result.uppercase() == "PASS") {
                    Log.d(TAG, " Skipping issues save for individual song ${originalSong.songName} - PASS result")
                }
            }

            Log.d(TAG, " Successfully saved ${insertedTests.size} individual songs to Supabase")
            return@withContext true

        } catch (e: Exception) {
            Log.e(TAG, " Failed to save individual songs to Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext false
        }
    }

    /**
     * Save issues to normalized song_test_issues table.
     * Parses issues string (can be JSON array or plain text) and saves each issue separately.
     */
    private suspend fun saveIssues(testId: Long, issuesString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse issues - could be JSON array or plain text
            val issuesList = parseIssuesOrSolutions(issuesString)

            if (issuesList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val issuesData = issuesList.map { issueText ->
                SupabaseSongTestIssue(
                    test_id = testId,
                    issue_text = issueText
                )
            }

            // Bulk insert all issues at once
            if (issuesData.isNotEmpty()) {
                client.from("song_test_issues")
                    .insert(issuesData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${issuesList.size} issues to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving issues to normalized table", e)
            throw e
        }
    }

    /**
     * Save solutions to normalized song_test_solutions table.
     * Parses solutions string (can be JSON array or plain text) and saves each solution separately.
     */
    private suspend fun saveSolutions(testId: Long, solutionsString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse solutions - could be JSON array or plain text
            val solutionsList = parseIssuesOrSolutions(solutionsString)

            if (solutionsList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val solutionsData = solutionsList.map { solutionText ->
                SupabaseSongTestSolution(
                    test_id = testId,
                    solution_text = solutionText
                )
            }

            // Bulk insert all solutions at once
            if (solutionsData.isNotEmpty()) {
                client.from("song_test_solutions")
                    .insert(solutionsData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${solutionsList.size} solutions to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving solutions to normalized table", e)
            throw e
        }
    }

    /**
     * Parse issues or solutions string.
     * Handles both JSON array format (["Issue 1", "Issue 2"]) and plain text.
     */
    private fun parseIssuesOrSolutions(text: String): List<String> {
        if (text.isBlank()) {
            return emptyList()
        }

        return try {
            // Try to parse as JSON array
            if (text.trim().startsWith("[")) {
                val json = kotlinx.serialization.json.Json
                val jsonElement = json.parseToJsonElement(text)
                if (jsonElement is kotlinx.serialization.json.JsonArray) {
                    jsonElement.map { element ->
                        if (element is kotlinx.serialization.json.JsonPrimitive) {
                            element.content
                        } else {
                            element.toString()
                        }
                    }
                } else {
                    listOf(text.trim())
                }
            } else {
                // Plain text - split by comma or newline, or return as single item
                text.split(Regex("[,;\\n]"))
                    .map { it.trim() }
                    .filter { it.isNotBlank() }
                    .ifEmpty { listOf(text.trim()) } // If no delimiters, return as single item
            }
        } catch (e: Exception) {
            // If parsing fails, treat as plain text
            Log.w(TAG, " Failed to parse as JSON, treating as plain text: $text")
            listOf(text.trim())
        }
    }

    /**
     * Get song tests for a specific serial number.
     */
    suspend fun getSongTestsForSerial(serialNumber: String): List<SongTest> = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, cannot get song tests")
            return@withContext emptyList()
        }

        try {
            val response = client.from("song_tests")
                .select {
                    filter {
                        eq("serial_number", serialNumber)
                    }
                    order("entry_timestamp", Order.DESCENDING)
                }

            val supabaseTests = response.decodeList<SupabaseSongTest>()

            return@withContext supabaseTests.map { supabaseTest ->
                SongTest(
                    testId = supabaseTest.test_id ?: 0,
                    serialNumber = supabaseTest.serial_number,
                    operatorName = supabaseTest.operator_name,
                    entryTimestamp = supabaseTest.entry_timestamp,
                    exitTimestamp = supabaseTest.exit_timestamp,
                    totalTimeSpent = supabaseTest.total_time_spent,
                    songName = supabaseTest.song_name,
                    result = supabaseTest.result,
                    issues = "", // Issues are in normalized table, would need separate query
                    solutions = "", // Solutions are in normalized table, would need separate query
                    remark = supabaseTest.remark ?: "", // Handle null from Supabase
                    timestamp = supabaseTest.timestamp,
                    iterationNumber = supabaseTest.iteration_number,
                    isOverallResult = supabaseTest.is_overall_result,
                    needsSync = false, // Already synced
                    lastSyncTimestamp = System.currentTimeMillis()
                )
            }
        } catch (e: Exception) {
            Log.e(TAG, " Failed to get song tests from Supabase", e)
            return@withContext emptyList()
        }
    }
}

package com.example.fqcapp.supabase

import android.util.Log
import com.example.fqcapp.data.entities.FrequencyTest
import io.github.jan.supabase.postgrest.from
import io.github.jan.supabase.postgrest.query.Columns
import io.github.jan.supabase.postgrest.query.Order
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import kotlinx.serialization.Serializable
import org.json.JSONArray

/**
 * Data transfer object for Supabase frequency_tests table.
 * This matches the structure of the FrequencyTest entity but uses snake_case for Supabase.
 */
@Serializable
data class SupabaseFrequencyTest(
    val test_id: Long? = null, // Auto-generated, nullable for inserts
    val serial_number: String,
    val operator_name: String,
    @Serializable(with = IntOrStringSerializer::class)
    val operator_id: String = "", // Operator ID (unique identifier) - handles Int or String from Supabase
    val entry_timestamp: String, // When user entered/started the frequency test
    val exit_timestamp: String, // When user exited/completed the frequency test
    val total_time_spent: String, // Total time in HH:MM:SS format (e.g., "00:05:30")
    val result: String, // "PASS" or "FAIL"
    val issues: String? = null, // JSON array string (nullable for backward compatibility)
    val solutions: String? = null, // JSON array string (nullable for backward compatibility)
    val remark: String,
    val iteration_number: Int = 1,
    val test_stage: String = "Primary Test", // "Primary Test", "Secondary Test", or "Final Test"
    val timestamp: String = "", // ISO format (kept for backward compatibility)
    val needs_sync: Boolean? = null, // Not needed in Supabase as it's always synced
    val last_sync_timestamp: Long? = null // Not needed in Supabase
)

/**
 * Minimal DTO for inserts (excludes issues/solutions if columns don't exist in schema).
 * This is used when the table doesn't have issues/solutions columns yet.
 */
@Serializable
data class SupabaseFrequencyTestInsert(
    val serial_number: String,
    val operator_name: String,
    @Serializable(with = IntOrStringSerializer::class)
    val operator_id: String = "", // Operator ID (unique identifier) - handles Int or String from Supabase
    val entry_timestamp: String,
    val exit_timestamp: String,
    val total_time_spent: String,
    val result: String,
    val remark: String,
    val iteration_number: Int = 1,
    val test_stage: String = "Primary Test", // "Primary Test", "Secondary Test", or "Final Test"
    val timestamp: String = ""
)

/**
 * Data transfer object for Supabase frequency_test_issues table (normalized).
 */
@Serializable
data class SupabaseFrequencyTestIssue(
    val issue_id: Long? = null, // Auto-generated, nullable for inserts
    val test_id: Long,
    val issue_text: String
)

/**
 * Data transfer object for Supabase frequency_test_solutions table (normalized).
 */
@Serializable
data class SupabaseFrequencyTestSolution(
    val solution_id: Long? = null, // Auto-generated, nullable for inserts
    val test_id: Long,
    val solution_text: String
)

/**
 * Service for saving and retrieving frequency test data from Supabase.
 */
class SupabaseFrequencyTestService {
    private val TAG = "SupabaseFreqTest"
    private val deviceService = SupabaseDeviceService()

    /**
     * Convert FrequencyTest entity to Supabase DTO.
     * Note: issues and solutions are stored as empty strings if not in schema,
     * they will be saved to normalized tables separately.
     */
    private fun toSupabaseFormat(test: FrequencyTest, operatorId: String): SupabaseFrequencyTest {
        return SupabaseFrequencyTest(
            test_id = if (test.testId > 0) test.testId else null, // Only include if already has ID
            serial_number = test.serialNumber,
            operator_name = test.operatorName,
            operator_id = operatorId,
            entry_timestamp = test.entryTimestamp,
            exit_timestamp = test.exitTimestamp,
            total_time_spent = test.totalTimeSpent,
            result = test.result,
            issues = test.issues, // Will be empty string if column doesn't exist
            solutions = test.solutions, // Will be empty string if column doesn't exist
            remark = test.remark,
            iteration_number = test.iterationNumber,
            test_stage = test.testStage,
            timestamp = test.timestamp, // For backward compatibility
            needs_sync = false, // Not relevant for Supabase
            last_sync_timestamp = null // Not relevant for Supabase
        )
    }

    /**
     * Save a frequency test to Supabase.
     * Returns the inserted test ID if successful, null otherwise.
     * 
     * Note: Uses minimal insert DTO (without issues/solutions) to avoid schema errors
     * if those columns don't exist. Issues/solutions will be saved to normalized tables separately.
     * 
     * @param test The frequency test entity
     * @param operatorId The operator ID (unique identifier) - must be provided
     */
    suspend fun saveFrequencyTest(test: FrequencyTest, operatorId: String): Long? = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping save")
            return@withContext null
        }

        try {
            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] UPLOADING FREQUENCY TEST")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Operator: ${test.operatorName}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Entry Timestamp: ${test.entryTimestamp}")
            Log.d(TAG, "   Exit Timestamp: ${test.exitTimestamp}")
            Log.d(TAG, "   Total Time: ${test.totalTimeSpent}")
            Log.d(TAG, "")

            // CRITICAL: Ensure device exists in Supabase before inserting test data
            // This is required because of the foreign key constraint
            Log.d(TAG, " [STEP 1/3] Ensuring device exists in Supabase...")
            val deviceExists = deviceService.ensureDeviceExists(
                serialNumber = test.serialNumber,
                macAddress = null, // MAC address not available in FrequencyTest entity
                color = null
            )

            if (!deviceExists) {
                Log.e(TAG, "    [ERROR] Failed to ensure device exists, cannot save frequency test")
                Log.e(TAG, "")
                return@withContext null
            }
            Log.d(TAG, "    [SUCCESS] Device exists or created successfully")

            // Use minimal insert DTO (excludes issues/solutions to avoid schema errors)
            Log.d(TAG, " [STEP 2/3] Preparing data for upload...")

            // Map result values to database-compatible values
            // Database constraint only allows "PASS" or "FAIL"
            // Map "CAN_BE_PASSED" and "CAN_PASS" to "FAIL" (since they're not a pass)
            // Preserve original result in remark if it's different
            val originalResult = test.result.uppercase()
            val dbResult = when (originalResult) {
                "PASS" -> "PASS"
                "FAIL" -> "FAIL"
                "CAN_BE_PASSED", "CAN_PASS", "CAN_PASSED" -> {
                    Log.d(TAG, "    [MAPPING] Mapping '${test.result}' to 'FAIL' for database constraint")
                    "FAIL"
                }
                "TEST_NOT_DONE" -> {
                    Log.d(TAG, "    [MAPPING] Mapping 'TEST_NOT_DONE' to 'FAIL' for database constraint")
                    "FAIL"
                }
                else -> {
                    Log.w(TAG, "    [MAPPING] Unknown result '${test.result}', mapping to 'FAIL'")
                    "FAIL"
                }
            }

            // Preserve original result in remark if it was mapped
            val remarkWithOriginalResult = if (originalResult != dbResult && !test.remark.contains("Original Result:")) {
                val originalResultNote = "Original Result: ${test.result}"
                if (test.remark.isBlank()) {
                    originalResultNote
                } else {
                    "$originalResultNote | ${test.remark}"
                }
            } else {
                test.remark
            }

            val insertData = SupabaseFrequencyTestInsert(
                serial_number = test.serialNumber,
                operator_name = test.operatorName,
                operator_id = operatorId,
                entry_timestamp = test.entryTimestamp,
                exit_timestamp = test.exitTimestamp,
                total_time_spent = test.totalTimeSpent,
                result = dbResult, // Use mapped result
                remark = remarkWithOriginalResult, // Include original result in remark
                iteration_number = test.iterationNumber,
                timestamp = test.timestamp
            )

            Log.d(TAG, "    [UPLOAD] Data prepared:")
            Log.d(TAG, "      - serial_number: ${insertData.serial_number}")
            Log.d(TAG, "      - operator_name: ${insertData.operator_name}")
            Log.d(TAG, "      - operator_id: ${insertData.operator_id}")
            Log.d(TAG, "      - entry_timestamp: ${insertData.entry_timestamp}")
            Log.d(TAG, "      - exit_timestamp: ${insertData.exit_timestamp}")
            Log.d(TAG, "      - total_time_spent: ${insertData.total_time_spent}")
            Log.d(TAG, "      - result: ${insertData.result}")
            Log.d(TAG, "      - iteration_number: ${insertData.iteration_number}")
            Log.d(TAG, "      - remark: ${insertData.remark}")

            Log.d(TAG, " [STEP 3/3] Inserting into frequency_tests table...")
            Log.d(TAG, "    [QUERY] INSERT INTO frequency_tests (...) VALUES (...)")

            val response = client.from("frequency_tests")
                .insert(insertData) {
                    select(Columns.ALL)
                }

            // Parse the response to get the inserted ID
            val inserted = response.decodeSingle<SupabaseFrequencyTest>()
            val insertedId = inserted.test_id ?: return@withContext null

            Log.d(TAG, "")
            Log.d(TAG, " [SUPABASE] FREQUENCY TEST UPLOADED SUCCESSFULLY")
            Log.d(TAG, "   Test ID: $insertedId")
            Log.d(TAG, "   Serial Number: ${test.serialNumber}")
            Log.d(TAG, "   Iteration: ${test.iterationNumber}")
            Log.d(TAG, "   Result: ${test.result}")
            Log.d(TAG, "")

            // Save issues to normalized table (if any)
            // IMPORTANT: For PASS results, checklist items are NOT saved as issues
            // Only actual problems (for FAIL/CAN_BE_PASSED) should be saved as issues.
            Log.d(TAG, " [NORMALIZED] Checking if issues need to be saved...")
            Log.d(TAG, "   Test result: ${test.result}")
            Log.d(TAG, "   Issues string: '${test.issues}' (length: ${test.issues.length})")
            Log.d(TAG, "   Issues isBlank: ${test.issues.isBlank()}")
            Log.d(TAG, "   Result is PASS: ${test.result.uppercase() == "PASS"}")

            if (test.issues.isNotBlank() && test.result.uppercase() != "PASS") {
                try {
                    Log.d(TAG, " [NORMALIZED] Saving issues to frequency_test_issues table...")
                    Log.d(TAG, "   Test ID: $insertedId")
                    Log.d(TAG, "   Issues JSON: ${test.issues}")
                    saveIssues(insertedId, test.issues)
                    Log.d(TAG, "    Issues saved successfully")
                } catch (e: Exception) {
                    Log.e(TAG, "    [ERROR] Failed to save issues to normalized table")
                    Log.e(TAG, "   Error message: ${e.message}")
                    Log.e(TAG, "   Error type: ${e.javaClass.simpleName}")
                    Log.e(TAG, "   Stack trace: ${e.stackTraceToString()}")
                    // Don't fail the whole operation if issues save fails
                }
            } else if (test.result.uppercase() == "PASS") {
                Log.d(TAG, "    [SKIP] Skipping issues save for PASS result - checklist items are implicit in PASS")
            } else {
                Log.d(TAG, "    [INFO] No issues to save (empty or PASS result)")
            }

            // Save solutions to normalized table (if any)
            Log.d(TAG, " [NORMALIZED] Checking if solutions need to be saved...")
            Log.d(TAG, "   Solutions string: '${test.solutions}' (length: ${test.solutions.length})")
            Log.d(TAG, "   Solutions isBlank: ${test.solutions.isBlank()}")

            if (test.solutions.isNotBlank() && test.result.uppercase() != "PASS") {
                try {
                    Log.d(TAG, " [NORMALIZED] Saving solutions to frequency_test_solutions table...")
                    Log.d(TAG, "   Test ID: $insertedId")
                    Log.d(TAG, "   Solutions JSON: ${test.solutions}")
                    saveSolutions(insertedId, test.solutions)
                    Log.d(TAG, "    Solutions saved successfully")
                } catch (e: Exception) {
                    Log.e(TAG, "    [ERROR] Failed to save solutions to normalized table")
                    Log.e(TAG, "   Error message: ${e.message}")
                    Log.e(TAG, "   Error type: ${e.javaClass.simpleName}")
                    Log.e(TAG, "   Stack trace: ${e.stackTraceToString()}")
                    // Don't fail the whole operation if solutions save fails
                }
            } else if (test.result.uppercase() == "PASS") {
                Log.d(TAG, "    [SKIP] Skipping solutions save for PASS result - checklist items are implicit in PASS")
            } else {
                Log.d(TAG, "    [INFO] No solutions to save (empty or PASS result)")
            }

            return@withContext insertedId

        } catch (e: Exception) {
            Log.e(TAG, " Failed to save frequency test to Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext null
        }
    }

    /**
     * Update an existing frequency test in Supabase.
     */
    suspend fun updateFrequencyTest(test: FrequencyTest): Boolean = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, skipping update")
            return@withContext false
        }

        if (test.testId <= 0) {
            Log.w(TAG, " Test ID is invalid (${test.testId}), cannot update")
            return@withContext false
        }

        try {
            // Note: operatorId is not available in update context, using empty string as fallback
            // This should be fixed by passing operatorId when calling update
            val supabaseTest = toSupabaseFormat(test, "")

            Log.d(TAG, " Updating frequency test in Supabase: ID=${test.testId}, serial=${test.serialNumber}")

            client.from("frequency_tests")
                .update(supabaseTest) {
                    filter {
                        eq("test_id", test.testId)
                    }
                }

            Log.d(TAG, " Successfully updated frequency test in Supabase")
            return@withContext true

        } catch (e: Exception) {
            Log.e(TAG, " Failed to update frequency test in Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext false
        }
    }

    /**
     * Get frequency tests for a specific serial number from Supabase.
     */
    suspend fun getFrequencyTestsForSerial(serialNumber: String): List<FrequencyTest> = withContext(Dispatchers.IO) {
        val client = SupabaseConfig.getClient()
        if (client == null) {
            Log.w(TAG, " Supabase client not configured, returning empty list")
            return@withContext emptyList()
        }

        try {
            val response = client.from("frequency_tests")
                .select {
                    filter {
                        eq("serial_number", serialNumber)
                    }
                    order("entry_timestamp", Order.DESCENDING)
                }

            val supabaseTests = response.decodeList<SupabaseFrequencyTest>()

            // Convert back to FrequencyTest entities
            return@withContext supabaseTests.map { supabaseTest ->
                FrequencyTest(
                    testId = supabaseTest.test_id ?: 0,
                    serialNumber = supabaseTest.serial_number,
                    operatorName = supabaseTest.operator_name,
                    entryTimestamp = supabaseTest.entry_timestamp,
                    exitTimestamp = supabaseTest.exit_timestamp,
                    totalTimeSpent = supabaseTest.total_time_spent,
                    result = supabaseTest.result,
                    issues = supabaseTest.issues ?: "", // Handle nullable issues
                    solutions = supabaseTest.solutions ?: "", // Handle nullable solutions
                    remark = supabaseTest.remark,
                    iterationNumber = supabaseTest.iteration_number,
                    timestamp = supabaseTest.timestamp, // For backward compatibility
                    needsSync = false,
                    lastSyncTimestamp = supabaseTest.last_sync_timestamp ?: 0
                )
            }

        } catch (e: Exception) {
            Log.e(TAG, " Failed to fetch frequency tests from Supabase", e)
            Log.e(TAG, "Error details: ${e.message}")
            e.printStackTrace()
            return@withContext emptyList()
        }
    }

    /**
     * Save issues to normalized frequency_test_issues table.
     */
    private suspend fun saveIssues(testId: Long, issuesString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse issues - could be JSON array or plain text
            val issuesList = parseIssuesOrSolutions(issuesString)

            if (issuesList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val issuesData = issuesList.map { issueText ->
                SupabaseFrequencyTestIssue(
                    test_id = testId,
                    issue_text = issueText
                )
            }

            // Bulk insert all issues at once
            if (issuesData.isNotEmpty()) {
                client.from("frequency_test_issues")
                    .insert(issuesData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${issuesList.size} issues to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving issues to normalized table", e)
            throw e
        }
    }

    /**
     * Save solutions to normalized frequency_test_solutions table.
     */
    private suspend fun saveSolutions(testId: Long, solutionsString: String) {
        val client = SupabaseConfig.getClient() ?: return

        try {
            // Parse solutions - could be JSON array or plain text
            val solutionsList = parseIssuesOrSolutions(solutionsString)

            if (solutionsList.isEmpty()) {
                return
            }

            // Convert to DTOs
            val solutionsData = solutionsList.map { solutionText ->
                SupabaseFrequencyTestSolution(
                    test_id = testId,
                    solution_text = solutionText
                )
            }

            // Bulk insert all solutions at once
            if (solutionsData.isNotEmpty()) {
                client.from("frequency_test_solutions")
                    .insert(solutionsData) {
                        select(Columns.ALL)
                    }
            }

            Log.d(TAG, " Saved ${solutionsList.size} solutions to normalized table for test_id=$testId")

        } catch (e: Exception) {
            Log.e(TAG, " Error saving solutions to normalized table", e)
            throw e
        }
    }

    /**
     * Parse issues or solutions string (JSON array or plain text) into a list of strings.
     */
    private fun parseIssuesOrSolutions(text: String): List<String> {
        if (text.isBlank()) {
            return emptyList()
        }

        return try {
            // Try to parse as JSON array
            if (text.trim().startsWith("[")) {
                val jsonArray = JSONArray(text)
                val result = mutableListOf<String>()
                for (i in 0 until jsonArray.length()) {
                    val item = jsonArray.getString(i)
                    if (item.isNotBlank()) {
                        result.add(item.trim())
                    }
                }
                result
            } else {
                // Plain text - split by comma or newline
                text.split(",", "\n")
                    .map { it.trim() }
                    .filter { it.isNotBlank() }
            }
        } catch (e: Exception) {
            Log.w(TAG, " Failed to parse as JSON, treating as plain text: $text", e)
            // Fallback: treat as plain text
            text.split(",", "\n")
                .map { it.trim() }
                .filter { it.isNotBlank() }
        }
    }
