echo ================================================================================
echo Cursor Chat Recovery Tool
echo ================================================================================
echo.
echo This script will attempt to recover your Cursor chat conversations.
echo.
pause

echo.
echo Step 1: Running Python recovery scripts...
python extract_cursor_chats.py
if errorlevel 1 (
    echo ERROR: Python script failed. Make sure Python is installed.
    pause
    exit /b 1
)

echo.
echo Step 2: Running deep extraction...
python deep_extract_chats.py
if errorlevel 1 (
    echo WARNING: Deep extraction had issues, but continuing...
)

echo.
echo ================================================================================
echo Recovery Complete!
echo ================================================================================
echo.
echo Check the following folder for recovered data:
echo %USERPROFILE%\Desktop\recovered_cursor_chats\
echo.
echo Next steps:
echo 1. Open Cursor and press Alt+Ctrl+' to check history panel
echo 2. Review the recovered files in the folder above
echo 3. Consider installing SpecStory extension to prevent future loss
echo.
pause

