#!/usr/bin/env python3
"""
Export COMPLETE conversations directly from database with ALL content
"""

import sqlite3
import json
import os
from pathlib import Path
from datetime import datetime
import re

def extract_full_text_from_richtext(rich_text):
    """Extract complete text from richText"""
    text_parts = []

    if isinstance(rich_text, str):
        try:
            rich_text = json.loads(rich_text)
        except:
            return rich_text

    if isinstance(rich_text, dict):
        if 'root' in rich_text:
            root = rich_text['root']
            if isinstance(root, dict) and 'children' in root:
                for child in root['children']:
                    text = extract_text_from_node(child)
                    if text:
                        text_parts.append(text)

    return '\n'.join(text_parts).strip()

def extract_text_from_node(node):
    """Recursively extract text"""
    if isinstance(node, dict):
        if 'text' in node:
            return node['text']
        if 'children' in node:
            parts = []
            for child in node['children']:
                text = extract_text_from_node(child)
                if text:
                    parts.append(text)
            return '\n'.join(parts)
    elif isinstance(node, str):
        return node
    return ''

def format_complete_message(bubble_data, bubble_id, idx):
    """Format complete message with ALL content"""
    output = []

    msg_type = bubble_data.get('type', 0)
    role = "ðŸ‘¤ USER" if msg_type == 1 else "ðŸ¤– ASSISTANT" if msg_type == 2 else "â“ UNKNOWN"

    output.append(f"\n{'='*100}")
    output.append(f"[{idx}] {role} - Bubble ID: {bubble_id}")
    output.append(f"{'='*100}\n")

    # Extract text
    text_content = None

    # Try text field
    if 'text' in bubble_data and bubble_data['text']:
        text_content = str(bubble_data['text']).strip()

    # Try richText
    if not text_content and 'richText' in bubble_data and bubble_data['richText']:
        text_content = extract_full_text_from_richtext(bubble_data['richText'])

    # Display text
    if text_content:
        output.append("ðŸ“ MESSAGE:")
        output.append("-" * 100)
        output.append(text_content)
        output.append("")

    # Code blocks
    code_blocks = []
    if 'codeBlocks' in bubble_data and bubble_data['codeBlocks']:
        code_blocks.extend(bubble_data['codeBlocks'])
    if 'suggestedCodeBlocks' in bubble_data and bubble_data['suggestedCodeBlocks']:
        code_blocks.extend(bubble_data['suggestedCodeBlocks'])

    if code_blocks:
        output.append("ðŸ’» CODE BLOCKS:")
        output.append("-" * 100)
        for i, block in enumerate(code_blocks, 1):
            if isinstance(block, dict):
                lang = block.get('language', block.get('lang', ''))
                code = block.get('code', block.get('text', ''))
                file_path = block.get('file', block.get('filePath', ''))

                output.append(f"\n[Code Block {i}]")
                if file_path:
                    output.append(f"File: {file_path}")
                if lang:
                    output.append(f"Language: {lang}")
                if code:
                    output.append(f"\n```{lang}")
                    output.append(code)
                    output.append("```")
                output.append("")

    # Tool results
    if 'toolResults' in bubble_data and bubble_data['toolResults']:
        output.append("ðŸ”§ TOOL RESULTS:")
        output.append("-" * 100)
        for i, result in enumerate(bubble_data['toolResults'], 1):
            if isinstance(result, dict):
                tool_name = result.get('toolName', result.get('name', 'Unknown'))
                content = result.get('content', result.get('result', ''))
                output.append(f"\n[Tool {i}] {tool_name}")
                if content:
                    if isinstance(content, dict):
                        output.append(json.dumps(content, indent=2, ensure_ascii=False))
                    else:
                        output.append(str(content))
                output.append("")

    # File changes/diffs
    diffs = []
    if 'assistantSuggestedDiffs' in bubble_data:
        diffs.extend(bubble_data['assistantSuggestedDiffs'])
    if 'gitDiffs' in bubble_data:
        diffs.extend(bubble_data['gitDiffs'])
    if 'diffsForCompressingFiles' in bubble_data:
        diffs.extend(bubble_data['diffsForCompressingFiles'])

    if diffs:
        output.append("ðŸ“„ FILE CHANGES:")
        output.append("-" * 100)
        for i, diff in enumerate(diffs, 1):
            if isinstance(diff, dict):
                file_path = diff.get('filePath', diff.get('uri', {}).get('fsPath', 'Unknown'))
                diff_text = diff.get('diff', diff.get('content', ''))
                output.append(f"\n[Change {i}] {file_path}")
                if diff_text:
                    output.append(diff_text)
                else:
                    output.append(json.dumps(diff, indent=2, ensure_ascii=False)[:2000])
                output.append("")

    # Thinking blocks
    if 'thinking' in bubble_data and bubble_data['thinking']:
        output.append("ðŸ§  THINKING:")
        output.append("-" * 100)
        thinking = bubble_data['thinking']
        if isinstance(thinking, str):
            output.append(thinking)
        elif isinstance(thinking, list):
            for thought in thinking:
                output.append(str(thought))
        else:
            output.append(json.dumps(thinking, indent=2, ensure_ascii=False))
        output.append("")

    if 'allThinkingBlocks' in bubble_data and bubble_data['allThinkingBlocks']:
        output.append("ðŸ§  ALL THINKING BLOCKS:")
        output.append("-" * 100)
        for i, block in enumerate(bubble_data['allThinkingBlocks'], 1):
            output.append(f"\n[Thinking Block {i}]")
            if isinstance(block, dict):
                output.append(json.dumps(block, indent=2, ensure_ascii=False))
            else:
                output.append(str(block))
            output.append("")

    # Context information
    context_info = []
    if 'codebaseContextChunks' in bubble_data and bubble_data['codebaseContextChunks']:
        context_info.append(f"Codebase Context: {len(bubble_data['codebaseContextChunks'])} chunks")
    if 'attachedCodeChunks' in bubble_data and bubble_data['attachedCodeChunks']:
        context_info.append(f"Attached Code: {len(bubble_data['attachedCodeChunks'])} chunks")
    if 'relevantFiles' in bubble_data and bubble_data['relevantFiles']:
        context_info.append(f"Relevant Files: {len(bubble_data['relevantFiles'])} files")

    if context_info:
        output.append("ðŸ“š CONTEXT:")
        output.append("-" * 100)
        output.append(", ".join(context_info))
        output.append("")

    # If no content found, show what's available
    if not text_content and not code_blocks and not diffs:
        output.append("âš ï¸  NO READABLE CONTENT FOUND")
        output.append("Available fields:")
        for key in sorted(bubble_data.keys()):
            if bubble_data[key]:
                output.append(f"  - {key}")
        output.append("\n(Full JSON available in separate file)")

    return "\n".join(output)

def export_complete_conversation(composer_id, db_path, output_dir):
    """Export complete conversation directly from database"""
    output_path = Path(output_dir)
    output_path.mkdir(exist_ok=True)

    try:
        conn = sqlite3.connect(str(db_path))
        conn.row_factory = sqlite3.Row
        cursor = conn.cursor()

        # Get composer data
        cursor.execute("SELECT value FROM cursorDiskKV WHERE key = ?", (f"composerData:{composer_id}",))
        composer_row = cursor.fetchone()

        composer_info = {}
        if composer_row:
            value = composer_row['value']
            if isinstance(value, bytes):
                value_str = value.decode('utf-8')
            else:
                value_str = str(value)
            composer_info = json.loads(value_str)

        # Get ALL bubbles
        bubble_prefix = f"bubbleId:{composer_id}:"
        cursor.execute("SELECT key, value FROM cursorDiskKV WHERE key LIKE ?", (f"{bubble_prefix}%",))
        rows = cursor.fetchall()

        bubbles = []
        for row in rows:
            key = row['key']
            value = row['value']

            try:
                if isinstance(value, bytes):
                    value_str = value.decode('utf-8')
                else:
                    value_str = str(value)

                bubble_data = json.loads(value_str)
                bubble_id = key.replace(bubble_prefix, '')

                bubbles.append({
                    'bubble_id': bubble_id,
                    'data': bubble_data
                })
            except Exception as e:
                print(f"    Error parsing bubble {key}: {e}")

        conn.close()

        # Sort by timestamp
        def get_ts(b):
            d = b['data']
            for field in ['createdAt', 'timestamp']:
                if field in d:
                    val = d[field]
                    if isinstance(val, (int, float)):
                        return val
            return 0

        bubbles.sort(key=get_ts)

        # Format complete export
        output = []
        output.append("=" * 100)
        output.append("COMPLETE CONVERSATION EXPORT - ALL CONTENT INCLUDED")
        output.append("=" * 100)
        output.append(f"\nComposer ID: {composer_id}")

        if composer_info.get('title'):
            output.append(f"Title: {composer_info['title']}")

        if composer_info.get('createdAt'):
            ts = composer_info['createdAt']
            if isinstance(ts, (int, float)):
                dt = datetime.fromtimestamp(ts / 1000)
                output.append(f"Created: {dt.strftime('%Y-%m-%d %H:%M:%S')}")

        output.append(f"Total Messages: {len(bubbles)}")
        output.append("\n" + "=" * 100)

        # Format each message
        for idx, bubble in enumerate(bubbles, 1):
            formatted = format_complete_message(bubble['data'], bubble['bubble_id'], idx)
            output.append(formatted)

        # Save text file
        safe_id = composer_id[:20]
        txt_file = output_path / f"COMPLETE_EXPORT_{safe_id}.txt"
        with open(txt_file, 'w', encoding='utf-8') as f:
            f.write("\n".join(output))

        # Save JSON file with all data
