@echo off
echo ================================================================================
echo VERIFYING AUTOMATIC BACKUP SYSTEM
echo ================================================================================
echo.

set BACKUP_DIR=%USERPROFILE%\Desktop\cursor-chat-backups
set ERROR_COUNT=0

echo [1/6] Checking backup directory...
if exist "%BACKUP_DIR%" (
    echo    âœ“ Backup directory exists: %BACKUP_DIR%
) else (
    echo    âœ— Backup directory NOT found!
    set /a ERROR_COUNT+=1
)

echo.
echo [2/6] Checking Git repository...
cd /d "%BACKUP_DIR%" 2>nul
if exist ".git" (
    echo    âœ“ Git repository initialized
    git status >nul 2>&1
    if errorlevel 1 (
        echo    âš  Git not working properly
        set /a ERROR_COUNT+=1
    ) else (
        echo    âœ“ Git is working
    )

    echo.
    echo    Checking Git remote...
    git remote -v >nul 2>&1
    if errorlevel 1 (
        echo    âš  No Git remote configured (GitHub backup not set up)
    ) else (
        git remote -v
        echo    âœ“ Git remote configured
    )
) else (
    echo    âœ— Git repository NOT initialized!
    set /a ERROR_COUNT+=1
)

echo.
echo [3/6] Checking auto-backup script...
if exist "%BACKUP_DIR%\auto-backup.bat" (
    echo    âœ“ Auto-backup script exists
) else (
    echo    âœ— Auto-backup script NOT found!
    set /a ERROR_COUNT+=1
)

echo.
echo [4/6] Checking scheduled task...
schtasks /Query /TN "CursorChatAutoBackup" >nul 2>&1
if errorlevel 1 (
    echo    âœ— Scheduled task NOT found!
    echo    âš  Auto-backup will NOT run automatically
    set /a ERROR_COUNT+=1
) else (
    echo    âœ“ Scheduled task exists
    echo.
    echo    Task details:
    schtasks /Query /TN "CursorChatAutoBackup" /FO LIST /V | findstr /C:"Status" /C:"Next Run Time" /C:"Task To Run"
)

echo.
echo [5/6] Checking SpecStory folder...
cd /d "%CD%" 2>nul
if exist ".specstory" (
    echo    âœ“ SpecStory folder found in current project
    echo    âœ“ SpecStory is saving conversations
    dir /B ".specstory\conversations" 2>nul | find /C /V "" >nul
    if errorlevel 1 (
        echo    âš  No conversations saved yet (this is OK if you just installed)
    ) else (
        echo    âœ“ Conversations found in SpecStory folder
    )
) else (
    echo    âš  SpecStory folder NOT found in current directory
    echo    âš  Make sure SpecStory extension is installed in Cursor
    echo    âš  Current directory: %CD%
)

echo.
echo [6/6] Testing backup script...
if exist "%BACKUP_DIR%\auto-backup.bat" (
    echo    Running test backup...
    call "%BACKUP_DIR%\auto-backup.bat"
    if errorlevel 1 (
        echo    âš  Backup script had errors (check output above)
    ) else (
        echo    âœ“ Backup script ran successfully
    )

    echo.
    echo    Checking if files were backed up...
    if exist "%BACKUP_DIR%\conversations" (
        echo    âœ“ Conversations folder exists
        dir /B "%BACKUP_DIR%\conversations" 2>nul | find /C /V "" >nul
        if errorlevel 1 (
            echo    âš  No conversations backed up yet
        ) else (
            echo    âœ“ Conversations found in backup
        )
    )

    if exist "%BACKUP_DIR%\databases" (
        echo    âœ“ Databases folder exists
        if exist "%BACKUP_DIR%\databases\state.vscdb" (
            echo    âœ“ Cursor database backed up
        )
    )
) else (
    echo    âœ— Cannot test - backup script not found
    set /a ERROR_COUNT+=1
)

echo.
echo ================================================================================
echo VERIFICATION SUMMARY
echo ================================================================================
echo.

if %ERROR_COUNT% EQU 0 (
    echo âœ“ ALL CHECKS PASSED!
    echo.
    echo Your automatic backup system is working correctly.
    echo.
    echo What's happening:
    echo   - SpecStory saves conversations automatically
    echo   - Auto-backup runs every 5 minutes
    echo   - Backups are saved to: %BACKUP_DIR%
    echo   - Git commits changes automatically
    echo.
    echo Next: Have a conversation in Cursor, wait 5 minutes, then check:
    echo   %BACKUP_DIR%\conversations
) else (
    echo âš  FOUND %ERROR_COUNT% ISSUE(S)
    echo.
    echo Please review the errors above and fix them.
    echo.
    echo Common fixes:
    echo   1. Run QUICK_SETUP_AUTO_BACKUP.bat again
    echo   2. Install SpecStory extension in Cursor
    echo   3. Set up Git remote (see START_HERE_AUTO_BACKUP.md)
)

echo.
pause

