# Complete verification of backup system
Write-Host "================================================================================" -ForegroundColor Cyan
Write-Host "COMPLETE BACKUP SYSTEM VERIFICATION" -ForegroundColor Cyan
Write-Host "================================================================================" -ForegroundColor Cyan
Write-Host ""

$backupDir = "$env:USERPROFILE\Desktop\cursor-chat-backups"
$errors = @()
$warnings = @()

# Check 1: Backup directory
Write-Host "[1/7] Checking backup directory..." -ForegroundColor Yellow
if (Test-Path $backupDir) {
    Write-Host "   âœ“ EXISTS: $backupDir" -ForegroundColor Green
} else {
    Write-Host "   âœ— NOT FOUND" -ForegroundColor Red
    $errors += "Backup directory not found"
}

# Check 2: Git repository
Write-Host ""
Write-Host "[2/7] Checking Git repository..." -ForegroundColor Yellow
Push-Location $backupDir -ErrorAction SilentlyContinue
if ($?) {
    if (Test-Path ".git") {
        Write-Host "   âœ“ Git initialized" -ForegroundColor Green

        # Check Git is working
        $gitStatus = git status 2>&1
        if ($LASTEXITCODE -eq 0) {
            Write-Host "   âœ“ Git is working" -ForegroundColor Green
        } else {
            Write-Host "   âœ— Git not working properly" -ForegroundColor Red
            $errors += "Git not working"
        }

        # Check remote
        Write-Host ""
        Write-Host "   Git remote status:" -ForegroundColor Cyan
        $remote = git remote -v 2>&1
        if ($remote -and $remote -notmatch "fatal") {
            Write-Host "   âœ“ Remote configured:" -ForegroundColor Green
            $remote | ForEach-Object { Write-Host "      $_" -ForegroundColor Gray }
        } else {
            Write-Host "   âš  No remote configured" -ForegroundColor Yellow
            $warnings += "GitHub remote not configured (optional but recommended)"
        }
    } else {
        Write-Host "   âœ— Git NOT initialized" -ForegroundColor Red
        $errors += "Git repository not initialized"
    }
} else {
    Write-Host "   âœ— Cannot access backup directory" -ForegroundColor Red
    $errors += "Cannot access backup directory"
}

Pop-Location

# Check 3: Auto-backup script
Write-Host ""
Write-Host "[3/7] Checking auto-backup script..." -ForegroundColor Yellow
$backupScript = "$backupDir\auto-backup.bat"
if (Test-Path $backupScript) {
    Write-Host "   âœ“ EXISTS: $backupScript" -ForegroundColor Green
} else {
    Write-Host "   âœ— NOT FOUND" -ForegroundColor Red
    $errors += "Auto-backup script not found"
}

# Check 4: Scheduled task
Write-Host ""
Write-Host "[4/7] Checking scheduled task..." -ForegroundColor Yellow
$task = Get-ScheduledTask -TaskName "CursorChatAutoBackup" -ErrorAction SilentlyContinue
if ($task) {
    Write-Host "   âœ“ EXISTS and ACTIVE" -ForegroundColor Green
    Write-Host ""
    Write-Host "   Task details:" -ForegroundColor Cyan
    $taskInfo = Get-ScheduledTaskInfo -TaskName "CursorChatAutoBackup"
    Write-Host "      Status: $($task.State)" -ForegroundColor Gray
    Write-Host "      Last Run: $($taskInfo.LastRunTime)" -ForegroundColor Gray
    Write-Host "      Next Run: $($taskInfo.NextRunTime)" -ForegroundColor Gray
} else {
    Write-Host "   âœ— NOT FOUND" -ForegroundColor Red
    Write-Host "   âš  Auto-backup will NOT run automatically" -ForegroundColor Yellow
    $errors += "Scheduled task not found"
    $warnings += "Need to create scheduled task (requires admin)"
}

# Check 5: SpecStory folders
Write-Host ""
Write-Host "[5/7] Checking SpecStory folders..." -ForegroundColor Yellow
$specstoryFound = $false
$desktop = "$env:USERPROFILE\Desktop"
if (Test-Path $desktop) {
    $folders = Get-ChildItem -Path $desktop -Directory -ErrorAction SilentlyContinue
    foreach ($folder in $folders) {
        $specstoryPath = Join-Path $folder.FullName ".specstory"
        if (Test-Path $specstoryPath) {
            Write-Host "   âœ“ Found: $($folder.Name)\.specstory" -ForegroundColor Green
            $specstoryFound = $true

            # Count conversations
            $convPath = Join-Path $specstoryPath "conversations"
            if (Test-Path $convPath) {
                $convCount = (Get-ChildItem -Path $convPath -File -ErrorAction SilentlyContinue).Count
                Write-Host "      Conversations: $convCount" -ForegroundColor Gray
            }
        }
    }
}
if (-not $specstoryFound) {
    Write-Host "   âš  No SpecStory folders found" -ForegroundColor Yellow
    Write-Host "   âš  Make sure SpecStory extension is installed in Cursor" -ForegroundColor Yellow
    $warnings += "SpecStory not found - install extension in Cursor"
}

# Check 6: Test backup script
Write-Host ""
Write-Host "[6/7] Testing backup script..." -ForegroundColor Yellow
if (Test-Path $backupScript) {
    Write-Host "   Running test backup..." -ForegroundColor Cyan
    Push-Location $backupDir
    & cmd /c $backupScript
    if ($LASTEXITCODE -eq 0) {
        Write-Host "   âœ“ Backup script ran successfully" -ForegroundColor Green
    } else {
        Write-Host "   âš  Backup script had errors" -ForegroundColor Yellow
        $warnings += "Backup script had errors"
    }
    Pop-Location

    # Check backup results
    Write-Host ""
    Write-Host "   Checking backup results..." -ForegroundColor Cyan
    $convDir = "$backupDir\conversations"
    if (Test-Path $convDir) {
        $convFolders = (Get-ChildItem -Path $convDir -Directory -ErrorAction SilentlyContinue).Count
        Write-Host "   âœ“ Conversations folder exists ($convFolders project(s))" -ForegroundColor Green
    } else {
        Write-Host "   âš  No conversations backed up yet" -ForegroundColor Yellow
    }

    $dbDir = "$backupDir\databases"
    if (Test-Path "$dbDir\state.vscdb") {
        Write-Host "   âœ“ Cursor database backed up" -ForegroundColor Green
    } else {
        Write-Host "   âš  Database not backed up yet" -ForegroundColor Yellow
    }
} else {
    Write-Host "   âœ— Cannot test - script not found" -ForegroundColor Red
}

# Check 7: Git status
Write-Host ""
Write-Host "[7/7] Checking Git repository status..." -ForegroundColor Yellow
Push-Location $backupDir -ErrorAction SilentlyContinue
if ($?) {
    $gitStatus = git status --short 2>&1
    if ($LASTEXITCODE -eq 0) {
        if ($gitStatus) {
            Write-Host "   âœ“ Changes detected (ready to commit)" -ForegroundColor Green
            Write-Host "      $gitStatus" -ForegroundColor Gray
        } else {
            Write-Host "   âœ“ Repository is clean" -ForegroundColor Green
        }
    } else {
        Write-Host "   âš  Git status check failed" -ForegroundColor Yellow
    }
}
Pop-Location

# Summary
Write-Host ""
Write-Host "================================================================================" -ForegroundColor Cyan
Write-Host "VERIFICATION SUMMARY" -ForegroundColor Cyan
Write-Host "================================================================================" -ForegroundColor Cyan
Write-Host ""

if ($errors.Count -eq 0 -and $warnings.Count -eq 0) {
    Write-Host "âœ“ ALL SYSTEMS OPERATIONAL!" -ForegroundColor Green
    Write-Host ""
    Write-Host "Your automatic backup system is working correctly:" -ForegroundColor Green
    Write-Host "  âœ“ Backup directory exists" -ForegroundColor Green
    Write-Host "  âœ“ Git repository initialized" -ForegroundColor Green
    Write-Host "  âœ“ Auto-backup script ready" -ForegroundColor Green
    Write-Host "  âœ“ Scheduled task active" -ForegroundColor Green
    Write-Host ""
    Write-Host "Backups will run automatically every 5 minutes!" -ForegroundColor Cyan
} else {
    if ($errors.Count -gt 0) {
        Write-Host "âœ— ERRORS FOUND ($($errors.Count)):" -ForegroundColor Red
        foreach ($error in $errors) {
            Write-Host "   - $error" -ForegroundColor Red
        }
        Write-Host ""
    }

    if ($warnings.Count -gt 0) {
        Write-Host "âš  WARNINGS ($($warnings.Count)):" -ForegroundColor Yellow
        foreach ($warning in $warnings) {
            Write-Host "   - $warning" -ForegroundColor Yellow
        }
        Write-Host ""
    }

    Write-Host "ACTION REQUIRED:" -ForegroundColor Yellow
    Write-Host "  1. Fix the errors above" -ForegroundColor White
    Write-Host "  2. Run QUICK_SETUP_AUTO_BACKUP.bat again if needed" -ForegroundColor White
    Write-Host "  3. Install SpecStory extension in Cursor" -ForegroundColor White
}

Write-Host ""
Write-Host "Backup location: $backupDir" -ForegroundColor Cyan
Write-Host ""

