if (Test-Path $backupScript) {
    Write-Host "   Running test backup..." -ForegroundColor Cyan
    Push-Location $backupDir
    & cmd /c $backupScript
    if ($LASTEXITCODE -eq 0) {
        Write-Host "   âœ“ Backup script ran successfully" -ForegroundColor Green
    } else {
        Write-Host "   âš  Backup script had errors" -ForegroundColor Yellow
        $warnings += "Backup script had errors"
    }
    Pop-Location
    
    # Check backup results
    Write-Host ""
    Write-Host "   Checking backup results..." -ForegroundColor Cyan
    $convDir = "$backupDir\conversations"
    if (Test-Path $convDir) {
        $convFolders = (Get-ChildItem -Path $convDir -Directory -ErrorAction SilentlyContinue).Count
        Write-Host "   âœ“ Conversations folder exists ($convFolders project(s))" -ForegroundColor Green
    } else {
        Write-Host "   âš  No conversations backed up yet" -ForegroundColor Yellow
    }
    
    $dbDir = "$backupDir\databases"
    if (Test-Path "$dbDir\state.vscdb") {
        Write-Host "   âœ“ Cursor database backed up" -ForegroundColor Green
    } else {
        Write-Host "   âš  Database not backed up yet" -ForegroundColor Yellow
    }
} else {
    Write-Host "   âœ— Cannot test - script not found" -ForegroundColor Red
}

# Check 7: Git status
Write-Host ""
Write-Host "[7/7] Checking Git repository status..." -ForegroundColor Yellow
Push-Location $backupDir -ErrorAction SilentlyContinue
if ($?) {
    $gitStatus = git status --short 2>&1
    if ($LASTEXITCODE -eq 0) {
        if ($gitStatus) {
            Write-Host "   âœ“ Changes detected (ready to commit)" -ForegroundColor Green
            Write-Host "      $gitStatus" -ForegroundColor Gray
        } else {
            Write-Host "   âœ“ Repository is clean" -ForegroundColor Green
        }
    } else {
        Write-Host "   âš  Git status check failed" -ForegroundColor Yellow
    }
}
Pop-Location

# Summary
Write-Host ""
Write-Host "================================================================================" -ForegroundColor Cyan
Write-Host "VERIFICATION SUMMARY" -ForegroundColor Cyan
Write-Host "================================================================================" -ForegroundColor Cyan
Write-Host ""

if ($errors.Count -eq 0 -and $warnings.Count -eq 0) {
    Write-Host "âœ“ ALL SYSTEMS OPERATIONAL!" -ForegroundColor Green
    Write-Host ""
    Write-Host "Your automatic backup system is working correctly:" -ForegroundColor Green
    Write-Host "  âœ“ Backup directory exists" -ForegroundColor Green
    Write-Host "  âœ“ Git repository initialized" -ForegroundColor Green
    Write-Host "  âœ“ Auto-backup script ready" -ForegroundColor Green
    Write-Host "  âœ“ Scheduled task active" -ForegroundColor Green
    Write-Host ""
    Write-Host "Backups will run automatically every 5 minutes!" -ForegroundColor Cyan
} else {
